INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (33, 15, '19', 'Articolul 19', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Ob╚¢inerea aprob─ârii de dezvoltare, a autoriza╚¢iei de ├«nfiin╚¢are  ╚Öi a licen╚¢ei de producere, ├«n vederea construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, NULL, '(1)...Autoriza╚¢ia de ├«nfiin╚¢are a capacit─â╚¢ii energetice se emite de c─âtre ANRE, ├«n conformitate cu prevederile Legii nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioare, precum ╚Öi ale reglement─ârilor specifice aprobate de c─âtre ANRE....(2)...P├ón─â cel mai t├órziu la data de 31 decembrie 2024, ANRE are obliga╚¢ia s─â emit─â reglement─ârile privind acordarea autoriza╚¢iilor de ├«nfiin╚¢are a centralelor electrice eoliene offshore.', NULL, 19, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (103, 16, '36', 'Articolul 36', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile operatorului de transport ╚Öi de sistem...(1)...Operatorul de transport ╚Öi de sistem presteaz─â serviciul public de transport pentru to╚¢i utilizatorii re╚¢elelor electrice de transport, ├«n condi╚¢ii nediscriminatorii, asigur├ónd accesul la acestea oric─ârui solicitant ├«n condi╚¢iile legii ╚Öi evit├ónd ├«n special discriminarea ├«n favoarea operatorilor economici afilia╚¢i, cu respectarea normelor ╚Öi performan╚¢elor prev─âzute ├«n reglement─ârile tehnice ├«n vigoare....(2)...Operatorul de transport ╚Öi de sistem poate s─â participe la tranzac╚¢ionarea energiei electrice numai ├«n urm─âtoarele situa╚¢ii:a)pentru acoperirea consumului ├«n re╚¢elele electrice proprii ╚Öi ├«n locurile de consum care ├«i apar╚¢in;...b)pentru men╚¢inerea echilibrului produc╚¢ie-consum, prin opera╚¢iuni de v├ónzare-cump─ârare ├«n pia╚¢a de echilibrare sau prin opera╚¢iuni de v├ónzare-cump─ârare cu al╚¢i operatori de transport din ╚¢─ârile vecine, conform reglement─ârilor ├«n vigoare ╚Öi normelor ENTSO-E.......(3)...Operatorul de transport ╚Öi de sistem poate efectua opera╚¢iuni de v├ónzare-cump─ârare de servicii tehnologice de sistem cu operatorii de transport ╚Öi de sistem ai ╚¢─ârilor cu care Rom├ónia a ├«ncheiat acorduri care faciliteaz─â crearea ╚Öi dezvoltarea pie╚¢elor regionale, cu produc─âtorii sau clien╚¢ii, conform reglement─ârilor emise de autoritatea competent─â....(4)...Tranzac╚¢iile cu energie electric─â ╚Öi servicii tehnologice de sistem se realizeaz─â pe baza unor proceduri transparente ╚Öi nediscriminatorii, prin mecanisme concuren╚¢iale, conform reglement─ârilor autorit─â╚¢ii competente....(5)...Operatorul de transport ╚Öi de sistem presteaz─â serviciul de sistem ├«n condi╚¢ii nediscriminatorii pentru to╚¢i utilizatorii SEN, asigur├ónd conducerea operativ─â, ├«n scopul realiz─ârii siguran╚¢ei func╚¢ion─ârii, stabilit─â╚¢ii frecven╚¢ei ╚Öi tensiunii, continuit─â╚¢ii ├«n alimentarea clien╚¢ilor ╚Öi coordon─ârii schimburilor de energie electric─â cu alte sisteme electroenergetice....(6)...Operatorul de transport ╚Öi de sistem poate de╚¢ine ac╚¢iuni la societ─â╚¢i comerciale ├«nfiin╚¢ate pe teritoriul na╚¢ional sau al altor state, av├ónd ca obiect principal de activitate alocarea capacit─â╚¢ilor de interconexiune ╚Öi verificarea siguran╚¢ei re╚¢elei la nivel regional, acoperind teritoriul a dou─â sau mai multe state....(7)...Operatorul de transport ╚Öi de sistem desf─â╚Öoar─â, ├«n principal, urm─âtoarele activit─â╚¢i:a)asigur─â capacitatea pe termen lung a re╚¢elei de transport de a satisface cererile rezonabile de transport de energie electric─â ╚Öi exploateaz─â, ├«ntre╚¢ine, reabiliteaz─â ╚Öi dezvolt─â ├«n condi╚¢ii economice re╚¢eaua de transport pentru a-i asigura siguran╚¢a, fiabilitatea ╚Öi eficien╚¢a, cu respectarea normelor privind protec╚¢ia mediului;...b)garanteaz─â mijloacele adecvate pentru ├«ndeplinirea obliga╚¢iilor de serviciu public;...c)contribuie la realizarea siguran╚¢ei ├«n alimentarea cu energie electric─â, prin asigurarea unor capacit─â╚¢i de transport adecvate ╚Öi prin men╚¢inerea fiabilit─â╚¢ii acestora;...d)asigur─â gestionarea fluxurilor de energie ├«n re╚¢eaua de transport, ╚¢in├ónd seama de schimburile de energie cu alte sisteme interconectate;...e)achizi╚¢ioneaz─â serviciile tehnologice de sistem ╚Öi calific─â produc─âtorii ╚Öi clien╚¢ii dispecerizabili care furnizeaz─â servicii tehnologice de sistem, pe baza unei proceduri proprii, aprobat─â de autoritatea competent─â;...f)realizeaz─â schimbul de informa╚¢ii cu al╚¢i operatori de transport ╚Öi de sistem interconecta╚¢i ╚Öi cu al╚¢i operatori economici din sectorul energiei, cu respectarea reglement─ârilor ENTSO-E privind protocoalele de schimb de informa╚¢ii, rapoartele, structura ╚Öi procedurile de acces la bazele de date;...g)asigur─â alocarea capacit─â╚¢ilor de interconexiune, colecteaz─â venituri rezultate din gestionarea congestiilor ╚Öi efectueaz─â pl─â╚¢i ├«n temeiul mecanismului de compensare ├«ntre operatorii de transport ╚Öi de sistem ├«n conformitate cu art. 13 din Regulamentul (CE) nr. 714/2009, acord├ónd ╚Öi gestion├ónd accesul ter╚¢ilor ╚Öi oferind explica╚¢ii motivate atunci c├ónd refuz─â un astfel de acces, sub supravegherea ANRE;...h)exploateaz─â, ├«ntre╚¢ine ╚Öi dezvolt─â instala╚¢iile de m─âsurare ╚Öi contorizare a transferului de energie electric─â prin re╚¢elele electrice de transport ╚Öi la interfa╚¢a cu utilizatorii re╚¢elelor electrice de transport care ├«i apar╚¢in, instala╚¢iile de informatic─â ╚Öi telecomunica╚¢ii din re╚¢elele electrice de transport aferente SEN;...i)analizeaz─â ╚Öi avizeaz─â ├«ndeplinirea condi╚¢iilor tehnice de racordare de c─âtre utilizatorii re╚¢elelor electrice de transport, ├«n conformitate cu prevederile reglement─ârilor tehnice ├«n vigoare;...j)asigur─â transmiterea rezultatelor m─âsur─ârilor de energie electric─â la operatorul pie╚¢ei de energie electric─â ├«n vederea realiz─ârii decont─ârii tranzac╚¢iilor din pia╚¢a de echilibrare ╚Öi a dezechilibrelor p─âr╚¢ilor responsabile cu echilibrarea, precum ╚Öi accesul beneficiarilor serviciului de transport pentru verificarea grupurilor de m─âsurare;...k)realizeaz─â planificarea opera╚¢ional─â ╚Öi conducerea operativ─â a SEN la nivel central ╚Öi teritorial pe baza prognozei proprii, conform reglement─ârilor legale ├«n vigoare, acord├ónd prioritate instala╚¢iilor de producere care utilizeaz─â surse regenerabile de energie sau care produc energie electric─â ├«n cogenerare de ├«nalt─â eficien╚¢─â, ├«n m─âsura ├«n care func╚¢ionarea sigur─â a re╚¢elei na╚¢ionale de energie electric─â permite acest lucru;...l)autorizeaz─â personalul care realizeaz─â conducerea operativ─â, conform reglement─ârilor ├«n vigoare;...m)culege, ├«nregistreaz─â ╚Öi arhiveaz─â datele statistice privind func╚¢ionarea SEN;...n)elaboreaz─â ╚Öi supune aprob─ârii autorit─â╚¢ii competente normele tehnice ╚Öi reglement─ârile specifice necesare pentru realizarea activit─â╚¢ii de conducere operativ─â, cu consultarea participan╚¢ilor la pia╚¢a de energie electric─â;...o)elaboreaz─â, ├«n condi╚¢iile legii, planul de ap─ârare a SEN ├«mpotriva perturba╚¢iilor majore;...p)elaboreaz─â studii, programe ╚Öi lucr─âri privind dezvoltarea SEN;...q)elaboreaz─â ╚Öi supune aprob─ârii autorit─â╚¢ii competente regulile privind managementul congestiilor, inclusiv pe capacit─â╚¢ile de interconexiune, precum ╚Öi normele de atribuire a capacit─â╚¢ilor de interconexiune;...r)organizeaz─â ╚Öi administreaz─â pia╚¢a de echilibrare a energiei electrice.......(8)...├Än caz de avarie major─â care pericliteaz─â siguran╚¢a func╚¢ion─ârii SEN, operatorul de transport ╚Öi de sistem poate dispune, pe o perioad─â limitat─â, folosirea rezervei de ap─â ├«n afara programelor de exploatare a acumul─ârilor, cu obliga╚¢ia anun╚¢─ârii autorit─â╚¢ilor competente ├«n gospod─ârirea apelor....(9)...Restric╚¢iile ├«n aprovizionarea cu energie electric─â ├«n situa╚¢ii de urgen╚¢─â trebuie s─â respecte criterii predefinite ├«n ceea ce prive╚Öte gestionarea dezechilibrelor de c─âtre operatorul de transport ╚Öi de sistem. Orice m─âsur─â de siguran╚¢─â trebuie luat─â ├«n str├óns─â colaborare ╚Öi cu consultarea altor operatori de transport ╚Öi de sistem implica╚¢i, cu respectarea contractelor bilaterale aplicabile, inclusiv a acordurilor privind schimbul de informa╚¢ii....(10)...Operatorul de transport ╚Öi de sistem ╚Öi proprietarul re╚¢elei de transport public─â informa╚¢iile referitoare la propriile activit─â╚¢i, necesare utilizatorilor de re╚¢ea, conform reglement─ârilor ANRE, ├«n vederea asigur─ârii accesului eficient la re╚¢ea, a unei concuren╚¢e efective ╚Öi a func╚¢ion─ârii eficiente a pie╚¢ei de energie, nefiindu-le permis─â divulgarea informa╚¢iilor sensibile din punct de vedere comercial ob╚¢inute pe parcursul activit─â╚¢ilor acestora, inclusiv a celor ob╚¢inute de la ter╚¢i ├«n contextul acord─ârii sau al negocierii accesului la re╚¢ea....(11)...Cheltuielile pentru modificarea instala╚¢iilor de transport al energiei electrice, ca urmare a racord─ârii de noi utilizatori sau a schimb─ârii caracteristicilor energetice ini╚¢iale ale utilizatorilor existen╚¢i, inclusiv pentru eliberarea unor amplasamente, sunt suportate conform reglement─ârilor ├«n vigoare....(12)...Operatorul de transport ╚Öi de sistem aloc─â capacit─â╚¢i de interconexiune pe una sau mai multe grani╚¢e ├«mpreun─â cu al╚¢i operatori economici, inclusiv pentru cei ├«nregistra╚¢i pe teritoriul unui stat membru al Uniunii Europene sau al Tratatului de instituire a Comunit─â╚¢ii Energiei, ├«n condi╚¢iile semn─ârii unui memorandum de ├«n╚¢elegere ├«ntre p─âr╚¢i, avizat de ANRE.', NULL, 36, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (15, 15, '1', 'Articolul 1', NULL, NULL, NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, '(1)...Prezenta lege reglementeaz─â cadrul general necesar implement─ârii ├«n Rom├ónia a proiectelor de producere a energiei electrice din resurse eoliene offshore....(2)...Implementarea proiectelor de producere a energiei electrice din resurse eoliene offshore se realizeaz─â cu respectarea principiului nediscrimin─ârii, ├«n condi╚¢ii de concuren╚¢─â loial─â, ╚Öi cu respectarea principiului de prevenire ╚Öi controlul integrat al polu─ârii prin utilizarea celor mai bune tehnici disponibile pentru activit─â╚¢ile cu impact semnificativ asupra mediului.', NULL, 1, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (16, 15, '2', 'Articolul 2', NULL, NULL, NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, '├Än sensul prezentei legi, termenii ╚Öi expresiile de mai jos au urm─âtoarele semnifica╚¢ii:a)aprobare de dezvoltare a centralei electrice eoliene offshore - act asimilat, din punctul de vedere al efectelor juridice, autoriza╚¢iei de construire, reprezent├ónd autoriza╚¢ia de construire/desfiin╚¢are pentru lucr─ârile efectuate ├«ntr-un perimetru offshore determinat, precum ╚Öi pentru cele efectuate pe uscat, p├ón─â la punctul de racordare cu re╚¢eaua de transport al energiei electrice, acordat─â de c─âtre Autoritatea Competent─â de Reglementare a Opera╚¢iunilor Petroliere Offshore la Marea Neagr─â concesionarului unui perimetru eolian offshore;...b)autoritate competent─â pentru concesionarea perimetrelor offshore - Ministerul Energiei;...c)autoritate competent─â pentru emiterea aprob─ârii de dezvoltare/desfiin╚¢are a centralelor electrice eoliene offshore -Autoritatea Competent─â de Reglementare a Opera╚¢iunilor Petroliere Offshore la Marea Neagr─â, denumit─â ├«n continuare ACROPO;...d)autoritate competent─â pentru emiterea autoriza╚¢iei de ├«nfiin╚¢are ╚Öi a licen╚¢ei de producere a energiei electrice pentru activit─â╚¢ile supuse regimului autoriz─ârii - Autoritatea Na╚¢ional─â de Reglementare ├«n Domeniul Energiei, denumit─â ├«n continuare ANRE, conform prevederilor art. 8 din Legea energiei electrice ╚Öi a gazelor naturale nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioare;...e)autoriza╚¢ie de ├«nfiin╚¢are - autoriza╚¢ie emis─â de c─âtre ANRE pentru realizarea unor capacit─â╚¢i energetice de producere a energiei electrice, ├«n conformitate cu prevederile art. 8-10 din Legea nr. 123/2012,  cu modific─ârile ╚Öi complet─ârile ulterioare;...f)cablu subacvatic - cablu instalat pe fundul m─ârii, utilizat pentru transportul energiei electrice produse ├«n centralele electrice eoliene offshore;...g)central─â electric─â eolian─â offshore - capacitate de producere a energiei electrice situat─â ├«ntr-o zon─â a perimetrului eolian offshore concesionat, care grupeaz─â mai multe turbine eoliene ╚Öi instala╚¢ii electrice aferente, cu scopul de a transforma energia eolian─â primar─â ├«n energie electric─â;...h)concesionar - operatorul economic care a ├«ncheiat cu Ministerul Energiei un contract de concesiune pentru explorarea perimetrului eolian offshore ╚Öi exploatarea ulterioar─â a acestui perimetru prin construirea ╚Öi exploatarea unei centrale electrice eoliene offshore;...i)contract de concesiune - contract ├«ncheiat ├«ntre Ministerul Energiei ╚Öi concesionar ├«n vederea desf─â╚Öur─ârii activit─â╚¢ilor specifice de explorare a perimetrului eolian offshore, respectiv de exploatare a acestui perimetru prin construirea ╚Öi exploatarea centralelor electrice eoliene offshore;...j)desfiin╚¢are - retragerea din exploatare a centralei electrice eoliene offshore, ├«n baza autoriza╚¢iei ACROPO, concretizat─â prin lucr─âri privind demontarea construc╚¢iilor ce o alc─âtuiesc, gestionarea de╚Öeurilor rezultate ╚Öi repunerea perimetrului ├«n situa╚¢ia anterioar─â exploat─ârii ╚Öi construirii centralei electrice eoliene offshore;...k)dezvoltatorul centralei electrice eoliene offshore - operatorul economic care a ├«ncheiat contract de concesiune pentru explorarea unui perimetru eolian offshore ╚Öi pentru exploatarea acestui perimetru prin construirea ╚Öi exploatarea centralei electrice eoliene offshore, acesta reprezent├ónd gestionarul instala╚¢iei de producere a energiei electrice, ├«n conformitate cu prevederile art. 2 pct. 7 din Regulamentul (UE) 2016/631 al Comisiei din 14 aprilie 2016 de instituire a unui cod de re╚¢ea