INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (27, 15, '13', 'Articolul 13', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Concesionarea perimetrelor eoliene offshore', NULL, NULL, '(1)...Concesionarea se acord─â unui operator economic pentru explorarea perimetrului eolian offshore, lucr─ârile de construire a centralei electrice eoliene offshore ╚Öi exploatarea acesteia ca urmare a organiz─ârii unei proceduri competitive de atribuire pentru fiecare perimetru/grup de perimetre inclus ├«n hot─âr├órea Guvernului prev─âzut─â laart. 7 alin. (3)....(2)...Operatorii economici interesa╚¢i de ob╚¢inerea unei concesiuni, indiferent dac─â aceasta include sau nu acordarea unei scheme de sprijin, trebuie s─â ├«ndeplineasc─â condi╚¢iile de eligibilitate prev─âzute laart. 9 alin. (2).', NULL, 13, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (28, 15, '14', 'Articolul 14', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Concesionarea perimetrelor eoliene offshore', NULL, NULL, '(1)...Anun╚¢ul de concesionare include informa╚¢ii care privesc urm─âtoarele aspecte:a)o descriere a obiectului concesiunii, a criteriilor de calificare ╚Öi selec╚¢ie; criteriile de calificare ╚Öi selec╚¢ie vor include ├«n mod obligatoriu informa╚¢ii privind demonstrarea experien╚¢ei similare, a poten╚¢ialului tehnic, financiar ╚Öi organizatoric al fiec─ârui operator economic participant la procedur─â, poten╚¢ial care trebuie s─â reflecte posibilitatea concret─â a acestuia de a ├«ndeplini cerin╚¢ele contractului de concesiune ╚Öi de a rezolva eventualele dificult─â╚¢i legate de ├«ndeplinirea acestuia, ├«n cazul ├«n care oferta sa va fi declarat─â c├ó╚Ötig─âtoare;...b)perimetrele eoliene offshore propuse pentru concesionare;...c)informa╚¢ii privind schema de sprijin acordat─â, dac─â este cazul;...d)o descriere detaliat─â a criteriilor de atribuire ╚Öi a modalit─â╚¢ii de acordare a punctajului aferent;...e)informa╚¢ii privind criteriile nonfinanciare care vor fi punctate suplimentar ╚Öi care demonstreaz─â angajamentul operatorului economic de a se asocia ╚Öi cu societ─â╚¢i ├«nfiin╚¢ate ├«n Rom├ónia pentru derularea activit─â╚¢ii de explorare, construc╚¢ie ╚Öi exploatare a centralelor eoliene offshore, pentru realizarea de investi╚¢ii la nivel local relevante pentru dezvoltarea de centrale electrice eoliene offshore, dezvoltarea lan╚¢urilor valorice locale, utilizarea facilit─â╚¢ilor portuare locale, utilizarea for╚¢ei de munc─â locale, dezvoltarea unor activit─â╚¢i de formare profesional─â, dezvoltarea de activit─â╚¢i de protejare a mediului, suplimentar fa╚¢─â de cele solicitate prin anun╚¢ul de concesionare, ├«n condi╚¢iile respect─ârii normelor na╚¢ionale ╚Öi europene ├«n materia concuren╚¢ei ╚Öi nediscrimin─ârii;...f)cerin╚¢ele obligatorii de asigurare a protec╚¢iei mediului;...g)tipuri de garan╚¢ii solicitate, nivelul acestora ╚Öi condi╚¢iile de returnare;...h)data ├«nceperii procedurii de atribuire, durata procedurii de calificare, criteriile de evaluare ╚Öi punctajele de atribuire, modalitatea de contestare a rezultatelor procedurii, cu precizarea organismelor/entit─â╚¢ilor cu atribu╚¢ii ├«n ceea ce prive╚Öte activit─â╚¢ile respective;...i)termenul p├ón─â la care se pot depune solicit─ârile operatorilor economici interesa╚¢i;...j)lista lucr─ârilor necesare de realizat, a informa╚¢iilor solicitate ╚Öi termenele stabilite pentru transmiterea informa╚¢iilor finale de c─âtre concesionar;...k)tipuri de raport─âri care trebuie depuse de operatorul economic concesionar pe perioada contractului de concesiune, frecven╚¢a acestora;...l)cuantumul taxelor ╚Öi redeven╚¢elor.......(2)...Anun╚¢ul prev─âzut laalin. (1)se public─â pe website-ul propriu, ├«ntr-o sec╚¢iune dedicat─â, de c─âtre Ministerul Energiei, cu cel pu╚¢in 3 luni ├«nainte de data-limit─â la care operatorii economici pot depune ofertele....(3)...Orice operator economic interesat are dreptul de a solicita clarific─âri sau informa╚¢ii suplimentare ├«n leg─âtur─â cu documenta╚¢ia de atribuire, conform dispozi╚¢iilor hot─âr├órii Guvernului prin care se adopt─â procedura de concesionare.', NULL, 14, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (29, 15, '15', 'Articolul 15', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Concesionarea perimetrelor eoliene offshore', NULL, NULL, '(1)...Contractul de concesiune se ├«ncheie pentru o perioad─â de cel mult 30 de ani, cu posibilitatea unei singure perioade de prelungire de cel mult 10 ani....(2)...├Äncep├ónd cu data punerii ├«n func╚¢iune a centralei electrice eoliene offshore, ├«n vederea exploat─ârii, titularul contractului de concesiune pl─âte╚Öte, anual, o redeven╚¢─â stabilit─â pentru perimetrul eolian offshore concesionat potrivit dispozi╚¢iilorart. 41, care se face venit la bugetul de stat....(3)...Pentru perimetrele situate ├«n afara apelor maritime interioare ╚Öi a m─ârii teritoriale, concesionarul pl─âte╚Öte, ├«ncep├ónd cu momentul ob╚¢inerii aprob─ârii de dezvoltare, o tax─â pentru suprafa╚¢a concesionat─â a perimetrului eolian offshore construit ╚Öi pentru suprafa╚¢a ocupat─â de cablurile electrice subacvatice de leg─âtur─â cu punctele de racordare la re╚¢ea din afara perimetrului concesionat, stabilit─â potrivit dispozi╚¢iilorart. 42, care se face venit la bugetul de stat....(4)...Pentru perimetrele situate pe suprafa╚¢a bunurilor administrate de Administra╚¢ia Na╚¢ional─â ΓÇ₧Apele Rom├óneΓÇ£, conform dispozi╚¢iilor Legii apelor nr. 107/1996, cu modific─ârile ╚Öi complet─ârile ulterioare, concesionarul pl─âte╚Öte, ├«ncep├ónd cu momentul ob╚¢inerii aprob─ârii de dezvoltare, o tax─â pentru suprafa╚¢a concesionat─â a perimetrului eolian offshore construit, care se face venit la bugetul de stat,