INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (30, 15, '16', 'Articolul 16', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Concesionarea perimetrelor eoliene offshore', NULL, NULL, 'Explorarea perimetrului eolian offshore ╚Öi exploatarea acestui perimetru prin construirea ╚Öi exploatarea centralei electrice eoliene offshore se realizeaz─â ├«n baza contractului de concesiune ├«ncheiat cu Ministerul Energiei pentru fiecare perimetru eolian offshore, a aprob─ârii de dezvoltare emise de c─âtre ACROPO, ├«n condi╚¢iile prezentei legi, precum ╚Öi a autoriza╚¢iei de ├«nfiin╚¢are ╚Öi a licen╚¢ei de producere emise de c─âtre ANRE.', NULL, 16, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (31, 15, '17', 'Articolul 17', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Activitatea de explorare a perimetrului eolian offshore', NULL, NULL, '(1)...├Än cadrul perioadei de concesiune, concesionarii au dreptul s─â exploreze perimetrul eolian offshore pentru o durat─â de maximum 2 ani de la momentul semn─ârii contractului de concesiune, cu drept de prelungire pe o durat─â de maximum 6 luni, pentru motive justificate ╚Öi cu acordul autorit─â╚¢ii concedente....(2)...Concesionarii au obliga╚¢ia de a efectua lucr─ârile de explorare numai ├«n cadrul perimetrului concesionat....(3)...Concesionarii care execut─â lucr─ârile de explorare prezint─â Ministerului Energiei rapoarte trimestriale, precum ╚Öi un raport final privind lucr─ârile executate, metodele de investigare utilizate ╚Öi rezultatele ob╚¢inute....(4)...Toate datele ╚Öi informa╚¢iile cu privire la caracteristicile perimetrelor explorate ╚Öi a poten╚¢ialului eolian offshore, dob├óndite ├«n urma activit─â╚¢ii de explorare, sunt puse la dispozi╚¢ia Ministerului Energiei, ├«n mod gratuit, eviden╚¢a ╚Öi gestiunea lor fiind f─âcute la nivel na╚¢ional de c─âtre autoritatea concedent─â ├«n condi╚¢iile stabilite prin prezenta lege.', NULL, 17, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (32, 15, '18', 'Articolul 18', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Activitatea de explorare a perimetrului eolian offshore', NULL, NULL, 'Permisul de explorare se acord─â concesionarului de c─âtre Ministerul Energiei, dup─â ├«ncheierea contractului de concesiune. Metodologia de acordare a permisului de explorare se aprob─â p├ón─â la data de 30 iunie 2025, prin ordin al ministrului energiei, care se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I.', NULL, 18, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (34, 15, '20', 'Articolul 20', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Ob╚¢inerea aprob─ârii de dezvoltare, a autoriza╚¢iei de ├«nfiin╚¢are  ╚Öi a licen╚¢ei de producere, ├«n vederea construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, NULL, '(1)...Ulterior emiterii autoriza╚¢iei de ├«nfiin╚¢are, lucr─ârile se execut─â ├«n baza aprob─ârii de dezvoltare emise de c─âtre ACROPO....(2)...Prin derogare de la prevederile art. 4 alin. (1) din Legea nr. 50/1991 privind autorizarea execut─ârii lucr─ârilor de construc╚¢ii, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare, prin emiterea aprob─ârii de dezvoltare ACROPO autorizeaz─â ╚Öi lucr─ârile de construire aferente proiectului centralei eoliene offshore efectuate pe uscat, p├ón─â la punctul de racordare....(3)...Autoriza╚¢ia de ├«nfiin╚¢are ╚Öi aprobarea de dezvoltare pentru construirea centralei electrice eoliene offshore dau dreptul titularului contractului de concesiune s─â construiasc─â o central─â electric─â eolian─â offshore ├«ntr-un anumit spa╚¢iu definit de perimetrul eolian offshore concesionat ╚Öi s─â execute lucr─âri ├«n vederea racord─ârii la re╚¢eaua de transport al energiei electrice ├«n punctul/punctele convenit(e) cu operatorul de transport ╚Öi sistem....(4)...Sunt exceptate de la aprobarea de dezvoltare prev─âzut─â de prezenta lege toate opera╚¢iunile zilnice, periodice ori temporare destinate p─âstr─ârii ├«n func╚¢iune, optimiz─ârii sau men╚¢inerii ├«n condi╚¢ii de siguran╚¢─â a centralei electrice eoliene offshore ╚Öi a liniilor de interconectare, respectiv: repara╚¢iile, ├«ntre╚¢inerea, interven╚¢iile de urgen╚¢─â....(5)...ACROPO emite aprobarea de dezvoltare prev─âzut─â laalin. (1)├«n termen de 60 de zile de la data depunerii documenta╚¢iei complete de c─âtre concesionar....(6)...├Än termen de maximum 12 luni de la momentul emiterii aprob─ârii de dezvoltare, concesionarul are obliga╚¢ia demar─ârii lucr─ârilor de construc╚¢ie. De la data ├«nceperii lucr─ârilor, aprobarea de dezvoltare are o valabilitate de cel mult 5 ani....(7)...Aprobarea de dezvoltare prev─âzut─â laalin. (1)se poate emite cu privire la ansamblul lucr─ârilor sau cu privire la sec╚¢iuni, etape, tronsoane, sectoare, conform solicit─ârii concesionarului. Aprobarea de dezvoltare va prevedea posibilitatea titularului de a executa lucr─ârile ╚Öi pe sec╚¢iuni, etape, tronsoane, sectoare, obiective sau categorii de lucr─âri, conform solicit─ârii acestuia....(8)...├Änainte de ├«nceperea lucr─ârilor de construire, titularul are obliga╚¢ia de a notifica Direc╚¢ia hidrografic─â maritim─â din cadrul Ministerului Ap─âr─ârii Na╚¢ionale, Autoritatea Naval─â Rom├ón─â ╚Öi secretariatul Comitetului de amenajare a spa╚¢iului maritim, ├«n vederea marc─ârii coordonatelor acestora ├«n planul de amenajare a spa╚¢iului maritim, cu cel pu╚¢in 30 de zile ├«nainte de demararea efectiv─â a procesului de execu╚¢ie pe sec╚¢iuni, etape, tronsoane, sectoare, obiective sau categorii de lucr─âri....(9)...Pe perioada construc╚¢iei centralei, ACROPO monitorizeaz─â stadiul efectu─ârii lucr─ârilor ├«n conformitate cu documenta╚¢ia tehnico-economic─â a proiectului....(10)...Utilizarea scafandrilor pe perioada lucr─ârilor de construire, precum ╚Öi pe timpul exploat─ârii centralei electrice eoliene offshore necesit─â ob╚¢inerea autoriza╚¢iei pentru desf─â╚Öurarea activit─â╚¢ilor cu scafandri. ├Än acest sens, dezvoltatorul centralei electrice eoliene offshore are obliga╚¢ia de a se adresa Centrului de scafandri din cadrul Ministerului Ap─âr─ârii Na╚¢ionale.', NULL, 20, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
