INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (39, 15, '25', 'Articolul 25', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Ob╚¢inerea aprob─ârii de dezvoltare, a autoriza╚¢iei de ├«nfiin╚¢are  ╚Öi a licen╚¢ei de producere, ├«n vederea construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, NULL, '(1)...Dup─â recep╚¢ia lucr─ârilor ╚Öi perioada de prob─â, ANRE emite licen╚¢a de producere a energiei electrice, conform reglement─ârilor proprii....(2)...ANRE are obliga╚¢ia de a emite reglement─âri privind acordarea licen╚¢ei de producere a energiei electrice, lu├ónd ├«n considerare caracteristicile centralelor electrice eoliene offshore ╚Öi monitorizarea func╚¢ion─ârii acestora, conform licen╚¢ei, p├ón─â la data de 30 iunie 2025.', NULL, 25, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (40, 15, '26', 'Articolul 26', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Racordarea la re╚¢eaua electric─â de transport', NULL, NULL, '├Än cadrul planurilor de dezvoltare RET, elaborate ├«ncep├ónd cu data de 1 ianuarie 2025, operatorul de transport ╚Öi sistem asigur─â elaborarea de studii de sistem pentru a analiza poten╚¢ialul impact al unor mari cantit─â╚¢i de energie eolian─â offshore asupra sistemului energetic na╚¢ional, ├«n conformitate cu cele mai bune practici interna╚¢ionale, ╚Öi analizeaz─â impactul pe care cerin╚¢ele de racordare a centralelor eoliene offshore ├«l au asupra sistemului energetic na╚¢ional.', NULL, 26, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
