INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (43, 15, '29', 'Articolul 29', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Racordarea la re╚¢eaua electric─â de transport', NULL, NULL, '(1)...├Än cazul ├«n care concesionarul, dezvoltator al centralei electrice eoliene offshore, nu reu╚Öe╚Öte s─â construiasc─â ╚Öi s─â conecteze centrala la re╚¢ea, ├«n conformitate cu licen╚¢ele/autoriza╚¢iile acordate, este considerat responsabil pentru daunele suferite de operatorul de transport ╚Öi sistem ca urmare a ac╚¢iunilor sale. O astfel de r─âspundere nu va fi incident─â dac─â proprietarul centralei electrice eoliene offshore dovede╚Öte c─â ├«nt├órzierile nu s-au produs din culpa sa....(2)...Operatorul de transport ╚Öi sistem r─âspunde pentru daunele suferite de concesionar, dezvoltator al centralei electrice eoliene offshore, dac─â nu respect─â termenul-limit─â pentru racordarea la re╚¢ea ├«n situa╚¢ia ├«n care se dovede╚Öte c─â ├«nt├órzierile s-au produs din culpa sa....(3)...P├ón─â la data de 31 decembrie 2024, ANRE emite modele de aviz tehnic de racordare ╚Öi contracte de racordare la re╚¢ea, precum ╚Öi certificate de racordare specifice centralelor electrice eoliene offshore ╚Öi modific─â ├«n mod corespunz─âtor reglement─ârile necesare....(4)...Redispecerizarea de c─âtre operatorul de transport ╚Öi sistem a centralelor electrice eoliene offshore se face ├«n condi╚¢iile prevederilor art. 13 din Regulamentul (UE) 2019/943 al Parlamentului European ╚Öi al Consiliului din 5 iunie 2019 privind pia╚¢a intern─â de energie electric─â ╚Öi ale reglement─ârilor de punere ├«n aplicare aprobate de ANRE.', NULL, 29, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (44, 15, '30', 'Articolul 30', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Racordarea la re╚¢eaua electric─â de transport', NULL, NULL, 'Pe perioada exploat─ârii centralei electrice eoliene offshore, operatorul de transport ╚Öi sistem poate solicita limitarea temporar─â sau ├«nchiderea temporar─â a produc╚¢iei de energie electric─â, din motive tehnice, precum ╚Öi la solicitarea concedentului sau a institu╚¢iilor cu atribu╚¢ii ├«n domeniul ap─âr─ârii, ordinii publice sau securit─â╚¢ii na╚¢ionale, pentru motive justificate.', NULL, 30, '2025-11-07 23:44:16.324009', '2025-11-07 23:44:16.324009', 'pending', NULL, NULL, NULL, NULL);
