INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (94, 16, '27', 'Articolul 27', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Producerea de energie electric─â...(1)...Producerea de energie electric─â ╚Öi producerea de energie electric─â ╚Öi termic─â ├«n cogenerare se realizeaz─â de c─âtre operatorii economici care de╚¢in unit─â╚¢i de producere a energiei electrice, ├«n condi╚¢iile prezentei legi....(2)...Produc─âtorii de energie electric─â pot beneficia de accesul garantat sau de accesul prioritar la re╚¢elele electrice, conform reglement─ârilor ANRE ├«n vigoare.', NULL, 27, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (101, 16, '34', 'Articolul 34', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Condi╚¢ii de certificare a operatorului de transport ╚Öi de sistem...(1)...Certificarea operatorului de transport ╚Öi de sistem se realizeaz─â dac─â sunt ├«ndeplinite urm─âtoarele cerin╚¢e:a)aceea╚Öi persoan─â sau acelea╚Öi persoane nu au dreptul:......(i) s─â exercite, direct sau indirect, controlul asupra unui operator economic care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere ori de furnizare ╚Öi ├«n acela╚Öi timp s─â exercite, direct sau indirect, controlul ori s─â exercite vreun drept asupra operatorului de transport ╚Öi de sistem sau asupra unei re╚¢ele de transport; ori...(├«i) s─â exercite, direct sau indirect, controlul asupra operatorului de transport ╚Öi de sistem ori asupra unei re╚¢ele de transport ╚Öi s─â exercite, direct sau indirect, controlul ori s─â exercite vreun drept asupra unui operator economic care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere sau de furnizare;b)aceea╚Öi persoan─â sau acelea╚Öi persoane nu sunt abilitate s─â numeasc─â membri ├«n consiliul de supraveghere, ├«n consiliul de administra╚¢ie ori ├«n alte organisme care reprezint─â operatorul de transport ╚Öi de sistem din punct de vedere juridic ├«n cazul unui operator de transport ╚Öi de sistem sau ├«n cazul unei re╚¢ele de transport ╚Öi, de asemenea, s─â exercite, direct ori indirect, controlul sau s─â exercite vreun drept asupra unui operator economic care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere ori de furnizare;...c)aceea╚Öi persoan─â nu este abilitat─â s─â fie membru ├«n consiliul de supraveghere, ├«n consiliul de administra╚¢ie sau ├«n alte organisme care reprezint─â operatorul economic din punct de vedere juridic, at├ót ├«n cazul unui operator economic care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere sau de furnizare, c├ót ╚Öi ├«n cazul unui operator de transport ╚Öi de sistem ori ├«n cazul unei re╚¢ele de transport;...d)operatorul de transport ╚Öi de sistem dispune de resursele financiare, tehnice, fizice ╚Öi umane pentru ├«ndeplinirea obliga╚¢iilor prev─âzute la art. 36;...e)operatorul de transport ╚Öi de sistem ├«╚Öi asum─â angajamentul de a respecta un plan de dezvoltare a re╚¢elei de transport pe 10 ani, aprobat de autoritatea competent─â;...f)operatorul de transport ╚Öi de sistem are capacitatea de a respecta obliga╚¢iile ce ├«i revin ├«n temeiul Regulamentului (CE) nr. 714/2009, inclusiv ├«n ceea ce prive╚Öte cooperarea cu al╚¢i operatori de transport ╚Öi de sistem la nivel european ╚Öi regional;...g)proprietarul re╚¢elei de transport ├«ndepline╚Öte cerin╚¢ele prev─âzute la art. 37.......(2)...Drepturile prev─âzute la alin. (1) lit. a) ╚Öi b) cuprind, ├«n special:...(i) competen╚¢a de a exercita drepturi de vot;...(├«i) competen╚¢a de a numi membri ├«n consiliul de supraveghere, consiliul de administra╚¢ie sau alte organe care reprezint─â operatorul economic din punct de vedere juridic; sau...(iii) de╚¢inerea unei cote majoritare....(3)...├Än sensul alin. (1) lit. a), no╚¢iunea "operator economic care desf─â╚Öoar─â activitate de producere sau de furnizare de energie" include ╚Öi activit─â╚¢ile de producere ╚Öi furnizare de gaze naturale, iar termenii "operator de transport ╚Öi sistem" ╚Öi "re╚¢ea de transport" includ ╚Öi termenii care se utilizeaz─â ├«n acela╚Öi sens ├«n sectorul gazelor naturale.', NULL, 34, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (95, 16, '28', 'Articolul 28', NULL, 'Energia electric─â', NULL, 'Energia electric─â', NULL, NULL, NULL, NULL, 'Obliga╚¢iile produc─âtorilor...Produc─âtorii au, ├«n principal, urm─âtoarele obliga╚¢ii:a)s─â asigure livr─ârile de energie electric─â ╚Öi serviciile tehnologice de sistem, cu respectarea condi╚¢iilor impuse prin licen╚¢e, clauze contractuale ╚Öi reglement─âri ├«n vigoare;...b)├«n cazul unit─â╚¢ilor dispecerizabile s─â oferteze ├«ntreaga putere electric─â disponibil─â pe pia╚¢a de echilibrare, definit─â conform reglement─ârilor emise de autoritatea competent─â;...c)s─â oferteze public ╚Öi nediscriminatoriu pe pia╚¢a concuren╚¢ial─â ├«ntreaga energie electric─â disponibil─â;...d)s─â oferteze nediscriminatoriu serviciile tehnologice de sistem;...e)s─â nu transmit─â la operatorul de transport ╚Öi de sistem notific─âri fizice ├«n dezechilibru negativ fa╚¢─â de contractele pe care le au ├«ncheiate, cu excep╚¢ia produc─âtorilor care beneficiaz─â de scheme de sprijin, conform prevederilor prezentului titlu;...f)s─â men╚¢in─â o rezerv─â de combustibil la un nivel suficient sau, dup─â caz, o rezerv─â suficient─â de ap─â, pentru ├«ndeplinirea obliga╚¢iilor de produc╚¢ie ╚Öi furnizare continu─â a energiei electrice, prev─âzute de reglement─ârile ├«n vigoare;...g)s─â se conformeze, din punct de vedere operativ, cerin╚¢elor operatorului de transport ╚Öi de sistem ╚Öi s─â ├«nfiin╚¢eze, dup─â caz, trepte proprii de conducere operativ─â;...h)s─â transmit─â autorit─â╚¢ii competente un raport anual de activitate, conform reglement─ârilor ├«n vigoare, chiar ├«n condi╚¢iile ├«n care nu de╚¢ine licen╚¢a de producere sau capacit─â╚¢ile sunt transferate