INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (165, 16, '98', 'Articolul 98', NULL, 'Gazele naturale', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, 'Domeniul de reglementare...(1)...Prezentul titlu stabile╚Öte cadrul de reglementare pentru desf─â╚Öurarea activit─â╚¢ilor privind produc╚¢ia, transportul, distribu╚¢ia, furnizarea ╚Öi ├«nmagazinarea gazelor naturale, modalit─â╚¢ile de organizare ╚Öi func╚¢ionare a sectorului gazelor naturale, de acces pe pia╚¢─â, precum ╚Öi criteriile ╚Öi procedurile aplicabile pentru acordarea de autoriza╚¢ii ╚Öi/sau licen╚¢e ├«n sectorul gazelor naturale....(2)...Prevederile prezentului titlu se aplic─â ├«ntr-un mod nediscriminatoriu ╚Öi pentru biogaz, gaz ob╚¢inut din biomas─â sau alte tipuri de gaze, ├«n m─âsura ├«n care este posibil din punct de vedere tehnic ca acestea s─â fie injectate ori transportate prin sistemele de transport/distribu╚¢ie al/a gazelor naturale ╚Öi prin conductele de alimentare din amonte ╚Öi/sau s─â fie utilizate ├«n instala╚¢ii de utilizare, ├«n deplin─â siguran╚¢─â.', NULL, 98, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (166, 16, '99', 'Articolul 99', NULL, 'Gazele naturale', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, 'Obiectivele activit─â╚¢ilor ├«n sectorul gazelor naturale...Activit─â╚¢ile ├«n sectorul gazelor naturale trebuie s─â se desf─â╚Öoare pentru realizarea urm─âtoarelor obiective de baz─â:a)asigurarea dezvolt─ârii durabile a economiei na╚¢ionale;...b)asigurarea continuit─â╚¢ii ╚Öi a siguran╚¢ei ├«n alimentarea cu gaze naturale a clien╚¢ilor;...c)protejarea intereselor legitime ale clien╚¢ilor finali de gaze naturale;...d)promovarea, stimularea ╚Öi asigurarea concuren╚¢ei pe pia╚¢a de gaze naturale;...e)armonizarea legisla╚¢iei na╚¢ionale cu legisla╚¢ia Uniunii Europene ├«n domeniu, cu respectarea principiului subsidiarit─â╚¢ii;...f)transparen╚¢a pre╚¢urilor ╚Öi a tarifelor reglementate ├«n sectorul gazelor naturale;...g)dezvoltarea sectorului gazelor naturale ├«n condi╚¢ii de eficien╚¢─â economic─â ╚Öi protec╚¢ie a mediului;...h)promovarea producerii ╚Öi utiliz─ârii surselor noi ╚Öi regenerabile de gaze;...i)asigurarea accesului nediscriminatoriu la sursele de gaze naturale;...j)asigurarea m─âsurilor de securitate ├«n vederea prevenirii ╚Öi combaterii actelor de terorism ╚Öi sabotaj asupra infrastructurii sectorului gazelor naturale;...k)dezvoltarea interconect─ârilor sistemelor de transport al gazelor naturale cu sistemele similare din ╚¢─ârile vecine ╚Öi cu alte infrastructuri de transport ╚Öi integrarea Sistemului na╚¢ional de transport al gazelor naturale ├«n Re╚¢eaua european─â de transport ╚Öi sistem de gaze naturale - ENTSO-G, denumit─â ├«n continuare ENTSO-G;...l)asigurarea condi╚¢iilor necesare pentru buna func╚¢ionare a pie╚¢ei gazelor naturale ╚Öi pentru integrarea pie╚¢ei na╚¢ionale ├«n pia╚¢a intern─â european─â a gazelor naturale;...m)asigurarea accesului nediscriminatoriu al ter╚¢ilor la conductele de alimentare din amonte, depozitele de ├«nmagazinare, sistemele de transport, sistemele de distribu╚¢ie a gazelor naturale, precum ╚Öi la instala╚¢iile GNL;...n)asigurarea func╚¢ion─ârii instala╚¢iilor tehnologice de suprafa╚¢─â din c├ómpurile de produc╚¢ie, a depozitelor de ├«nmagazinare, a sistemelor de transport ╚Öi de distribu╚¢ie a gazelor naturale, ├«n condi╚¢ii de siguran╚¢─â ╚Öi eficien╚¢─â;...o)asigurarea capacit─â╚¢ii de ├«nmagazinare a gazelor naturale pentru nevoile curente ╚Öi pentru cele care contribuie la securitatea energetic─â a ╚¢─ârii....', NULL, 99, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
