INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (170, 16, '103', 'Articolul 103', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Obiectul concesiunii...Bunurile proprietate public─â aferente obiectivelor/sistemelor de transport ╚Öi ├«nmagazinare a gazelor naturale, precum ╚Öi serviciile de transport, de ├«nmagazinare ╚Öi de distribu╚¢ie a gazelor naturale fac obiectul concesion─ârii c─âtre persoane juridice rom├óne sau str─âine, ├«n condi╚¢iile legii.', NULL, 103, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (171, 16, '104', 'Articolul 104', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Concesionarea serviciului public de distribu╚¢ie a gazelor naturale...(1)...Serviciul public de distribu╚¢ie a gazelor naturale se concesioneaz─â pentru una sau mai multe zone delimitate - unit─â╚¢i administrativ-teritoriale; concesiunea este exclusiv─â pentru zonele delimitate ├«n care s-a acordat....(2)...Ministerul de resort ini╚¢iaz─â procesul de atribuire a concesiunii, ├«n urma solicit─ârii primite de la o persoan─â sau autoritate public─â interesat─â, ├«n condi╚¢iile legii; solicitarea este ├«nso╚¢it─â de un studiu de fezabilitate aprobat de c─âtre ANRE....(3)...Studiul de fezabilitate prev─âzut la alin. (2) se ├«ntocme╚Öte, ├«n conformitate cu prevederile metodologiei elaborate ╚Öi aprobate de c─âtre ANRE, de un operator economic autorizat de ANRE, se avizeaz─â prin decizie a ANRE ╚Öi este parte integrant─â din documenta╚¢ia de atribuire a concesiunii....(4)...Decizia favorabil─â a ANRE este obligatorie pentru ini╚¢ierea procesului de atribuire a concesiunii serviciului public de distribu╚¢ie a gazelor naturale....(5)...Concedentul, ├«n colaborare cu ANRE, va lua m─âsurile ce se impun astfel ├«nc├ót s─â se asigure, dup─â caz, concesionarea mai multor unit─â╚¢i administrativ-teritoriale ├«nvecinate ├«n cadrul unui singur contract; ministerul de resort ╚Öi autoritatea competent─â vor urm─âri ca solu╚¢ia tehnic─â de alimentare a zonei concesionate s─â fie realizat─â, de regul─â, prin intermediul unui singur racord....(6)...├Än condi╚¢ii justificate, concedentul serviciului public de distribu╚¢ie, cu avizul prealabil al ANRE, poate decide extinderea zonei concesionate pentru una sau mai multe localit─â╚¢i apar╚¢in─âtoare unit─â╚¢ii administrativ-teritoriale, din cadrul c─âreia, pentru cel pu╚¢in o localitate, serviciul de distribu╚¢ie a fost deja concesionat; extinderea zonei concesionate se realizeaz─â prin act adi╚¢ional la contractul de concesiune ├«ncheiat cu concesionarul din zona respectiv─â, definit─â conform prevederilor alin. (1)....(7)...Dup─â adjudecarea concesiunii, ├«n vederea desf─â╚Öur─ârii activit─â╚¢ii, concesionarul solicit─â autoriza╚¢iile/licen╚¢ele specifice prev─âzute de legisla╚¢ia ├«n vigoare....(8)...Solicitan╚¢ii de gaze naturale situa╚¢i ├«n zone delimitate care nu sunt concesionate pot fi alimenta╚¢i ├«n condi╚¢iile prev─âzute la art. 152.', NULL, 104, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (172, 16, '105', 'Articolul 105', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Asigurarea urm─âririi execut─ârii lucr─ârilor...Concesionarul serviciului public de distribu╚¢ie a gazelor naturale trebuie s─â asigure urm─ârirea execut─ârii lucr─ârilor prev─âzute ├«n contractul de concesiune, prin personal propriu, autorizat conform reglement─ârilor ANRE, precum ╚Öi prin personal contractual, autorizat conform legisla╚¢iei ├«n vigoare.', NULL, 105, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (173, 16, '106', 'Articolul 106', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Retragerea concesiunii...(1)...Dreptul de concesiune poate fi retras de concedent ├«n situa╚¢iile ├«n care titularul:a)nu efectueaz─â volumul de lucr─âri ├«n termenele prev─âzute ├«n contract;...b)nu respect─â clauzele esen╚¢iale definite ca atare de c─âtre p─âr╚¢ile din contract, care includ obligatoriu clauzele privind plata redeven╚¢elor ╚Öi protec╚¢ia mediului;...c)├«ncalc─â ├«n mod sistematic condi╚¢iile de valabilitate ale licen╚¢elor de distribu╚¢ie a gazelor naturale sau ale legisla╚¢iei privitoare la siguran╚¢a ├«n func╚¢ionare a obiectivelor.......(2)...Decizia de retragere a concesiunii emis─â de concedent poate fi contestat─â, ├«n termen de 60 de zile de la comunicare, la instan╚¢a de contencios administrativ competent─â. Decizia r─âmas─â definitiv─â va fi publicat─â conform prevederilor legale....(3)...Licen╚¢a corelativ─â concesiunii se retrage de ANRE, ├«n condi╚¢iile ├«ncet─ârii contractului de concesiune.', NULL, 106, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (174, 16, '107', 'Articolul 107', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, '├Äncetarea contractului de concesiune...(1)...La ├«ncetarea din orice motiv a contractului de concesiune, bunurile aferente serviciului public de distribu╚¢ie aflate ├«n proprietatea concesionarului pot fi preluate, ├«n tot sau ├«n parte, de c─âtre concedent sau de c─âtre un alt concesionar, cu acordul concedentului, ├«n schimbul pl─â╚¢ii unei compensa╚¢ii egale cu valoarea reglementat─â r─âmas─â neamortizat─â, stabilit─â de ANRE....(2)...├Än cazul ├«n care contractul de concesiune ├«nceteaz─â din vina exclusiv─â a concesionarului, acesta nu poate beneficia de compensa╚¢ii de la concedent sau alt concesionar, cu excep╚¢ia unei compensa╚¢ii egale cu valoarea reglementat─â r─âmas─â neamortizat─â, stabilit─â de ANRE.', NULL, 107, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (175, 16, '108', 'Articolul 108', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Drepturile concesionarului...(1)...Pentru realizarea serviciului de transport sau de distribu╚¢ie, ├«n cazul utiliz─ârii bunurilor proprietate a ter╚¢ilor, concesionarul are urm─âtoarele drepturi:a)s─â foloseasc─â aceste bunuri prin efectul legii;...b)f─âr─â a se aduce atingere dreptului prev─âzut la lit. a), condi╚¢iile de exercitare a acestuia sunt cele prev─âzute ├«n procesul-verbal de predare ├«n exploatare ╚Öi/sau ├«n contract;...c)s─â includ─â costurile aferente lucr─ârilor de exploatare, ├«ntre╚¢inere, repara╚¢ii ╚Öi modernizare ╚Öi altele, efectuate pentru bunuri, la stabilirea tarifului pentru serviciul prestat, ├«n condi╚¢iile reglement─ârilor ANRE specifice;...d)cu acordul proprietarului, s─â preia aceste bunuri ├«n proprietatea sa, cu o just─â desp─âgubire;...e)s─â dezvolte sistemul;...f)s─â utilizeze ├«ntreaga capacitate a bunului;...g)s─â asigure accesul unor noi solicitan╚¢i, cu respectarea uneia dintre urm─âtoarele condi╚¢ii:......1. prezentarea acceptului proprietarului;...2. prezentarea acordului solicitantului de acces privind desp─âgubirea proprietarului; acordul solicitantului se prezum─â atunci c├ónd acesta consimte ├«n scris, printr-un act autentic, s─â ├«l desp─âgubeasc─â pe proprietar pentru partea ce ├«i revine din investi╚¢ia f─âcut─â;h)s─â solicite proprietarului bunului documente tehnico-economice care s─â fac─â dovada calit─â╚¢ii materialelor ╚Öi lucr─ârilor efectuate, precum ╚Öi a valorii investi╚¢iei;...i)s─â ├«nlocuiasc─â bunurile ├«n urma unor necesit─â╚¢i ap─ârute ├«n scopul moderniz─ârii, m─âririi capacit─â╚¢ii sistemului sau ca urmare a cre╚Öterii gradului de uzur─â ori deterior─ârii acestora; concesionarul are obliga╚¢ia s─â ├«nlocuiasc─â aceste bunuri ├«n conformitate cu reglement─ârile aprobate de ANRE ├«n acest scop; concesionarul este proprietarul bunurilor astfel ├«nlocuite.......(2)...Corelativ cu drepturile prev─âzute la alin. (1), operatorii sistemelor de transport/distribu╚¢ie se oblig─â:a)s─â finan╚¢eze, la expirarea duratei de func╚¢ionare, investi╚¢iile aferente ├«nlocuirii bunurilor;...b)s─â opereze ╚Öi s─â ├«ntre╚¢in─â bunurile pentru func╚¢ionarea ├«n condi╚¢ii de siguran╚¢─â a sistemului.......(3)...├Än vederea asigur─ârii continuit─â╚¢ii ╚Öi a siguran╚¢ei ├«n alimentarea cu gaze naturale a clien╚¢ilor, ├«n cazul ├«n care prestarea serviciului public de transport/distribu╚¢ie se realizeaz─â prin intermediul bunurilor proprietate a ter╚¢ilor, se interzice schimbarea destina╚¢iei pentru care aceste bunuri au fost construite.', NULL, 108, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (176, 16, '109', 'Articolul 109', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Dreptul de uz ╚Öi dreptul de servitute...Asupra terenurilor ╚Öi altor bunuri proprietate public─â sau proprietate privat─â a persoanelor fizice sau juridice, precum ╚Öi asupra activit─â╚¢ilor desf─â╚Öurate de persoane fizice sau juridice ├«n vecin─âtatea capacit─â╚¢ilor, concesionarii din sectorul gazelor naturale beneficiaz─â, ├«n condi╚¢iile legii, pe durata lucr─ârilor de dezvoltare, reabilitare, modernizare, respectiv de exploatare ╚Öi de ├«ntre╚¢inere a capacit─â╚¢ilor respective, de urm─âtoarele drepturi:a)dreptul de uz pentru executarea lucr─ârilor necesare ├«n vederea realiz─ârii, reabilit─ârii sau moderniz─ârii obiectivelor/sistemelor;...b)dreptul de uz pentru asigurarea func╚¢ion─ârii normale a capacit─â╚¢ii prin efectuarea reviziilor, repara╚¢iilor ╚Öi a interven╚¢iilor necesare;...c)dreptul de servitute legal─â de trecere subteran─â, de suprafa╚¢─â sau aerian─â pentru instalarea de re╚¢ele, de conducte, de linii sau de alte echipamente aferente obiectivelor/sistemelor ╚Öi pentru accesul la locul de amplasare a acestora;...d)dreptul de a ob╚¢ine restr├óngerea sau ├«ncetarea unor activit─â╚¢i care ar putea pune ├«n pericol persoane ╚Öi bunuri;...e)dreptul de acces la utilit─â╚¢ile publice....', NULL, 109, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (207, 16, '140', 'Articolul 140', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, '├Änmagazinarea gazelor naturale...(1)...├Änmagazinarea gazelor naturale se realizeaz─â de c─âtre operatorul de ├«nmagazinare....(2)...Gazele naturale se ├«nmagazineaz─â ├«n scopul:a)asigur─ârii securit─â╚¢ii ├«n alimentarea cu gaze naturale a clien╚¢ilor finali;...b)armoniz─ârii varia╚¢iilor consumului sezonier, zilnic ╚Öi orar cu sursele de gaze disponibile;...c)asigur─ârii permanente a echilibrului fizic al SNT;...d)realiz─ârii altor activit─â╚¢i comerciale....', NULL, 140, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (177, 16, '110', 'Articolul 110', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Dreptul de uz pentru executarea lucr─ârilor...├Än exercitarea dreptului de uz pentru executarea lucr─ârilor necesare ├«n vederea realiz─ârii, reabilit─ârii sau moderniz─ârii obiectivelor/sistemelor, concesionarul poate:a)s─â depoziteze pe terenurile proprietate privat─â, ├«n m─âsura strict necesar─â, materiale, echipamente, utilaje ╚Öi instala╚¢ii;...b)s─â desfiin╚¢eze culturi sau planta╚¢ii ori alte amenaj─âri existente ori numai s─â le restr├óng─â, ├«n m─âsura strict necesar─â, pentru executarea lucr─ârilor, ├«n condi╚¢iile legii;...c)s─â extrag─â materiale, s─â capteze ap─â, ├«n condi╚¢iile prev─âzute de legisla╚¢ia ├«n vigoare;...d)s─â instaleze utilaje ╚Öi s─â lucreze cu acestea, s─â amplaseze birouri ╚Öi locuin╚¢e de ╚Öantier;...e)s─â opreasc─â ori s─â restr├óng─â activit─â╚¢i ale proprietarului, ├«n m─âsura strict necesar─â, pentru executarea lucr─ârilor pentru obiectivul/sistemul respectiv....', NULL, 110, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (178, 16, '111', 'Articolul 111', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Dreptul de uz pentru asigurarea func╚¢ion─ârii normale a obiectivului/sistemului...(1)...Dreptul de uz pentru asigurarea func╚¢ion─ârii normale a obiectivului/sistemului se ├«ntinde pe toat─â durata de func╚¢ionare a acestuia, iar exercitarea lui se face ori de c├óte ori este necesar pentru asigurarea func╚¢ion─ârii normale a obiectivului/sistemului....(2)...├Än exercitarea dreptului prev─âzut la alin. (1) concesionarul poate:a)s─â depoziteze materiale, echipamente, utilaje, instala╚¢ii pentru ├«ntre╚¢inere, revizii, repara╚¢ii ╚Öi interven╚¢ii;...b)s─â instaleze utilaje ╚Öi s─â lucreze cu acestea;...c)s─â afecteze culturi, planta╚¢ii sau alte amenaj─âri existente ╚Öi s─â restr├óng─â activit─â╚¢i ale proprietarului, ├«n m─âsura ╚Öi pe durata strict necesar─â, ├«n vederea execut─ârii opera╚¢iunilor de ├«ntre╚¢inere, repara╚¢ii, revizii sau interven╚¢ii....', NULL, 111, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (179, 16, '112', 'Articolul 112', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Dreptul de servitute legal─â de trecere subteran─â, de suprafa╚¢─â sau aerian─â...Servitutea legal─â de trecere subteran─â, de suprafa╚¢─â sau aerian─â cuprinde dreptul la instalare de re╚¢ele, de conducte, linii, st├ólpi ╚Öi de alte echipamente aferente capacit─â╚¢ii, precum ╚Öi accesul la locul de amplasare a acestora pentru interven╚¢ii, ├«ntre╚¢inere, repara╚¢ii, revizii, modific─âri ╚Öi exploatare, conform prevederilor legale ├«n vigoare.', NULL, 112, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (180, 16, '113', 'Articolul 113', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Exercitarea drepturilor de uz ╚Öi de servitute...(1)...Drepturile de uz ╚Öi de servitute au ca obiect utilitatea public─â, au caracter legal, iar con╚¢inutul acestora este prev─âzut la art. 108 ╚Öi se exercit─â f─âr─â ├«nscriere ├«n cartea funciar─â, pe toat─â durata existen╚¢ei obiectivului/sistemului de gaze naturale sau, temporar, cu ocazia retehnologiz─ârii unei capacit─â╚¢i ├«n func╚¢iune, repara╚¢iei, reviziei, lucr─ârilor de interven╚¢ie, ├«n caz de avarie....(2)...Exercitarea drepturilor de uz ╚Öi de servitute asupra propriet─â╚¢ilor private afectate de obiectivul/sistemul care se vor realiza dup─â intrarea ├«n vigoare a prezentei legi se face ├«n conformitate cu regulile procedurale privind condi╚¢iile ╚Öi termenii referitori la durat─â, con╚¢inutul ╚Öi limitele de exercitare a acestor drepturi, prev─âzute ├«ntr-o conven╚¢ie-cadru, precum ╚Öi pentru determinarea cuantumului indemniza╚¢iilor ╚Öi a desp─âgubirilor ╚Öi a modului de plat─â a acestora, care se aprob─â, ├«mpreun─â cu conven╚¢ia-cadru, prin hot─âr├óre a Guvernului, la propunerea ministerului de resort....(3)...Proprietarii terenurilor afectate de exercitarea drepturilor de uz ╚Öi de servitute de c─âtre titularii de licen╚¢e ╚Öi autoriza╚¢ii pot solicita ├«ncheierea de conven╚¢ii, conform prevederilor alin. (2)....(4)...Titularii de licen╚¢e ╚Öi autoriza╚¢ii sunt obliga╚¢i s─â procedeze la ├«ncheierea conven╚¢iilor-cadru prev─âzute la alin. (2), ├«n termen de maximum 30 de zile de la solicitarea proprietarilor afecta╚¢i....(5)...Dac─â, cu ocazia interven╚¢iilor pentru dezvoltare, modernizare, repara╚¢ii, revizii, avarii, se produc pagube proprietarilor din vecin─âtatea obiectivelor/sistemelor din domeniul gazelor naturale, concesionarii au obliga╚¢ia s─â pl─âteasc─â desp─âgubiri ├«n condi╚¢iile legii....(6)...Desp─âgubirile se stabilesc av├óndu-se ├«n vedere urm─âtoarele criterii:a)suprafa╚¢a de teren afectat─â cu ocazia lucr─ârilor;...b)valorile pentru produc╚¢iile estimate ale culturilor ╚Öi planta╚¢iilor afectate, comunicate de organismele abilitate, precum ╚Öi amenaj─ârile afectate de lucr─âri;...c)valoarea de circula╚¢ie a bunurilor imobile afectate.......(7)...Cuantumul desp─âgubirii se stabile╚Öte prin acordul p─âr╚¢ilor sau, ├«n cazul ├«n care p─âr╚¢ile nu se ├«n╚¢eleg, prin hot─âr├óre judec─âtoreasc─â.', NULL, 113, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (181, 16, '114', 'Articolul 114', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Dreptul de a ob╚¢ine restr├óngerea sau sistarea activit─â╚¢ilor...(1)...Pentru a evita punerea ├«n pericol a persoanelor, a bunurilor sau a unor activit─â╚¢i desf─â╚Öurate ├«n zona de executare a lucr─ârilor de dezvoltare, reabilitare, modernizare a obiectivelor/sistemelor, precum ╚Öi a opera╚¢iunilor de revizie, de ├«ntre╚¢inere sau de repara╚¢ie la obiectivul/sistemul aflat ├«n func╚¢iune, concesionarul are dreptul de a ob╚¢ine restr├óngerea sau sistarea, ├«n m─âsura necesar─â ╚Öi pe durata lucr─ârilor sau a opera╚¢iunilor men╚¢ionate, a activit─â╚¢ilor desf─â╚Öurate ├«n vecin─âtate de alte persoane....(2)...Operatorul sistemului de distribu╚¢ie are dreptul s─â intervin─â ├«n orice moment asupra re╚¢elelor de distribu╚¢ie a gazelor naturale ├«n vederea realiz─ârii unor lucr─âri de repara╚¢ii ├«n regim de urgen╚¢─â, ├«n conformitate cu reglement─ârile ANRE, f─âr─â a fi necesar─â ob╚¢inerea ├«n prealabil a avizelor ╚Öi autoriza╚¢iilor prev─âzute de prezentul titlu, ├«n vederea remedierii acelor defecte care pun ├«n pericol siguran╚¢a ╚Öi securitatea consumatorilor.', NULL, 114, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (182, 16, '115', 'Articolul 115', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Dreptul de acces la utilit─â╚¢ile publice...Dreptul de acces la utilit─â╚¢ile publice trebuie exercitat de concesionar cu bun─â-credin╚¢─â, f─âr─â a prejudicia accesul altor persoane la respectivele utilit─â╚¢i publice.', NULL, 115, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (183, 16, '116', 'Articolul 116', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Dreptul de a contracta furnizarea sau prestarea de servicii...Pentru consecin╚¢ele secundare cauzate sau care pot fi produse prin dezvoltarea, reabilitarea sau modernizarea, respectiv prin func╚¢ionarea unor obiective/sisteme din sectorul gazelor naturale, concesionarul poate contracta cu persoanele interesate, ├«n calitate de beneficiar, furnizarea sau prestarea de servicii corespunz─âtoare, ├«n condi╚¢iile legii.', NULL, 116, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (184, 16, '117', 'Articolul 117', NULL, 'Gazele naturale', NULL, 'Concesionarea ╚Öi regimul drepturilor asupra propriet─â╚¢ii altuia', NULL, NULL, NULL, NULL, 'Instituirea zonelor de protec╚¢ie sau siguran╚¢─â...Pentru protec╚¢ia ╚Öi func╚¢ionarea normal─â a obiectivelor/sistemelor din sectorul gazelor naturale, ANRE instituie, prin norme tehnice, zone de protec╚¢ie ╚Öi de siguran╚¢─â a acestora.', NULL, 117, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (185, 16, '118', 'Articolul 118', NULL, 'Gazele naturale', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, NULL, NULL, 'Regimul de autorizare...(1)...├Änfiin╚¢area de noi conducte de alimentare din amonte aferente produc╚¢iei de gaze naturale, precum ╚Öi de noi obiective de produc╚¢ie de biogaz, biometan, GNL ╚Öi GNCV sau de noi sisteme de transport, ├«nmagazinare ╚Öi distribu╚¢ie a gazelor naturale de c─âtre persoane juridice rom├óne sau str─âine se desf─â╚Öoar─â pe baz─â de autoriza╚¢ii de ├«nfiin╚¢are....(2)...Desf─â╚Öurarea activit─â╚¢ilor de furnizare a gazelor naturale, a biogazului/biometanului, GNL, GNCV, GPL, de operare a conductelor de alimentare din amonte aferente produc╚¢iei sau de ├«nmagazinare a gazelor naturale, a sistemelor de transport, de distribu╚¢ie, a terminalelor GNL, precum ╚Öi administrarea pie╚¢elor centralizate de gaze naturale se realizeaz─â pe baz─â de licen╚¢─â....(3)...Procedura de acordare, modificare, suspendare ╚Öi retragere a autoriza╚¢iilor ╚Öi licen╚¢elor, termenele ╚Öi condi╚¢iile de acordare se stabilesc prin regulamente elaborate de ANRE.', NULL, 118, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (186, 16, '119', 'Articolul 119', NULL, 'Gazele naturale', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, NULL, NULL, 'Categorii de autoriza╚¢ii ╚Öi licen╚¢e...Autoritatea competent─â emite:...1. autoriza╚¢ii de proiectare/execu╚¢ie/exploatare pentru:a)conducte de alimentare din amonte;...b)sisteme de transport al gazelor naturale;...c)sisteme de distribu╚¢ie a gazelor naturale;...d)instala╚¢ii de utilizare a gazelor naturale;...e)instala╚¢ii aferente activit─â╚¢ii de producere/stocare GNCV/biogaz/biometan;...f)instala╚¢ii tehnologice de suprafa╚¢─â aferente ├«nmagazin─ârii gazelor naturale;......2. autoriza╚¢ii pentru ├«nfiin╚¢area:a)conductelor de alimentare din amonte;...b)instala╚¢iilor tehnologice de suprafa╚¢─â aferente ├«nmagazin─ârii, ├«n cazul unor noi depozite;...c)instala╚¢iilor de producere a biogazului/biometanului;...d)sistemelor de transport/distribu╚¢ie a gazelor naturale, ├«n cazul unor noi sisteme;...e)instala╚¢iilor GNL, GNCV;......3. licen╚¢e pentru desf─â╚Öurarea activit─â╚¢ilor de:a)furnizare de gaze naturale, biogaz/biometan, GNL, GNCV, GPL;...b)operare a sistemelor de transport, distribu╚¢ie sau de ├«nmagazinare subteran─â;...c)operare a conductelor de alimentare din amonte aferente produc╚¢iei gazelor naturale;...d)operare a terminalelor GNL;...e)administrare a pie╚¢elor centralizate....', NULL, 119, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (187, 16, '120', 'Articolul 120', NULL, 'Gazele naturale', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, NULL, NULL, 'Refuz de acordare a unei autoriza╚¢ii sau licen╚¢e...(1)...ANRE poate refuza acordarea unei autoriza╚¢ii/licen╚¢e ├«n urm─âtoarele cazuri:a)solicitantul nu ├«ndepline╚Öte condi╚¢iile prev─âzute ├«n regulamentele specifice;...b)solicitantul se afl─â ├«n procedura de reorganizare judiciar─â sau de faliment;...c)solicitantului i-a fost retras─â de c─âtre ANRE o autoriza╚¢ie/licen╚¢─â ├«n ultimii 5 ani pentru motive imputabile acestuia;...d)pentru construirea ╚Öi exploatarea sistemelor de distribu╚¢ie a gazelor naturale ├«ntr-o zon─â delimitat─â pentru care a fost emis─â autoriza╚¢ie/licen╚¢─â unui alt agent economic.......(2)...Motivele pentru care se refuz─â acordarea unei autoriza╚¢ii/licen╚¢e trebuie s─â fie obiective ╚Öi nediscriminatorii, refuzul se dispune ╚Öi se motiveaz─â prin decizie a pre╚Öedintelui ANRE, solicitantul put├ónd ataca decizia la instan╚¢a judec─âtoreasc─â de contencios administrativ, ├«n condi╚¢iile legii....(3)...Motivele pentru care se refuz─â acordarea unei autoriza╚¢ii de ├«nfiin╚¢are se notific─â Comisiei Europene.', NULL, 120, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (188, 16, '121', 'Articolul 121', NULL, 'Gazele naturale', NULL, 'Autoriza╚¢ii ╚Öi licen╚¢e', NULL, NULL, NULL, NULL, 'Proiectarea ╚Öi execu╚¢ia obiectivelor din sectorul gazelor naturale...(1)...├Än vederea proiect─ârii, execu╚¢iei ╚Öi exploat─ârii obiectivelor/sistemelor din sectorul gazelor naturale, persoanele juridice au obliga╚¢ia de a de╚¢ine autoriza╚¢ii emise de ANRE ├«n baza unui regulament specific....(2)...Obiectivele din sectorul gazelor naturale, precum ╚Öi lucr─ârile de modificare, modernizare sau extindere a acestora sunt proiectate ╚Öi executate de persoane juridice autorizate....(3)...├Än vederea proiect─ârii ╚Öi execu╚¢iei obiectivelor/sistemelor din sectorul gazelor naturale, persoanele fizice au obliga╚¢ia de a de╚¢ine calitatea de instalator autorizat, acordat─â de ANRE ├«n baza unui regulament specific.', NULL, 121, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (189, 16, '122', 'Articolul 122', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Obliga╚¢ii comune ale titularilor de licen╚¢e...(1)...Obliga╚¢iile comune ale titularilor de licen╚¢e din sectorul gazelor naturale, indiferent de forma de proprietate sau de regimul juridic al acestora, sunt urm─âtoarele:a)s─â ╚¢in─â, ├«n sistemul de contabilitate intern─â, eviden╚¢e contabile separate pe tipuri de activit─â╚¢i desf─â╚Öurate ╚Öi pentru fiecare dintre activit─â╚¢ile reglementate, a╚Öa cum li s-ar cere s─â fac─â dac─â respectivele activit─â╚¢i ar fi realizate de operatori economici separa╚¢i, astfel ├«nc├ót s─â permit─â reflectarea exact─â a veniturilor ╚Öi cheltuielilor aferente fiec─ârei activit─â╚¢i, ├«n scopul evit─ârii discrimin─ârii ╚Öi subven╚¢iilor ├«ncruci╚Öate, precum ╚Öi pentru ├«ncurajarea competi╚¢iei; de asemenea, ace╚Ötia ╚¢in eviden╚¢e contabile separate, care pot fi consolidate, pentru alte activit─â╚¢i din sectorul gazelor naturale, precum ╚Öi pentru alte activit─â╚¢i din afara sectorului gazelor naturale; veniturile rezultate din de╚¢inerea ├«n proprietate a re╚¢elei de transport sau de distribu╚¢ie trebuie specificate separat ├«n conturi analitice de venituri; eviden╚¢ele contabile interne cuprind un bilan╚¢ ╚Öi un cont de profit ╚Öi de pierderi pentru fiecare activitate ├«n parte;...b)s─â ├«ntocmeasc─â, s─â supun─â spre auditare ╚Öi s─â publice situa╚¢iile financiare anuale la nivel de operator economic, care nu cuprind ├«n mod distinct sediile secundare f─âr─â personalitate juridic─â, conform legisla╚¢iei specifice, adoptate ├«n conformitate cu cea de-a Patra Directiv─â 78/660/CEE a Consiliului din 25 iulie 1978;...c)s─â p─âstreze confiden╚¢ialitatea informa╚¢iilor comerciale ob╚¢inute de la ter╚¢i;...d)s─â nu abuzeze de informa╚¢iile sensibile din punct de vedere comercial, ob╚¢inute de la ter╚¢i ├«n procesul de asigurare a accesului la sistem;...e)s─â furnizeze informa╚¢iile necesare pentru accesul ter╚¢ilor la sistem ├«ntr-o manier─â clar─â, transparent─â, u╚Öor accesibil─â ╚Öi la intervale adecvate;...f)s─â de╚¢in─â toate autoriza╚¢iile ╚Öi licen╚¢ele prev─âzute de legisla╚¢ia ├«n vigoare;...g)s─â respecte condi╚¢iile de valabilitate asociate autoriza╚¢iilor ╚Öi licen╚¢elor acordate de ANRE;...h)s─â utilizeze gazele naturale cu respectarea prevederilor art. 181;...i)s─â pun─â la dispozi╚¢ia ANRE, la cerere, copii de pe toate contractele de achizi╚¢ie/v├ónzare-cump─ârare/furnizare a gazelor naturale ╚Öi de tranzit al gazelor naturale, dup─â caz.......(2)...Operatorii economici care nu au obliga╚¢ia de a public─â situa╚¢iile financiare anuale p─âstreaz─â la sediul lor un exemplar din acestea, la dispozi╚¢ia publicului....(3)...Activitatea de audit prev─âzut─â la alin. (1) lit. b) const─â, ├«n special, ├«n verificarea respect─ârii obliga╚¢iei de a evita discrimin─ârile ╚Öi subven╚¢iile ├«ncruci╚Öate ├«ntre activit─â╚¢ile desf─â╚Öurate de operatorul economic auditat....(4)...├Än contabilitatea lor intern─â, operatorii economici sunt obliga╚¢i s─â stabileasc─â regulile de alocare a activului ╚Öi pasivului, a cheltuielilor ╚Öi veniturilor, precum ╚Öi a pierderilor, pe care le vor aplica pentru a ╚¢ine eviden╚¢ele separate prev─âzute la alin. (1) lit. a); aceste reguli pot fi modificate numai ├«n cazuri excep╚¢ionale ╚Öi trebuie s─â fie men╚¢ionate ╚Öi justificate ├«n mod corespunz─âtor, conform reglement─ârilor ANRE....(5)...Operatorii economici indic─â prin note explicative la eviden╚¢ele contabile reglementate orice tranzac╚¢ie realizat─â cu ├«ntreprinderile conexe, de o anumit─â importan╚¢─â, conform reglement─ârilor ANRE; prin ├«ntreprinderi conexe se ├«n╚¢elege ├«ntreprinderi afiliate, ├«n sensul art. 41 din a ╚ÿaptea Directiv─â 83/349/CEE a Consiliului din 13 iunie 1983 ╚Öi/sau ├«ntreprinderi asociate ├«n sensul art. 33 alin. (1) din acela╚Öi document, ╚Öi/sau ├«ntreprinderi care apar╚¢in acelora╚Öi ac╚¢ionari.', NULL, 122, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
