INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (191, 16, '124', 'Articolul 124', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Obliga╚¢iile ╚Öi drepturile produc─âtorului de gaze naturale...(1)...Obliga╚¢iile principale ale produc─âtorului de gaze naturale sunt:a)s─â de╚¢in─â autoriza╚¢iile pentru ├«nfiin╚¢area conductelor de alimentare din amonte aferente activit─â╚¢ii de produc╚¢ie a gazelor naturale ╚Öi licen╚¢a de operare a acestora;...b)s─â asigure operarea conductelor de alimentare din amonte aferente produc╚¢iei de gaze naturale ├«n condi╚¢ii de siguran╚¢─â, eficien╚¢─â ╚Öi de protec╚¢ie a mediului;...c)s─â asigure accesul ter╚¢ilor la conductele de alimentare din amonte ├«n condi╚¢ii nediscriminatorii, conform reglement─ârilor specifice;...d)s─â desf─â╚Öoare activit─â╚¢i conexe celei de operare a conductelor de alimentare din amonte, conform reglement─ârilor specifice elaborate de ANRE, ├«n limitele drepturilor conferite prin condi╚¢iile de valabilitate asociate licen╚¢ei;...e)s─â pun─â cu prioritate la dispozi╚¢ia furnizorilor cantit─â╚¢ile de gaze naturale rezultate din activitatea de produc╚¢ie, necesare acoperirii consumului pe pia╚¢a reglementat─â, ├«n conformitate cu reglement─ârile ANRE privind respectarea graficului de liberalizare a pre╚¢urilor ╚Öi de asigurare a gazelor naturale pentru clien╚¢ii captivi, furnizorii av├ónd obliga╚¢ia respect─ârii destina╚¢iei acestor cantit─â╚¢i de gaze naturale; restul produc╚¢iei proprii, mai pu╚¢in cantitatea de gaze naturale aferent─â consumului tehnologic definit la art. 100 pct. 35, va fi pus la dispozi╚¢ia pie╚¢ei concuren╚¢iale;...f)s─â asigure odorizarea gazelor naturale conform reglement─ârilor ├«n vigoare, pentru gazele naturale predate ├«n punctele de predare-preluare comercial─â a gazelor naturale c─âtre clien╚¢ii racorda╚¢i direct ├«n conductele din amonte, precum ╚Öi c─âtre sistemele de distribu╚¢ie.......(2)...Principalele drepturi ale produc─âtorului de gaze naturale sunt:a)s─â elaboreze norme tehnice/comerciale specifice activit─â╚¢ii proprii ╚Öi s─â le supun─â spre aprobare ANRE;...b)s─â comercializeze gazele naturale rezultate ca urmare a procesului de extrac╚¢ie, ├«n limitele licen╚¢ei de furnizare;...c)s─â ├«ntrerup─â func╚¢ionarea instala╚¢iilor pe timpul strict necesar pentru executarea lucr─ârilor de ├«ntre╚¢inere ╚Öi de repara╚¢ii, precum ╚Öi ├«n alte situa╚¢ii prev─âzute de lege, cu anun╚¢area prealabil─â a dispecerilor sistemelor afectate ╚Öi, dup─â caz, a clien╚¢ilor finali;...d)s─â refuze accesul ter╚¢ilor la conductele de alimentare din amonte:......1. atunci c├ónd exist─â, ├«n ceea ce prive╚Öte specifica╚¢iile tehnice, o incompatibilitate care nu poate fi rezolvat─â ├«ntr-un mod acceptabil;...2. pentru a evita dificult─â╚¢i care nu pot fi dep─â╚Öite ├«ntr-un mod acceptabil ╚Öi care ar putea prejudicia produc╚¢ia eficient─â, curent─â ╚Öi viitoare, de hidrocarburi, inclusiv din z─âc─âminte a c─âror viabilitate economic─â este sc─âzut─â;...3. pentru a respecta nevoile rezonabile ╚Öi justificate ├«n mod corespunz─âtor ale proprietarului sau ale operatorului re╚¢elei de conducte de alimentare din amonte, cu privire la transportul ╚Öi prelucrarea gazelor naturale, precum ╚Öi interesele tuturor celorlal╚¢i utilizatori ai re╚¢elei de conducte de alimentare din amonte sau ai principalelor instala╚¢ii de prelucrare ╚Öi manipulare care pot fi afectate;...4. ├«n situa╚¢iile prev─âzute la art. 149;e)s─â refuze racordarea ter╚¢ilor la conductele de alimentare din amonte, conform prevederilor art. 148....', NULL, 124, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (192, 16, '125', 'Articolul 125', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Transportul gazelor naturale...(1)...Activitatea de transport al gazelor naturale constituie serviciu public de interes na╚¢ional....(2)...Transportul gazelor naturale se realizeaz─â de c─âtre operatorul de transport ╚Öi de sistem, certificat de autoritatea competent─â ├«n condi╚¢iile legii....(3)...Sistemul na╚¢ional de transport este proprietatea public─â a statului....(4)...Lucr─ârile de dezvoltare, reabilitare, modernizare, exploatare ╚Öi ├«ntre╚¢inere a obiectivelor/sistemelor de transport al gazelor naturale sunt lucr─âri de utilitate public─â....(5)...Punctele de delimitare ale sistemului de transport sunt de la robinetul de la intrarea ├«n sta╚¢ia de reglare-m─âsurare-predare apar╚¢in├ónd produc─âtorilor sau operatorilor sistemelor de ├«nmagazinare, respectiv punctul de trecere a frontierei, ├«n cazul conductelor de interconectare la sistemele de transport din ╚¢─ârile vecine, p├ón─â la robinetul de la ie╚Öirea din sta╚¢ia de reglare-m─âsurare-predare apar╚¢in├ónd operatorului de transport, respectiv punctul de trecere a frontierei, ├«n cazul conductelor de interconectare la sistemele de transport din ╚¢─ârile vecine....(6)...Operatorul de transport ╚Öi de sistem are obliga╚¢ia de a elabora planuri de investi╚¢ii ╚Öi de dezvoltare a sistemului de transport pe 10 ani, ├«n concordan╚¢─â cu stadiul actual ╚Öi evolu╚¢ia viitoare a consumului de gaze naturale ╚Öi a surselor, inclusiv imp