INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (251, 16, '184', 'Articolul 184', NULL, 'Gazele naturale', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Solicitarea informa╚¢iilor ╚Öi documentelor...├Än realizarea investiga╚¢iilor, precum ╚Öi a competen╚¢elor conferite ├«n baza prezentului titlu, autoritatea competent─â poate solicita operatorilor economici informa╚¢iile ╚Öi documentele care sunt necesare, men╚¢ion├ónd baza legal─â ╚Öi scopul solicit─ârii, ╚Öi poate stabili termene p├ón─â la care aceste informa╚¢ii ╚Öi documente s─â ├«i fie furnizate.', NULL, 184, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (193, 16, '126', 'Articolul 126', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Certificarea operatorului de transport ╚Öi de sistem...(1)...Operatorul de transport ╚Öi de sistem se organizeaz─â ╚Öi func╚¢ioneaz─â dup─â modelul "operator de sistem independent" ╚Öi este persoana juridic─â certificat─â de ANRE ├«n condi╚¢iile prev─âzute la art. 128....(2)...Pentru a respecta condi╚¢iile prev─âzute la art. 128, prin hot─âr├óre a Guvernului elaborat─â la propunerea ministerului de resort, se stabile╚Öte entitatea public─â ce reprezint─â statul ca ac╚¢ionar majoritar la operatorii economici care desf─â╚Öoar─â activit─â╚¢i de producere ╚Öi furnizare, pe de o parte, precum ╚Öi entitatea public─â ce reprezint─â statul ca ac╚¢ionar majoritar la operatorul de transport ╚Öi sistem, pe de alt─â parte....(3)...├Än termen de 15 zile de la adoptarea m─âsurilor prev─âzute la alin. (2), operatorul de transport ╚Öi de sistem va ├«nainta ANRE, cu avizul proprietarului sistemului de transport, cererea de certificare ├«nso╚¢it─â de documenta╚¢ia justificativ─â privind ├«ndeplinirea cerin╚¢elor prev─âzute la art. 128....(4)...ANRE emite o decizie de certificare preliminar─â, ├«n termen de 4 luni de la data ├«nregistr─ârii cererii operatorului de transport ╚Öi de sistem, care se notific─â Comisiei Europene, ├«nso╚¢it─â de documenta╚¢ia aferent─â....(5)...Procedura de certificare a operatorului de transport ╚Öi de sistem se finalizeaz─â conform prevederilor art. 3 din Regulamentul (CE) nr. 715/2009 ....(6)...Desemnarea operatorului de transport ╚Öi de sistem ├«n calitate de operator de sistem independent se aprob─â de Comisia European─â ca urmare a comunic─ârii de c─âtre ANRE a operatorului economic certificat, dup─â ├«ncheierea procedurii de certificare conform alin. (5)....(7)...Pe l├óng─â decizia de certificare, autoritatea competent─â este obligat─â s─â notifice Comisiei Europene orice solicitare de certificare a proprietarului sistemului de transport sau a operatorului de transport ╚Öi de sistem, care este controlat de o persoan─â sau de persoane dintr-una sau mai multe ╚¢─âri ter╚¢e, precum ╚Öi orice circumstan╚¢e ├«n urma c─ârora o persoan─â sau persoane dintr-una sau mai multe ╚¢─âri ter╚¢e ar dob├óndi controlul asupra sistemului de transport ori asupra operatorului de transport ╚Öi de sistem, dac─â este cazul.', NULL, 126, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (194, 16, '127', 'Articolul 127', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Certificarea operatorilor de transport ╚Öi de sistem controla╚¢i de ╚¢─âri ter╚¢e...(1)...├Änainte de certificarea unui operator de transport ╚Öi de sistem care este controlat de o persoan─â sau de persoane dintr-una sau mai multe ╚¢─âri ter╚¢e, ANRE solicit─â un aviz Comisiei Europene cu privire la faptul c─â:a)entitatea respectiv─â respect─â cerin╚¢ele art. 128; ╚Öi...b)acordarea certific─ârii nu pune ├«n pericol siguran╚¢a aliment─ârii cu gaze naturale a Uniunii Europene.......(2)...ANRE refuz─â certificarea unui operator de transport ╚Öi de sistem care este controlat de o persoan─â sau de persoane dintr-una sau mai multe ╚¢─âri ter╚¢e, ├«n urm─âtoarele situa╚¢ii:a)entitatea ├«n cauz─â nu ├«ndepline╚Öte cerin╚¢ele de la art. 128;...b)acordarea certific─ârii poate pune ├«n pericol siguran╚¢a aliment─ârii cu gaze naturale pe teritoriul na╚¢ional ╚Öi al Uniunii Europene; la examinarea acestui aspect, ANRE ╚¢ine seama de:......(i) drepturile ╚Öi obliga╚¢iile Uniunii Europenefa╚¢─â de ╚¢ara ter╚¢─â respectiv─â ├«n temeiul dreptului interna╚¢ional, inclusiv al oric─ârui acord ├«ncheiat cu una sau mai multe ╚¢─âri ter╚¢e la care Uniunea European─â este parte ╚Öi ├«n care sunt abordate aspecte legate de siguran╚¢─â aliment─ârii cu gaze naturale;...(├«i) drepturile ╚Öi obliga╚¢iile Rom├óniei fa╚¢─â de ╚¢ara ter╚¢─â respectiv─â ├«n temeiul acordurilor ├«ncheiate cu ╚¢ara ter╚¢─â respectiv─â, ├«n m─âsura ├«n care acestea respect─â dreptul comunitar european; precum ╚Öi...(iii) alte elemente specifice legate de caz sau de ╚¢ara ter╚¢─â implicat─â....(3)...├Än termen de dou─â luni de la emiterea avizului de c─âtre Comisia European─â, ANRE adopt─â decizia final─â cu privire la certificare, ╚¢in├ónd cont, ├«n cel mai ├«nalt grad, de acest aviz. Decizia ANRE se public─â ├«mpreun─â cu avizul Comisiei Europene. ├Än situa╚¢ia ├«n care Comisia European─â nu emite un aviz ├«n termenul de dou─â luni de la primirea solicit─ârii, se poate considera c─â acesta nu a ridicat obiec╚¢ii privind decizia ANRE....(4)...├Än cazul ├«n care decizia final─â a ANRE difer─â de avizul Comisiei Europene, se public─â motivele acestei decizii....(5)...ANRE poate, ├«n orice situa╚¢ie, s─â refuze certificarea prev─âzut─â la alin. (1) dac─â acordarea acesteia ar pune ├«n pericol siguran╚¢a aliment─ârii cu gaze naturale pe teritoriul na╚¢ional sau pe teritoriul altui stat membru al Uniunii Europene.', NULL, 127, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (195, 16, '128', 'Articolul 128', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Condi╚¢ii de certificare a operatorului de transport ╚Öi de sistem...(1)...Certificarea operatorului de transport ╚Öi de sistem se realizeaz─â dac─â sunt ├«ndeplinite urm─âtoarele cerin╚¢e:a)aceea╚Öi persoan─â sau acelea╚Öi persoane nu au dreptul:......(i) s─â exercite, direct sau indirect, controlul asupra unui operator economic care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere sau de furnizare ╚Öi ├«n acela╚Öi timp s─â exercite, direct sau indirect, controlul sau s─â exercite vreun drept asupra operatorului de transport ╚Öi de sistem sau asupra unui sistem de transport; sau...(├«i) s─â exercite, direct sau indirect, controlul asupra operatorului de transport ╚Öi de sistem ori asupra unui sistem de transport ╚Öi s─â exercite, direct sau indirect, controlul ori s─â exercite vreun drept asupra unui operator economic care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere sau de furnizare;b)aceea╚Öi persoan─â sau acelea╚Öi persoane nu sunt abilitate s─â numeasc─â membri ├«n consiliul de supraveghere, ├«n consiliul de administra╚¢ie sau ├«n alte organisme care reprezint─â operatorul de transport ╚Öi de sistem din punct de vedere juridic ├«n cazul unui operator de transport ╚Öi de sistem sau ├«n cazul unei re╚¢ele de transport ╚Öi, de asemenea, s─â exercite, direct sau indirect, controlul ori s─â exercite vreun drept asupra unui operator economic care desf─â╚Öoar─â oricare din