INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (197, 16, '130', 'Articolul 130', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Obliga╚¢iile ╚Öi drepturile operatorului de transport ╚Öi de sistem...(1)...Operatorul de transport ╚Öi de sistem are ├«n principal urm─âtoarele obliga╚¢ii:a)s─â opereze sistemul de transport ╚Öi s─â asigure echilibrul fizic rezidual al acestuia, respectiv programarea, dispecerizarea ╚Öi func╚¢ionarea sistemului de transport ├«n condi╚¢ii de siguran╚¢─â;...b)s─â ├«ntre╚¢in─â, s─â reabiliteze, s─â modernizeze ╚Öi s─â dezvolte sistemul de transport ├«n condi╚¢ii de siguran╚¢─â, de eficien╚¢─â ╚Öi de protec╚¢ie a mediului;...c)s─â realizeze, s─â ├«ntre╚¢in─â ╚Öi s─â dezvolte un sistem informatic de monitorizare, comand─â ╚Öi achizi╚¢ie de date, care s─â permit─â monitorizarea ╚Öi conducerea operativ─â a func╚¢ion─ârii sistemului de transport al gazelor naturale;...d)s─â asigure accesul ter╚¢ilor la sistemul de transport, conform unor reglement─âri specifice, ├«n condi╚¢ii nediscriminatorii, ├«n limitele capacit─â╚¢ilor de transport ╚Öi cu respectarea regimurilor tehnologice;...e)s─â asigure racordarea ter╚¢ilor la sistemul de transport, conform unor reglement─âri specifice, ├«n limitele capacit─â╚¢ilor de transport ╚Öi cu respectarea regimurilor tehnologice;...f)s─â desf─â╚Öoare activit─â╚¢i conexe celei de operare a sistemului, conform reglement─ârilor specifice aprobate de ANRE, ├«n limitele drepturilor conferite prin condi╚¢iile de valabilitate asociate licen╚¢ei;...g)s─â elaboreze ╚Öi s─â aplice regimurile optime de transport ╚Öi de livrare pentru cantit─â╚¢ile de gaze naturale notificate de utilizatorii de re╚¢ea, pentru o anumit─â perioad─â, conform contractelor ├«ncheiate;...h)s─â elaboreze ╚Öi s─â actualizeze acordurile tehnice de exploatare ├«n zona de grani╚¢─â ╚Öi s─â le transmit─â spre avizare ANRE, anterior intr─ârii ├«n vigoare;...i)s─â ├«ntocmeasc─â ╚Öi s─â urm─âreasc─â bilan╚¢ul de gaze naturale intrate ├«n sistem ╚Öi, respectiv, ie╚Öite din sistem, conform reglement─ârilor ANRE;...j)s─â de╚¢in─â ├«n depozitele subterane sau s─â asigure achizi╚¢ia de gaze, inclusiv din import, pentru cantit─â╚¢ile necesare oper─ârii ╚Öi asigur─ârii echilibrului fizic al sistemului de transport, conform reglement─ârilor specifice aprobate de ANRE;...k)s─â asigure odorizarea gazelor naturale la ie╚Öirea din SNT, corespunz─âtor reglement─ârilor propuse de operatorul de transport ╚Öi de sistem ╚Öi aprobate de ANRE;...l)s─â elaboreze la solicitarea ANRE ╚Öi s─â transmit─â spre aprobare ANRE metodologiile de tarifare aferente desf─â╚Öur─ârii activit─â╚¢ii de furnizare de servicii de echilibrare;...m)s─â realizeze schimbul de informa╚¢ii cu al╚¢i operatori de transport ╚Öi de sistem interconecta╚¢i, cu operatori de ├«nmagazinare GNL ╚Öi de distribu╚¢ie ╚Öi cu al╚¢i colaboratori ├«n domeniul energetic, cu respectarea reglementarilor ENTSO-G privind protocoalele de schimb de informa╚¢ii, rapoartele, structura ╚Öi procedurile de acces la bazele de date;...n)s─â realizeze servicii de sistem necesare pentru accesul ╚Öi exploatarea re╚¢elelor de transport;...o)s─â elaboreze reglement─âri specifice necesare pentru realizarea activit─â╚¢ii de conducere operativ─â, cu consultarea participan╚¢ilor la pia╚¢a de gaze naturale, pe care le supune spre aprobare ANRE;...p)s─â elaboreze studii, programe ╚Öi lucr─âri privind dezvoltarea sistemului de transport al gazelor naturale;...q)s─â asigure alocarea capacit─â╚¢ilor pe conductele de interconectare cu respectarea Regulamentului (CE) nr. 715/2009;...r)s─â asigure aplicarea regulilor privind managementul congestiilor, inclusiv pe conductele de interconectare, precum ╚Öi a normelor de atribuire a capacit─â╚¢ilor de pe aceste conducte;...s)s─â organizeze ╚Öi s─â administreze pia╚¢a de echilibrare a gazelor naturale;...╚Ö)s─â asigure exploatarea sta╚¢iilor de preluare a gazelor naturale din conductele din amonte sau din sistemele de ├«nmagazinare ├«n sistemul de transport, precum ╚Öi a sta╚¢iilor de predare a gazelor naturale c─âtre sistemele de distribu╚¢ie, clien╚¢ii finali sau sistemele de ├«nmagazinare.......(2)...Operatorul sistemului de transport ╚Öi de sistem are, ├«n principal, urm─âtoarele drepturi:a)s─â perceap─â tarife nediscriminatorii, corespunz─âtoare serviciilor prestate, s─â limiteze ╚Öi/sau s─â ├«ntrerup─â prestarea serviciului, conform reglement─ârilor specifice;...b)s─â refuze accesul ter╚¢ilor la sistemul de transport, ├«n condi╚¢iile legii;...c)s─â ├«ntrerup─â sau s─â limiteze transportul gazelor naturale ├«n condi╚¢iile ├«n care sunt periclitate siguran╚¢a ╚Öi integritatea sistemului de transport, conform reglement─ârilor specifice;...d)s─â ├«ntrerup─â func╚¢ionarea instala╚¢iilor pentru timpul strict necesar, ├«n vederea execut─ârii lucr─ârilor de ├«ntre╚¢inere ╚Öi de repara╚¢ii, precum ╚Öi ├«n alte situa╚¢ii prev─âzute de lege, cu anun╚¢area prealabil─â a dispecerilor sistemelor afectate ╚Öi, dup─â caz, a clien╚¢ilor;...e)s─â elaboreze norme tehnice/comerciale/opera╚¢ionale specifice activit─â╚¢ii proprii ╚Öi s─â le supun─â spre aprobare ANRE;...f)s─â elaboreze proiecte de reglement─âri obiective, transparente ╚Öi nediscriminatorii pentru echilibrarea fizic─â a sistemului de transport al gazelor naturale, inclusiv propuneri pentru fundamentarea tarifelor care trebuie pl─âtite de utilizatorii de sistem ├«n cazul dezechilibrului fizic, ╚Öi s─â le supun─â spre aprobare ANRE;...g)s─â stocheze gaze naturale ├«n sistemul de transport, ├«n condi╚¢iile unor reglement─âri specifice aprobate de ANRE;...h)s─â foloseasc─â, cu titlu gratuit, terenurile proprietate public─â ocupate de obiectivele aferente sistemului de transport, precum ╚Öi terenurile proprietate public─â utilizate pentru lucr─ârile de execu╚¢ie, exploatare, ├«ntre╚¢inere ╚Öi repara╚¢ii;...i)s─â administreze pia╚¢a de echilibrare ├«n vederea asigur─ârii echilibrului fizic ╚Öi men╚¢inerii ├«n parametrii opera╚¢ionali a sistemului, cu efectuarea opera╚¢iunilor comerciale respective; eviden╚¢ele comerciale privind astfel de opera╚¢iuni se ╚¢in distinct de cele privind activitatea de transport.......(3)...Operarea conductelor prin care se realizeaz─â interconectarea cu statele ce nu sunt membre ale Uniunii Europene, precum ╚Öi alocarea capacit─â╚¢ilor pe acestea se realizeaz─â conform prevederilor acordurilor cu aceste state....(4)...Operatorul de transport ╚Öi de sistem ╚Öi proprietarul sistemului de transport public─â informa╚¢iile referitoare la propriile activit─â╚¢i, necesare utilizatorilor de sistem, conform reglement─ârilor ANRE, ├«n vederea asigur─ârii accesului eficient la sistem, a unei concuren╚¢e efective ╚Öi func╚¢ion─ârii eficiente a pie╚¢ei de gaze naturale, nefiindu-le permis─â divulgarea informa╚¢iilor sensibile din punct de vedere comercial ob╚¢inute pe parcursul activit─â╚¢ilor acestora, inclusiv cele ob╚¢inute de la ter╚¢i, ├«n contextul acord─ârii accesului la re╚¢ea.', NULL, 130, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
