INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (225, 16, '158', 'Articolul 158', NULL, 'Gazele naturale', NULL, 'Asigurarea calit─â╚¢ii echipamentelor, instala╚¢iilor, aparatelor, produselor ╚Öi procedeelor utilizate ├«n sectorul gazelor naturale', NULL, NULL, NULL, NULL, 'Utilizarea echipamentelor, instala╚¢iilor, aparatelor, produselor ╚Öi procedeelor ├«n sectorul gazelor naturale...(1)...Utilizarea echipamentelor, instala╚¢iilor, aparatelor, produselor ╚Öi procedeelor ├«n sectorul gazelor naturale este permis─â dup─â ob╚¢inerea ├«n prealabil a acceptului operatorului sistemului, ├«n baza unor metodologii/proceduri elaborate de c─âtre acesta ╚Öi avizate de ANRE....(2)...ANRE asigur─â transparen╚¢a acestui proces prin publicarea metodologiilor/procedurilor avizate pe site-ul propriu.', NULL, 158, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (199, 16, '132', 'Articolul 132', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Separarea proprietarului sistemului de transport...(1)...├Än cazul ├«n care proprietarul sistemului de transport face parte dintr-un operator economic integrat pe vertical─â, proprietarul sistemului de transport este independent cel pu╚¢in ├«n ceea ce prive╚Öte statutul s─âu juridic, organizarea ╚Öi procesul decizional fa╚¢─â de alte activit─â╚¢i care nu au leg─âtur─â cu transportul gazelor naturale....(2)...Pentru a asigura independen╚¢a proprietarului sistemului de transport ├«n condi╚¢iile alin. (1), se aplic─â urm─âtoarele criterii:a)persoanele cu func╚¢ie de conducere ale proprietarului sistemului de transport nu pot face parte din structurile operatorului economic integrat din domeniul gazelor naturale care r─âspunde, direct sau indirect, de gestionarea zilnic─â a activit─â╚¢ilor de producere, distribu╚¢ie ╚Öi furnizare a gazelor naturale;...b)persoanele cu func╚¢ie de conducere din cadrul proprietarului sistemului de transport ac╚¢ioneaz─â independent de orice interes de pia╚¢─â ├«n ├«ndeplinirea atribu╚¢iilor de serviciu;...c)proprietarul sistemului de transport stabile╚Öte un program de conformitate, care con╚¢ine m─âsurile luate pentru a garanta excluderea practicilor discriminatorii ╚Öi stabile╚Öte ╚Öi obliga╚¢iile specifice impuse angaja╚¢ilor pentru realizarea obiectivului de independen╚¢─â;...d)proprietarul sistemului de transport desemneaz─â o persoan─â sau un organism, denumit agent de conformitate, care s─â asigure monitorizarea adecvat─â a respect─ârii programului de conformitate ╚Öi care depune la ANRE ├«n luna decembrie a fiec─ârui an un raport cu privire la m─âsurile luate, raport care se public─â pe site-ul operatorului de transport ╚Öi de sistem.......(3)...Proprietarul re╚¢elei de transport transmite spre aprobare ANRE toate proiectele de contracte ce urmeaz─â s─â fie ├«ncheiate cu operatorul de transport ╚Öi de sistem, inclusiv cele referitoare la utilizarea bunurilor existente, precum ╚Öi a celor realizate ca urmare a investi╚¢iilor ├«n re╚¢eaua de transport....(4)...Persoanele care au exercitat ├«n cadrul operatorului de transport ╚Öi de sistem func╚¢ii de conducere sau alte func╚¢ii relevante ├«n temeiul c─ârora au avut acces la informa╚¢ii comerciale sensibile, definite potrivit legii, nu pot ocupa func╚¢ii similare ├«n cadrul operatorilor economici din domeniul producerii ╚Öi/sau furniz─ârii de gaze naturale, pe o perioad─â de minimum 6 luni de la data ├«ncet─ârii raporturilor contractuale cu operatorul de transport ╚Öi de sistem.', NULL, 132, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (200, 16, '133', 'Articolul 133', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Interdic╚¢ii privind de╚¢inerea controlului asupra operatorilor de transport ╚Öi de sistem...Operatorilor economici care desf─â╚Öoar─â oricare dintre activit─â╚¢ile de producere sau de furnizare de gaze naturale le este interzis ca, direct sau indirect, s─â exercite controlul sau s─â exercite vreun drept cu privire la operatorii de transport ╚Öi de sistem separa╚¢i din alte state ale Uniunii Europene care aplic─â dispozi╚¢iile art. 9 alin. (1) din Directiva 2009/73/CE a Parlamentului European ╚Öi a Consiliului din 13 iulie 2009 privind normele comune pentru pia╚¢a intern─â ├«n sectorul gazelor naturale ╚Öi de abrogare a Directivei 2003/55/CE.', NULL, 133, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (201, 16, '134', 'Articolul 134', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Distribu╚¢ia gazelor naturale...(1)...Distribu╚¢ia gazelor naturale se realizeaz─â de c─âtre operatorul de distribu╚¢ie....(2)...Operatorul de distribu╚¢ie presteaz─â serviciul de distribu╚¢ie pentru to╚¢i utilizatorii sistemului de distribu╚¢ie, ├«n condi╚¢ii nediscriminatorii, asigur├ónd accesul la acesta oric─ârui solicitant care ├«ndepline╚Öte cerin╚¢ele prezentului titlu, cu respectarea normelor ╚Öi standardelor de performan╚¢─â prev─âzute ├«n reglement─ârile tehnice ├«n vigoare....(3)...Delimitarea unui sistem de distribu╚¢ie se realizeaz─â, dup─â caz:a)de obiectivele de produc╚¢ie de la robinetul aflat la ie╚Öirea din sta╚¢ia de reglare-m─âsurare-predare a produc─âtorului;...b)de sistemul de transport de la ie╚Öirea din sta╚¢ia de reglare-m─âsurare-predare a operatorului de transport;...c)de alt sistem de distribu╚¢ie de la ie╚Öirea din sta╚¢ia de reglare/m─âsurare dintre operatorii de distribu╚¢ie;...d)de clien╚¢ii finali de la ie╚Öirea din sta╚¢iile/posturile de reglare/m─âsurare sau, dup─â caz, ie╚Öirea din robinetul de bran╚Öament c─âtre instala╚¢iile de utilizare ale acestora....', NULL, 134, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (202, 16, '135', 'Articolul 135', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Caracterul activit─â╚¢ii de distribu╚¢ie...Activitatea de distribu╚¢ie a gazelor naturale, cu excep╚¢ia celei realizate prin sistemele de distribu╚¢ie ├«nchise, constituie serviciu public de interes general.', NULL, 135, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (203, 16, '136', 'Articolul 136', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Independen╚¢a operatorului de distribu╚¢ie...(1)...├Än cazul ├«n care operatorul de distribu╚¢ie face parte dintr-un operator economic integrat pe vertical─â, acesta trebuie s─â fie independent cel pu╚¢in ├«n ceea ce prive╚Öte forma sa juridic─â, organizarea ╚Öi procesul decizional, ├«n raport cu celelalte activit─â╚¢i care nu au leg─âtur─â cu distribu╚¢ia; aceast─â regul─â nu creeaz─â obliga╚¢ia separ─ârii propriet─â╚¢ii asupra activelor apar╚¢in├ónd operatorului de distribu╚¢ie fa╚¢─â de operatorul economic integrat pe vertical─â....(2)...├Än vederea asigur─ârii independen╚¢ei operatorului de distribu╚¢ie care face parte dintr-un operator economic integrat pe vertical─â fa╚¢─â de celelalte activit─â╚¢i desf─â╚Öurate de acesta din urm─â, se aplic─â urm─âtoarele criterii:a)persoanele care asigur─â conducerea operatorului de distribu╚¢ie nu pot face parte din structurile operatorului integrat pe vertical─â care r─âspund, direct sau indirect, de gestionarea activit─â╚¢ilor de produc╚¢ie, transport ╚Öi furnizare a gazelor naturale;...b)luarea m─âsurilor corespunz─âtoare pentru a se asigura c─â interesele profesionale ale persoanelor responsabile pentru managementul operatorului de distribu╚¢ie sunt luate ├«n considerare, ├«ntr-o manier─â de natur─â s─â asigure independen╚¢a de ac╚¢iune a acestora;...c)operatorul de distribu╚¢ie are dreptul de a lua decizii, ├«n mod efectiv, independent de operatorul economic integrat pe vertical─â, cu privire la activele necesare pentru exploatarea, ├«ntre╚¢inerea sau dezvoltarea sistemului de distribu╚¢ie; pentru a ├«ndeplini aceste atribu╚¢ii, operatorul de distribu╚¢ie are la dispozi╚¢ie resursele necesare, inclusiv resursele umane, tehnice, financiare ╚Öi fizice; aceasta nu trebuie s─â afecteze existen╚¢a unor mecanisme de coordonare corespunz─âtoare care trebuie s─â asigure c─â sunt protejate drepturile de supraveghere economic─â ╚Öi managerial─â ale societ─â╚¢ii-mam─â cu privire la rentabilitatea activelor de c─âtre filial─â; aceste mecanisme vor permite ├«n mod special societ─â╚¢ii-mam─â s─â aprobe planul financiar anual sau orice instrument echivalent al operatorului de distribu╚¢ie ╚Öi s─â stabileasc─â limitele globale de ├«ndatorare ale filialei; nu se va permite ├«ns─â societ─â╚¢ii-mam─â s─â dea instruc╚¢iuni privind opera╚¢iunile curente ╚Öi nici privind decizii individuale ce au ├«n vedere construirea sau modernizarea obiectivelor apar╚¢in├ónd sistemelor de distribu╚¢ie care nu dep─â╚Öesc termenii stabili╚¢i ├«n planul financiar aprobat sau ├«n oricare alt instrument echivalent;...d)operatorul de distribu╚¢ie trebuie s─â stabileasc─â un program de conformitate, ├«n care s─â prezinte m─âsurile asumate ce asigur─â excluderea oric─ârui comportament discriminatoriu ╚Öi garanteaz─â c─â respectarea acestuia este monitorizat─â ├«n mod adecvat; acest program trebuie s─â prezinte ╚Öi obliga╚¢iile specifice ale angaja╚¢ilor pentru realizarea acestui obiectiv; persoana sau organismul responsabil pentru monitorizarea programului de conformitate, denumit agent de conformitate, transmite ANRE ╚Öi public─â, ├«n luna decembrie a fiec─ârui an, un raport cuprinz├ónd m─âsurile luate; agentul de conformitate al operatorului de distribu╚¢ie este complet independent ╚Öi are acces la toate informa╚¢iile operatorului de distribu╚¢ie sau ale oric─ârui operator economic afiliat, care sunt necesare ├«n vederea ├«ndeplinirii atribu╚¢iei sale.......(3)...Operatorii de distribu╚¢ie integra╚¢i vertical nu trebuie, ├«n activit─â╚¢ile lor de comunicare ╚Öi de publicitate, s─â creeze confuzie cu privire la identitatea separat─â a filialei de furnizare din cadrul operatorului economic integrat vertical....(4)...Fac excep╚¢ie de la prevederile alin. (1)-(3) operatorii economici care desf─â╚Öoar─â activit─â╚¢i de distribu╚¢ie a gazelor naturale ╚Öi care deservesc un num─âr de cel mult 100.000 de clien╚¢i finali.', NULL, 136, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (204, 16, '137', 'Articolul 137', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Obliga╚¢ia de p─âstrare a confiden╚¢ialit─â╚¢ii...(1)...Operatorul de distribu╚¢ie este obligat s─â p─âstreze confiden╚¢ialitatea asupra informa╚¢iilor sensibile din punct de vedere comercial ob╚¢inute ├«n cursul desf─â╚Öur─ârii activit─â╚¢ii sale....(2)...Operatorul de distribu╚¢ie este obligat s─â ├«mpiedice divulgarea discriminatorie a informa╚¢iilor privind propria activitate, care pot fi avantajoase din punct de vedere comercial.', NULL, 137, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (205, 16, '138', 'Articolul 138', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Obliga╚¢iile ╚Öi drepturile operatorului de distribu╚¢ie...(1)...Distribuitorul de gaze naturale are, ├«n principal, urm─âtoarele obliga╚¢ii:a)s─â opereze, s─â ├«ntre╚¢in─â, s─â repare, s─â modernizeze ╚Öi s─â dezvolte sistemul de distribu╚¢ie ├«n condi╚¢ii de siguran╚¢─â, eficien╚¢─â economic─â ╚Öi de protec╚¢ie a mediului, activit─â╚¢ile urm├ónd a fi desf─â╚Öurate ├«n baza autoriza╚¢iilor specifice pentru proiectare ╚Öi execu╚¢ie a sistemelor de distribu╚¢ie a gazelor naturale, iar operarea urm├ónd s─â se desf─â╚Öoare ├«n baza licen╚¢ei de distribu╚¢ie;...b)s─â asigure odorizarea gazelor naturale corespunz─âtor reglement─ârilor aprobate de ANRE, ├«n baza contractelor de prest─âri de servicii ├«ncheiate cu operatorul din amonte, ╚Öi, acolo unde este cazul, prin odorizare suplimentar─â ├«n sistemul de distribu╚¢ie;...c)s─â realizeze interconect─âri cu alte sisteme, dup─â caz, ╚Öi s─â asigure capacitatea sistemului de distribu╚¢ie pe termen lung;...d)s─â asigure accesul ter╚¢ilor la sistemele de distribu╚¢ie, ├«n condi╚¢ii nediscriminatorii, ├«n limitele capacit─â╚¢ilor de distribu╚¢ie, cu respectarea regimurilor tehnologice, conform reglement─ârilor specifice elaborate de ANRE;...e)s─â ├«ntocmeasc─â ╚Öi s─â urm─âreasc─â bilan╚¢ul de gaze naturale intrate ╚Öi, respectiv, ie╚Öite din sistemul propriu;...f)s─â evite subven╚¢ia ├«ncruci╚Öat─â ├«ntre categoriile de clien╚¢i finali cu privire la repartizarea costurilor;...g)s─â preia pentru o perioad─â determinat─â, dar nu mai mult de 2 ani, la solicitarea ╚Öi conform reglement─ârilor ANRE, operarea unui sistem de distribu╚¢ie ├«n cazul ├«n care operatorului ini╚¢ial i-a fost retras─â licen╚¢a de distribu╚¢ie sau a fost reziliat contractul de concesiune;...h)s─â asigure echilibrul permanent al sistemului operat;...i)s─â asigure condi╚¢iile de securitate ├«n alimentarea cu gaze naturale;...j)s─â desf─â╚Öoare activit─â╚¢i conexe celei de operare a sistemului, conform reglement─ârilor specifice elaborate de ANRE, ├«n limitele stabilite prin condi╚¢iile de valabilitate asociate licen╚¢ei;...k)s─â elaboreze ╚Öi s─â trimit─â ANRE planurile de investi╚¢ii pe 5 ani ale sistemelor pe care le opereaz─â; aceste planuri se actualizeaz─â anual de c─âtre operator p├ón─â la sf├ór╚Öitul lunii decembrie ╚Öi se aprob─â de c─âtre ANRE.......(2)...Operatorul de distribu╚¢ie a gazelor naturale are, ├«n principal, urm─âtoarele drepturi:a)s─â desf─â╚Öoare activit─â╚¢i comerciale legate de serviciul de distribu╚¢ie a gazelor naturale;...b)s─â ├«ncaseze contravaloarea tarifelor corespunz─âtoare serviciilor prestate, s─â limiteze ╚Öi/sau s─â ├«ntrerup─â prestarea serviciului, conform reglement─ârilor specifice;...c)s─â ├«ntrerup─â func╚¢ionarea obiectivelor sistemului de distribu╚¢ie ╚Öi alimentarea cu gaze naturale a clien╚¢ilor pentru timpul strict necesar execut─ârii lucr─ârilor de ├«ntre╚¢inere ╚Öi repara╚¢ii, precum ╚Öi ├«n alte situa╚¢ii prev─âzute de prezentul titlu sau ├«n caz de for╚¢─â major─â, cu anun╚¢area prealabil─â a dispecerilor sistemelor afectate ╚Öi, dup─â caz, a clien╚¢ilor;...d)s─â foloseasc─â, cu titlu gratuit, terenurile proprietate public─â local─â ocupate de obiectivele sistemului de distribu╚¢ie, precum ╚Öi pentru realizarea lucr─ârilor de execu╚¢ie, operare, ├«ntre╚¢inere ╚Öi repara╚¢ii, ├«n condi╚¢iile legii;...e)s─â aib─â acces la instala╚¢iile de utilizare ale clien╚¢ilor finali pentru verificarea informa╚¢iilor referitoare la debitele instalate, modific─ârile efectuate, precum ╚Öi ori de c├óte ori este necesar─â interven╚¢ia ├«n scopul men╚¢inerii siguran╚¢ei ├«n func╚¢ionare a acestora;...f)s─â sisteze alimentarea cu gaze naturale a aparatelor/instala╚¢iilor de utilizare care nu respect─â prevederile legisla╚¢iei ├«n vigoare ╚Öi care pot pune ├«n pericol securitatea persoanelor sau integritatea bunurilor, dup─â certificarea neconformit─â╚¢ilor de c─âtre un operator economic autorizat de ANRE, altul dec├ót operatorul de distribu╚¢ie;...g)s─â stocheze gaze naturale ├«n sistemele de distribu╚¢ie, conform reglement─ârilor aprobate de ANRE;...h)s─â refuze racordarea la sistemul de distribu╚¢ie ├«n condi╚¢iile art. 150;...i)s─â elaboreze norme tehnice/comerciale specifice activit─â╚¢ii proprii ╚Öi s─â le supun─â spre aprobare ANRE;...j)├«n cazul interven╚¢iilor de c─âtre persoane neautorizate asupra instala╚¢iilor de reglare-m─âsurare aflate la limita de proprietate, care pun ├«n pericol siguran╚¢a aliment─ârii cu gaze naturale, operatorul de distribu╚¢ie este ├«ndrept─â╚¢it s─â ├«ntrerup─â alimentarea, ├«n conformitate cu reglement─ârile specifice ale ANRE....', NULL, 138, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (206, 16, '139', 'Articolul 139', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Sistemul de distribu╚¢ie ├«nchis...(1)...Sistemul de distribu╚¢ie ├«nchis este acel sistem prin care se distribuie gaze naturale ├«ntr-o zon─â industrial─â, comercial─â sau de servicii comune, limitat─â din punct de vedere geografic ╚Öi care, f─âr─â a aduce atingere prevederilor alin. (4), nu alimenteaz─â clien╚¢ii casnici, dac─â, din ra╚¢iuni de organizare tehnologic─â, activit─â╚¢ile derulate de utilizatorii sistemului respectiv sunt integrate sau sistemul respectiv asigur─â gazele naturale ├«n principal c─âtre proprietarul sistemului, operatorul sistemului sau un operator economic afiliat acestora, dup─â caz....(2)...Obliga╚¢iile privind dezvoltarea sistemului de distribu╚¢ie, prev─âzute la art. 138 alin. (1) lit. c), g) ╚Öi k), nu sunt aplicabile operatorului unui sistem de distribu╚¢ie ├«nchis....(3)...Tarifele pentru serviciul de distribu╚¢ie dintr-un sistem de distribu╚¢ie ├«nchis sau metodologiile care stau la baza calcul─ârii acestora sunt aprobate de ANRE, la cererea operatorului sistemului de distribu╚¢ie ├«nchis....(4)...Exceptarea prev─âzut─â la alin. (2) se aplic─â ╚Öi ├«n cazul ├«n care ├«n zona deservit─â de un sistem de distribu╚¢ie ├«nchis sunt amplasa╚¢i clien╚¢i casnici, numai dac─â ace╚Ötia se afl─â ├«ntr-un raport de munc─â sau ├«ntr-o form─â de asociere cu proprietarul sistemului de distribu╚¢ie la momentul racord─ârii....(5)...├Änfiin╚¢area, respectiv operarea unui sistem de distribu╚¢ie ├«nchis se realizeaz─â prin autoriza╚¢ie de ├«nfiin╚¢are, respectiv pe baz─â de licen╚¢─â, acordate de ANRE.', NULL, 139, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (236, 16, '169', 'Articolul 169', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Reguli ╚Öi mecanisme pentru gestionarea ╚Öi alocarea capacit─â╚¢ii...La acordarea unei derog─âri, ANRE poate decide asupra regulilor ╚Öi mecanismelor pentru gestionarea ╚Öi alocarea capacit─â╚¢ii, ├«n cazul ├«n care acestea nu ├«mpiedic─â punerea ├«n aplicare a contractelor pe termen lung.', NULL, 169, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (208, 16, '141', 'Articolul 141', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Independen╚¢a operatorului de ├«nmagazinare...(1)...Un operator de ├«nmagazinare care face parte dintr-un operator economic integrat pe vertical─â trebuie s─â fie independent cel pu╚¢in ├«n ceea ce prive╚Öte forma juridic─â, organizarea ╚Öi procesul de luare a deciziilor fa╚¢─â de alte activit─â╚¢i care nu au leg─âtur─â cu transportul, distribu╚¢ia sau ├«nmagazinarea....(2)...Prevederile alin. (1) se aplic─â numai cu privire la instala╚¢iile de ├«nmagazinare care sunt necesare din punct de vedere tehnic ╚Öi/sau economic pentru asigurarea unui acces eficient la sistem ├«n scopul aliment─ârii clien╚¢ilor, ├«n conformitate cu reglement─ârile specifice....(3)...├Än vederea asigur─ârii independen╚¢ei operatorului de ├«nmagazinare fa╚¢─â de operatorul economic integrat pe vertical─â din care face parte ╚Öi care desf─â╚Öoar─â cel pu╚¢in una din activit─â╚¢ile de produc╚¢ie sau de furnizare, se aplic─â urm─âtoarele criterii minime:a)persoanele care asigur─â conducerea operatorului de ├«nmagazinare nu pot face parte din structurile operatorului economic integrat pe vertical─â care r─âspund, direct sau indirect, de coordonarea activit─â╚¢ii de produc╚¢ie ╚Öi furnizare a gazelor naturale;...b)trebuie luate m─âsuri adecvate pentru a garanta c─â interesele profesionale ale persoanelor cu func╚¢ii de conducere din cadrul operatorului de ├«nmagazinare sunt luate ├«n considerare, astfel ├«nc├ót s─â se asigure faptul c─â acestea au posibilitatea de a ac╚¢iona independent;...c)operatorul de ├«nmagazinare dispune de suficiente competen╚¢e de luare a deciziilor, independent de societatea-mam─â, cu privire la elementele de active necesare pentru exploatarea, ├«ntre╚¢inerea sau dezvoltarea instala╚¢iilor de ├«nmagazinare; aceasta nu ├«mpiedic─â existen╚¢a unor mecanisme de coordonare adecvate care s─â garanteze protejarea drepturilor de supraveghere economic─â ╚Öi a celor de supraveghere a gestiunii ale societ─â╚¢ii-mam─â asupra randamentului activelor unei filiale; societatea-mam─â are dreptul s─â aprobe planul financiar anual al operatorului de ├«nmagazinare sau orice document echivalent ╚Öi s─â stabileasc─â limite globale ale nivelului de ├«ndatorare a filialei sale; societatea-mam─â nu are dreptul s─â dea instruc╚¢iuni privind gestionarea curent─â, nici cu privire la deciziile individuale referitoare la construirea sau modernizarea instala╚¢iilor de ├«nmagazinare, care nu dep─â╚Öesc limitele din planul financiar aprobat sau orice document echivalent;...d)operatorul de ├«nmagazinare stabile╚Öte un program de conformitate, care con╚¢ine m─âsurile luate pentru a garanta excluderea practicilor discriminatorii ╚Öi stabile╚Öte ╚Öi obliga╚¢iile specifice impuse angaja╚¢ilor pentru realizarea obiectivului de independen╚¢─â;...e)operatorul de ├«nmagazinare desemneaz─â o persoan─â sau un organism, denumit agent de conformitate, care s─â asigure monitorizarea adecvat─â a respect─ârii programului de conformitate ╚Öi care transmite autorit─â╚¢ii competente ├«n luna decembrie a fiec─ârui an un raport cu privire la m─âsurile luate, raport care se public─â pe site-ul operatorului de ├«nmagazinare....', NULL, 141, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (209, 16, '142', 'Articolul 142', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Obliga╚¢iile ╚Öi drepturile operatorului de ├«nmagazinare...(1)...Operatorul de ├«nmagazinare are, ├«n principal, urm─âtoarele obliga╚¢ii:a)s─â opereze, s─â ├«ntre╚¢in─â, s─â reabiliteze ╚Öi s─â modernizeze instala╚¢iile tehnologice de suprafa╚¢─â aferente depozitelor de ├«nmagazinare, ├«n condi╚¢ii de siguran╚¢─â, de eficien╚¢─â ╚Öi de protec╚¢ie a mediului;...b)s─â asigure accesul ter╚¢ilor la depozitele de ├«nmagazinare, pe baza unor criterii obiective, transparente ╚Öi nediscriminatorii, conform reglement─ârilor ANRE;...c)s─â publice lista instala╚¢iilor de ├«nmagazinare sau a unor p─âr╚¢i dintre acestea care sunt oferite spre acces ter╚¢ilor;...d)s─â furnizeze informa╚¢ii utilizatorilor sistemului de ├«nmagazinare, necesare pentru un acces eficient la sistem;...e)s─â elaboreze ╚Öi s─â trimit─â ANRE planurile de investi╚¢ii pe 5 ani ale sistemelor pe care le opereaz─â; aceste planuri se actualizeaz─â anual de c─âtre operator p├ón─â la sf├ór╚Öitul lunii decembrie ╚Öi se aprob─â de c─âtre ANRE.......(2)...Operatorul depozitului de ├«nmagazinare are, ├«n principal, urm─âtoarele drepturi:a)s─â ├«ncaseze tariful aferent prest─ârii serviciului de ├«nmagazinare subteran─â a gazelor naturale, s─â limiteze ╚Öi/sau s─â ├«ntrerup─â prestarea serviciului, conform reglement─ârilor specifice;...b)s─â elaboreze norme tehnice/comerciale specifice activit─â╚¢ii proprii ╚Öi s─â le supun─â spre aprobare ANRE;...c)s─â ├«ntrerup─â func╚¢ionarea instala╚¢iilor pentru timpul strict necesar, ├«n vederea execut─ârii lucr─ârilor de ├«ntre╚¢inere ╚Öi de repara╚¢ii, precum ╚Öi ├«n alte situa╚¢ii prev─âzute de lege, cu anun╚¢area prealabil─â a dispecerilor sistemelor afectate ╚Öi, dup─â caz, a clien╚¢ilor;...d)s─â refuze ├«n mod justificat accesul ter╚¢ilor la depozitele de ├«nmagazinare, ├«n condi╚¢iile legii.......(3)...Operatorul de ├«nmagazinare public─â informa╚¢iile referitoare la propriile activit─â╚¢i, necesare utilizatorilor de sistem/solicitan╚¢ilor de acces, conform reglement─ârilor ANRE, ├«n vederea asigur─ârii accesului eficient la sistem, a unei concuren╚¢e efective ╚Öi func╚¢ion─ârii eficiente a pie╚¢ei de gaze naturale, nefiindu-i permis─â divulgarea informa╚¢iilor sensibile din punct de vedere comercial ob╚¢inute pe parcursul activit─â╚¢ilor acestuia, inclusiv cele ob╚¢inute de la ter╚¢i ├«n contextul acord─ârii accesului la re╚¢ea.', NULL, 142, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (210, 16, '143', 'Articolul 143', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Obliga╚¢iile ╚Öi drepturile furnizorului de gaze naturale...(1)...Furnizorul de gaze naturale are, ├«n principal, urm─âtoarele obliga╚¢ii:a)s─â desf─â╚Öoare activitatea de furnizare a gazelor naturale pe baz─â de contracte comerciale ├«ncheiate conform reglement─ârilor ANRE;...b)s─â respecte standardele de performan╚¢─â pentru serviciul de furnizare prestat ├«n baza contractelor-cadru;...c)s─â asigure ├«nmagazinarea gazelor naturale, conform reglement─ârilor ANRE;...d)s─â ├«ncheie contracte de achizi╚¢ie a gazelor naturale, astfel ├«nc├ót s─â asigure acoperirea consumului pentru clien╚¢ii s─âi;...e)s─â pun─â la dispozi╚¢ia clien╚¢ilor datele relevante privind consumul acestora pe o perioad─â ce cuprinde cel pu╚¢in ultimii 5 ani;...f)s─â ├«nfiin╚¢eze puncte unice de contact pentru informarea clien╚¢ilor finali cu privire la drepturile acestora, la legisla╚¢ia ├«n vigoare ╚Öi la c─âile de solu╚¢ionare a litigiilor, ├«n cazul unui diferend;...g)s─â permit─â clien╚¢ilor schimbarea efectiv─â a furnizorului de gaze naturale ├«n termen de 3 s─âpt─âm├óni de la data solicit─ârii;...h)s─â pun─â la dispozi╚¢ia clien╚¢ilor mai multe modalit─â╚¢i de plat─â a contravalorii consumului de gaze naturale ╚Öi s─â permit─â acestora s─â opteze pentru oricare dintre acestea;...i)s─â pl─âteasc─â contravaloarea gazelor naturale achizi╚¢ionate, conform contractelor ├«ncheiate.......(2)...Furnizorul de gaze naturale are, ├«n principal, urm─âtoarele drepturi:a)s─â ├«ncaseze contravaloarea gazelor naturale furnizate, conform contractelor ├«ncheiate cu clien╚¢ii;...b)s─â ├«ncaseze contravaloarea gazelor naturale furnizate ├«n regim de furnizare de ultim─â instan╚¢─â, conform reglement─ârilor ANRE;...c)s─â limiteze ╚Öi/sau s─â sisteze furnizarea gazelor naturale la clien╚¢i, ├«n condi╚¢iile specificate ├«n contracte....', NULL, 143, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (211, 16, '144', 'Articolul 144', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Obliga╚¢iile furnizorului de ultim─â instan╚¢─â...(1)...Furnizorul de ultim─â instan╚¢─â are obliga╚¢ia de a asigura furnizarea gazelor naturale clien╚¢ilor finali, ├«n conformitate cu reglement─ârile ANRE, la pre╚¢uri reglementate de ANRE....(2)...Furnizorul de ultim─â instan╚¢─â are obliga╚¢ia de a furniza, conform reglement─ârilor emise de ANRE, gaze naturale clien╚¢ilor finali al c─âror furnizor se afl─â ├«n situa╚¢ia de a i se retrage licen╚¢a de furnizare ├«n cursul desf─â╚Öur─ârii activit─â╚¢ii sau ├«n orice alt─â situa╚¢ie identificat─â de ANRE ├«n care clien╚¢ii finali nu au asigurat─â furnizarea de gaze naturale din nicio alt─â surs─â.', NULL, 144, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (212, 16, '145', 'Articolul 145', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Obliga╚¢iile ╚Öi drepturile clientului final de gaze naturale...(1)...Clien╚¢ii finali au obliga╚¢ia s─â achite facturile reprezent├ónd contravaloarea serviciilor prestate de c─âtre furnizorul/operatorul sistemului, ├«n termenul ╚Öi ├«n condi╚¢iile prev─âzute ├«n contractul ├«ncheiat cu acesta....(2)...Pentru ne├«ndeplinirea obliga╚¢iilor contractuale, altele dec├ót obliga╚¢ia de plat─â a clientului final, partea ├«n culp─â pl─âte╚Öte celeilalte p─âr╚¢i daune-interese p├ón─â la acoperirea integral─â a prejudiciului cauzat, ├«n conformitate cu prevederile stipulate ├«n contract....(3)...├Än cazul interven╚¢iilor asupra sistemelor de alimentare cu gaze naturale, cu excep╚¢ia instala╚¢iilor de utilizare a gazelor naturale, de c─âtre persoane neautorizate, care pun ├«n pericol siguran╚¢a aliment─ârii cu gaze naturale, operatorul de transport/distribu╚¢ie este ├«ndrept─â╚¢it s─â ├«ntrerup─â alimentarea, ├«n conformitate cu reglement─ârile specifice ale ANRE. ├Än cazul constat─ârii, conform prevederilor legale ├«n vigoare, a unor ac╚¢iuni menite s─â denatureze ├«n orice fel indica╚¢iile echipamentelor de m─âsurare sau s─â sustrag─â gaze naturale prin ocolirea echipamentelor de m─âsurare, clientul final este obligat s─â constituie o garan╚¢ie financiar─â pentru un consum maxim echivalent pentru un an....(4)...Clien╚¢ii finali de gaze naturale au urm─âtoarele drepturi:a)s─â aib─â acces la sistem ├«n condi╚¢iile legii ╚Öi s─â fie alimenta╚¢i cu gaze naturale ├«n conformitate cu prevederile contractului de furnizare, ├«ncheiat ├«n conformitate cu contractul-cadru aprobat de ANRE;...b)s─â solicite furnizorului/operatorului sistemului modificarea ╚Öi completarea contractului ├«ncheiat cu acesta, atunci c├ónd apar elemente noi ori c├ónd consider─â necesar─â detalierea sau completarea unor clauze contractuale, ├«n conformitate cu prevederile legale ├«n vigoare;...c)s─â fie notifica╚¢i ├«n mod corespunz─âtor cu privire la orice inten╚¢ie de modificare a contractului ╚Öi s─â fie informa╚¢i, ├«n momentul notific─ârii, cu privire la dreptul de a denun╚¢a contractul, ├«n cazul ├«n care nu accept─â noile condi╚¢ii;...d)s─â solicite furnizorului ├«ntreruperea furniz─ârii gazelor naturale, ├«n cazul ├«n care ├«ntreruperea este legat─â de func╚¢ionarea ├«n condi╚¢ii de siguran╚¢─â a instala╚¢iilor clientului final sau ale operatorului de sistem;...e)s─â solicite furnizorului/operatorului de sistem s─â ia m─âsuri pentru remedierea defec╚¢iunilor survenite;...f)s─â solicite ╚Öi s─â primeasc─â penalit─â╚¢ile suportate de c─âtre furnizor/operatorul de sistem pentru ne├«ndeplinirea obliga╚¢iilor, ├«n conformitate cu prevederile standardelor de performan╚¢─â;...g)s─â li se pun─â la dispozi╚¢ie cel pu╚¢in dou─â modalit─â╚¢i de plat─â, care s─â le permit─â s─â ├«╚Öi ├«ndeplineasc─â obliga╚¢iile de plat─â a facturii prev─âzute ├«n contract;...h)s─â ├«╚Öi schimbe furnizorul ├«n mod gratuit, cu respectarea condi╚¢iilor contractuale, ├«n termen de 3 s─âpt─âm├óni de la data solicit─ârii, conform unei proceduri aprobate de ANRE, care stabile╚Öte ├«n principal etapele procesului de schimbare a furnizorului, modalitatea de stingere a obliga╚¢iilor de plat─â datorate de clientul final furnizorului ce urmeaz─â a fi schimbat, datele ce pot fi solicitate de clientul final sau de noul furnizor ├«n procesul de schimbare, precum ╚Öi operatorii de sistem care sunt obliga╚¢i s─â le furnizeze;...i)s─â primeasc─â un decont final de lichidare, dup─â schimbarea furnizorului de gaze naturale, ├«n termen de 6 s─âpt─âm├óni de la schimbarea furnizorului;...j)s─â solicite ╚Öi s─â primeasc─â de la furnizor/operatorul de sistem toate datele relevante privind propriul consum pe ultimii 5 ani, f─âr─â s─â li se perceap─â costuri suplimentare pentru acest serviciu.......(5)...Clien╚¢ii finali nu au dreptul s─â v├ónd─â gazele naturale.', NULL, 145, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (213, 16, '146', 'Articolul 146', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind produc╚¢ia, transportul, distribu╚¢ia, ├«nmagazinarea subteran─â, furnizarea ╚Öi administrarea pie╚¢elor centralizate de gaze naturale', NULL, NULL, NULL, NULL, 'Obliga╚¢iile ╚Öi drepturile operatorului pie╚¢ei de gaze naturale...(1)...Operatorul pie╚¢ei de gaze naturale este persoana juridic─â titular─â de licen╚¢─â care asigur─â administrarea pie╚¢elor centralizate de gaze naturale, cu excep╚¢ia pie╚¢ei de echilibrare, ├«n vederea tranzac╚¢ion─ârii de gaze naturale pe termen scurt, mediu ╚Öi lung, conform reglement─ârilor emise de autoritatea competent─â....(2)...Operatorului pie╚¢ei de gaze naturale nu ├«i este permis─â divulgarea informa╚¢iilor legate de tranzac╚¢iile de gaze naturale pe care le de╚¢ine, ob╚¢inute pe parcursul activit─â╚¢ii sale, altfel dec├ót ├«n condi╚¢iile legii....(3)...Pre╚¢urile stabilite pe pie╚¢ele centralizate de gaze naturale se fac publice conform reglement─ârilor ANRE.', NULL, 146, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (214, 16, '147', 'Articolul 147', NULL, 'Gazele naturale', NULL, 'Accesul ╚Öi racordarea la re╚¢ea', NULL, NULL, NULL, NULL, 'Regimul accesului ter╚¢ilor la re╚¢ea...Accesul ter╚¢ilor la conductele de alimentare din amonte, la sistemele de transport, la depozitele de ├«nmagazinare, la sistemele GNL ╚Öi la sistemele de distribu╚¢ie a gazelor naturale se realizeaz─â ├«n regim reglementat.', NULL, 147, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (237, 16, '170', 'Articolul 170', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Consultarea cu alte state membre sau autorit─â╚¢i de reglementare...├Än cazul unei conducte de interconectare, orice decizie de derogare se ia dup─â consult─âri cu celelalte state membre sau, dup─â caz, cu autorit─â╚¢ile de reglementare ori cu Agen╚¢ia de Cooperare a Reglementatorilor ├«n Domeniul Energiei - ACER.', NULL, 170, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (215, 16, '148', 'Articolul 148', NULL, 'Gazele naturale', NULL, 'Accesul ╚Öi racordarea la re╚¢ea', NULL, NULL, NULL, NULL, 'Regimul racord─ârii la obiective din sectorul gazelor naturale...(1)...Racordarea ter╚¢ilor la conductele de alimentare din amonte, la sistemele de transport, la depozitele de ├«nmagazinare, la sistemele GNL ╚Öi la sistemele de distribu╚¢ie a gazelor naturale se realizeaz─â ├«n regim reglementat prin emiterea unui acord de acces, conform reglement─ârilor specifice elaborate de ANRE....(2)...Racordarea la conductele de alimentare din amonte ╚Öi la sistemul de transport este permis─â urm─âtoarelor categorii de solicitan╚¢i:a)titularilor contractelor de concesiune a serviciului public de distribu╚¢ie a gazelor naturale, ├«n vederea ├«ndeplinirii obliga╚¢iilor contractuale ce le revin ├«n aceast─â calitate;...b)operatorilor terminalului GNL;...c)operatorilor depozitelor de ├«nmagazinare subteran─â a gazelor naturale;...d)clien╚¢ilor industriali noi;...e)operatorilor economici titulari ai licen╚¢ei de distribu╚¢ie;...f)produc─âtorilor de gaze naturale;...g)altor categorii de clien╚¢i dac─â ace╚Ötia nu au solu╚¢ie de alimentare, din punctul de vedere al debitului orar solicitat, din sistemul de distribu╚¢ie a gazelor naturale din zona delimitat─â concesionat─â unde urmeaz─â s─â fie realizat─â racordarea; lipsa solu╚¢iei de alimentare din sistemul de distribu╚¢ie este comunicat─â obligatoriu, ├«n scris, de operatorul de distribu╚¢ie ╚Öi avizat─â de ANRE....', NULL, 148, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (216, 16, '149', 'Articolul 149', NULL, 'Gazele naturale', NULL, 'Accesul ╚Öi racordarea la re╚¢ea', NULL, NULL, NULL, NULL, 'Refuzul de acces...(1)...Refuzul accesului ter╚¢ilor la obiectivele/sistemele din sectorul gazelor naturale se poate face ├«n urm─âtoarele situa╚¢ii:a)capacitatea obiectivului/sistemului este insuficient─â;...b)accesul la sistem ├«mpiedic─â ├«ndeplinirea obliga╚¢iilor de serviciu public ╚Öi siguran╚¢─â ├«n exploatare;...c)accesul la sistem poate conduce la serioase dificult─â╚¢i economice ╚Öi/sau financiare legate de contractele de tip "take-or-pay" pentru titularul de licen╚¢─â/autoriza╚¢ie c─âruia i se solicit─â accesul;...d)calitatea gazelor naturale care urmeaz─â s─â fie introduse ├«n sisteme ╚Öi/sau ├«n depozite nu corespunde cerin╚¢elor impuse de reglement─ârile ├«n vigoare.......(2)...Refuzul de acces la sistem se materializeaz─â printr-un document denumit "Refuz de acces", care cuprinde ├«n mod obligatoriu motivele refuzului.', NULL, 149, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (217, 16, '150', 'Articolul 150', NULL, 'Gazele naturale', NULL, 'Accesul ╚Öi racordarea la re╚¢ea', NULL, NULL, NULL, NULL, 'Refuzul de racordare...Refuzul de racordare la obiectivele/sistemele din sectorul gazelor naturale se poate face ├«n urm─âtoarele situa╚¢ii:a)operatorul de sistem a emis un refuz de acces la sistem;...b)nu exist─â obiective/conducte p─âr╚¢i componente ale sistemelor la care urmeaz─â s─â fie realizat─â conectarea;...c)├«n cazul ne├«ndeplinirii obliga╚¢iei de plat─â a tarifului de racordare....', NULL, 150, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (218, 16, '151', 'Articolul 151', NULL, 'Gazele naturale', NULL, 'Accesul ╚Öi racordarea la re╚¢ea', NULL, NULL, NULL, NULL, 'Finan╚¢area lucr─ârilor pentru realizarea obiectivelor/conductelor necesare racord─ârii...(1)...Operatorul de distribu╚¢ie sau operatorul de transport ╚Öi de sistem nu poate refuza accesul la sistem ╚Öi este obligat s─â finan╚¢eze lucr─ârile dac─â realizarea obiectivelor/conductelor necesare racord─ârii este economic justificat─â ╚Öi confirmat─â ├«n acest sens de c─âtre ANRE....(2)...├Än cazul ├«n care realizarea obiectivelor/conductelor, altele dec├ót cele prev─âzute la alin. (1), nu este economic justificat─â pentru operatorul de distribu╚¢ie sau operatorul de transport ╚Öi de sistem, solicitantul poate participa ├«n cot─â-parte la finan╚¢area obiectivelor/conductelor....(3)...Activele care rezult─â ├«n condi╚¢iile alin. (2) se preiau ├«n folosin╚¢─â de c─âtre operatorul de distribu╚¢ie sau de c─âtre operatorul de transport ╚Öi de sistem, pe baza unui contract ├«n care se stipuleaz─â ├«n mod explicit condi╚¢iile financiare, ├«n baza procedurii aprobate de ANRE....(4)...Solicitantul are dreptul s─â recupereze contravaloarea sumei investite conform alin. (2), din momentul punerii ├«n func╚¢iune a obiectivului/conductei, de la urm─âtorii utilizatori racorda╚¢i la obiectivele/sistemele realizate ├«n conformitate cu prevederile alin. (2); modalitatea de recuperare a cotei-p─âr╚¢i din investi╚¢ie se face ├«n conformitate cu reglement─ârile ANRE....(5)...Evaluarea condi╚¢iilor privind asigurarea accesului solicitan╚¢ilor la sistem, ├«n condi╚¢iile prev─âzute la alin. (1) ╚Öi (2), se determin─â ├«n baza unui studiu tehnico-economic realizat conform unor criterii aprobate de c─âtre ANRE, care au la baz─â principiul recuper─ârii costurilor investi╚¢iei, pe care operatorul are obliga╚¢ia de a-l realiza ├«n termen de 30 de zile de la data primirii solicit─ârii....(6)...La expirarea termenului prev─âzut la alin. (5), studiul se transmite solicitan╚¢ilor, cu titlu gratuit....(7)...Operatorul de distribu╚¢ie sau operatorul de transport ╚Öi de sistem are obliga╚¢ia de a realiza ╚Öi de a pune ├«n func╚¢iune obiectivul/conducta realizat─â ├«n termen de 9 luni de la data emiterii acordului de acces ori de la data semn─ârii contractului, dup─â caz, sau de cel mult 6 luni de la data ob╚¢inerii autoriza╚¢iei de construire.', NULL, 151, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (219, 16, '152', 'Articolul 152', NULL, 'Gazele naturale', NULL, 'Accesul ╚Öi racordarea la re╚¢ea', NULL, NULL, NULL, NULL, 'Magistrale directe...(1)...├Än vederea asigur─ârii necesarului de gaze naturale, solicitan╚¢ii care se afl─â ├«n alte zone dec├ót cele prev─âzute la art. 104 alin. (1) pot solicita ANRE aprobarea pentru construirea unei magistrale directe....(2)...ANRE emite autoriza╚¢ii pentru construirea unei magistrale directe numai ├«n absen╚¢a unei perspective de acces la sistem ├«n urm─âtorii 2 ani....(3)...Magistrala direct─â este finan╚¢at─â integral de solicitan╚¢ii prev─âzu╚¢i la alin. (1) ╚Öi se afl─â ├«n proprietatea acestora....(4)...Criteriile transparente ╚Öi nediscriminatorii pentru acordarea autoriza╚¢iilor de ├«nfiin╚¢are, precum ╚Öi condi╚¢iile de operare a magistralei directe se aprob─â de ANRE.', NULL, 152, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (220, 16, '153', 'Articolul 153', NULL, 'Gazele naturale', NULL, 'Accesul ╚Öi racordarea la re╚¢ea', NULL, NULL, NULL, NULL, 'Solu╚¢ionarea divergen╚¢elor de acces la sistem...(1)...Pentru solu╚¢ionarea pe cale administrativ-jurisdic╚¢ional─â a divergen╚¢elor de acces la sistem se constituie ├«n cadrul ANRE o comisie de specialitate....(2)...Organizarea ╚Öi func╚¢ionarea comisiei prev─âzute la alin. (1) se realizeaz─â ├«n baza unui regulament aprobat de ANRE....(3)...Comisia prev─âzut─â la alin. (1) adopt─â o hot─âr├óre ├«n termen de 60 de zile de la primirea reclama╚¢iei....(4)...Hot─âr├órea comisiei prev─âzut─â la alin. (3) are caracter definitiv, este obligatorie pentru p─âr╚¢i ╚Öi poate fi atacat─â ├«n condi╚¢iile Legii contenciosului administrativ nr. 554/2004, cu modific─ârile ╚Öi complet─ârile ulterioare.', NULL, 153, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (221, 16, '154', 'Articolul 154', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind gazul petrolier lichefiat (GPL), gazul natural comprimat pentru vehicule (GNCV) ╚Öi gazul natural lichefiat (GNL)', NULL, NULL, NULL, NULL, 'Reglementarea utiliz─ârii GPL...Reglement─ârile tehnice ╚Öi comerciale cu privire la stocarea, distribu╚¢ia ╚Öi utilizarea GPL sunt elaborate/aprobate de ANRE cu consultarea p─âr╚¢ilor interesate.', NULL, 154, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (222, 16, '155', 'Articolul 155', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind gazul petrolier lichefiat (GPL), gazul natural comprimat pentru vehicule (GNCV) ╚Öi gazul natural lichefiat (GNL)', NULL, NULL, NULL, NULL, 'Reglementarea utiliz─ârii GNCV...(1)...Reglement─ârile tehnice ╚Öi comerciale privind tratarea ╚Öi comprimarea gazelor naturale pentru producerea GNCV, precum ╚Öi cele privind stocarea acestora ├«n recipientele din sta╚¢iile de umplere sunt aprobate de ANRE cu consultarea p─âr╚¢ilor interesate....(2)...├Än scopul realiz─ârii cadrului general de reglementare pentru producerea, stocarea, livrarea ╚Öi utilizarea GNCV, ANRE stabile╚Öte Codul tehnic al GNCV.', NULL, 155, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (223, 16, '156', 'Articolul 156', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind gazul petrolier lichefiat (GPL), gazul natural comprimat pentru vehicule (GNCV) ╚Öi gazul natural lichefiat (GNL)', NULL, NULL, NULL, NULL, 'Reglementarea utiliz─ârii GNL...Cadrul general de reglementare privind GNL se stabile╚Öte de ANRE prin Codul tehnic al GNL.', NULL, 156, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (224, 16, '157', 'Articolul 157', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind gazul petrolier lichefiat (GPL), gazul natural comprimat pentru vehicule (GNCV) ╚Öi gazul natural lichefiat (GNL)', NULL, NULL, NULL, NULL, 'Regimul de autorizare ├«n domeniul GNL, GNCV ╚Öi GPL...Operatorii economici ╚Öi/sau persoanele fizice care desf─â╚Öoar─â activit─â╚¢i de proiectare, execu╚¢ie ╚Öi exploatare ├«n domeniul GNL, GNCV, GPL trebuie s─â de╚¢in─â autoriza╚¢ii/licen╚¢e emise de ANRE ├«n baza unor regulamente specifice.', NULL, 157, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (226, 16, '159', 'Articolul 159', NULL, 'Gazele naturale', NULL, 'Asigurarea calit─â╚¢ii echipamentelor, instala╚¢iilor, aparatelor, produselor ╚Öi procedeelor utilizate ├«n sectorul gazelor naturale', NULL, NULL, NULL, NULL, 'Sisteme de m─âsurare inteligente...(1)...P├ón─â la data de 3 septembrie 2012, ANRE evalueaz─â implementarea sistemelor de m─âsurare inteligente care contribuie la participarea activ─â a consumatorilor pe pia╚¢a furniz─ârii de gaze naturale, din punctul de vedere al costurilor ╚Öi beneficiilor pe termen lung pentru pia╚¢─â ╚Öi pentru consumatorii individuali, al tipului de contorizare inteligent─â, precum ╚Öi al termenelor fezabile de implementare....(2)...├Än condi╚¢iile ├«n care, prin evaluarea prev─âzut─â la alin. (1), se constat─â c─â implementarea sistemelor de m─âsurare inteligente este avantajoas─â pentru func╚¢ionarea pie╚¢ei gazelor naturale, ANRE elaboreaz─â, ├«n consultare cu operatorii de distribu╚¢ie ╚Öi de transport, ╚Öi aprob─â un calendar de implementare, acord├ónd aten╚¢ia cuvenit─â utiliz─ârii de standarde adecvate ╚Öi de bune practici, precum ╚Öi importan╚¢ei dezvolt─ârii pie╚¢ei gazelor naturale.', NULL, 159, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (227, 16, '160', 'Articolul 160', NULL, 'Gazele naturale', NULL, 'Asigurarea calit─â╚¢ii echipamentelor, instala╚¢iilor, aparatelor, produselor ╚Öi procedeelor utilizate ├«n sectorul gazelor naturale', NULL, NULL, NULL, NULL, 'Verificarea proiectelor...(1)...├Änainte de execu╚¢ia lucr─ârilor pentru obiectivele/sistemele din sectorul gazelor naturale, ├«n vederea respect─ârii cerin╚¢elor privind calitatea ├«n construc╚¢ii, operatorii economici care exploateaz─â obiectivele/sistemele respective au obliga╚¢ia de a verifica proiectele de execu╚¢ie prin intermediul verificatorilor de proiecte atesta╚¢i de c─âtre ANRE....(2)...Proiectele aferente execu╚¢iei/modific─ârilor instala╚¢iilor de utilizare se verific─â de c─âtre verificatori atesta╚¢i de c─âtre Ministerul Dezvolt─ârii Regionale ╚Öi Turismului....(3)...Proiectele aferente sistemelor ╚Öi instala╚¢iilor de utilizare din domeniul gazelor naturale se consider─â a fi avizate dac─â au fost declarate conforme de c─âtre verificatorul de proiect.', NULL, 160, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (228, 16, '161', 'Articolul 161', NULL, 'Gazele naturale', NULL, 'Asigurarea calit─â╚¢ii echipamentelor, instala╚¢iilor, aparatelor, produselor ╚Öi procedeelor utilizate ├«n sectorul gazelor naturale', NULL, NULL, NULL, NULL, 'Atestarea verificatorilor de proiecte...(1)...Atestarea verificatorilor de proiecte prev─âzu╚¢i la art. 160 alin. (1) se face conform unui regulament aprobat de c─âtre ANRE....(2)...Verificatorii de proiecte care au fost atesta╚¢i de alte institu╚¢ii ├«╚Öi p─âstreaz─â competen╚¢ele prin echivalarea atestatului, conform regulamentului prev─âzut la alin. (1)....(3)...Verificatorii de proiecte atesta╚¢i r─âspund ├«n mod solidar cu proiectan╚¢ii pentru asigurarea prin proiect a tuturor cerin╚¢elor tehnice ╚Öi de calitate prev─âzute de legisla╚¢ia ├«n vigoare, ├«n vederea realiz─ârii ╚Öi exploat─ârii ├«n condi╚¢ii de siguran╚¢─â a obiectivului proiectat, precum ╚Öi pentru eventualele modific─âri ale proiectului pe durata execu╚¢iei.', NULL, 161, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (229, 16, '162', 'Articolul 162', NULL, 'Gazele naturale', NULL, 'Asigurarea calit─â╚¢ii echipamentelor, instala╚¢iilor, aparatelor, produselor ╚Öi procedeelor utilizate ├«n sectorul gazelor naturale', NULL, NULL, NULL, NULL, 'Recep╚¢ia lucr─ârilor...(1)...Recep╚¢ia obiectivelor/sistemelor din sectorul gazelor naturale se realizeaz─â de c─âtre speciali╚Öti, instalatori autoriza╚¢i de ANRE, angaja╚¢i ai operatorilor economici licen╚¢ia╚¢i care au urm─ârit execu╚¢ia acestora, conform prevederilor normelor tehnice specifice....(2)...Recep╚¢ia instala╚¢iilor de utilizare a gazelor naturale apar╚¢in├ónd consumatorilor finali se realizeaz─â de c─âtre beneficiarul lucr─ârii ╚Öi de c─âtre speciali╚Ötii operatorului economic autorizat de ANRE care a executat lucrarea respectiv─â, conform prevederilor normelor tehnice specifice.', NULL, 162, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (230, 16, '163', 'Articolul 163', NULL, 'Gazele naturale', NULL, 'Asigurarea calit─â╚¢ii echipamentelor, instala╚¢iilor, aparatelor, produselor ╚Öi procedeelor utilizate ├«n sectorul gazelor naturale', NULL, NULL, NULL, NULL, 'Expertiza lucr─ârilor...(1)...Obiectivele/Sistemele din sectorul gazelor naturale se supun, dup─â caz, expertiz─ârii tehnice de c─âtre exper╚¢i autoriza╚¢i, conform unui regulament aprobat de c─âtre ANRE....(2)...Exper╚¢ii care au fost atesta╚¢i de alte institu╚¢ii ├«╚Öi p─âstreaz─â competen╚¢ele prin echivalarea atestatului, conform regulamentului prev─âzut la alin. (1).', NULL, 163, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (231, 16, '164', 'Articolul 164', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Condi╚¢iile de derogare pentru noile infrastructuri...(1)...Noile infrastructuri majore pentru gaze naturale, cum ar fi interconect─ârile dintre statele membre, instala╚¢iile GNL ╚Öi instala╚¢iile de ├«nmagazinare, pot beneficia de o derogare, pe o perioad─â determinat─â de timp, la cerere, de la prevederile legisla╚¢iei ├«n vigoare, referitoare la accesul ter╚¢ilor la sistemele de transport, ├«nmagazinare ╚Öi la conductele de alimentare din amonte, precum ╚Öi de la metodologiile de tarifare, ├«n urm─âtoarele condi╚¢ii:a)investi╚¢ia trebuie s─â ├«nt─âreasc─â concuren╚¢a ├«n furnizarea de gaze naturale ╚Öi s─â ├«mbun─ât─â╚¢easc─â siguran╚¢a aliment─ârii;...b)nivelul de risc legat de investi╚¢ie este de a╚Öa natur─â ├«nc├ót investi╚¢ia s─â nu se realizeze dec├ót dac─â se acord─â o derogare;...c)infrastructura trebuie s─â fie ├«n proprietatea unei persoane juridice care este separat─â cel pu╚¢in ├«n privin╚¢a formei sale juridice de operatorii de sistem ├«n ale c─âror sisteme se construie╚Öte infrastructura;...d)se percep tarife pentru utilizatorii infrastructurii respective;...e)derogarea nu influen╚¢eaz─â negativ concuren╚¢a, func╚¢ionarea eficient─â a pie╚¢ei interne a gazelor naturale sau func╚¢ionarea eficient─â a sistemului reglementat la care este conectat─â infrastructura.......(2)...Prevederile alin. (1) se aplic─â ╚Öi cre╚Öterilor semnificative de capacitate a infrastructurilor existente, precum ╚Öi modific─ârilor acestor infrastructuri care permit dezvoltarea unor noi surse de furnizare de gaze naturale.', NULL, 164, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (232, 16, '165', 'Articolul 165', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Acordarea derog─ârilor...ANRE hot─âr─â╚Öte asupra acord─ârii derog─ârii prev─âzute la art. 164, adopt├ónd o decizie motivat─â corespunz─âtor. Decizia ANRE se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I.', NULL, 165, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (233, 16, '166', 'Articolul 166', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Tipul derog─ârii...Derogarea poate acoperi ├«n ├«ntregime sau par╚¢ial noua infrastructur─â, infrastructura existent─â cu capacitate semnificativ m─ârit─â sau modificarea infrastructurii existente.', NULL, 166, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (234, 16, '167', 'Articolul 167', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Condi╚¢ii de acordare a derog─ârii...La luarea deciziei de acordare a unei derog─âri se ia ├«n considerare, dup─â caz, necesitatea de a impune condi╚¢ii referitoare la durata derog─ârii ╚Öi accesul nediscriminatoriu la conducta de interconectare.', NULL, 167, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (235, 16, '168', 'Articolul 168', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Condi╚¢ii de acordare a derog─ârii ├«n cazul unei conducte de interconectare...La luarea deciziei privind condi╚¢iile prev─âzute la art. 170 se vor avea ├«n vedere, ├«n principal, durata contractelor, capacitatea suplimentar─â ce va fi construit─â sau modificarea capacit─â╚¢ii existente, orizontul de timp al proiectului ╚Öi condi╚¢iile na╚¢ionale.', NULL, 168, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (239, 16, '172', 'Articolul 172', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Informa╚¢ii relevante...Informa╚¢iile prev─âzute la art. 171 trebuie s─â cuprind─â, ├«n principal:a)motivele detaliate pe baza c─ârora autoritatea de reglementare sau statul membru a acordat derogarea, inclusiv informa╚¢iile financiare care justific─â necesitatea;...b)analiza efectuat─â ├«n privin╚¢a efectului asupra concuren╚¢ei ╚Öi func╚¢ion─ârii eficiente a pie╚¢ei interne a gazelor naturale rezult├ónd din acordarea derog─ârii;...c)motivele pentru perioada de timp ╚Öi partea din capacitatea total─â a infrastructurii gazelor naturale respective pentru care s-a acordat derogare;...d)├«n cazul ├«n care derogarea se refer─â la o conduct─â de interconectare, rezultatul consult─ârii cu statele membre respective sau cu autorit─â╚¢ile de reglementare;...e)contribu╚¢ia infrastructurii la diversificarea surselor de alimentare cu gaze naturale....', NULL, 172, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (240, 16, '173', 'Articolul 173', NULL, 'Gazele naturale', NULL, 'Obliga╚¢ia de serviciu public', NULL, NULL, NULL, NULL, 'Obliga╚¢ia de serviciu public...(1)...Titularii de licen╚¢e de ├«nmagazinare, transport, distribu╚¢ie ╚Öi furnizare a gazelor naturale au obliga╚¢ia s─â-╚Öi desf─â╚Öoare activit─â╚¢ile cu respectarea obliga╚¢iilor stipulate ├«n licen╚¢ele, respectiv ├«n autoriza╚¢iile emise de ANRE, privind siguran╚¢a, calitatea, continuitatea aprovizion─ârii, eficien╚¢a energetic─â, cu respectarea normelor de securitate ╚Öi s─ân─âtate a muncii ╚Öi de protec╚¢ie a mediului, precum ╚Öi a prevederilor din contractele directe cu clien╚¢ii....(2)...ANRE poate stabili prin reglement─ârile specifice obliga╚¢ii de serviciu public pentru fiecare activitate din sectorul gazelor naturale, aplicabile tuturor titularilor de licen╚¢─â, sau autoriza╚¢ii ├«n mod transparent, echidistant ╚Öi nediscriminatoriu....(3)...Obliga╚¢iile de serviciu public prev─âzute la alin. (2) nu trebuie s─â ├«mpiedice liberalizarea pie╚¢ei gazelor naturale, s─â constituie bariere pentru intrarea unor noi operatori pe pia╚¢─â sau s─â distorsioneze concuren╚¢a ╚Öi func╚¢ionarea transparent─â a pie╚¢ei....(4)...Obliga╚¢iile de serviciu public de natur─â a afecta pia╚¢a gazelor naturale ├«n sensul celor prev─âzute la alin. (3) se notific─â Consiliului Concuren╚¢ei....(5)...Costurile efectuate ├«ntr-o manier─â prudent─â de operatorii economici pentru realizarea obliga╚¢iilor de serviciu public sunt costuri justificate ╚Öi se acoper─â prin pre╚¢urile sau tarifele practicate de ace╚Ötia, ├«n conformitate cu reglement─ârile specifice ale ANRE....(6)...ANRE reexamineaz─â la fiecare 2 ani necesitatea ╚Öi modalitatea de impunere a obliga╚¢iilor de serviciu public, ╚¢in├ónd seama de evolu╚¢ia sectorului de gaze naturale....(7)...ANRE elaboreaz─â ╚Öi transmite primului-ministru ╚Öi ministerului de resort un raport cu privire la m─âsurile adoptate pentru ├«ndeplinirea obliga╚¢iilor de serviciu public, inclusiv protec╚¢ia consumatorului ╚Öi a mediului, ╚Öi la posibilul lor efect asupra concuren╚¢ei interne ╚Öi interna╚¢ionale, care va fi reactualizat, la fiecare 2 ani, av├ónd ├«n vedere modific─ârile aduse acestor m─âsuri. Acest raport se transmite comisiilor de specialitate din Parlamentul Rom├óniei ╚Öi Comisiei Europene, pentru informare.', NULL, 173, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (241, 16, '174', 'Articolul 174', NULL, 'Gazele naturale', NULL, 'Pia╚¢a gazelor naturale', NULL, NULL, NULL, NULL, 'Structura pie╚¢ei de gaze naturale...(1)...Pia╚¢a de gaze naturale este compus─â din pia╚¢a reglementat─â ╚Öi pia╚¢a concuren╚¢ial─â, iar tranzac╚¢iile cu gaze naturale se fac angro sau cu am─ânuntul....(2)...Cre╚Öterea ponderii pie╚¢ei concuren╚¢iale se realizeaz─â gradual, prin asigurarea accesului pe aceast─â pia╚¢─â pentru c├ót mai mul╚¢i participan╚¢i, furnizori ╚Öi clien╚¢i finali, ├«n conformitate cu prevederile art. 175....(3)...Participan╚¢ii la pia╚¢a de gaze naturale trebuie s─â respecte regulile de func╚¢ionare a acesteia, aprobate de ANRE....(4)...Participan╚¢ii la pia╚¢a de gaze naturale ╚Öi structurile opera╚¢ionale asociate sunt: produc─âtorii, furnizorii, clien╚¢ii finali, operatorul/operatorii de transport ╚Öi de sistem, operatorii pie╚¢elor centralizate de gaze naturale, operatorii de distribu╚¢ie ╚Öi operatorii de ├«nmagazinare/stocare....(5)...Participan╚¢ii la pia╚¢a de gaze naturale sunt obliga╚¢i s─â ├«╚Öi asume responsabilitatea financiar─â pentru plata dezechilibrelor pe care le genereaz─â pe pia╚¢a de gaze naturale, ├«n conformitate cu reglement─ârile aprobate de ANRE....(6)...Clien╚¢ii finali de gaze naturale au dreptul s─â ├«╚Öi aleag─â furnizorul ╚Öi s─â negocieze direct contracte de v├ónzare-cump─ârare cu acesta....(7)...Dac─â ╚Öi-au exercitat dreptul de eligibilitate, clien╚¢ii finali nu mai au dreptul s─â revin─â la furnizarea reglementat─â....(8)...Se ├«nfiin╚¢eaz─â Comisia de solu╚¢ionare a disputelor, ca organism care solu╚¢ioneaz─â disputele pe pia╚¢a angro ╚Öi cu am─ânuntul ap─ârute ├«ntre participan╚¢ii la pia╚¢a de gaze naturale....(9)...Comisia de solu╚¢ionare a disputelor este format─â din 5 membri care sunt numi╚¢i prin decizie a pre╚Öedintelui ANRE, pe o perioad─â de 3 ani, dintre salaria╚¢ii ANRE av├ónd o vechime de cel pu╚¢in 5 ani ├«n domeniul gazelor naturale....(10)...Comisia de solu╚¢ionare a disputelor ├«╚Öi desf─â╚Öoar─â activitatea pe baza unui regulament de organizare ╚Öi func╚¢ionare aprobat prin decizie a pre╚Öedintelui ANRE, dup─â consultare public─â.', NULL, 174, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (242, 16, '175', 'Articolul 175', NULL, 'Gazele naturale', NULL, 'Pia╚¢a gazelor naturale', NULL, NULL, NULL, NULL, 'Func╚¢ionarea pie╚¢ei reglementate de gaze naturale...(1)...Pia╚¢a reglementat─â de gaze naturale func╚¢ioneaz─â ├«n principal pentru asigurarea aliment─ârii cu gaze naturale a clien╚¢ilor finali prev─âzu╚¢i la art. 179 alin. (2) lit. a)-c)....(2)...Pe pia╚¢a reglementat─â de gaze naturale autoritatea competent─â are dreptul:a)s─â impun─â obliga╚¢ii de serviciu public, conform art. 173;...b)s─â impun─â furnizorilor proceduri transparente de achizi╚¢ie a gazelor naturale de pe pia╚¢a concuren╚¢ial─â destinate realiz─ârii activit─â╚¢ilor prev─âzute la art. 179 alin. (2) lit. a)-c);...c)s─â stabileasc─â pre╚¢urile aplicate de furnizorii de gaze naturale de ultim─â instan╚¢─â clien╚¢ilor finali;...d)s─â aprobe metodologii de verificare/control a cheltuielilor cu achizi╚¢ia gazelor naturale.......(3)...Furnizarea gazelor naturale pe pia╚¢a reglementat─â se face pe baz─â de contracte-cadru aprobate de ANRE....(4)...ANRE monitorizeaz─â continuu efectul pie╚¢ei reglementate asupra pie╚¢ei concuren╚¢iale de gaze naturale ╚Öi ia m─âsurile necesare pentru evitarea eventualelor distorsiuni ale concuren╚¢ei ╚Öi pentru cre╚Öterea gradului de transparen╚¢─â a tranzac╚¢iilor comerciale....(5)...ANRE organizeaz─â, ├«n cadrul ac╚¢iunii de monitorizare, un proces de evaluare a func╚¢ion─ârii pie╚¢ei de gaze naturale ├«n condi╚¢iile renun╚¢─ârii la aplicarea pre╚¢urilor reglementate pentru clien╚¢ii finali, ├«n care vor fi utilizate cel pu╚¢in urm─âtoarele criterii generale:a)num─ârul de furnizori activi ├«n pia╚¢a de gaze naturale ├«n fiecare an;...b)cota de pia╚¢─â a fiec─âruia dintre furnizorii activi;...c)capabilitatea economico-financiar─â a furnizorilor activi ╚Öi comportamentul acestora ├«n pia╚¢─â;...d)evolu╚¢ia num─ârului anual de schimb─âri ale furnizorului de gaze naturale;...e)nivelul ╚Öi evolu╚¢ia pre╚¢urilor ├«n pia╚¢─â;...f)num─ârul ╚Öi tipul consumatorilor vulnerabili....', NULL, 175, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (243, 16, '176', 'Articolul 176', NULL, 'Gazele naturale', NULL, 'Pia╚¢a gazelor naturale', NULL, NULL, NULL, NULL, 'M─âsuri de salvgardare...(1)...├Än cazul unor situa╚¢ii de criz─â nea╚Öteptate pe pia╚¢a de gaze naturale ╚Öi ├«n cazul ├«n care este amenin╚¢at─â siguran╚¢a fizic─â sau securitatea persoanelor, a aparatelor sau a instala╚¢iilor ori integritatea sistemului, operatorul de transport ╚Öi de sistem propune ministerului de resort adoptarea unor m─âsuri de siguran╚¢─â....(2)...M─âsurile men╚¢ionate la alin. (1) trebuie s─â afecteze c├ót mai pu╚¢in buna func╚¢ionare a pie╚¢ei interne a Uniunii Europene ╚Öi s─â se rezume strict la remedierea situa╚¢iei de criz─â care le-a generat....(3)...Punerea ├«n aplicare a m─âsurilor prev─âzute la alin. (1) se face prin hot─âr├óre a Guvernului ini╚¢iat─â de ministerul de resort....(4)...Ministerul de resort notific─â ├«n regim de urgen╚¢─â, celorlalte state membre ale Uniunii Europene, precum ╚Öi Comisiei Europene m─âsurile de siguran╚¢─â adoptate ├«n fiecare caz.', NULL, 176, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (250, 16, '183', 'Articolul 183', NULL, 'Gazele naturale', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Dispunerea efectu─ârii investiga╚¢iilor...Pre╚Öedintele autorit─â╚¢ii competente dispune prin decizie efectuarea de investiga╚¢ii ├«n condi╚¢iile art. 185 de c─âtre personalul propriu ├«mputernicit ├«n acest sens, din oficiu sau ca r─âspuns la o pl├óngere ├«nregistrat─â la autoritatea competent─â, formulat─â de c─âtre o persoan─â fizic─â sau juridic─â afectat─â ├«n mod real ╚Öi direct de o poten╚¢ial─â ├«nc─âlcare a prevederilor prezentului titlu, numai ├«n domeniile ├«n care ANRE are competen╚¢a de investigare, potrivit legii.', NULL, 183, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (293, 18, '35', 'Articolul 35', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Operatorul de distribu╚¢ie ├«ncheie cu operatorul de re╚¢ea din amonte un contract pentru serviciul de transport sau de distribu╚¢ie, dup─â caz, conform prevederilor Regulamentului de furnizare a energiei electrice la clien╚¢ii finali, aprobat prin ordin al pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei, ├«n vigoare.', NULL, 5, '2025-11-07 23:44:17.187201', '2025-11-07 23:44:17.187201', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (244, 16, '177', 'Articolul 177', NULL, 'Gazele naturale', NULL, 'Pia╚¢a gazelor naturale', NULL, NULL, NULL, NULL, 'Func╚¢ionarea pie╚¢ei concuren╚¢iale de gaze naturale...(1)...Pe pia╚¢a concuren╚¢ial─â, tranzac╚¢iile comerciale cu gaze naturale se fac angro sau cu am─ânuntul, cu respectarea reglement─ârilor ANRE, iar pre╚¢urile se formeaz─â pe baza cererii ╚Öi a ofertei, ca rezultat al mecanismelor concuren╚¢iale....(2)...Pia╚¢a concuren╚¢ial─â angro func╚¢ioneaz─â pe baz─â de:a)contracte bilaterale ├«ntre operatorii economici din domeniul gazelor naturale;...b)tranzac╚¢ii pe pie╚¢e centralizate, administrate de c─âtre operatorul pie╚¢ei de gaze naturale sau operatorul pie╚¢ei de echilibrare, dup─â caz;...c)alte tipuri de tranzac╚¢ii sau contracte.......(3)...Pe pia╚¢a concuren╚¢ial─â cu am─ânuntul, furnizorii v├ónd gaze naturale clien╚¢ilor finali prin contracte la pre╚¢uri negociate sau oferte-tip....(4)...Datele relevante cum ar fi durata, regulile privind livrarea ╚Öi decontarea, cantitatea, termenele de executare, pre╚¢urile tranzac╚¢iei, mijloacele de identificare ale clientului angro, cu privire la toate tranzac╚¢iile din cadrul contractelor de furnizare de gaze naturale ╚Öi al instrumentelor derivate din domeniul gazelor naturale ├«ncheiate cu clien╚¢i angro ╚Öi cu operatorii de transport ╚Öi de sistem, precum ╚Öi cu operatorii de ├«nmagazinare ╚Öi de GNL se p─âstreaz─â de furnizori cel pu╚¢in 5 ani ╚Öi se pun la dispozi╚¢ia ANRE, Consiliului Concuren╚¢ei, Comisiei Europene ╚Öi a celorlalte autorit─â╚¢i na╚¢ionale competente, la cererea acestora....(5)...Obliga╚¢ia de p─âstrare a datelor privitoare la tranzac╚¢iile din cadrul instrumentelor derivate se aplic─â numai dup─â publicarea liniilor directoare de c─âtre Comisia European─â....(6)...Datele prev─âzute la alin. (5) pot fi publicate cu respectarea confiden╚¢ialit─â╚¢ii informa╚¢iilor sensibile comercial.', NULL, 177, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (245, 16, '178', 'Articolul 178', NULL, 'Gazele naturale', NULL, 'Pre╚¢uri ╚Öi tarife', NULL, NULL, NULL, NULL, 'Sistemul de pre╚¢uri ╚Öi tarife. Principii...(1)...Sistemul de pre╚¢uri ╚Öi tarife pentru gazele naturale este conceput astfel ├«nc├ót s─â asigure:a)apropierea de valoarea de pia╚¢─â a combustibililor alternativi, promovarea competi╚¢iei pe pia╚¢a gazelor naturale, diversificarea surselor de alimentare cu gaze naturale ╚Öi m─ârirea siguran╚¢ei furniz─ârii;...b)recuperarea costurilor efectuate ├«ntr-o manier─â prudent─â, aferente activit─â╚¢ilor reglementate, asigurarea unei rate rezonabile a rentabilit─â╚¢ii pentru capitalul investit ├«n activit─â╚¢ile reglementate, stimularea dezvolt─ârii capacit─â╚¢ilor de produc╚¢ie, transport, ├«nmagazinare ╚Öi distribu╚¢ie a gazelor naturale, at├ót pe termen scurt, c├ót ╚Öi pe termen lung;...c)economisirea de energie la clien╚¢ii finali;...d)├«mbun─ât─â╚¢irea calit─â╚¢ii gazelor naturale ╚Öi a serviciilor prestate clien╚¢ilor.......(2)...Principiile care stau la baza elabor─ârii reglement─ârilor privind sistemele de pre╚¢uri ╚Öi tarife pentru activit─â╚¢ile reglementate sunt urm─âtoarele:a)pre╚¢urile/tarifele trebuie s─â fie nediscriminatorii, bazate pe criterii obiective ╚Öi determinate ├«ntr-o manier─â transparent─â, pe baza metodologiilor aprobate ╚Öi publicate de autoritatea competent─â;...b)stimularea utiliz─ârii eficiente a gazelor naturale, asigurarea calit─â╚¢ii serviciilor, asigurarea puterii calorice a gazelor naturale, promovarea concuren╚¢ei pe pia╚¢a gazelor naturale ╚Öi protec╚¢ia intereselor clien╚¢ilor;...c)prevenirea specula╚¢iilor ╚Öi a comportamentelor speculative pe pia╚¢a gazelor naturale;...d)├«ncurajarea trecerii cererii din perioada de v├órf de consum ├«n perioadele de consum redus.......(3)...Este interzis─â recuperarea costurilor corespunz─âtoare serviciului asigurat pentru o anumit─â categorie de clien╚¢i finali prin tarifele practicate altor categorii de clien╚¢i finali....(4)...Este interzis─â subven╚¢ia ├«ncruci╚Öat─â ├«ntre activit─â╚¢ile reglementate, precum ╚Öi ├«ntre activit─â╚¢ile reglementate ╚Öi cele nereglementate ale unui operator economic.', NULL, 178, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (246, 16, '179', 'Articolul 179', NULL, 'Gazele naturale', NULL, 'Pre╚¢uri ╚Öi tarife', NULL, NULL, NULL, NULL, 'Activit─â╚¢ile aferente pie╚¢ei reglementate...(1)...Pe pia╚¢a reglementat─â care cuprinde activit─â╚¢ile cu caracter de monopol natural, activit─â╚¢ile conexe acestora ╚Öi furnizarea la pre╚¢ reglementat ╚Öi ├«n baza contractelor-cadru, sistemele de pre╚¢uri ╚Öi tarife se stabilesc de ANRE....(2)...Activit─â╚¢ile aferente pie╚¢ei reglementate cuprind:a)furnizarea gazelor naturale la pre╚¢ reglementat ╚Öi ├«n baza contractelor-cadru p├ón─â la 31 decembrie 2014 pentru clien╚¢ii noncasnici, cu excep╚¢ia cazului ├«n care la aceast─â dat─â se constat─â existen╚¢a unei diferen╚¢e semnificative ├«ntre pre╚¢ul de comercializare a produc╚¢iei interne ╚Öi pre╚¢ul european de import, care ar putea periclita stabilitatea pie╚¢ei, situa╚¢ie ├«n care termenul se prelunge╚Öte p├ón─â la 31 decembrie 2015;...b)furnizarea gazelor naturale la pre╚¢ reglementat ╚Öi ├«n baza contractelor-cadru p├ón─â la 31 decembrie 2018 pentru clien╚¢ii casnici;...c)furnizarea de ultim─â instan╚¢─â a gazelor naturale la pre╚¢ reglementat ╚Öi ├«n baza contractelor-cadru pentru clien╚¢ii finali;...d)administrarea pie╚¢elor centralizate;...e)transportul gazelor naturale;...f)transportul gazelor prin conductele de alimentare din amonte, conform prevederilor condi╚¢iilor de valabilitate a licen╚¢ei;...g)├«nmagazinarea subteran─â a gazelor naturale;...h)stocarea gazelor naturale ├«n conduct─â;...i)distribu╚¢ia gazelor naturale ╚Öi a biogazului/biometanului;...j)activit─â╚¢ile conexe desf─â╚Öurate de c─âtre operatorii licen╚¢ia╚¢i.......(3)...Calendarul de eliminare treptat─â a pre╚¢urilor reglementate pentru clien╚¢ii finali cu ├«ncepere de la 1 decembrie 2012 pentru clien╚¢ii noncasnici, respectiv din 1 iulie 2013 pentru clien╚¢ii casnici este stabilit de c─âtre Guvern, ├«n conformitate cu graficul de e╚Öalonare a pre╚¢urilor produc─âtorilor, propus de ANRE ╚Öi ANRM, ╚¢in├ónd seama de posibilele efecte negative ale elimin─ârii pre╚¢urilor reglementate, astfel ├«nc├ót acestea s─â fie c├ót mai pu╚¢in resim╚¢ite de clien╚¢i....(4)...Pentru activit─â╚¢ile aferente pie╚¢ei reglementate, pre╚¢urile ╚Öi tarifele se stabilesc pe baza metodologiilor aprobate ╚Öi publicate de c─âtre ANRE....(5)...Metodologiile de reglementare a pre╚¢urilor ╚Öi tarifelor se aprob─â de ANRE, dup─â informarea ╚Öi consultarea tuturor p─âr╚¢ilor interesate....(6)...Ordinul de aprobare a pre╚¢urilor ╚Öi tarifelor reglementate ├«n sectorul gazelor naturale, precum ╚Öi ordinul de aprobare a metodologiilor de reglementare a acestora se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I. Ordinul de aprobare a pre╚¢urilor ╚Öi tarifelor reglementate ├«n sectorul gazelor naturale va con╚¢ine inclusiv data intr─ârii ├«n vigoare a acestora....(7)...ANRE monitorizeaz─â anual rezultatele calendarului de eliminare treptat─â a pre╚¢urilor reglementate pentru clien╚¢ii finali ╚Öi propune Guvernului, dup─â caz, valorificarea produc╚¢iei interne a gazelor naturale pe pia╚¢a intern─â p├ón─â la finalizarea calendarului aprobat, respectiv 31 decembrie 2018.', NULL, 179, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (247, 16, '180', 'Articolul 180', NULL, 'Gazele naturale', NULL, 'Pre╚¢uri ╚Öi tarife', NULL, NULL, NULL, NULL, 'Limitarea pre╚¢urilor/tarifelor...(1)...├Än situa╚¢ia unui dezechilibru major ├«ntre cerere ╚Öi ofert─â ╚Öi/sau a unei disfunc╚¢ionalit─â╚¢i evidente a pie╚¢ei de gaze naturale, Guvernul, la propunerea ANRE ╚Öi cu avizul Consiliului Concuren╚¢ei, poate limita cre╚Öterea excesiv─â a pre╚¢urilor/tarifelor sau blocarea acestora pentru o perioad─â determinat─â de maximum 6 luni, care poate fi prelungit─â succesiv pentru durate de c├óte cel mult 3 luni, c├ót timp persist─â circumstan╚¢ele care au determinat adoptarea respectivei decizii, prin:a)fixarea unei limite superioare a pre╚¢ului;...b)limitarea venitului din activitatea reglementat─â.......(2)...Costurile recunoscute ╚Öi am├ónate, potrivit alin. (1), se recupereaz─â integral, ├«n conformitate cu procedura aprobat─â de autoritatea competent─â.', NULL, 180, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (248, 16, '181', 'Articolul 181', NULL, 'Gazele naturale', NULL, 'Pre╚¢uri ╚Öi tarife', NULL, NULL, NULL, NULL, 'Stabilirea structurilor de amestec...(1)...Pentru acoperirea necesarului de consum, clien╚¢ii finali au dreptul s─â fie alimenta╚¢i cu gaze naturale ├«n amestec intern/import, conform structurilor avizate/stabilite de c─âtre ANRE....(2)...Prevederile alin. (1) se aplic─â p├ón─â la convergen╚¢a pre╚¢ului gazelor naturale din produc╚¢ia intern─â cu cel al gazelor naturale din import....(3)...ANRE poate stabili:a)p├ón─â la 31 decembrie 2014, respectiv p├ón─â la 31 decembrie 2015, ├«n condi╚¢iile art. 179 alin. (2) lit. a), o structur─â specific─â de amestec import/intern pentru cantitatea de gaze naturale destinat─â consumului clien╚¢ilor casnici ╚Öi produc─âtorilor de energie termic─â, numai pentru cantitatea de gaze naturale utilizat─â la producerea de energie termic─â ├«n centralele de cogenerare ╚Öi ├«n centralele termice destinat─â consumului popula╚¢iei, ╚Öi o structur─â specific─â de amestec import/intern pentru cantitatea de gaze naturale destinat─â consumului clien╚¢ilor noncasnici, cu excep╚¢ia produc─âtorilor de energie termic─â, pentru cantitatea de gaze naturale utilizat─â la producerea de energie termic─â ├«n centralele de cogenerare ╚Öi ├«n centralele termice destinat─â consumului popula╚¢iei;...b)├«ncep├ónd cu 1 ianuarie 2015, respectiv cu 1 ianuarie 2016, ├«n condi╚¢iile art. 179 alin. (2) lit. a), ╚Öi p├ón─â la 31 decembrie 2018 o structur─â de amestec import/intern pentru cantitatea de gaze naturale destinat─â consumului clien╚¢ilor casnici.......(4)...├Än vederea asigur─ârii suportabilit─â╚¢ii costurilor legate de factura energetic─â, ├«n special a celor legate de ├«nc─âlzirea popula╚¢iei, stabilirea structurii de amestec import/intern ╚Öi aprobarea pre╚¢ului final reglementat pentru clien╚¢ii casnici vor avea ├«n vedere ╚Öi premisele considerate la stabilirea datelor pentru eliminarea pre╚¢urilor reglementate c─âtre aceast─â categorie de clien╚¢i....(5)...Pre╚¢ul de achizi╚¢ie a gazelor din produc╚¢ia intern─â pentru pia╚¢a reglementat─â se stabile╚Öte prin hot─âr├óre a Guvernului, la propunerea ministerului de resort ╚Öi se actualizeaz─â de ANRE ╚Öi ANRM, ├«n concordan╚¢─â cu prevederile Calendarului de eliminare treptat─â a pre╚¢urilor reglementate pentru clien╚¢ii finali....(6)...Sunt exceptate de la respectarea structurilor amestecurilor de gaze naturale stabilite/avizate de ANRE:a)cantit─â╚¢ile de gaze naturale reinjectate ├«n z─âc─âminte, exceptate de la plata redeven╚¢ei, ├«n condi╚¢iile legii;...b)cantit─â╚¢ile de gaze naturale destinate consumurilor tehnologice specifice opera╚¢iunilor petroliere desf─â╚Öurate de titularii de acorduri petroliere;...c)cantit─â╚¢ile de gaze naturale destinate echilibr─ârii SNT.......(7)...Excep╚¢iile de la respectarea structurilor amestecurilor de gaze naturale stabilite/avizate de ANRE se stabilesc prin hot─âr├óre a Guvernului, la propunerea ministerului de resort, ├«n termen de 45 de zile de la intrarea ├«n vigoare a prezentei legi....(8)...├Än baza datelor prev─âzute la art. 179 alin. (2) ╚Öi (3), ANRE va elimina treptat pre╚¢urile reglementate pentru furnizarea gazelor naturale pentru clien╚¢ii finali.', NULL, 181, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (249, 16, '182', 'Articolul 182', NULL, 'Gazele naturale', NULL, 'Pre╚¢uri ╚Öi tarife', NULL, NULL, NULL, NULL, 'Obliga╚¢ia de separare contabil─â...Persoanele juridice din sectorul gazelor naturale care practic─â activit─â╚¢i reglementate conform art. 179 alin. (2) sunt obligate s─â asigure separarea contabil─â, conform normelor legale ╚Öi reglement─ârilor ANRE.', NULL, 182, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (252, 16, '185', 'Articolul 185', NULL, 'Gazele naturale', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Drepturi de investigare...(1)...Pentru investigarea ├«nc─âlc─ârii prevederilor prezentului titlu, ├«n condi╚¢iile art. 183, personalul ANRE ├«mputernicit ├«n acest sens are urm─âtoarele drepturi:a)s─â ├«ntre ├«n spa╚¢iile, terenurile sau mijloacele de transport pe care operatorii economici le de╚¢in legal;...b)s─â examineze orice documente, registre, acte financiar-contabile ╚Öi comerciale sau alte eviden╚¢e legate de activitatea operatorilor economici, indiferent de locul ├«n care sunt depozitate;...c)s─â cear─â oric─ârui reprezentant sau angajat al operatorului economic explica╚¢ii cu privire la faptele sau documentele legate de obiectul ╚Öi scopul investiga╚¢iei ╚Öi s─â consemneze sau s─â ├«nregistreze r─âspunsurile acestuia;...d)s─â ridice sau s─â ob╚¢in─â ├«n orice form─â copii ori extrase din orice documente, registre, acte financiar-contabile ╚Öi comerciale sau din alte eviden╚¢e legate de activitatea operatorului economic;...e)s─â sigileze orice amplasament destinat activit─â╚¢ilor operatorului economic ╚Öi orice documente, registre, acte financiar-contabile ╚Öi comerciale sau alte eviden╚¢e legate de activitatea operatorului economic, pe durata ╚Öi ├«n m─âsura necesar─â investig─ârii.......(2)...Autoritatea competent─â va proceda la ac╚¢iunile prev─âzute la alin. (1) numai dac─â exist─â indicii c─â pot fi g─âsite documente sau pot fi ob╚¢inute informa╚¢ii considerate necesare pentru ├«ndeplinirea competen╚¢elor sale, iar rezultatul investiga╚¢iei va fi consemnat ├«ntr-un proces-verbal de constatare ╚Öi inventariere....(3)...Autoritatea competent─â poate face inspec╚¢ii inopinate ╚Öi poate solicita spre a-i fi prezentate ├«ntr-un termen rezonabil orice fel de informa╚¢ii sau justific─âri legate de ├«ndeplinirea competen╚¢elor de investigare, at├ót la fa╚¢a locului, c├ót ╚Öi prin convocare la sediul s─âu.', NULL, 185, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (253, 16, '186', 'Articolul 186', NULL, 'Gazele naturale', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Autorizarea judiciar─â a investiga╚¢iilor...├Än baza autoriz─ârii judiciare date prin ├«ncheiere, conform art. 187, personalul ANRE ├«mputernicit ├«n condi╚¢iile art. 183 poate efectua inspec╚¢ii ├«n orice alte spatii, inclusiv domiciliul, terenuri sau mijloace de transport apar╚¢in├ónd conduc─âtorilor, administratorilor, directorilor ╚Öi altor angaja╚¢i ai operatorilor economici sau asocia╚¢iilor operatorilor economici supu╚Öi investiga╚¢iei.', NULL, 186, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (254, 16, '187', 'Articolul 187', NULL, 'Gazele naturale', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Ob╚¢inerea autoriz─ârii judiciare...(1)...Personalul ANRE efectueaz─â inspec╚¢ii ├«n conformitate cu prevederile art. 186 numai ├«n baza deciziei de ├«mputernicire emise de c─âtre pre╚Öedintele autorit─â╚¢ii competente ╚Öi cu autorizarea judiciar─â dat─â prin ├«ncheiere de c─âtre pre╚Öedintele Cur╚¢ii de Apel Bucure╚Öti sau de c─âtre un judec─âtor delegat de acesta. O copie certificat─â a deciziei de ├«mputernicire ╚Öi a autoriza╚¢iei judiciare se comunic─â obligatoriu persoanei supuse inspec╚¢iei ├«nainte de ├«nceperea acesteia....(2)...Cererea de autorizare se judec─â ├«n camera de consiliu, f─âr─â citarea p─âr╚¢ilor. Judec─âtorul se pronun╚¢─â asupra cererii de autorizare ├«n termen de cel mult 48 de ore de la data ├«nregistr─ârii cererii. ├Äncheierea se motiveaz─â ╚Öi se comunic─â autorit─â╚¢ii competente ├«n termen de cel mult 48 de ore de la pronun╚¢are....(3)...├Än cazul ├«n care inspec╚¢ia trebuie desf─â╚Öurat─â simultan ├«n mai multe spa╚¢ii dintre cele prev─âzute la art. 186, autoritatea competent─â va introduce o singur─â cerere, instan╚¢a pronun╚¢├óndu-se printr-o ├«ncheiere ├«n care se vor indica spa╚¢iile ├«n care urmeaz─â s─â se desf─â╚Öoare inspec╚¢ia....(4)...Cererea de autorizare trebuie s─â cuprind─â toate informa╚¢iile de natur─â s─â justifice inspec╚¢ia, iar judec─âtorul sesizat este ╚¢inut s─â verifice dac─â cererea este ├«ntemeiat─â....(5)...Oricare ar fi ├«mprejur─ârile, inspec╚¢ia se desf─â╚Öoar─â ├«ntre orele 8,00 ╚Öi 18,00 ╚Öi trebuie efectuat─â ├«n prezen╚¢a persoanei la care se efectueaz─â inspec╚¢ia sau a reprezentantului s─âu. Inspec╚¢ia poate continua ╚Öi dup─â ora 18,00 numai cu acordul persoanei la care se efectueaz─â inspec╚¢ia sau a reprezentantului s─âu....(6)...Inventarele ╚Öi punerile de sigilii se fac conform dispozi╚¢iilor Codului de procedur─â penal─â....(7)...├Äncheierea prev─âzut─â la alin. (1) poate fi atacat─â cu recurs la ├Änalta Curte de Casa╚¢ie ╚Öi Justi╚¢ie, ├«n termen de 48 de ore. Termenul de recurs pentru autoritatea competent─â curge de la momentul comunic─ârii ├«ncheierii, potrivit prevederilor alin. (2). ├Än ceea ce prive╚Öte persoana supus─â inspec╚¢iei, termenul de recurs curge de la momentul comunic─ârii ├«ncheierii, potrivit prevederilor alin. (1). Recursul nu este suspensiv de executare....(8)...Pre╚Öedintele Cur╚¢ii de Apel Bucure╚Öti sau judec─âtorul delegat de acesta are competen╚¢a s─â emit─â autoriza╚¢ia judiciar─â ├«n vederea efectu─ârii inspec╚¢iei potrivit art. 186. Instan╚¢a de judecat─â verific─â dac─â decizia de ├«mputernicire emis─â de c─âtre pre╚Öedintele ANRE este autentic─â ╚Öi dac─â m─âsurile coercitive preconizate nu sunt nici arbitrare ╚Öi nici excesive, av├ónd ├«n vedere, ├«n special, gravitatea ├«nc─âlc─ârii suspectate, importan╚¢a elementelor de prob─â c─âutate, implicarea ├«ntreprinderii ├«n cauz─â ╚Öi probabilitatea rezonabil─â ca registrele ╚Öi documentele privind activitatea care au leg─âtur─â cu obiectul inspec╚¢iei s─â fie p─âstrate ├«n incintele pentru care se solicit─â autoriza╚¢ia. Instan╚¢a de judecat─â poate solicita ANRE explica╚¢ii detaliate privind elementele care ├«i sunt necesare pentru a-i permite s─â verifice propor╚¢ionalitatea m─âsurilor coercitive preconizate.', NULL, 187, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (255, 16, '188', 'Articolul 188', NULL, 'Gazele naturale', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Accesul la documente ╚Öi informa╚¢ii...(1)...Organele administra╚¢iei publice centrale ╚Öi locale, precum ╚Öi orice alte institu╚¢ii ╚Öi autorit─â╚¢i publice sunt obligate s─â permit─â autorit─â╚¢ii competente accesul la documentele, datele ╚Öi informa╚¢iile de╚¢inute de acestea, cu respectarea dispozi╚¢iilor legale....(2)...Autoritatea competent─â, primind acces la documentele, datele ╚Öi informa╚¢iile men╚¢ionate la alin. (1), are obliga╚¢ia de a respecta caracterul de secret de stat sau secret de serviciu atribuit legal respectivelor documente, date ╚Öi informa╚¢ii.', NULL, 188, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (256, 16, '189', 'Articolul 189', NULL, 'Gazele naturale', NULL, 'Procedura de desf─â╚Öurare a investiga╚¢iilor', NULL, NULL, NULL, NULL, 'Procedura de investigare...Procedura de investigare se face ├«n conformitate cu regulamentul privind organizarea ╚Öi desf─â╚Öurarea activit─â╚¢ii de investigare aprobat prin ordin al pre╚Öedintelui ANRE.', NULL, 189, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (257, 16, '190', 'Articolul 190', NULL, 'Gazele naturale', NULL, 'Interdic╚¢ii', NULL, NULL, NULL, NULL, 'Protec╚¢ia obiectivelor/sistemelor...Pentru protec╚¢ia obiectivelor/sistemelor din sectorul gazelor naturale se interzice ter╚¢ilor:a)s─â realizeze construc╚¢ii de orice fel ├«n zona de siguran╚¢─â a obiectivelor de gaze naturale; ├«n cazul ├«n care, ├«n mod excep╚¢ional, este necesar ca pe terenul pe care sunt amplasate acestea s─â se execute o construc╚¢ie, solicitantul va suporta toate cheltuielile aferente modific─ârilor necesare, cu respectarea tuturor prevederilor referitoare la proiectarea ╚Öi execu╚¢ia lucr─ârilor ├«n sectorul gazelor naturale ╚Öi sub condi╚¢ia ced─ârii ├«n patrimoniu operatorului a bunului rezultat;...b)s─â efectueze s─âp─âturi sau lucr─âri de orice fel ├«n zona de protec╚¢ie a obiectivelor de gaze naturale, f─âr─â avizul prealabil al operatorului de sistem;...c)s─â depoziteze materiale pe c─âile de acces ╚Öi ├«n zona de protec╚¢ie a obiectivelor de gaze naturale;...d)s─â intervin─â ├«n orice mod asupra conductelor, echipamentelor ╚Öi instala╚¢iilor de gaze naturale....', NULL, 190, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (258, 16, '191', 'Articolul 191', NULL, 'Gazele naturale', NULL, 'Interdic╚¢ii', NULL, NULL, NULL, NULL, 'Interdic╚¢ii...├Än vederea exploat─ârii ├«n condi╚¢ii de siguran╚¢─â a sistemului de transport a gazelor naturale, se interzice, cu excep╚¢ia cazului de for╚¢─â major─â, ├«ntreruperea aliment─ârii cu energie electric─â, a leg─âturilor telefonice sau de radiocomunica╚¢ii ╚Öi a transportului feroviar.', NULL, 191, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (259, 16, '192', 'Articolul 192', NULL, 'Gazele naturale', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'R─âspunderi...├Änc─âlcarea prevederilor prezentului titlu atrage r─âspunderea disciplinar─â, civil─â, contraven╚¢ional─â sau penal─â, dup─â caz, a persoanelor vinovate.', NULL, 192, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (334, 19, '7', 'Articolul 7', NULL, NULL, NULL, NULL, NULL, 'Calculul pre╚¢ului lunar de v├ónzare a energiei electrice  ╚Öi al contribu╚¢iei la Fondul de Tranzi╚¢ie Energetic─â', NULL, NULL, 'Contribu╚¢ia la Fondul de Tranzi╚¢ie Energetic─â - C_s se determin─â ├«n condi╚¢iile ├«n care P_lv > 400 lei/MWh ╚Öi se calculeaz─â ca reprezent├ónd 80% din produsul ├«ntre diferen╚¢a dintre pre╚¢ul lunar de v├ónzare ╚Öi 400 de lei/MWh ╚Öi cantitatea lunar─â livrat─â fizic din produc╚¢ia proprie ╚Öi/sau transferat─â din portofoliul de produc╚¢ie ├«n cel de furnizare - C_l.C_s = 0.8 * (P_lv - 400) * C_l', NULL, 35, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (260, 16, '193', 'Articolul 193', NULL, 'Gazele naturale', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Infrac╚¢iuni...(1)...Deteriorarea, modificarea f─âr─â drept sau blocarea func╚¢ion─ârii echipamentului de m─âsurare a consumului de gaze naturale furnizate constituie infrac╚¢iune ╚Öi se pedepse╚Öte cu ├«nchisoare de la 3 luni la 2 ani sau cu amend─â....(2)...Executarea sau folosirea de instala╚¢ii clandestine ├«n scopul racord─ârii directe la sistemul de alimentare cu gaze naturale sau pentru ocolirea echipamentelor de m─âsurare constituie infrac╚¢iune ╚Öi se pedepse╚Öte cu ├«nchisoare de la 3 luni la 2 ani sau cu amend─â....(3)...├Än cazul ├«n care infrac╚¢iunile prev─âzute la alin. (1) ╚Öi (2) sunt s─âv├ór╚Öite de un salariat al unui titular de licen╚¢─â, limitele speciale se majoreaz─â cu jum─âtate....(4)...Tentativa la infrac╚¢iunile prev─âzute la alin. (1) ╚Öi (2) se pedepse╚Öte.', NULL, 193, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (261, 16, '194', 'Articolul 194', NULL, 'Gazele naturale', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Contraven╚¢ii...Constituie contraven╚¢ii la normele privind desf─â╚Öurarea activit─â╚¢ilor ├«n sectorul gazelor naturale urm─âtoarele fapte:...1. proiectarea, avizarea, execu╚¢ia, recep╚¢ia, punerea ├«n func╚¢iune ╚Öi/sau exploatarea de lucr─âri noi, modific─âri, extinderi sau revizii ale obiectivelor din sectorul gazelor naturale de c─âtre persoane fizice sau juridice neautorizate;...2. proiectarea ╚Öi/sau execu╚¢ia de lucr─âri noi, modific─âri, extinderi ale obiectivelor din sectorul gazelor naturale f─âr─â ob╚¢inerea acordurilor, avizelor ╚Öi autoriza╚¢iilor necesare ╚Öi/sau f─âr─â respectarea restric╚¢iilor/condi╚¢iilor stabilite prin acestea;...3. proiectarea ╚Öi/sau execu╚¢ia de lucr─âri noi, modific─âri, extinderi ale obiectivelor din sectorul gazelor naturale, cu ├«nc─âlcarea reglement─ârilor tehnice emise ├«n domeniu;...4. avizarea documenta╚¢iilor tehnice pentru execu╚¢ia, recep╚¢ia ╚Öi/sau punerea ├«n func╚¢iune de lucr─âri noi, modific─âri, extinderi ale obiectivelor din sectorul gazelor naturale, care nu corespund cerin╚¢elor reglement─ârilor tehnice ├«n vigoare;...5. execu╚¢ia de lucr─âri noi, modific─âri, extinderi ale obiectivelor din sectorul gazelor naturale, cu ├«nc─âlcarea prevederilor art. 104, 105 ╚Öi 151;...6. execu╚¢ia de lucr─âri noi, modific─âri, extinderi ale obiectivelor din sectorul gazelor naturale f─âr─â documenta╚¢ie tehnic─â de execu╚¢ie verificat─â, conform prevederilor legale;...7. execu╚¢ia de lucr─âri noi, modific─âri, extinderi ale obiectivelor din sectorul gazelor naturale, excluz├ónd instala╚¢iile de utilizare de orice fel, f─âr─â urm─ârirea acestora de c─âtre un operator licen╚¢iat;...8. utilizarea de echipamente, instala╚¢ii, aparate, produse ╚Öi procedee neconforme cu reglement─ârile tehnice ├«n vigoare;...9. utilizarea/exploatarea de instala╚¢ii, echipamente ╚Öi aparate care nu au verific─ârile metrologice ╚Öi/sau verific─ârile/reviziile tehnice ├«n termen de valabilitate, conform reglement─ârilor ├«n vigoare;...10. efectuarea, f─âr─â acordul operatorului licen╚¢iat, a oric─âror lucr─âri, opera╚¢ii, manevre sau interven╚¢ii, de orice natur─â, la conductele, aparatele, echipamentele, instala╚¢iile de m─âsurare ╚Öi accesoriile, aferente obiectivelor/sistemelor de produc╚¢ie, ├«nmagazinare/stocare, transport sau distribu╚¢ie a gazelor naturale;...11. recep╚¢ia, punerea ├«n func╚¢iune ╚Öi/sau exploatarea obiectivelor/sistemelor/ instala╚¢iilor/echipamentelor din sectorul gazelor naturale pentru care nu au fost ├«ntocmite documentele prev─âzute de reglement─ârile ├«n vigoare ╚Öi/sau pentru care nu au fost ob╚¢inute autoriza╚¢iile necesare;...12. ne├«ndeplinirea ╚Öi/sau ├«ndeplinirea necorespunz─âtoare a condi╚¢iilor de valabilitate ale autoriza╚¢iilor prev─âzute la art. 119 pct. 1, altele dec├ót cele men╚¢ionate ├«n cuprinsul prezentului articol;...13. ne├«ndeplinirea ╚Öi/sau ├«ndeplinirea necorespunz─âtoare a condi╚¢iilor de valabilitate ale autoriza╚¢iilor/licen╚¢elor precizate la art. 119 pct. 2 ╚Öi 3, altele dec├ót cele men╚¢ionate ├«n cuprinsul prezentului articol;...14. nerespectarea prevederilor regulamentelor privind accesul ╚Öi/sau racordarea la depozitele de ├«nmagazinare subteran─â a gazelor naturale, sistemele de transport/distribu╚¢ie ╚Öi/sau la conductele de alimentare din amonte;...15. nerespectarea contractelor-cadru aprobate de c─âtre ANRE;...16. nerespectarea prevederilor standardelor de performan╚¢─â din sectorul gazelor naturale;...17. ├«ntreruperea sau ├«nt├órzierea nejustificat─â a aliment─ârii cu gaze naturale;...18. refuzul nejustificat al accesului/racord─ârii solicitan╚¢ilor la conductele de alimentare din amonte, la sistemul de transport, la sistemul de distribu╚¢ie sau la depozitele de ├«nmagazinare a gazelor naturale;...19. rev├ónzarea gazelor naturale de c─âtre clien╚¢ii finali;...20. refuzul sau obstruc╚¢ionarea agen╚¢ilor constatatori ├«mputernici╚¢i de c─âtre ANRE de a efectua verific─âri ╚Öi/sau inspec╚¢ii, conform prevederilor legale;...21. nefurnizarea sau furnizarea incomplet─â/eronat─â a datelor ╚Öi informa╚¢iilor solicitate de ANRE, ne├«ndeplinirea m─âsurilor dispuse ╚Öi/sau refuzul de a da curs convoc─ârii adresate de c─âtre ANRE;...22. nerespectarea obliga╚¢iilor/cerin╚¢elor prev─âzute ├«n regulamentele pentru autorizarea ╚Öi verificarea persoanelor fizice ╚Öi operatorilor economici care desf─â╚Öoar─â activit─â╚¢i de proiectare, execu╚¢ie ╚Öi exploatare ├«n domeniul gazelor naturale;...23. nerespectarea obliga╚¢iilor/cerin╚¢elor prev─âzute ├«n regulamentele privind acordarea autoriza╚¢iilor ╚Öi licen╚¢elor ├«n sectorul gazelor naturale;...24. nerespectarea programului de ├«nmagazinare, stabilit conform reglement─ârilor ├«n vigoare;...25. neconstituirea stocului minim de gaze naturale, pe care titularii licen╚¢elor de furnizare/transport au obliga╚¢ia s─â ├«l de╚¢in─â ├«n depozitele de ├«nmagazinare subteran─â, stabilit conform reglement─ârilor ├«n vigoare;...26. nerespectarea normelor privind zonele de protec╚¢ie ╚Öi siguran╚¢─â, stabilite conform legii;...27. nerespectarea reglement─ârilor comerciale aprobate de ANRE, inclusiv a metodologiilor privind pre╚¢urile ╚Öi tarifele;...28. nerespectarea reglement─ârilor privind separarea legal─â ╚Öi/sau contabil─â a activit─â╚¢ilor reglementate ├«n sectorul gazelor naturale;...29. desf─â╚Öurarea oric─âror activit─â╚¢i comerciale ╚Öi/sau prest─âri de servicii f─âr─â a de╚¢ine licen╚¢a/autoriza╚¢ia necesar─â, emis─â conform prevederilor prezentului titlu ╚Öi reglement─ârilor elaborate ├«n temeiul acestuia;...30. nerespectarea prevederilor legale privind schimbarea furnizorului de gaze naturale;...31. nerespectarea prevederilor legale privind m─âsurarea gazelor naturale;...32. nerespectarea cerin╚¢elor de raportare a informa╚¢iilor prev─âzute de reglement─ârile ├«n vigoare, inclusiv ├«n regulamentele europene;...33. nerespectarea prevederilor privind informarea consumatorilor de gaze naturale;...34. nerespectarea prevederilor reglement─ârilor tehnice emise/aprobate de ANRE;...35. nerespectarea de c─âtre operatorul de transport ╚Öi de sistem a prevederilor privind condi╚¢iile de acces la re╚¢elele pentru transportul gazelor naturale, prev─âzute de regulamentele europene;...36. nerespectarea de c─âtre operatorul de transport ╚Öi de sistem a prevederilor referitoare la mecanismele de alocare a capacit─â╚¢ii ╚Öi a procedurilor de gestionare a congestiilor, prev─âzute de regulamentele europene;...37. nerespectarea cerin╚¢elor privind transparen╚¢a, prev─âzute de regulamentele europene;...38. nefurnizarea sau furnizarea incomplet─â de c─âtre operatorul de transport ╚Öi de sistem a informa╚¢iilor necesare utilizatorilor de re╚¢ea pentru luarea de m─âsuri corective oportune ├«n vederea evit─ârii dezechilibrelor;...39. nerespectarea ordinelor ╚Öi deciziilor ANRE, altele dec├ót cele prev─âzute ├«n cuprinsul prezentului articol;...40. nerespectarea prevederilor privind confiden╚¢ialitatea.', NULL, 194, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (262, 16, '195', 'Articolul 195', NULL, 'Gazele naturale', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Sanc╚¢iuni...(1)...Contraven╚¢iile prev─âzute la art. 194 se sanc╚¢ioneaz─â astfel:...1. ├«n cazul ├«n care au fost s─âv├ór╚Öite de c─âtre persoane fizice:a)cu amend─â de la 2.000 lei la 6.000 lei, cele de la pct. 9, 20 ╚Öi 21;...b)cu amend─â de la 4.500 lei la 10.000 lei, cele de la pct. 2, 3, 8, 22 ╚Öi 34;...c)cu amend─â de la 6.000 lei la 15.000 lei, cele de la pct. 4, 10, 26 ╚Öi 40;......2. ├«n cazul ├«n care au fost s─âv├ór╚Öite de c─âtre persoane juridice:a)cu amend─â de la 7.000 lei la 15.000 lei, cele de la pct. 1, 2, 9, 12, 20, 22 ╚Öi 33;...b)cu amend─â de la 15.000 lei la 25.000 lei, cele de la pct. 3, 6, 8, 15, 16, 19, 21, 23, 30 ╚Öi 34;...c)cu amend─â de la 50.000 lei la 100.000 lei, cele de la pct. 4, 5, 7, 10, 11, 13, 14, 17, 18, 24, 26, 27, 29, 31, 32, 39 ╚Öi 40;...d)cu amend─â de 500.000 lei, cele de la pct. 25, 28, 36, 37 ╚Öi 38;...e)cu amend─â cuprins─â ├«ntre 1%-10% din cifra de afaceri anual─â, cea de la pct. 35.......(2)...Pentru contraven╚¢iile prev─âzute la art. 194, s─âv├ór╚Öite ├«n mod repetat de c─âtre persoanele juridice, autoritatea de reglementare poate aplica o amend─â de p├ón─â la 5% din cifra de afaceri anual─â a persoanei contraveniente....(3)...Prin contraven╚¢ie s─âv├ór╚Öit─â ├«n mod repetat se ├«n╚¢elege s─âv├ór╚Öirea de cel pu╚¢in 3 ori pe parcursul unui an calendaristic a uneia ╚Öi aceleia╚Öi fapte contraven╚¢ionale, ├«n cadrul aceleia╚Öi structuri organizatorice, dintre cele prev─âzute la art. 194 pct. 10, 11, 17, 21, 22, 28, 29, 36, 37, 38 ╚Öi 39....(4)...Prin cifra de afaceri anual─â se ├«n╚¢elege cifra de afaceri a persoanei juridice contraveniente realizat─â din activitatea reglementat─â pentru care s-a stabilit contraven╚¢ia, ├«n anul financiar anterior sanc╚¢ion─ârii faptei....(5)...Contravenientul poate achita, pe loc sau ├«n termen de cel mult 48 de ore de la data ├«ncheierii procesului-verbal de constatare ╚Öi sanc╚¢ionare ori, dup─â caz, de la data comunic─ârii acestuia, jum─âtate din minimul amenzii prev─âzute la alin. (1) ╚Öi (2), agentul constatator f─âc├ónd men╚¢iune despre aceast─â posibilitate ├«n procesul-verbal....(6)...Aplicarea sanc╚¢iunii amenzii contraven╚¢ionale se prescrie ├«n termen de 2 ani de la data s─âv├ór╚Öirii faptei.', NULL, 195, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (263, 16, '196', 'Articolul 196', NULL, 'Gazele naturale', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Constatarea contraven╚¢iilor ╚Öi aplicarea sanc╚¢iunilor...Constatarea contraven╚¢iilor ╚Öi aplicarea sanc╚¢iunilor se fac de c─âtre persoane ├«mputernicite ├«n acest scop de c─âtre pre╚Öedintele ANRE.', NULL, 196, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (264, 16, '197', 'Articolul 197', NULL, 'Gazele naturale', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Actualizarea limitelor amenzilor...Actualizarea limitelor amenzilor prev─âzute la art. 195 se va face prin hot─âr├óre a Guvernului.', NULL, 197, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (265, 16, '198', 'Articolul 198', NULL, 'Gazele naturale', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Regimul juridic al contraven╚¢iilor...Contraven╚¢iilor prev─âzute la art. 194 le sunt aplicabile dispozi╚¢iile Ordonan╚¢ei Guvernului nr. 2/2001, aprobat─â cu modific─âri ╚Öi complet─âri prin Legea nr. 180/2002, cu modific─ârile ╚Öi complet─ârile ulterioare.', NULL, 198, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (266, 16, '199', 'Articolul 199', NULL, 'Gazele naturale', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'Accesul ├«n vederea constat─ârii contraven╚¢iilor...(1)...├Än vederea constat─ârii contraven╚¢iilor, agen╚¢ii constatatori au acces, ├«n condi╚¢iile legii, la obiectivele de produc╚¢ie, la sistemele din sectorul gazelor naturale, inclusiv la instala╚¢iile ╚Öi echipamentele aferente acestora, precum ╚Öi la instala╚¢iile de utilizare a gazelor naturale....(2)...Proprietarii instala╚¢iilor de utilizare sau cei care le exploateaz─â sunt obliga╚¢i s─â pun─â la dispozi╚¢ia agen╚¢ilor constatatori documente, date ╚Öi/sau informa╚¢ii relevante....(3)...Organele de poli╚¢ie, precum ╚Öi celelalte organe cu atribu╚¢ii ├«n domeniu sunt obligate s─â acorde sprijin, la cerere, agen╚¢ilor constatatori.', NULL, 199, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (267, 16, '200', 'Articolul 200', NULL, 'Gazele naturale', NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, 'Intrarea ├«n vigoare...(1)...Dispozi╚¢iile art. 127 intr─â ├«n vigoare la data de 3 martie 2013....(2)...La data intr─ârii ├«n vigoare a prezentei legi se abrog─â:a)Legea gazelor nr. 351/2004, publicat─â ├«n Monitorul Oficial al Rom├óniei, Partea I, nr. 679 din 28 iulie 2004, cu modific─ârile ╚Öi complet─ârile ulterioare, cu excep╚¢ia art. 6-10;...b)orice alte dispozi╚¢ii contrare.......(3)...├Än termen de 9 luni de la data intr─ârii ├«n vigoare a prezentei legi, ANRE va adapta, dup─â caz, cadrul de reglementare ├«n concordan╚¢─â cu prezentul titlu....(4)...P├ón─â la adaptarea, dup─â caz, a cadrului de reglementare, toate actele normative emise ├«n temeiul Legii nr. 351/2004, cu modific─ârile ╚Öi complet─ârile ulterioare, ├«╚Öi p─âstreaz─â valabilitatea, cu excep╚¢ia prevederilor care sunt contrare prezentei legi.', NULL, 200, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (268, 16, '201', 'Articolul 201', NULL, 'Gazele naturale', NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, 'Dispozi╚¢ii finale...(1)...Ministerul de resort notific─â Comisiei Europene adoptarea acestei legi ╚Öi a altor acte administrative necesare pentru punerea ├«n aplicare a prevederilor Directivei 2009/73/CE , inclusiv prin transmiterea textelor acestor acte normative....(2)...├Än termen de 24 de luni de la data intr─ârii ├«n vigoare a prezentei legi, ministerul de resort realizeaz─â o evaluare a func╚¢ion─ârii operatorului de transport ╚Öi de sistem ├«n baza modelului "operator independent de sistem" ╚Öi, dac─â este cazul, propune Guvernului adoptarea modelului de separare a propriet─â╚¢ii, conform art. 9 alin. (1) din Directiva 2009/73/CE . Un operator economic integrat pe vertical─â care de╚¢ine ├«n proprietate o re╚¢ea de transport nu poate fi ├«mpiedicat s─â adopte m─âsurile necesare implement─ârii modelului de separare a propriet─â╚¢ii prev─âzut la art. 9 alin. (1) din Directiva 2009/73/CE ....(3)...Guvernul, cu avizul Consiliului Concuren╚¢ei, poate decide constituirea unui fond de solidaritate pentru sus╚¢inerea financiar─â a consumatorului vulnerabil, din contribu╚¢ia ╚Öi/sau impozitarea suplimentar─â a profiturilor nea╚Öteptate ale produc─âtorilor ╚Öi furnizorilor de energie electric─â ╚Öi gaze naturale, realizate ca urmare a unor situa╚¢ii favorabile ale pie╚¢ei ╚Öi/sau a unor tranzac╚¢ii conjuncturale. Modul de constituire ╚Öi utilizare a fondului se stabile╚Öte prin hot─âr├óre a Guvernului.', NULL, 201, '2025-11-07 23:44:16.533426', '2025-11-07 23:44:16.533426', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (269, 17, '1', 'Articolul 1', NULL, NULL, NULL, 'Scop ╚Öi domeniu de aplicare', NULL, NULL, NULL, NULL, 'Prezenta metodologie stabile╚Öte indicatorii prin care se evalueaz─â performan╚¢a ├«nregistrat─â de operatorii de distribu╚¢ie a energiei electrice concesionari ╚Öi de operatorul de transport ╚Öi de sistem cu privire la dezvoltarea unei re╚¢ele electrice inteligente care s─â promoveze eficien╚¢a energetic─â ╚Öi integrarea energiei electrice produse din surse regenerabile, a unit─â╚¢ilor de stocare de energie ╚Öi a serviciilor de flexibilitate, modul de determinare a valorii acestor indicatori ╚Öi condi╚¢iile pentru aplicarea prevederilor reglement─ârilor ├«n vigoare privind acordarea de stimulente sau penalit─â╚¢i ├«n leg─âtur─â cu valorile acestor indicatori.', NULL, 1, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (335, 19, '8', 'Articolul 8', NULL, NULL, NULL, NULL, NULL, 'Calculul pre╚¢ului lunar de v├ónzare a energiei electrice  ╚Öi al contribu╚¢iei la Fondul de Tranzi╚¢ie Energetic─â', NULL, NULL, 'Calculul prev─âzut laart. 6╚Öi7se realizeaz─â lunar de c─âtre produc─âtorii de energie electric─â prev─âzu╚¢i laart. 14din prezenta ordonan╚¢─â de urgen╚¢─â/entit─â╚¢ile agregate.', NULL, 36, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (270, 17, '2', 'Articolul 2', NULL, NULL, NULL, 'Scop ╚Öi domeniu de aplicare', NULL, NULL, NULL, NULL, 'Prezenta metodologie este utilizat─â de c─âtre:a)operatorii de distribu╚¢ie a energiei electrice concesionari, pentru organizarea activit─â╚¢ilor privind colectarea ╚Öi ├«nregistrarea datelor necesare determin─ârii indicatorilor ╚Öi pentru calculul indicatorilor, precum ╚Öi pentru ├«ntocmirea ╚Öi transmiterea raport─ârilor prev─âzute ├«n prezenta metodologie;...b)operatorul de transport ╚Öi de sistem, pentru organizarea activit─â╚¢ilor privind colectarea ╚Öi ├«nregistrarea datelor necesare determin─ârii indicatorilor ╚Öi pentru calculul indicatorilor, precum ╚Öi pentru ├«ntocmirea ╚Öi transmiterea raport─ârilor prev─âzute ├«n prezenta metodologie;...c)Autoritatea Na╚¢ional─â de Reglementare ├«n Domeniul Energiei, ├«n procesul de analiz─â a datelor transmise de operatorii de re╚¢ea, ├«n vederea aplic─ârii prevederilor reglement─ârilor  ├«n vigoare privind acordarea de stimulente sau penalit─â╚¢i ├«n leg─âtur─â cu valorile indicatorilor de performan╚¢─â prev─âzu╚¢i de prezenta metodologie....', NULL, 2, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (271, 17, '3', 'Articolul 3', NULL, NULL, NULL, 'Defini╚¢ii ╚Öi abrevieri', NULL, NULL, NULL, NULL, 'Termenii utiliza╚¢i ├«n prezenta metodologie au semnifica╚¢ia prev─âzut─â ├«n urm─âtoarele acte normative:a)Legea energiei electrice ╚Öi a gazelor naturale nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioare;...b)Standardul de performan╚¢─â pentru serviciul de transport al energiei electrice ╚Öi pentru serviciul de sistem, aprobat prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 12/2016, cu modific─ârile ╚Öi complet─ârile ulterioare;...c)Standardul de performan╚¢─â pentru serviciul de distribu╚¢ie a energiei electrice, aprobat prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 46/2021, cu modific─ârile ╚Öi complet─ârile ulterioare;...d)Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 177/2018 privind aprobarea Condi╚¢iilor-cadru pentru realizarea calendarului de implementare a sistemelor de m─âsurare inteligent─â a energiei electrice la nivel na╚¢ional, cu modific─ârile ╚Öi complet─ârile ulterioare;...e)Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 124/2022 privind aprobarea Regulilor pentru gestionarea congestiilor prin utilizarea pe baz─â de pia╚¢─â de c─âtre operatorii de re╚¢ea a flexibilit─â╚¢ii resurselor din re╚¢elele de distribu╚¢ie ╚Öi a celor din re╚¢eaua de transport, a Regulilor aplicabile achizi╚¢iei de energie electric─â reactiv─â pentru reglajul tensiunii ├«n regim sta╚¢ionar de c─âtre operatorul de transport ╚Öi de sistem ╚Öi a Regulilor aplicabile achizi╚¢iei de energie electric─â reactiv─â pentru reglajul tensiunii ├«n regim sta╚¢ionar de c─âtre operatorii de distribu╚¢ie concesionari ╚Öi privind modificarea ╚Öi completarea Ordinului pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 127/2021 pentru aprobarea Regulamentului privind clauzele ╚Öi condi╚¢iile pentru furnizorii de servicii de echilibrare ╚Öi pentru furnizorii de rezerv─â de stabilizare a frecven╚¢ei ╚Öi a Regulamentului privind clauzele ╚Öi condi╚¢iile pentru p─âr╚¢ile responsabile cu echilibrarea ╚Öi pentru modificarea ╚Öi abrogarea unor ordine ale pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei, cu modific─ârile ulterioare....', NULL, 3, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (272, 17, '4', 'Articolul 4', NULL, NULL, NULL, 'Defini╚¢ii ╚Öi abrevieri', NULL, NULL, NULL, NULL, '├Än ├«n╚¢elesul prezentei metodologii, termenii ╚Öi abrevierile utilizate au urm─âtoarele semnifica╚¢ii:a)ANRE - Autoritatea Na╚¢ional─â de Reglementare ├«n Domeniul Energiei;...b)ATR - aviz tehnic de racordare;...c)CTSI - Centrul de telecomand─â ╚Öi supraveghere instala╚¢ii;...d)DLR - Dynamic Line Rating;...e)ENS (Energy Not Supplied) - energia nelivrat─â, definit─â ca energia nelivrat─â utilizatorilor/neprodus─â ├«n centrale din cauza ├«ntreruperilor de lung─â durat─â;...f)HES (Head End System) - centru de colectare a fluxului de date provenite din subsistemele de m─âsurare/concentratoarele de date, constituit din componente hardware ╚Öi software, cu func╚¢iuni limitate de validare a datelor primite, component─â a sistemului central de achizi╚¢ie, prelucrare ╚Öi administrare a datelor de m─âsurare;...g)INDLIN - indisponibilitatea medie a liniei electrice aeriene;...h)INDTRA - indisponibilitatea medie a transformatoarelor ╚Öi autotransformatoarelor;...i)LEA - linie electric─â aerian─â;...j)MDMS (Meter Data Management System) - platforma software pentru stocarea ╚Öi prelucrarea datelor provenite din unit─â╚¢ile HES ale sistemelor de m─âsurare inteligent─â a energiei electrice ╚Öi interfa╚¢area cu aplica╚¢ii de facturare, de informare a utilizatorilor ╚Öi a ter╚¢ilor interesa╚¢i, de prognozare a consumului de energie electric─â etc.;...k)Mrstdz - media anual─â a ratelor zilnice de succes al transmiterii datelor de la contoarele integrate ├«n SMI la HES/MDMS;...l)OD - operator de distribu╚¢ie a energiei electrice concesionar;...m)OR - operatorii de distribu╚¢ie a energiei electrice concesionari ╚Öi operatorul de transport ╚Öi de sistem;...n)OTS - operatorul de transport ╚Öi de sistem;...o)RRR - rat─â reglementat─â a rentabilit─â╚¢ii;...p)SAIDI (System Average Interruption Duration Index) - indicele durata medie a ├«ntreruperilor ├«n re╚¢ea (sistem) pentru un utilizator;...q)SAIFI (System Average Interruption Frequency Index) - indicele frecven╚¢a medie a ├«ntreruperilor ├«n re╚¢ea (sistem) pentru un utilizator;...r)SMI - sistem de m─âsurare inteligent─â a energiei electrice....', NULL, 4, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (273, 17, '5', 'Articolul 5', NULL, NULL, NULL, 'Reguli ╚Öi principii', NULL, NULL, NULL, NULL, 'Indicatorii pe baza c─ârora se evalueaz─â anual performan╚¢a realizat─â de OR cu privire la dezvoltarea unei re╚¢ele electrice inteligente care s─â promoveze eficien╚¢a energetic─â ╚Öi integrarea energiei electrice produse din surse regenerabile, a unit─â╚¢ilor de stocare de energie ╚Öi a serviciilor de flexibilitate sunt grupa╚¢i ├«n 3 categorii, dup─â cum urmeaz─â:a)categoria A - indicatori care reflect─â calitatea serviciului de distribu╚¢ie, respectiv de transport al energiei electrice;...b)categoria B - indicatori care reflect─â integrarea produc╚¢iei de energie electric─â din surse regenerabile, a unit─â╚¢ilor de stocare de energie ╚Öi a serviciilor de flexibilitate;...c)categoria C - indicatori care reflect─â nivelul de digitalizare a re╚¢elelor electrice....', NULL, 5, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (274, 17, '6', 'Articolul 6', NULL, NULL, NULL, 'Reguli ╚Öi principii', NULL, NULL, NULL, NULL, '(1)...Nivelul de performan╚¢─â realizat ├«ntr-un an t de OR se determin─â prin intermediul unui indicator de performan╚¢─â compus calculat conform prevederilorart. 14╚Öi18....(2)...Nivelul de performan╚¢─â fa╚¢─â de care valoarea RRR se majoreaz─â sau se diminueaz─â ├«n conformitate cu prevederile art. 76 din Metodologia de stabilire a tarifelor pentru serviciul de distribu╚¢ie a energiei electrice, aprobat─â prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 67/2024, respectiv ale art. 62 din Metodologia de stabilire a tarifelor pentru serviciul de transport al energiei electrice, aprobat─â prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 68/2024, corespunde unei valori de referin╚¢─â a indicatorului de performan╚¢─â compus prev─âzut laalin. (1), conform prevederilorart. 10.', NULL, 6, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (275, 17, '7', 'Articolul 7', NULL, NULL, NULL, 'Reguli ╚Öi principii', NULL, NULL, NULL, NULL, 'Raportarea valorilor realizate pentru indicatorii monitoriza╚¢i ╚Öi evaluarea nivelului de performan╚¢─â prev─âzut laart. 6 alin. (1)se efectueaz─â de c─âtre OR ╚Öi ANRE anual, ├«ncep├ónd cu anul al treilea al perioadei a V-a de reglementare. Raportarea ╚Öi evaluarea din anul al treilea al perioadei a V-a de reglementare se efectueaz─â pentru primii doi ani ai perioadei a V-a de reglementare.', NULL, 7, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (276, 17, '8', 'Articolul 8', NULL, NULL, NULL, 'Reguli ╚Öi principii', NULL, NULL, NULL, NULL, 'Valorile coeficien╚¢ilor de ponderare a indicatorilor din categoriile A, B ╚Öi C prev─âzute laart. 5se stabilesc astfel ├«nc├ót suma coeficien╚¢ilor de ponderare aferen╚¢i indicatorilor din fiecare categorie s─â fie egal─â cu 100%.', NULL, 8, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (277, 17, '9', 'Articolul 9', NULL, NULL, NULL, 'Reguli ╚Öi principii', NULL, NULL, NULL, NULL, '(1)...Nivelul de performan╚¢─â realizat ├«ntr-un an t aferent indicatorilor din categoria C se determin─â prin raportarea la valori-╚¢int─â stabilite pentru fiecare indicator....(2)...Pentru stabilirea ╚¢intelor necesare determin─ârii nivelului de performan╚¢─â reflectat prin indicatorii din categoria C:a)OR realizeaz─â, ├«ntr-o perioad─â de maximum doi ani de la data intr─ârii ├«n vigoare a prezentei metodologii, studii ╚Öi/sau analize privind  necesarul de digitalizare a re╚¢elelor electrice ├«n func╚¢ie de configura╚¢ia ╚Öi starea re╚¢elelor, regimurile de func╚¢ionare, prognozele pe termen mediu ╚Öi lung privind cererea de consum ╚Öi rezerva de capacitate necesar─â pentru integrarea produc╚¢iei de energie electric─â din surse regenerabile;...b)╚¢intele necesare determin─ârii nivelului de performan╚¢─â reflectat prin indicatorii din categoria C se stabilesc de ANRE pe baza propunerilor ├«naintate de OR, fundamentate prin rezultatele din studiile ╚Öi/sau analizele efectuate.......(3)...├Än situa╚¢ia ├«n care, pentru un indicator monitorizat din categoria C, a fost ├«ndeplinit─â ╚¢inta de c─âtre un OR, ├«ncep├ónd cu anul urm─âtor anului ├«n care a fost ├«ndeplinit─â ╚¢inta, ├«nceteaz─â monitorizarea respectivului indicator ╚Öi acest indicator se exclude din formula de calcul al indicatorului de performan╚¢─â compus.', NULL, 9, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (278, 17, '10', 'Articolul 10', NULL, NULL, NULL, 'Reguli ╚Öi principii', NULL, NULL, NULL, NULL, '(1)...Valoarea RRR se majoreaz─â ├«n anul t conform prevederilor Metodologiei de stabilire a tarifelor pentru serviciul de distribu╚¢ie a energiei electrice, aprobat─â prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 67/2024, respectiv ale Metodologiei de stabilire a tarifelor pentru serviciul de transport al energiei electrice, aprobat─â prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 68/2024, pentru OD, respectiv pentru OTS, ├«n situa╚¢ia ├«n care valoarea realizat─â a indicatorului de performan╚¢─â compus prev─âzut laart. 6pentru anul t este mai mare dec├ót 90%....(2)...Valoarea RRR se diminueaz─â ├«n anul t conform prevederilor Metodologiei de stabilire a tarifelor pentru serviciul de distribu╚¢ie a energiei electrice, aprobat─â prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 67/2024, respectiv ale Metodologiei de stabilire a tarifelor pentru serviciul de transport al energiei electrice, aprobat─â prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 68/2024, pentru OD, respectiv pentru OTS, ├«n situa╚¢ia ├«n care valoarea realizat─â a indicatorului de performan╚¢─â compus prev─âzut laart. 6pentru anul t este mai mic─â dec├ót 70%.', NULL, 10, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (279, 17, '11', 'Articolul 11', NULL, NULL, NULL, 'Indicatori de performan╚¢─â pentru serviciul de distribu╚¢ie a energiei electrice', NULL, NULL, NULL, NULL, '(1)...Indicatorii de monitorizare din categoria A, care reflect─â nivelul de calitate a serviciului de distribu╚¢ie a energiei electrice, sunt:a)SAIDI - indicele durata medie a ├«ntreruperilor ├«n re╚¢ea (sistem) pentru un utilizator din zona de concesiune, pentru cazul ├«ntreruperi neplanificate, altele dec├ót cele cauzate de condi╚¢ii meteorologice deosebite, de utilizatori sau ter╚¢i [min./an];...b)SAIFI - indicele frecven╚¢a medie a ├«ntreruperilor ├«n re╚¢ea (sistem) pentru un utilizator din zona de concesiune, pentru cazul ├«ntreruperi neplanificate, altele dec├ót cele cauzate de condi╚¢ii meteorologice deosebite, de utilizatori sau ter╚¢i [├«ntr./an];...c)num─ârul de reclama╚¢ii primite referitoare la calitatea curbei de tensiune ├«nregistrate pentru toate nivelurile de tensiune, ├«n re╚¢elele de distribu╚¢ie a energiei electrice din zona de concesiune;...d)Mrstdz - media anual─â a ratelor zilnice de succes al transmiterii datelor de la contoarele integrate ├«n SMI la HES/MDMS, ├«nregistrat─â ├«n zona de concesiune.......(2)...Valoarea indicatorilor de monitorizare prev─âzu╚¢i laalin. (1)se determin─â anual de OD conform prevederilor Standardului de performan╚¢─â pentru serviciul de distribu╚¢ie a energiei electrice, aprobat prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 46/2021, cu modific─ârile ╚Öi complet─ârile ulterioare, ╚Öi conform prevederilor din Condi╚¢iile-cadru pentru realizarea calendarului de implementare a sistemelor de m─âsurare inteligent─â a energiei electrice la nivel na╚¢ional, aprobate prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 177/2018, cu modific─ârile ╚Öi complet─ârile ulterioare....(3)...Indicatorii de performan╚¢─â I_Adi din categoria A, prev─âzu╚¢i laalin. (1) lit. a)-c), aferen╚¢i anului t, se calculeaz─â cu urm─âtoarea formul─â:unde:ΓÇôI_Adi - indicator care reflect─â performan╚¢a realizat─â de OD referitoare la calitatea serviciului de distribu╚¢ie ├«n anul t de raportare pentru indicatorul de monitorizare i, conform nominaliz─ârilor de laalin. (1);...ΓÇôMIN() - func╚¢ie matematic─â prin care se determin─â valoarea minim─â dintr-un ╚Öir de valori;...ΓÇôV_Adi - valoarea anual─â a indicatorului de monitorizare i, conform nominaliz─ârilor de laalin. (1);...ΓÇôan_t - anul de raportare;...ΓÇôan_t-4,...., an_t - perioad─â de cinci ani consecutivi, inclusiv anul t de raportare, pentru care se iau ├«n calcul valorile indicatorilor de monitorizare, pentru determinarea indicatorului de performan╚¢─â aferent anului t de raportare;...ΓÇôi - num─ârul de ordine al indicatorului de monitorizare/performan╚¢─â, conform nominaliz─ârilor de laalin. (1).......(4)...Indicatorul de performan╚¢─â I_Ad4 din categoria A, prev─âzut laalin. (1) lit. d), aferent anului t, se calculeaz─â cu urm─âtoarea formul─â:unde:ΓÇôI_Ad4 - indicator care reflect─â performan╚¢a realizat─â de OD referitoare la media anual─â a ratelor zilnice de succes al transmiterii datelor de la contoarele integrate ├«n SMI la HES/MDMS ├«n anul t de raportare, pentru indicatorul de monitorizare Mrstdz prev─âzut laalin. (1) lit. d);...ΓÇôMAX() - func╚¢ie matematic─â prin care se determin─â valoarea maxim─â dintr-un ╚Öir de valori;...ΓÇôV_Ad4 - valoarea anual─â a indicatorului de monitorizare  prev─âzut laalin. (1) lit. d);...ΓÇôan_t - anul de raportare;...ΓÇôan_t-4,...., an_t - perioad─â de cinci ani consecutivi, inclusiv anul t de raportare, pentru care se iau ├«n calcul valorile indicatorilor de monitorizare, pentru determinarea indicatorului de performan╚¢─â aferent anului t de raportare....', NULL, 11, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (280, 17, '12', 'Articolul 12', NULL, NULL, NULL, 'Indicatori de performan╚¢─â pentru serviciul de distribu╚¢ie a energiei electrice', NULL, NULL, NULL, NULL, '(1)...Indicatorii de monitorizare din categoria B care reflect─â integrarea produc╚¢iei de energie electric─â din surse regenerabile, a unit─â╚¢ilor de stocare de energie ╚Öi a serviciilor de flexibilitate sunt:a)NPd - ponderea num─ârului de ATR emise pentru racordarea la re╚¢elele electrice de distribu╚¢ie de medie ╚Öi ├«nalt─â tensiune de noi locuri de producere cu unit─â╚¢i de producere de energie electric─â din surse regenerabile (altele dec├ót cele apar╚¢in├ónd prosumatorilor), raportat la num─ârul cererilor de racordare de acest tip ├«nregistrate de OD (%);...b)PPd - ponderea puterii totale aprobate pentru evacuare prin ATR emise pentru racordarea la re╚¢elele electrice de distribu╚¢ie de medie ╚Öi ├«nalt─â tensiune de noi locuri de producere cu unit─â╚¢i de producere de energie electric─â din surse regenerabile (altele dec├ót cele apar╚¢in├ónd prosumatorilor), raportat─â la puterea total─â solicitat─â prin cererile de racordare de acest tip ├«nregistrate de OD (%);...c)NStoc - ponderea num─ârului de ATR emise pentru racordarea la re╚¢elele electrice de distribu╚¢ie de noi unit─â╚¢i de stocare de energie, raportat la num─ârul cererilor de racordare de acest tip ├«nregistrate de OD (%);...d)PStoc - ponderea puterii totale aprobate pentru evacuare prin ATR emise pentru racordarea la re╚¢elele electrice de distribu╚¢ie a unit─â╚¢ilor de stocare de energie, raportat─â la puterea total─â solicitat─â prin cererile de racordare de acest tip ├«nregistrate de OD (%);...e)NPros - ponderea num─ârului de prosumatori nou- racorda╚¢i ├«n re╚¢elele electrice de distribu╚¢ie din zona de concesiune, raportat la num─ârul cererilor de racordare de acest tip ├«nregistrate de OD (%);...f)PPros - ponderea puterii totale aprobate pentru evacuare prosumatorilor nou-racorda╚¢i ├«n re╚¢elele electrice de distribu╚¢ie din zona de concesiune, raportat─â la puterea total─â solicitat─â prin cererile de racordare de acest tip ├«nregistrate de OD (%);...g)NFlexi - num─ârul de produse de servicii de flexibilitate achizi╚¢ionate ├«n anul t de OD de la produc─âtori, consumatori dispecerizabili, agregatori, alte entit─â╚¢i care furnizeaz─â servicii de flexibilitate.......(2)...Cererile de racordare ╚Öi ATR emise pentru locuri de producere sau locuri de consum ╚Öi producere noi care includ unit─â╚¢i de stocare se consider─â o singur─â dat─â ├«n calculul indicatorilor prev─âzu╚¢i laalin. (1), ╚Öi anume ├«n calculul indicatorilor prev─âzu╚¢i lalit. a)╚Öib)saue)╚Öif), dup─â caz....(3)...Indicatorii de monitorizare prev─âzu╚¢i laalin. (1) lit. a)-f)se determin─â anual de OD, utiliz├ónd urm─âtoarele formule:a)pentru indicatorii de monitorizare prev─âzu╚¢i lalit. a)╚Öic):unde:ΓÇôN_i - indicator de monitorizare referitor la ponderea num─ârului de ATR emise pentru noi locuri de producere sau unit─â╚¢i de stocare, dup─â caz, raportat la num─ârul cererilor de racordare de acest tip ├«nregistrate de OD, calculat pentru un an t;...ΓÇôNATR_i - num─ârul de ATR emise de OD ├«n termenul reglementat ca urmare a cererilor de racordare ├«nregistrate ├«n anul t pentru racordarea la re╚¢ele electrice de medie ╚Öi ├«nalt─â tensiune de noi locuri de producere sau unit─â╚¢i de stocare, dup─â caz, care ├«ndeplinesc condi╚¢ia ca termenul pentru racordarea obiectivelor prev─âzut ├«n ATR s─â coincid─â cu termenul de PIF solicitat de utilizator prin cererea de racordare;...ΓÇôNSol_i - num─ârul de cereri pentru racordarea la re╚¢elele electrice de medie ╚Öi ├«nalt─â tensiune de noi locuri de producere sau unit─â╚¢i de stocare, dup─â caz, ├«nregistrate de OD ├«n anul t ╚Öi care, p├ón─â la data emiterii ATR, nu au fost clasate de OD cu ├«n╚Ötiin╚¢area utilizatorului conform prevederilor Regulamentului privind racordarea utilizatorilor la re╚¢elele electrice de interes public, aprobat prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 59/2013, cu modific─ârile ╚Öi complet─ârile ulterioare;...ΓÇôi - num─ârul de ordine al indicatorului monitorizat; i = 1 sau 3 conform indicatorilor nominaliza╚¢i laalin. (1);......b)pentru indicatorii de monitorizare prev─âzu╚¢i lalit. b)╚Öid):unde:ΓÇôP_i - indicator de monitorizare privind ponderea puterii totale aprobate prin ATR emise pentru noi locuri de producere sau unit─â╚¢i de stocare, dup─â caz, raportat─â la puterea total─â solicitat─â prin cererile de racordare de acest tip ├«nregistrate de OD;...ΓÇôPATR_i [MW] - puterea total─â aprobat─â pentru evacuare prin ATR emise de OD ├«n termenul reglementat ca urmare a cererilor de racordare ├«nregistrate ├«n anul t pentru racordarea de noi locuri de producere sau unit─â╚¢i de stocare, dup─â caz, care ├«ndeplinesc condi╚¢ia ca termenul pentru racordare din ATR s─â coincid─â cu termenul de PIF solicitat de utilizator prin cererea de racordare;...ΓÇôPSol_i [MW] - puterea total─â solicitat─â pentru evacuare prin cererile pentru racordarea noilor locuri de producere sau unit─â╚¢i de stocare, dup─â caz, ├«nregistrate de OD ├«n anul t ╚Öi care, p├ón─â la data emiterii ATR, nu au fost clasate de OD cu ├«n╚Ötiin╚¢area utilizatorului, conform prevederilor Regulamentului privind racordarea utilizatorilor la re╚¢elele electrice de interes public, aprobat prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 59/2013, cu modific─ârile ╚Öi complet─ârile ulterioare;...ΓÇôi - num─ârul de ordine al indicatorului monitorizat, i = 2 sau 4 conform indicatorilor nominaliza╚¢i laalin. (1);......c)pentru indicatorul de monitorizare prev─âzut la lit. e):unde:ΓÇôN_Pros - indicator de monitorizare referitor la ponderea num─ârului de prosumatori nou-racorda╚¢i, calculat pentru un an t;...ΓÇôNCR_Pros - num─ârul de certificate de racordare emise de OD ├«n anul t pentru racordarea noilor prosumatori;...ΓÇôNSol_Pros - num─ârul de cereri pentru racordarea noilor prosumatori, ├«nregistrate de OD ├«n anul t ╚Öi care nu au fost clasate de OD cu ├«n╚Ötiin╚¢area utilizatorului, conform prevederilor reglement─ârilor ├«n vigoare;......d)pentru indicatorul de monitorizare prev─âzut la lit. f):unde:ΓÇôP_Pros - indicator de monitorizare privind ponderea puterii totale aprobate aferent─â prosumatorilor nou-racorda╚¢i, calculat pentru un an t;...ΓÇôPCR_Pros [MW] - puterea total─â aprobat─â pentru evacuare prin certificatele de racordare emise de OD ├«n anul t pentru noi prosumatori;...ΓÇôPSol_Pros [MW] - puterea total─â solicitat─â pentru evacuare prin cererile pentru racordarea noilor prosumatori, ├«nregistrate de OD ├«n anul t ╚Öi care nu au fost clasate de OD cu ├«n╚Ötiin╚¢area utilizatorului, conform prevederilor reglement─ârilor ├«n vigoare..........(4)...Indicatorii de performan╚¢─â I_Bdj din categoria B, aferen╚¢i anului t, se calculeaz─â pe baza indicatorilor de monitorizare prev─âzu╚¢i laalin. (1), cu urm─âtoarea formul─â:unde:ΓÇôI_Bdj - indicator care reflect─â performan╚¢a realizat─â de OD cu privire la integrarea produc╚¢iei distribuite de energie electric─â din surse regenerabile ╚Öi a serviciilor de flexibilitate, ├«n anul t de raportare, pentru indicatorul j, conform nominaliz─ârilor de laalin. (1);...ΓÇôMAX () - func╚¢ie matematic─â prin care se determin─â valoarea maxim─â dintr-un ╚Öir de valori;...ΓÇôV_Bdj - valoarea anual─â a indicatorului de monitorizare j, conform nominaliz─ârilor de laalin. (1);...ΓÇôan_t - anul de raportare;...ΓÇôan_t-4,...., an_t  - perioad─â de cinci ani consecutivi, inclusiv anul t de raportare, pentru care se iau ├«n calcul valorile indicatorului de monitorizare, pentru determinarea indicatorului de performan╚¢─â aferent anului t de raportare;...ΓÇôj - num─ârul de ordine al indicatorului de monitorizare/performan╚¢─â, conform nominaliz─ârilor de laalin. (1).......(5)...Stabilirea valorii indicatorilor de performan╚¢─â I_Bdj aferen╚¢i anului t se face respect├ónd urm─âtoarele condi╚¢ii:a)dac─â valorile unui indicator de monitorizare V_Bdj sunt egale cu zero ├«n fiecare din cei cinci ani consecutivi, inclusiv ├«n anul t de raportare (an_t-4 ΓÇô an_t), valoarea indicatorului de performan╚¢─â I_Bdj aferent nu se ia ├«n considerare ├«n calculul indicatorului de performan╚¢─â compus;...b)dac─â num─ârul de cereri de racordare prev─âzut laalin. (3), respectiv NSol_i sau NSol_Pros, este egal cu zero ├«n anul t de raportare, valoarea V_Bdj a indicatorului de monitorizare aferent─â anului t este egal─â cu zero, iar valoarea indicatorului de performan╚¢─â I_Bdj aferent nu se ia ├«n considerare ├«n calculul indicatorului de performan╚¢─â compus;...c)dac─â valorile unui indicator de monitorizare V_Bdj aferente perioadei de cinci ani consecutivi, inclusiv anul t de raportare (an_t-4  ΓÇô an_t) conduc la rezultatul MAX(V_Bdj ant-4 ), ΓÇª, V_Bdj ant <= 50%, iar valoarea indicatorului de performan╚¢─â aferent calculat ├«ndepline╚Öte condi╚¢ia I_Bdj > 70%, valoarea I_Bdj care se ia ├«n considerare ├«n calculul indicatorului de performan╚¢─â compus se limiteaz─â la valoarea de 70%....', NULL, 12, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (281, 17, '13', 'Articolul 13', NULL, NULL, NULL, 'Indicatori de performan╚¢─â pentru serviciul de distribu╚¢ie a energiei electrice', NULL, NULL, NULL, NULL, '(1)...Indicatorii de monitorizare din categoria C, care reflect─â nivelul de digitalizare a re╚¢elelor electrice de distribu╚¢ie, sunt:a)GrSMI - gradul de implementare a SMI ├«n zona de concesiune (%);...b)ST - ponderea num─ârului de sta╚¢ii de transformare sau de conexiune automatizate, aflate ├«n gestiunea OD, care sunt monitorizate de la distan╚¢─â ╚Öi care permit ac╚¢ionarea de la distan╚¢─â a unor echipamente primare, raportat la num─ârul total al sta╚¢iilor de transformare sau de conexiune din zona de concesiune a OD (%);...c)PA - ponderea num─ârului de puncte de alimentare automatizate, aflate ├«n gestiunea OD, care sunt monitorizate de la distan╚¢─â ╚Öi care permit ac╚¢ionarea de la distan╚¢─â a unor echipamente primare, raportat la num─ârul total al punctelor de alimentare din zona de concesiune a OD (%);...d)PT - ponderea num─ârului de posturi de transformare, aflate ├«n gestiunea OD, care sunt monitorizate de la distan╚¢─â ╚Öi care permit ac╚¢ionarea de la distan╚¢─â a unor echipamente primare, raportat la num─ârul total al posturilor de transformare din zona de concesiune a OD (%);...e)TrafoIT/MT - ponderea num─ârului de transformatoare IT/MT automatizate ╚Öi care permit ac╚¢ionarea de la distan╚¢─â,  raportat la num─ârul total al transformatoarelor IT/MT din zona de concesiune a OD (%);...f)TrafoMT/JT - ponderea num─ârului de transformatoare MT/JT automatizate ╚Öi care permit ac╚¢ionarea de la distan╚¢─â, raportat la num─ârul total al transformatoarelor MT/JT din zona de concesiune a OD (%).......(2)...Valoarea indicatorilor de monitorizare prev─âzu╚¢i laalin. (1)se determin─â anual de OD pe baza num─ârului de echipamente existente ├«n func╚¢iune la 31 decembrie, conform datelor ├«nregistrate ├«n contabilitate privind punerea ├«n func╚¢iune a echipamentelor....(3)...Indicatorii de performan╚¢─â I_Cdk din categoria C, aferen╚¢i anului t, se calculeaz─â pe baza indicatorilor de monitorizare prev─âzu╚¢i laalin. (1) lit. b)-f)cu urm─âtoarea formul─â:unde:ΓÇôI_Cdk - indicator care reflect─â performan╚¢a realizat─â de OD cu privire la digitalizarea re╚¢elelor electrice de distribu╚¢ie, ├«n anul t, pentru indicatorul k, conform nominaliz─ârilor de laalin. (1);...ΓÇôV_Cdk - valoarea anual─â a indicatorului de monitorizare k, conform nominaliz─ârilor de laalin. (1), calculat─â pentru anul t;...ΓÇôV_╚¢int─âk - valoarea-╚¢int─â a indicatorului de monitorizare k, conform nominaliz─ârilor de laalin. (1), stabilit─â conform prevederilorart. 8;...ΓÇôk - num─ârul de ordine al indicatorului de monitorizare/performan╚¢─â, conform nominaliz─ârilor de laalin. (1); k = 2 - 6.......(4)...Indicatorul de performan╚¢─â din categoria C de laalin. (1) lit. a), aferent anului t, se calculeaz─â cu urm─âtoarea formul─â:unde:ΓÇôGr_SMI - gradul de integrare ├«n sistemele de m─âsurare inteligent─â a energiei electrice (SMI) a locurilor de consum/locurilor de producere ╚Öi de consum racordate ├«n re╚¢elele electrice de distribu╚¢ie din zona de concesiune, ├«n anul t;...ΓÇôNr_LC/LPCSMI - num─ârul de locuri de consum/de producere ╚Öi de consum din zona de concesiune care au fost integrate ├«n SMI p├ón─â la 31 decembrie ├«n anul t;...ΓÇôNrt_LC/LPC - num─ârul total de locuri de consum/de producere ╚Öi consum din zona de concesiune, de pe nivelurile de tensiune pe care sunt locuri de consum/de producere ╚Öi consum integrate ├«n SMI, ├«nregistrat ├«n anul t;...', NULL, 13, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (282, 17, '14', 'Articolul 14', NULL, NULL, NULL, 'Indicatori de performan╚¢─â pentru serviciul de distribu╚¢ie a energiei electrice', NULL, NULL, NULL, NULL, '(1)...Valoarea indicatorului de performan╚¢─â compus care reflect─â performan╚¢a global─â a unui OD, ├«n leg─âtur─â cu dezvoltarea unei re╚¢ele electrice inteligente care s─â promoveze eficien╚¢a energetic─â ╚Öi integrarea energiei electrice produse din surse regenerabile, se calculeaz─â cu urm─âtoarea formul─â:unde:ΓÇôI_Adi - indicator de performan╚¢─â din categoria A, determinat conform prevederilorart. 11 alin. (3)╚Öi(4);...ΓÇôI_Bdj - indicator de performan╚¢─â din categoria B, determinat conform prevederilorart. 12 alin. (4);...ΓÇôI_Cdk - indicator de performan╚¢─â din categoria C, determinat conform prevederilorart. 13 alin. (3);...ΓÇôGr_SMI - indicatorul grad de implementare a SMI, determinat conform prevederilorart. 13 alin. (4);...ΓÇôK_CSdi - coeficient de ponderare a fiec─ârui indicator de performan╚¢─â i din categoria A, aferent serviciului de distribu╚¢ie a energiei electrice;...ΓÇôK_Pdj - coeficient de ponderare a fiec─ârui indicator de performan╚¢─â j din categoria B, aferent serviciului de distribu╚¢ie a energiei electrice;...ΓÇôcoefpAd - coeficientul de ponderare aferent categoriei A de indicatori de performan╚¢─â, aferen╚¢i serviciului de distribu╚¢ie a energiei electrice;...ΓÇôcoefpBd - coeficientul de ponderare aferent categoriei B de indicatori de performan╚¢─â, aferen╚¢i serviciului de distribu╚¢ie a energiei electrice;...ΓÇôcoefpCd - coeficientul de ponderare aferent categoriei C de indicatori de performan╚¢─â, aferen╚¢i serviciului de distribu╚¢ie a energiei electrice;...ΓÇôn, m - num─ârul de indicatori de performan╚¢─â monitoriza╚¢i din categoriile A, respectiv B, aferen╚¢i OD;...ΓÇôp - num─ârul de indicatori de performan╚¢─â monitoriza╚¢i din categoria C, aferent OD.......(2)...Valoarea coeficien╚¢ilor de ponderare coefpAd, coefpBd ╚Öi coefpCd prev─âzu╚¢i laalin. (1)au urm─âtoarele valori: coefpAd = 50%, coefpBd = 50%; coefpCd = 0....(3)...Valorile coeficien╚¢ilor de ponderare K_CSdi ╚Öi K_Pdj prev─âzu╚¢i laalin. (1)se reg─âsesc ├«n tabelul dinanexa nr. 1.1....(4)...├Än cazul ├«n care din aplicarea condi╚¢iilor prev─âzute laart. 12 alin. (5)rezult─â c─â valorile unui/unor indicator(i) de performan╚¢─â I_Bdj nu sunt luate ├«n considerare pentru determinarea indicatorului de performan╚¢─â compus prev─âzut laalin. (1), coeficien╚¢ii de ponderare K_Pdj se recalculeaz─â astfel ├«nc├ót suma coeficien╚¢ilor de ponderare K_Pdj care intr─â ├«n calculul indicatorului de performan╚¢─â compus s─â fie egal cu 100%. Recalcularea coeficien╚¢ilor de ponderare K_Pdj se face prin majorarea celor care se men╚¢in ├«n calcul propor╚¢ional cu valoarea coeficientului de ponderare respectiv raportat─â la valoarea total─â a coeficien╚¢ilor de ponderare care se men╚¢in ├«n calcul....(5)...Valorile coeficien╚¢ilor de ponderare prev─âzute laalin. (2)╚Öi(3)pot fi modificate de ANRE ├«ncep├ónd cu al treilea an al perioadei a V-a de reglementare.', NULL, 14, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (283, 17, '15', 'Articolul 15', NULL, NULL, NULL, 'Indicatori de performan╚¢─â pentru serviciul de transport  al energiei electrice', NULL, NULL, NULL, NULL, '(1)...Indicatorii de monitorizare din categoria A care reflect─â nivelul de calitate a serviciului de transport al energiei electrice sunt:a)INDLIN - indisponibilitatea medie a LEA, determinat─â de evenimentele neplanificate (ore/an);...b)INDTRA - indisponibilitatea medie a transformatoarelor ╚Öi autotransformatoarelor, determinat─â de evenimentele neplanificate (ore/an);...c)ENS - energia nelivrat─â utilizatorilor sau neprodus─â ├«n centrale din cauza ├«ntreruperilor de lung─â durat─â neplanificate, ap─ârute din cauze interne ale OTS (MWh);...d)N_U - num─ârul sta╚¢iilor electrice ├«n care tensiunea de linie nu s-a ├«ncadrat ├«n limitele normate de varia╚¢ie, raportat la num─ârul total al sta╚¢iilor electrice monitorizate (%);...e)N_Q - num─ârul sta╚¢iilor electrice ├«n care cel pu╚¢in unul dintre parametrii norma╚¢i de calitate a curbei de tensiune a fost dep─â╚Öit pentru cel pu╚¢in o s─âpt─âm├ón─â, raportat la num─ârul total al sta╚¢iilor electrice monitorizate (%).......(2)...Valoarea indicatorilor de monitorizare prev─âzu╚¢i laalin. (1)se determin─â anual de OTS conform prevederilor Standardului de performan╚¢─â pentru serviciul de transport al energiei electrice ╚Öi pentru serviciul de sistem, aprobat prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 12/2016, cu modific─ârile ╚Öi complet─ârile ulterioare....(3)...Indicatorii de performan╚¢─â I_Ati din categoria A, aferen╚¢i anului t, se calculeaz─â pe baza indicatorilor de monitorizare prev─âzu╚¢i laalin. (1), cu urm─âtoarea formul─â:unde:ΓÇôI_Ati - indicator care reflect─â performan╚¢a realizat─â de OTS referitoare la calitatea serviciului ├«n anul t de raportare, pentru indicatorul i, conform nominaliz─ârilor de laalin. (1);...ΓÇôMIN() - func╚¢ie matematic─â prin care se determin─â valoarea minim─â dintr-un ╚Öir de valori;...ΓÇôV_Ati - valoarea anual─â a indicatorului de monitorizare i, conform nominaliz─ârilor de laalin. (1);...ΓÇôan_t - anul de raportare;...ΓÇôan_t-4,...., an_t - perioad─â de cinci ani consecutivi, inclusiv anul t de raportare, pentru care se iau ├«n calcul valorile indicatorilor monitoriza╚¢i, pentru determinarea indicatorului de performan╚¢─â aferent anului t de raportare;...ΓÇôi - num─ârul de ordine al indicatorului de performan╚¢─â, conform nominaliz─ârilor de laalin. (1)....', NULL, 15, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (284, 17, '16', 'Articolul 16', NULL, NULL, NULL, 'Indicatori de performan╚¢─â pentru serviciul de transport  al energiei electrice', NULL, NULL, NULL, NULL, '(1)...Indicatorii de monitorizare din categoria B care reflect─â integrarea produc╚¢iei de energie electric─â din surse regenerabile, a unit─â╚¢ilor de stocare de energie ╚Öi a serviciilor de flexibilitate sunt:a)NPd - ponderea num─ârului de ATR emise de OTS pentru racordarea de noi locuri de producere cu unit─â╚¢i de producere energie electric─â din surse regenerabile, raportat la num─ârul cererilor de racordare de acest tip ├«nregistrate de OTS (%);...b)PPd - ponderea puterii totale aprobate pentru evacuare prin ATR emise de OTS pentru racordarea de noi locuri de producere cu unit─â╚¢i de producere energie electric─â din surse regenerabile, raportat─â la puterea total─â solicitat─â prin cererile de racordare de acest tip ├«nregistrate de OTS (%);...c)NStoc - ponderea num─ârului de ATR emise de OTS pentru racordarea de noi unit─â╚¢i de stocare de energie, raportat la num─ârul cererilor de racordare de acest tip ├«nregistrate de OTS (%);...d)PStoc - ponderea puterii totale aprobate pentru evacuare prin ATR emise de OTS pentru racordarea de noi unit─â╚¢i de stocare de energie, raportat─â la puterea total─â solicitat─â prin cererile de racordare de acest tip ├«nregistrate de OTS (%).......(2)...Indicatorii de monitorizare prev─âzu╚¢i laalin. (1) lit. a)-d)se determin─â anual de OTS, utiliz├ónd urm─âtoarele formule:a)pentru indicatorii de monitorizare prev─âzu╚¢i lalit. a) ╚Öi c):unde:ΓÇôN_i - indicator de monitorizare referitor la ponderea num─ârului de ATR emise pentru noi locuri de producere sau unit─â╚¢i de stocare, dup─â caz, calculat pentru un an t;...ΓÇôNATR_i - num─ârul de ATR emise de OTS ├«n termenul reglementat ca urmare a cererilor de racordare ├«nregistrate ├«n anul t pentru racordarea de noi locuri de producere sau unit─â╚¢i de stocare, dup─â caz, care ├«ndeplinesc condi╚¢ia ca termenul pentru racordare din ATR s─â coincid─â cu termenul de PIF solicitat de utilizator prin cererea de racordare;...ΓÇôNsol_i - num─ârul de cereri pentru racordarea noilor locuri de producere sau unit─â╚¢i de stocare, dup─â caz, ├«nregistrate de OTS ├«n anul t ╚Öi care, p├ón─â la data emiterii ATR, nu au fost clasate de OTS cu ├«n╚Ötiin╚¢area utilizatorului conform prevederilor Regulamentului privind racordarea utilizatorilor la re╚¢elele electrice de interes public, aprobate prin Ordinul pre╚Öedintelui ANRE nr. 59/2013, cu modific─ârile ╚Öi complet─ârile ulterioare;...ΓÇôi - num─ârul de ordine al indicatorului monitorizat; i = 1 sau 3 conform indicatorilor nominaliza╚¢i laalin. (1),......b)pentru indicatorii de monitorizare prev─âzu╚¢i lalit. b) ╚Öi d):unde:ΓÇôP_i - indicator de monitorizare privind ponderea puterii totale aprobate prin ATR emise de OTS pentru noi locuri de producere sau unit─â╚¢i de stocare, dup─â caz, ├«n anul t, raportat─â la puterea total─â solicitat─â prin cererile de racordare de acest tip ├«nregistrate de OTS ├«n anul t;...ΓÇôPATR_i [MW] - puterea total─â aprobat─â pentru evacuare prin ATR emise de OTS ├«n termenul reglementat ca urmare a cererilor de racordare ├«nregistrate ├«n anul t pentru racordarea de noi locuri de producere sau unit─â╚¢i de stocare, dup─â caz, care ├«ndeplinesc condi╚¢ia ca termenul pentru racordare din ATR s─â coincid─â cu termenul de PIF solicitat de utilizator prin cererea de racordare;...ΓÇôPSol_i [MW] - puterea total─â solicitat─â pentru evacuare prin cererile pentru racordarea noilor locuri de producere sau unit─â╚¢i de stocare, dup─â caz, ├«nregistrate de OTS ├«n anul t ╚Öi care, p├ón─â la data emiterii ATR, nu au fost clasate de OTS cu ├«n╚Ötiin╚¢area utilizatorului conform prevederilor Regulamentului privind racordarea utilizatorilor la re╚¢elele electrice de interes public, aprobat prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 59/2013, cu modific─ârile ╚Öi complet─ârile ulterioare;...ΓÇôi - num─ârul de ordine al indicatorului monitorizat, i = 2 sau 4 conform indicatorilor nominaliza╚¢i laalin. (1)..........(3)...Indicatorii de performan╚¢─â I_Btj din categoria B, aferen╚¢i anului t, se calculeaz─â pe baza indicatorilor de monitorizare prev─âzu╚¢i laalin. (1), cu urm─âtoarea formul─â:unde:ΓÇôI_Btj - indicator din categoria B, conform nominaliz─ârii de laalin. (1), care reflect─â performan╚¢a ├«nregistrat─â de OTS ├«n anul t de raportare;...ΓÇôMAX() - func╚¢ie matematic─â prin care se determin─â valoarea maxim─â dintr-un ╚Öir de valori;...ΓÇôV_Btj - valoarea anual─â a indicatorilor de monitorizare din categoria B, conform nominaliz─ârii de laalin. (1);...ΓÇôan_t - anul de raportare;...ΓÇôan_t-4, ...., an_t - perioad─â de cinci ani consecutivi, inclusiv anul t de raportare, pentru care se iau ├«n calcul valorile indicatorilor de monitorizare, pentru determinarea indicatorului de performan╚¢─â aferent anului t de raportare;...ΓÇôj - num─ârul de ordine al indicatorului de performan╚¢─â monitorizat, conform nominaliz─ârii de laalin. (1).......(4)...Stabilirea valorii indicatorilor de performan╚¢─â I_Btj aferente anului t care este luat─â ├«n considerare pentru calculul indicatorului de performan╚¢─â compus se face respect├ónd urm─âtoarele condi╚¢ii:a)dac─â valorile unui indicator de monitorizare V_Btj sunt egale cu zero ├«n fiecare din cei cinci ani consecutivi, inclusiv ├«n anul t de raportare (an_t-4 ΓÇô an_t), valoarea indicatorului de performan╚¢─â I_Btj aferent nu se ia ├«n considerare ├«n calculul indicatorului de performan╚¢─â compus;...b)dac─â num─ârul de cereri de racordare prev─âzut laalin. (2), respectiv NSol_i este egal cu zero ├«n anul t de raportare, valoarea V_Btj a indicatorului de monitorizare aferent─â anului t este egal─â cu zero, iar valoarea indicatorului de performan╚¢─â I_Btj aferent nu se ia ├«n considerare ├«n calculul indicatorului de performan╚¢─â compus;...c)dac─â valorile unui indicator de monitorizare V_Btj aferente perioadei de cinci ani consecutivi, inclusiv anul t de raportare (an_t-4 ΓÇô an_t) conduc la rezultatul MAX(V_Btjant ΓÇô 4, ΓÇª, V_Btjant < = 50%, iar valoarea indicatorului de performan╚¢─â aferent calculat ├«ndepline╚Öte condi╚¢ia I_Btj>70%, valoarea I_Btj care se ia ├«n considerare ├«n calculul indicatorului de performan╚¢─â compus se limiteaz─â la valoarea de 70%....', NULL, 16, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (336, 19, '9', 'Articolul 9', NULL, NULL, NULL, NULL, NULL, 'Calculul pre╚¢ului lunar de v├ónzare a energiei electrice  ╚Öi al contribu╚¢iei la Fondul de Tranzi╚¢ie Energetic─â', NULL, NULL, 'Produc─âtorii de energie electric─â/Entit─â╚¢ile agregate pot depune declara╚¢ii rectificative, f─âr─â calcul suplimentar de accesorii fiscale, la 15 zile dup─â publicarea de c─âtre OPCOM a pre╚¢urilor finale aferente pie╚¢ei de echilibrare pentru luna de decontare.', NULL, 37, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (285, 17, '17', 'Articolul 17', NULL, NULL, NULL, 'Indicatori de performan╚¢─â pentru serviciul de transport  al energiei electrice', NULL, NULL, NULL, NULL, '(1)...Indicatorii de monitorizare din categoria C, care reflect─â nivelul de digitalizare a re╚¢elelor electrice de transport, sunt:a)ST - ponderea num─ârului de sta╚¢ii de transformare sau de conexiune automatizate care permit ac╚¢ionarea de la distan╚¢─â, care func╚¢ioneaz─â f─âr─â personal de deservire ├«n sta╚¢ie ╚Öi sunt monitorizate prin intermediul CTSI, aflate ├«n gestiunea OTS, raportat la num─ârul total al sta╚¢iilor de transformare sau de conexiune din gestiunea OTS (%);...b)Lin - ponderea num─ârului de linii electrice de ├«nalt─â tensiune prev─âzute cu sisteme de monitorizare ├«n timp real a parametrilor, aflate ├«n gestiunea OTS, raportat la num─ârul total al liniilor electrice din gestiunea OTS (%);...c)Trafo - ponderea num─ârului de transformatoare prev─âzute cu sisteme de monitorizare ├«n timp real a parametrilor, aflate ├«n gestiunea OTS, raportat la num─ârul total al transformatoarelor aflate ├«n func╚¢iune din gestiunea OTS (%).......(2)...Valoarea indicatorilor de monitorizare prev─âzu╚¢i laalin. (1)se determin─â anual de OTS pe baza num─ârului de echipamente existente ├«n func╚¢iune la 31 decembrie, conform datelor ├«nregistrate ├«n contabilitate privind punerea ├«n func╚¢iune a echipamentelor....(3)...Indicatorii de performan╚¢─â I_Ctk din categoria C, aferen╚¢i anului t, conform nominaliz─ârii de laalin. (1), se calculeaz─â cu urm─âtoarea formul─â:unde:ΓÇôI_Ctk - indicator care reflect─â performan╚¢a ├«nregistrat─â de OTS cu privire la digitalizarea re╚¢elei electrice de transport, ├«n anul t, pentru indicatorul k, conform nominaliz─ârilor de laalin. (1);...ΓÇôV_Ctk - valoarea anual─â a indicatorilor monitoriza╚¢i care reflect─â digitalizarea re╚¢elei electrice de transport, nominaliza╚¢i laalin. (1);...ΓÇôV╚¢int─â_k - valoarea-╚¢int─â a indicatorului de monitorizare k, conform nominaliz─ârilor de laalin. (1), stabilit─â conform prevederilorart. 9;...ΓÇôant - anul de raportare;...ΓÇôk - num─ârul de ordine al indicatorului de performan╚¢─â, conform nominaliz─ârilor de laalin. (1)....', NULL, 17, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (286, 17, '18', 'Articolul 18', NULL, NULL, NULL, 'Indicatori de performan╚¢─â pentru serviciul de transport  al energiei electrice', NULL, NULL, NULL, NULL, '(1)...Valoarea indicatorului de performan╚¢─â compus care reflect─â performan╚¢a global─â a OTS se calculeaz─â cu urm─âtoarea formul─â:unde:ΓÇôI_Ati - indicator de performan╚¢─â din categoria A, determinat conform prevederilorart. 15 alin. (3);...ΓÇôI_Btj - indicator de performan╚¢─â din categoria B, determinat conform prevederilorart. 16 alin. (3);...ΓÇôI_Ctk - indicator de performan╚¢─â din categoria C, determinat conform prevederilorart. 17 alin. (3);...ΓÇôK_CSti - coeficient de ponderare a fiec─ârui indicator de performan╚¢─â i din categoria A;...ΓÇôK_Ptj - coeficient de ponderare a fiec─ârui indicator de performan╚¢─â j din categoria B;...ΓÇôcoefpAt - coeficientul de ponderare aferent categoriei A de indicatori de performan╚¢─â;...ΓÇôcoefpBt - coeficientul de ponderare aferent categoriei B de indicatori de performan╚¢─â;...ΓÇôcoefpCt - coeficientul de ponderare aferent categoriei C de indicatori de performan╚¢─â;...ΓÇôn, m - num─ârul de indicatori de performan╚¢─â monitoriza╚¢i din categoriile A, respectiv B, aferent OTS;...ΓÇôp - num─ârul de indicatori de performan╚¢─â monitoriza╚¢i din categoria C, aferent OTS.......(2)...Valoarea coeficien╚¢ilor de ponderare coefpAt, coefpBt ╚Öi coefpCt prev─âzu╚¢i laalin. (1)au urm─âtoarele valori: coefpAt = 50%, coefpBt = 50%; coefpCt = 0....(3)...Valorile coeficien╚¢ilor de ponderare KCsti ╚Öi KPtj prev─âzu╚¢i laalin. (1)se reg─âsesc ├«n tabelul din anexa nr. 1.2....(4)...├Än cazul ├«n care, din aplicarea condi╚¢iilor prev─âzute laart. 16 alin. (4), rezult─â c─â valorile unui/unor indicator(i) de performan╚¢─â I_Btj nu sunt luate ├«n considerare pentru determinarea indicatorului de performan╚¢─â compus prev─âzut laalin. (1), coeficien╚¢ii de ponderare K_Ptj se recalculeaz─â astfel ├«nc├ót suma coeficien╚¢ilor de ponderare K_Ptj care intr─â ├«n calculul indicatorului de performan╚¢─â compus s─â fie egal─â cu 100%. Recalcularea coeficien╚¢ilor de ponderare K_Ptj se face prin majorarea celor care se men╚¢in ├«n calcul propor╚¢ional cu valoarea coeficientului de ponderare respectiv raportat─â la valoarea total─â a coeficien╚¢ilor de ponderare care se men╚¢in ├«n calcul....(5)...Valorile coeficien╚¢ilor de ponderare prev─âzute laalin. (2)╚Öi(3)pot fi modificate de ANRE ├«ncep├ónd cu al treilea an al perioadei a V-a de reglementare.', NULL, 18, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (287, 17, '19', 'Articolul 19', NULL, NULL, NULL, 'Dispozi╚¢ii finale', NULL, NULL, NULL, NULL, '(1)...Anual, ├«ncep├ónd cu al treilea an al perioadei a V-a de reglementare, OR transmit la ANRE, p├ón─â la data de 1 august, datele privind valorile realizate ale indicatorilor de monitorizare ╚Öi de performan╚¢─â aferente anului precedent, ├«n formatul prev─âzut ├«n tabelele dinanexa nr. 2.1, respectiv dinanexa nr. 2.2, ├«nso╚¢ite de datele primare care au stat la baza determin─ârii indicatorilor, ├«n formatul prev─âzut ├«nanexa nr. 3....(2)...Prin derogare de la prevederilealin. (1), ├«n al treilea an al perioadei a V-a de reglementare, OR transmit datele privind valorile realizate ale indicatorilor de monitorizare ╚Öi de performan╚¢─â aferente primilor doi ani ai perioadei, ├«n formatul prev─âzut ├«n tabelele dinanexa nr. 2.1, respectiv dinanexa nr. 2.2, ├«nso╚¢ite de datele primare care au stat la baza determin─ârii indicatorilor, ├«n formatul prev─âzut ├«nanexa nr. 3....(3)...Prin derogare de la prevederilealin. (1), ├«n al doilea an al perioadei a V-a de reglementare, OR transmit datele primare care stau la baza determin─ârii indicatorilor pentru primul an al perioadei, ├«n formatul prev─âzut ├«nanexa nr. 3....(4)...Determinarea indicatorilor de performan╚¢─â prev─âzu╚¢i ├«n anexa nr. 2.1, respectiv ├«nanexa nr. 2.2se realizeaz─â de c─âtre OR pe baza unor proceduri interne, elaborate ╚Öi aprobate conform regulilor interne, prin care se asigur─â: modul de identificare, colectare, ├«nregistrare ╚Öi p─âstrare a datelor necesare calculului indicatorilor de monitorizare ╚Öi de performan╚¢─â, documentarea modului de calcul al valorii anuale a indicatorilor de monitorizare ╚Öi de performan╚¢─â, nominalizarea responsabilit─â╚¢ilor pentru fiecare etap─â a procesului....(5)...OR p─âstreaz─â valorile istorice ale indicatorilor de monitorizare ╚Öi de performan╚¢─â determina╚¢i ╚Öi raporta╚¢i, ├«nso╚¢ite de datele utilizate pentru determinarea acestora, pentru o perioad─â cel pu╚¢in cinci ani ╚Öi le pun la dispozi╚¢ia ANRE, la cerere.', NULL, 19, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (288, 17, '20', 'Articolul 20', NULL, NULL, NULL, 'Dispozi╚¢ii finale', NULL, NULL, NULL, NULL, 'Anexele nr. 1.1-3fac parte integrant─â din prezenta metodologie.', NULL, 20, '2025-11-07 23:44:17.105351', '2025-11-07 23:44:17.105351', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (289, 18, '1', 'Articolul 1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Se aprob─â Regulamentul de furnizare a energiei electrice la clien╚¢ii finali, prev─âzut ├«nanexacare face parte integrant─â din prezentul ordin.', NULL, 1, '2025-11-07 23:44:17.187201', '2025-11-07 23:44:17.187201', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (290, 18, '2', 'Articolul 2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Contractul-cadru pentru prestarea serviciului de distribu╚¢ie a energiei electrice ├«ncheiat ├«ntre operatorul de distribu╚¢ie concesionar ╚Öi furnizor, aprobat prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 90/2015, publicat ├«n Monitorul Oficial al Rom├óniei, Partea I, nr. 462 din 26 iunie 2015, cu modific─ârile ╚Öi complet─ârile ulterioare, se modific─â ╚Öi se completeaz─â dup─â cum urmeaz─â:1.La articolul 4 alineatul (4), dup─â litera b) se introduce o nou─â liter─â, litera c), cu urm─âtorul cuprins:c)istoricul de consum pe o perioad─â de 3 ani care cuprinde indec╚Öii grupului de m─âsurare, inclusiv modul de determinare a acestora: autocitire, estimare, citire de c─âtre operatorul de distribu╚¢ie.......2.La articolul 6, dup─â alineatul (2) se introduce un nou alineat, alineatul (3), cu urm─âtorul cuprins:(3)Pentru punctul/punctele de m─âsurare la care locurile de consum nu sunt dotate cu echipamente de m─âsurare cu ├«nregistrare la nivel de interval de decontare, OD determin─â valorile fiec─ârui interval de decontare aferente consumului pe baza unui profil de consum, determinat ├«n conformitate cu prevederile unei proceduri elaborate de OD ╚Öi avizate de ANRE....3.La articolul 13, dup─â litera o) se introduce o nou─â liter─â, litera p), cu urm─âtorul cuprins:p)s─â ├«nregistreze ╚Öi s─â ╚¢in─â eviden╚¢a locurilor de consum unde locuiesc persoane care, din motive de s─ân─âtate, justificate pe baza documentelor eliberate de institu╚¢ii medicale abilitate, necesit─â men╚¢inerea ├«n via╚¢─â prin aparate electrice......4.La articolul 15 alineatul (1), litera f) se modific─â ╚Öi va avea urm─âtorul cuprins:f)s─â informeze OD cu privire la ├«ncadrarea/excluderea unui loc de consum al unui client casnic unde locuiesc persoane din categoria clien╚¢ilor vulnerabili care, din motive de s─ân─âtate, necesit─â men╚¢inerea ├«n via╚¢─â prin aparate electrice, ├«n maximum 2 zile lucr─âtoare de la ├«ncadrare/excludere;......5.La articolul 25, dup─â alineatul (3) se introduce un nou alineat, alineatul (4), cu urm─âtorul cuprins:(4)├Än cazul ├«n care de╚¢in─âtorul unei sta╚¢ii electrice asigur─â servicii de conexiune unui utilizator prin utilizarea sta╚¢iei electrice proprii, contractul de distribu╚¢ie se modific─â dup─â cum urmeaz─â:a)obiectul contractului precizeaz─â explicit asigurarea serviciilor de conexiune;...b)se includ clauze specifice pentru serviciul de conexiune negociate ├«ntre p─âr╚¢i ╚Öi se elimin─â clauzele care nu sunt aplicabile, cu respectarea prevederilor legale ├«n vigoare.......', NULL, 2, '2025-11-07 23:44:17.187201', '2025-11-07 23:44:17.187201', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (291, 18, '3', 'Articolul 3', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Prevederileart. 2se aplic─â ├«n contractele ├«n vigoare ├«ncheiate ├«n baza Contractului-cadru pentru prestarea serviciului de distribu╚¢ie a energiei electrice ├«ncheiat ├«ntre operatorul de distribu╚¢ie concesionar ╚Öi furnizor, aprobat prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 90/2015, publicat ├«n Monitorul Oficial al Rom├óniei, Partea I, nr. 462 din 26 iunie 2015, cu modific─ârile ╚Öi complet─ârile ulterioare, f─âr─â ├«ndeplinirea altor formalit─â╚¢i, ├«ncep├ónd cu data intr─ârii ├«n vigoare a prezentului ordin, cu excep╚¢ia prevederilor art. 2 pct. 1, care se aplic─â ├«ncep├ónd cu data de 1 aprilie 2023.', NULL, 3, '2025-11-07 23:44:17.187201', '2025-11-07 23:44:17.187201', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (292, 18, '4', 'Articolul 4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Metodologia de stabilire a tarifelor pentru serviciul de distribu╚¢ie a energiei electrice de operatori, al╚¢ii dec├ót operatorii de distribu╚¢ie concesionari, aprobat─â prin Ordinul pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei nr. 102/2016, publicat ├«n Monitorul Oficial al Rom├óniei, Partea I, nr. 1.017 din 19 decembrie 2016, cu modific─ârile ╚Öi complet─ârile ulterioare, se modific─â ╚Öi se completeaz─â dup─â cum urmeaz─â:1.La articolul 6 alineatul (2), litera e) se modific─â ╚Öi va avea urm─âtorul cuprins:e)m─âsoar─â energia electric─â distribuit─â ╚Öi, dup─â caz, furnizat─â utilizatorilor cu respectarea reglement─ârilor ├«n vigoare.......2.La articolul 35, alineatul (1) se modific─â ╚Öi va avea urm─âtorul cuprins:Articolul 35(1)Operatorul de distribu╚¢ie ├«ncheie cu operatorul de re╚¢ea din amonte un contract pentru serviciul de transport sau de distribu╚¢ie, dup─â caz, conform prevederilor Regulamentului de furnizare a energiei electrice la clien╚¢ii finali, aprobat prin ordin al pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei, ├«n vigoare....3.La articolul 35, dup─â alineatul (2) se introduce un nou alineat, alineatul (3), cu urm─âtorul cuprins:(3)Comercializarea energiei electrice de c─âtre un operator de distribu╚¢ie la un loc de consum alimentat direct din re╚¢eaua electric─â a acestuia se realizeaz─â ├«n conformitate cu prevederile Regulamentului de furnizare a energiei electrice la clien╚¢ii finali, aprobat prin ordin al pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Energiei, ├«n vigoare....4.Art. 36, art. 37 alin. (3), art. 38, art. 39 ╚Öi anexa nr. 5 se abrog─â....', NULL, 4, '2025-11-07 23:44:17.187201', '2025-11-07 23:44:17.187201', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (294, 18, '5', 'Articolul 5', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Operatorii de distribu╚¢ie sunt obliga╚¢i s─â asigure accesul nerestric╚¢ionat, gratuit ╚Öi garantat la informa╚¢iile din baza de date referitoare la locurile de consum racordate la re╚¢eaua electric─â de distribu╚¢ie pe care o exploateaz─â urm─âtoarelor categorii de beneficiari:a)clien╚¢ilor finali, pentru locurile proprii de consum;...b)furnizorilor clien╚¢ilor finali, pentru locurile de consum racordate la re╚¢eaua operatorului de distribu╚¢ie pentru care furnizorii au ├«ncheiate contracte de furnizare a energiei electrice ├«n vigoare;...c)altor furnizori, pentru locurile de consum pentru care nu au ├«ncheiate contracte de furnizare a energiei electrice pe baza acceptului clientului final;...d)agregatorilor clien╚¢ilor finali, pentru locurile de consum ale clien╚¢ilor finali agrega╚¢i.......(2)...Informa╚¢iile din baza de date aferente unui loc de consum ╚Öi orice alte informa╚¢ii referitoare la acesta, pe care le con╚¢ine baza de date a operatorului de distribu╚¢ie, conform legisla╚¢iei ├«n vigoare, vor putea fi accesate prin intermediul unei aplica╚¢ii securizate a c─ârei adres─â va fi publicat─â pe pagina de internet a operatorului de distribu╚¢ie, prin introducerea datelor de identificare ale locului de consum....(3)...Accesul la informa╚¢iile din baza de date prin intermediul aplica╚¢iei prev─âzute laalin. (2)se acord─â la solicitarea clientului final, ├«n conformitate cu prevederilealin. (1)....(4)...Accesul la informa╚¢iile din baza de date prin intermediul aplica╚¢iei prev─âzute laalin. (2)se acord─â la solicitarea furnizorului, ├«n conformitate cu prevederilealin. (1) lit. b), ╚Öi se retrage acestuia la data ├«ncet─ârii contractului de furnizare a energiei electrice ├«ncheiat cu clientul final pentru respectivul loc de consum....(5)...Accesul la informa╚¢iile din baza de date pentru un loc de consum, prin intermediul aplica╚¢iei prev─âzute laalin. (2), se acord─â la solicitarea oric─ârui alt furnizor de energie electric─â/agregator, diferit de furnizorul actual, ├«n conformitate cu prevederilealin. (1) lit. c)╚Öid), numai dup─â prezentarea acordului scris ╚Öi explicit al clientului final, cu men╚¢ionarea datelor de identificare ale acestuia, a datelor de identificare ale furnizorului ├«mputernicit/agregatorului ╚Öi ale locului de consum.', NULL, 6, '2025-11-07 23:44:17.187201', '2025-11-07 23:44:17.187201', 'pending', NULL, NULL, NULL, NULL);
