INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (337, 19, '10', 'Articolul 10', NULL, NULL, NULL, NULL, NULL, 'Calculul pre╚¢ului lunar de v├ónzare a energiei electrice  ╚Öi al contribu╚¢iei la Fondul de Tranzi╚¢ie Energetic─â', NULL, NULL, 'Contractele cu instrumente financiare derivate (hedging) prev─âzute la art. 5 se raporteaz─â Autorit─â╚¢ii de Supraveghere Financiar─â ╚Öi/sau Agen╚¢iei Na╚¢ionale de Administrare Fiscal─â, denumit─â ├«n continuare ANAF, ├«n termen de dou─â zile lucr─âtoare de la semnare de c─âtre p─âr╚¢i.', NULL, 38, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (302, 19, '3', 'Articolul 3', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Valoarea componentei de achizi╚¢ie se calculeaz─â de c─âtre fiecare furnizor, pentru fiecare lun─â din perioada de aplicare a prevederilor prezentei ordonan╚¢e de urgen╚¢─â, ca diferen╚¢─â ├«ntre pre╚¢ul final facturat prev─âzut laart. 1 alin. (1)╚Öi(3)╚Öi componentele prev─âzute laart. 2 alin. (1) lit. b)-d), dup─â caz....(2)...Pre╚¢ul mediu de achizi╚¢ie realizat se calculeaz─â de c─âtre fiecare furnizor, pentru fiecare lun─â din perioada de aplicare a prevederilor prezentei ordonan╚¢e de urgen╚¢─â, ╚Öi va include:a)contravaloarea energiei electrice/gazelor naturale achizi╚¢ionate prin toate contractele la termen ╚Öi din pia╚¢a de ziua urm─âtoare/pia╚¢a intrazilnic─â, inclusiv contravaloarea gazelor naturale extrase din depozitele de ├«nmagazinare subteran─â a gazelor naturale - excluz├ónd contravaloarea cantit─â╚¢ilor achizi╚¢ionate ├«n luna de analiz─â ╚Öi destinate ├«nmagazin─ârii ├«n depozitele de ├«nmagazinare subteran─â, precum ╚Öi valoarea de transfer ├«ntre activitatea de produc╚¢ie ╚Öi activitatea de furnizare ├«n cazul produc─âtorilor care furnizeaz─â energie electric─â/gaze naturale clien╚¢ilor finali;...b)contravaloarea dezechilibrului realizat, dar nu mai mult de 10% din contravaloarea energiei electrice/gazelor naturale prev─âzut─â lalit. a).......(3)...Prin excep╚¢ie de la prevederilealin. (2), pentru clien╚¢ii finali prev─âzu╚¢i laart. 1prelua╚¢i ├«n regim de ultim─â instan╚¢─â, furnizorii de ultim─â instan╚¢─â determin─â pre╚¢ul mediu de achizi╚¢ie realizat, pentru fiecare lun─â din perioada de aplicare a prevederilor prezentei ordonan╚¢e de urgen╚¢─â, ╚Öi va include:a)contravaloarea energiei electrice/gazelor naturale achizi╚¢ionate, prin contracte la termen ╚Öi din pia╚¢a de ziua urm─âtoare/pia╚¢a intrazilnic─â, inclusiv contravaloarea gazelor naturale extrase din depozitele de ├«nmagazinare subteran─â a gazelor naturale - excluz├ónd contravaloarea cantit─â╚¢ilor achizi╚¢ionate ├«n luna de analiz─â ╚Öi destinate ├«nmagazin─ârii ├«n depozitele de ├«nmagazinare subteran─â, precum ╚Öi valoarea de transfer ├«ntre activitatea de produc╚¢ie ╚Öi activitatea de furnizare ├«n cazul produc─âtorilor care furnizeaz─â energie electric─â/gaze naturale, pentru asigurarea consumului clien╚¢ilor prelua╚¢i ├«n regim de ultim─â instan╚¢─â;...b)suma aferent─â contravalorii dezechilibrului realizat lunar aferent clien╚¢ilor prelua╚¢i ├«n regim de ultim─â instan╚¢─â....', NULL, 3, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (303, 19, '4', 'Articolul 4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...├Än perioada 1 aprilie 2025-30 iunie 2025, componenta de furnizare pentru activitatea de furnizare a energiei electrice este de maximum 73 lei/MWh....(2)...├Än perioada 1 aprilie 2025-31 martie 2026, componenta de furnizare pentru activitatea de furnizare a gazelor naturale este de maximum 15 lei/MWh....(3)...Prin excep╚¢ie de la prevederilealin. (1), ├«n perioada 1 aprilie 2025-30 iunie 2025, componenta de furnizare din pre╚¢urile aplicate clien╚¢ilor prelua╚¢i ├«n regim de ultim─â instan╚¢─â este de maximum 80 lei/MWh pentru activitatea de furnizare a energiei electrice.', NULL, 4, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (304, 19, '5', 'Articolul 5', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...├Än perioada 1 aprilie 2025-30 iunie 2025, pentru clien╚¢ii finali de energie electric─â prev─âzu╚¢i laart. 1 alin. (1), pre╚¢ul final prev─âzut laart. 2 alin. (2) lit. c)se stabile╚Öte de c─âtre fiecare furnizor ca sum─â a urm─âtoarelor componente, dup─â caz:a)pre╚¢ul de achizi╚¢ie stabilit ├«n condi╚¢iile prev─âzute laart. 6;...b)componenta de furnizare stabilit─â ├«n condi╚¢iile prev─âzute laart. 4;...c)componentele reprezentate de tarifele reglementate/ stabilite conform reglement─ârilor ANRE ├«n vigoare pe perioada de aplicare a prevederilor prezentei ordonan╚¢e de urgen╚¢─â, pentru serviciile prestate de c─âtre operatorii re╚¢elelor de distribu╚¢ie ╚Öi transport al energiei electrice;...d)componentele reprezentate de TVA, accize, contribu╚¢ia aferent─â schemei de sprijin pentru promovarea cogener─ârii de ├«nalt─â eficien╚¢─â pe baza cererii de energie termic─â util─â, contravaloarea certificatelor verzi aferente sistemului de promovare a producerii energiei din surse regenerabile, contribu╚¢ia aferent─â schemei de sprijin de tipul contractelor pentru diferen╚¢─â.......(2)...├Än perioada 1 aprilie 2025-31 martie 2026, pentru clien╚¢ii finali de gaze naturale prev─âzu╚¢i laart. 1 alin. (3), pre╚¢ul final este cel prev─âzut laart. 2 alin. (2) lit. c), iar pentru clien╚¢ii finali de gaze naturale, al╚¢ii dec├ót cei prev─âzu╚¢i laart. 1 alin. (3), pre╚¢ul final se stabile╚Öte de c─âtre fiecare furnizor ca sum─â a urm─âtoarelor componente, dup─â caz:a)pre╚¢ul de achizi╚¢ie stabilit ├«n condi╚¢iile prev─âzute laart. 6;...b)componenta de furnizare stabilit─â ├«n condi╚¢iile prev─âzute laart. 4;...c)componentele reprezentate de tarifele reglementate/ stabilite conform reglement─ârilor ANRE ├«n vigoare pe perioada de aplicare a prevederilor prezentei ordonan╚¢e de urgen╚¢─â, pentru serviciile prestate de c─âtre operatorii sistemelor de distribu╚¢ie ╚Öi transport al gazelor naturale, inclusiv pentru serviciile prestate de c─âtre operatorii depozitelor de ├«nmagazinare subteran─â a gazelor naturale;...d)componentele reprezentate de TVA, accize.......(3)...├Än cazul ├«n care pre╚¢ul din contractul de furnizare ├«n vigoare ├«ncheiat cu clien╚¢ii finali prev─âzu╚¢i laalin. (1)╚Öi(2)este mai mic dec├ót pre╚¢ul final rezultat prin aplicarea prezentei ordonan╚¢e de urgen╚¢─â, se aplic─â pre╚¢ul contractual....(4)...Prin excep╚¢ie de la prevederilealin. (1)╚Öiart. 3, ├«n cazul produc─âtorilor de energie care transfer─â energie electric─â din portofoliul de produc╚¢ie c─âtre portofoliul de furnizare, pentru cantit─â╚¢ile de energie electric─â transferate, pre╚¢ul final este:a)pre╚¢ul contractual ├«n cazul ├«n care acesta asigur─â ├«ncadrarea ├«n plafoanele de laart. 1 alin. (1);...b)pre╚¢ul plafonat prev─âzut laart. 1 alin. (1)├«n cazul ├«n care pre╚¢ul contractual este mai mare dec├ót valoarea plafoanelor.......(5)...├Än cazul produc─âtorilor care transfer─â energie din portofoliul de produc╚¢ie c─âtre portofoliul de furnizare, componenta de achizi╚¢ie prev─âzut─â laalin. (1)╚Öiart. 3din contractele de furnizare pentru care se va achizi╚¢iona energie electric─â din pia╚¢─â va fi determinat─â lu├ónd ├«n calcul exclusiv pre╚¢ul de achizi╚¢ie realizat. Pentru cantitatea de energie achizi╚¢ionat─â pentru acoperirea acestor contracte, produc─âtorii vor ├«ntocmi ╚Öi vor men╚¢ine o eviden╚¢─â separat─â de cantitatea transferat─â din produc╚¢ia proprie.', NULL, 5, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (305, 19, '6', 'Articolul 6', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Pre╚¢ul de achizi╚¢ie prev─âzut laart. 5se calculeaz─â de c─âtre fiecare furnizor pentru fiecare lun─â, denumit─â luna n, din perioada de aplicare a prevederilor prezentei ordonan╚¢e de urgen╚¢─â ╚Öi va include:a)contravaloarea energiei electrice/gazelor naturale achizi╚¢ionate, care cuprinde ╚Öi cantit─â╚¢ile extrase din depozitele de ├«nmagazinare subteran─â, precum ╚Öi cele transferate ├«ntre activitatea de produc╚¢ie ╚Öi activitatea de furnizare ├«n cazul produc─âtorilor care furnizeaz─â energie electric─â/gaze naturale;...b)contravaloarea estimat─â a energiei electrice/gazelor naturale necesar a fi achizi╚¢ionate din pia╚¢a pentru ziua urm─âtoare/pia╚¢a intrazilnic─â;...c)o component─â de ajustare calculat─â pentru luna n ca diferen╚¢─â ├«ntre pre╚¢ul de achizi╚¢ie luat ├«n calcul ├«n pre╚¢ul final facturat ├«n luna n-1 ╚Öi pre╚¢ul de achizi╚¢ie realizat ├«n luna n-1 determinat ├«n conformitate cu prevederileart. 3 alin. (2).......(2)...Prin excep╚¢ie de la prevederilealin. (1), ├«n cazul clien╚¢ilor prev─âzu╚¢i laart. 5, prelua╚¢i ├«n regim de ultim─â instan╚¢─â, pre╚¢ul de achizi╚¢ie din pre╚¢ul final facturat se determin─â similar pre╚¢ului mediu de achizi╚¢ie realizat prev─âzut laart. 3 alin. (3).', NULL, 6, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (306, 19, '7', 'Articolul 7', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Valoarea rezultat─â ca produs ├«ntre cantitatea de gaze naturale facturat─â clien╚¢ilor finali, beneficiari ai schemei de sprijin instituite prin prezenta ordonan╚¢─â de urgen╚¢─â, ├«n fiecare lun─â din perioada de aplicare a prevederilor prezentei ordonan╚¢e de urgen╚¢─â ╚Öi diferen╚¢a pozitiv─â ├«ntre pre╚¢ul mediu de achizi╚¢ie realizat determinat conform prevederilorart. 3 alin. (2)sau, dup─â caz,alin. (3)╚Öi valoarea componentei de achizi╚¢ie determinat─â conform prevederilorart. 3 alin. (1), aferent─â lunii facturate, va fi compensat─â furnizorilor prin bugetul Ministerului Muncii, Familiei, Tineretului ╚Öi Solidarit─â╚¢ii Sociale, pentru clien╚¢ii casnici, ╚Öi prin bugetul Ministerului Energiei, pentru clien╚¢ii noncasnici, ├«n conformitate cu valorile calculate de ANRE, din Fondul de Tranzi╚¢ie Energetic─â....(2)...Documentele justificative ce trebuie prezentate lunar c─âtre ANRE de c─âtre furnizori, separat pentru clien╚¢ii casnici ╚Öi clien╚¢ii noncasnici, ├«n vederea acoperirii diferen╚¢elor de pre╚¢ compensate potrivitalin. (1)╚Öi(3)sunt:a)documentele justificative care atest─â contravaloarea energiei electrice/gazelor naturale achizi╚¢ionate, inclusiv cantit─â╚¢i extrase din depozitele de ├«nmagazinare subteran─â sau transferate din produc╚¢ia proprie c─âtre activitatea de furnizare, cum sunt, dar f─âr─â a se limita la: contracte de achizi╚¢ii, facturi, note interne;...b)documentele justificative care atest─â suma aferent─â contravalorii dezechilibrului realizat lunar, cum sunt, dar f─âr─â a se limita la: note de decontare, facturi;...c)cantitatea de energie electric─â/gaze naturale livrat─â lunar ├«n vederea acoperirii consumului clien╚¢ilor finali prev─âzu╚¢i laart. 1 alin. (1)╚Öi(3)pe categorii de clien╚¢i ╚Öi tran╚Öe de consum;...d)orice alte documente sau informa╚¢ii necesare solicitate de c─âtre ANRE, ├«n vederea verific─ârii ╚Öi determin─ârii valorilor prev─âzute laalin. (1)╚Öi(3).......(3)...Valoarea rezultat─â ca produs ├«ntre cantitatea de energie electric─â facturat─â clien╚¢ilor finali, beneficiari ai schemei de sprijin instituite prin prezenta ordonan╚¢─â de urgen╚¢─â, ├«n fiecare lun─â din perioada de aplicare a prevederilor prezentei ordonan╚¢e de urgen╚¢─â ╚Öi diferen╚¢a pozitiv─â ├«ntre pre╚¢ul mediu de achizi╚¢ie realizat determinat conform prevederilorart. 3 alin. (2)a c─ârui valoare este mai mic─â sau cel mult egal─â cu valoarea de 700 lei/MWh sau, dup─â caz, determinat conform prevederilorart. 3 alin. (3)╚Öi valoarea componentei de achizi╚¢ie determinat─â conform prevederilorart. 3 alin. (1), aferent─â lunii facturate, va fi compensat─â furnizorilor de la bugetul de stat, prin bugetul Ministerului Muncii, Familiei, Tineretului ╚Öi Solidarit─â╚¢ii Sociale, pentru clien╚¢ii casnici, ╚Öi prin bugetul Ministerului Energiei, pentru clien╚¢ii noncasnici, ├«n conformitate cu valorile calculate de ANRE....(4)...V├ónzarea succesiv─â, respectiv v├ónzarea ├«ntre grupuri de firme afiliate sau intragrup, a unor cantit─â╚¢i de energie electric─â sau gaze naturale de c─âtre traderi ╚Öi/sau furnizori cu activit─â╚¢i de trading sau de c─âtre traderi ╚Öi/sau furnizori cu activit─â╚¢i de trading care au acela╚Öi ac╚¢ionariat sau beneficiar final, ├«n scopul de a cre╚Öte pre╚¢ul, este interzis─â ╚Öi constituie contraven╚¢ie....(5)...Nerespectarea prevederiloralin. (4)se sanc╚¢ioneaz─â de c─âtre ANRE cu amend─â de 5% din cifra de afaceri anual─â a persoanei contraveniente.', NULL, 7, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (307, 19, '8', 'Articolul 8', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Valorile aferente compens─ârilor prev─âzute laart. 7 alin. (1)pentru fiecare furnizor se determin─â de c─âtre ANRE, ├«n termen de 30 de zile de la data primirii cererilor de decontare, al c─âror model este prev─âzut ├«nanexele nr. 1╚Öi2, depuse ╚Öi ├«nregistrate la Agen╚¢ia Na╚¢ional─â pentru Pl─â╚¢i ╚Öi Inspec╚¢ie Social─â, respectiv Ministerul Energiei ╚Öi, ├«n copie, c─âtre ANRE la adresa de e-mail: plafonare@anre.ro, de c─âtre furnizori....(2)...├Än situa╚¢ia ├«n care ANRE constat─â apari╚¢ia unor erori materiale, lipsa unor documente justificative sau erori de calcul ca urmare a ├«nc─ârc─ârii datelor de c─âtre furnizori pe platforma IT, termenul prev─âzut laalin. (1)se prelunge╚Öte cu termenele prev─âzute laart. 9 alin. (9)╚Öi(10).', NULL, 8, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (308, 19, '9', 'Articolul 9', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Furnizorii au obliga╚¢ia de a ╚¢ine eviden╚¢a lunar─â, ├«n mod diferen╚¢iat, a clien╚¢ilor finali prev─âzu╚¢i laart. 1pentru care se aplic─â diferen╚¢ele de pre╚¢ compensate prev─âzute de prezenta ordonan╚¢─â de urgen╚¢─â, precum ╚Öi a clien╚¢ilor finali prev─âzu╚¢i laart. 5....(2)...Clien╚¢ii se identific─â de c─âtre furnizori prin atributele de identificare aferente locului de consum, conform facturilor de consum emise ├«n baza contractelor de furnizare/v├ónzare ├«ncheiate ├«n mod valabil....(3)...La solicitarea ANRE, furnizorii au obliga╚¢ia s─â pun─â la dispozi╚¢ia acesteia eviden╚¢a prev─âzut─â laalin. (1), precum ╚Öi orice alte informa╚¢ii ├«n leg─âtur─â cu documentele prev─âzute laart. 7....(4)...R─âspunderea privind corectitudinea ╚Öi modul de calcul al sumelor solicitate prin cererea de decontare revine exclusiv furnizorilor....(5)...Pe baza facturilor emise pentru fiecare lun─â din perioada de aplicare prev─âzut─â laart. 1 alin. (1)╚Öi(3), furnizorii depun la Agen╚¢ia Na╚¢ional─â pentru Pl─â╚¢i ╚Öi Inspec╚¢ie Social─â la adresa de e-mail: plafonare.casnici@mmanpis.ro, respectiv Ministerul Energiei la adresa de e-mail: plafonare@energie.gov.ro ╚Öi, ├«n copie, la ANRE la adresa de e-mail: plafonare@anre.ro, prin ├«nc─ârcare pe platforma IT la adresa www.spv.anre.ro, cererea de decontare lunar─â a sumelor aferente compensa╚¢iei, ├«n mod diferen╚¢iat pentru clien╚¢ii casnici ╚Öi noncasnici prev─âzu╚¢i la art. 1, separat pentru energie electric─â ╚Öi/sau gaze naturale, conformanexei nr. 1sauanexei nr. 2....(6)...Agen╚¢ia Na╚¢ional─â pentru Pl─â╚¢i ╚Öi Inspec╚¢ie Social─â, respectiv Ministerul Energiei vor transmite ANRE, ├«n termen de 2 zile lucr─âtoare, confirmarea lu─ârii ├«n eviden╚¢─â a cererii de decontare depuse de furnizor....(7)...Odat─â cu depunerea cererii de decontare lunar─â la Agen╚¢ia Na╚¢ional─â pentru Pl─â╚¢i ╚Öi Inspec╚¢ie Social─â, respectiv la Ministerul Energiei ╚Öi ANRE, fiecare furnizor are obliga╚¢ia de a ├«nc─ârca ├«n termen de 5 zile lucr─âtoare documentele justificative prev─âzute laart. 7 alin. (2), ├«nso╚¢ite de declara╚¢ia pe propria r─âspundere, al c─ârei model este prev─âzut ├«nanexa nr. 3, pe platforma IT pus─â la dispozi╚¢ie de c─âtre ANRE....(8)...├Än cazul ├«n care se constat─â existen╚¢a unor erori materiale sau de calcul rezultate din lipsa comunic─ârii integrale a documenta╚¢iei justificative sau din cauza introducerii eronate de date ├«n platforma IT pus─â la dispozi╚¢ie de c─âtre ANRE, ca urmare a verific─ârilor efectuate de aceasta sau ca urmare a constat─ârilor furnizorilor comunicate c─âtre ANRE, autoritatea de reglementare va comunica furnizorului ╚Öi Agen╚¢iei Na╚¢ionale pentru Pl─â╚¢i ╚Öi Inspec╚¢ie Social─â, respectiv Ministerului Energiei aceste erori, precum ╚Öi valorile rectificate ale compensa╚¢iilor, ├«n termen de 5 zile de la data constat─ârii....(9)...├Än termen de 5 zile de la data comunic─ârii prev─âzute laalin. (8), furnizorul urmeaz─â a realiza rectific─ârile aferente ╚Öi va comunica c─âtre ANRE ╚Öi c─âtre Agen╚¢ia Na╚¢ional─â pentru Pl─â╚¢i ╚Öi Inspec╚¢ie Social─â sau Ministerul Energiei, dup─â caz, cererea de decontare ref─âcut─â....(10)...ANRE transmite valorile aferente compens─ârilor, prev─âzute laart. 7 alin (1), conformanexei nr. 4, c─âtre Agen╚¢ia Na╚¢ional─â pentru Pl─â╚¢i ╚Öi Inspec╚¢ie Social─â, respectiv Ministerul Energiei, iar acestea efectueaz─â plata c─âtre furnizori a sumelor reprezent├ónd valoarea compens─ârii consumurilor realizate ├«n perioada 1 aprilie 2025-30 iunie 2025 pentru energie electric─â, respectiv 1 aprilie 2025-31 martie 2026 pentru gaze naturale, dup─â cum urmeaz─â:a)40% din valoarea solicitat─â prin cererea de decontare depus─â conform prevederiloralin. (5), din Fondul de Tranzi╚¢ie Energetic─â ╚Öi din alte surse legal constituite, ├«n termen de 10 zile lucr─âtoare de la transferul fondurilor cu aceast─â destina╚¢ie de c─âtre Ministerul Finan╚¢elor ├«n contul Ministerului Energiei, respectiv Ministerului Muncii, Familiei, Tineretului ╚Öi Solidarit─â╚¢ii Sociale - Agen╚¢ia Na╚¢ional─â pentru Pl─â╚¢i ╚Öi Inspec╚¢ie Social─â;...b)diferen╚¢a de plat─â dintre valoarea determinat─â conform prevederilorart. 8 alin. (1)╚Öi valoarea prev─âzut─â lalit. a), dup─â validarea sumelor de c─âtre ANRE, conformanexei nr. 4, ├«n termen de 10 zile lucr─âtoare de la transferul fondurilor cu aceast─â destina╚¢ie de c─âtre Ministerul Finan╚¢elor ├«n contul Ministerului Energiei, respectiv Ministerului Muncii, Familiei, Tineretului ╚Öi Solidarit─â╚¢ii Sociale - Agen╚¢ia Na╚¢ional─â pentru Pl─â╚¢i ╚Öi Inspec╚¢ie Social─â;...c)dac─â valoarea determinat─â conformart. 8 alin. (1)este mai mic─â dec├ót valoarea decontat─â conformlit. a), furnizorii au obliga╚¢ia return─ârii diferen╚¢ei dintre cele dou─â valori ├«n termen de 10 zile lucr─âtoare de la data comunic─ârii valorii determinate de c─âtre ANRE.......(11)...Prin derogare de la prevederile art. 286 alin. (1) lit. a) din Legea nr. 227/2015 privind Codul fiscal, cu modific─ârile ╚Öi complet─ârile ulterioare, diferen╚¢ele prev─âzute laart. 7 alin. (1)nu sunt subven╚¢ii legate direct de pre╚¢ ╚Öi nu se includ ├«n baza de impozitare a TVA....(12)...Sumele aferente compensa╚¢iilor acordate potrivitart. 7 alin. (1)se asigur─â prin bugetul Ministerului Muncii, Familiei, Tineretului ╚Öi Solidarit─â╚¢ii Sociale ╚Öi bugetul Ministerului Energiei din Fondul de Tranzi╚¢ie Energetic─â....(13)...Toate cererile de decontare depuse de furnizori trebuie s─â se refere la cantit─â╚¢ile de energie ╚Öi/sau gaze naturale v├óndute conform perioadelor contractuale de facturare din facturile emise pentru luna pentru care se solicit─â decontul.', NULL, 9, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (309, 19, '10', 'Articolul 10', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Prin derogare de la prevederile art. 2 pct. 33 din Legea nr. 500/2002 privind finan╚¢ele publice, cu modific─ârile ╚Öi complet─ârile ulterioare, ├«n faza de lichidare a cheltuielilor, Ministerul Energiei, Agen╚¢ia Na╚¢ional─â pentru Pl─â╚¢i ╚Öi Inspec╚¢ie Social─â ╚Öi ANRE efectueaz─â verific─âri cu privire la concordan╚¢a sumelor totale din situa╚¢iile de plat─â, numai pe baza cererilor de decontare ╚Öi a declara╚¢iilor pe propria r─âspundere depuse de furnizori, ale c─âror modele sunt prev─âzute ├«nanexele nr. 1-4. Fazele ulterioare parcurse ale cheltuielilor bugetare se realizeaz─â exclusiv cu privire la sumele aprobate spre decontare, ca urmare a verific─ârilor realizate potrivit prezentei ordonan╚¢e de urgen╚¢─â.', NULL, 10, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (310, 19, '11', 'Articolul 11', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Prin derogare de la prevederile art. 143 alin. (2^1) din Legea nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioare, ├«n perioada 1 aprilie 2025-31 octombrie 2025, furnizorii de gaze naturale/produc─âtorii de energie termic─â ├«n centralele de cogenerare ╚Öi ├«n centralele termice ├«n calitate de clien╚¢i direc╚¢i ai produc─âtorilor de gaze naturale, denumi╚¢i ├«n continuare PET client direct, constituie ├«n depozitele de ├«nmagazinare subteran─â a gazelor naturale un stoc minim de gaze naturale pentru asigurarea continuit─â╚¢ii ╚Öi siguran╚¢ei ├«n alimentarea clien╚¢ilor lor finali/asigurarea consumului propriu ╚Öi suportabilit─â╚¢ii pre╚¢ului de c─âtre ace╚Ötia; stocul minim va reprezenta minimum 90% din capacitatea de ├«nmagazinare a depozitelor subterane la nivel na╚¢ional, conformanexei nr. 5....(2)...Furnizorii de gaze naturale/PET client direct ├«╚Öi constituie stocul minim de gaze naturale prin:a)├«nmagazinarea gazelor naturale ├«n nume propriu, prin ├«ncheierea de contracte de ├«nmagazinare subteran─â a gazelor naturale cu unul dintre titularii licen╚¢ei de operare a sistemelor de ├«nmagazinare subteran─â a gazelor naturale; ╚Öi/sau...b)├«ncheierea, p├ón─â la data de 31 octombrie 2025, de contracte de v├ónzare-cump─ârare ce au ca obiect cantit─â╚¢i de gaze naturale provenite din depozitele de ├«nmagazinare subteran─â a gazelor naturale, ├«nmagazinate de un alt furnizor de gaze naturale;...c)├«ncheierea de contracte de mandat cu un alt furnizor, ├«n vederea ├«nmagazin─ârii gazelor naturale....', NULL, 11, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (311, 19, '12', 'Articolul 12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...├Än perioada 1 aprilie 2025-31 martie 2026, produc─âtorii de gaze naturale care desf─â╚Öoar─â at├ót activit─â╚¢i de extrac╚¢ie onshore ╚Öi/sau offshore, indiferent de data ├«nceperii acestei activit─â╚¢i, c├ót ╚Öi activit─â╚¢i de v├ónzare a gazelor naturale au obliga╚¢ia s─â livreze, cu pre╚¢ul de 120 lei/MWh, cantit─â╚¢ile necesare de gaze naturale rezultate din activitatea de produc╚¢ie intern─â c─âtre furnizori/PET client direct/operatori re╚¢ea, pentru a asigura, ├«n urm─âtoarea ordine de prioritate:a)├«n perioada 1 aprilie 2025-31 octombrie 2025, necesarul de gaze naturale pentru constituirea stocului minim necesar pentru perioada 1 noiembrie 2025-31 martie 2026;...b)├«n perioada 1 aprilie 2025-31 martie 2026, necesarul de gaze pentru consumul curent al clien╚¢ilor casnici, stabilit conformanexei nr. 5;...c)├«n perioada 1 noiembrie 2025-31 martie 2026, necesarul de gaze pentru consumul curent al produc─âtorilor de energie termic─â, doar pentru energia termic─â destinat─â popula╚¢iei, stabilite conformanexei nr. 5;...d)├«n perioada 1 aprilie 2025-31 martie 2026, cantit─â╚¢ile necesare de gaze naturale rezultate din activitatea de produc╚¢ie intern─â c─âtre operatorul de transport ╚Öi sistem ╚Öi c─âtre operatorii concesionari de distribu╚¢ie titulari ai unei licen╚¢e de operare - operatori de re╚¢ea ├«n vederea asigur─ârii a 75% din cantitatea de gaze naturale destinat─â consumului tehnologic (CT), stabilite conformanexei nr. 5.......(2)...Pre╚¢ul gazelor naturale prev─âzut laalin. (1)aferent perioadei 1 aprilie 2025-31 martie 2026 va putea fi modificat prin hot─âr├óre a Guvernului, ini╚¢iat─â ├«n conformitate cu dispozi╚¢iile art. 180 din Legea nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioare, ├«n func╚¢ie de evolu╚¢iile ├«nregistrate pe pie╚¢ele interne ╚Öi interna╚¢ionale de gaze naturale ╚Öi de evolu╚¢ia geopolitic─â din vecin─âtatea Rom├óniei....(3)...La ├«ncheierea contractelor prev─âzute laalin. (1), aferente perioadei 1 aprilie 2025-31 martie 2026, p─âr╚¢ile vor include clauze care s─â permit─â aplicarea prevederiloralin. (2)....(4)...├Än categoria clien╚¢ilor casnici, prev─âzu╚¢i laalin. (1), intr─â ╚Öi clien╚¢ii casnici din condominii, asocia╚¢ii de proprietari, asocia╚¢ii de locatari ╚Öi cartiere de tip reziden╚¢ial....(5)...├Än func╚¢ie de evolu╚¢iile ulterioare de pe pia╚¢a gazelor naturale, ANRE va actualiza modul de calcul al cantit─â╚¢ilor de gaze naturale destinate consumului CC ╚Öi produc─âtor de energie termic─â, denumit ├«n continuare PET, pentru perioada 1 aprilie 2025-31 martie 2026, astfel cum este stabilit ├«nanexa nr. 5....(6)...P├ón─â la data de 31 martie 2026, redeven╚¢a datorat─â de produc─âtorii de gaze naturale pentru cantit─â╚¢ile de gaze naturale v├óndute conformalin. (1)se va stabili ├«n baza pre╚¢ului prev─âzut laalin. (1), prin ordin al pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale de Reglementare ├«n Domeniul Minier, Petrolier ╚Öi al Stoc─ârii Geologice a Dioxidului de Carbon, emis ├«n 10 zile de la data intr─ârii ├«n vigoare a prezentei ordonan╚¢e de urgen╚¢─â.', NULL, 12, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (312, 19, '13', 'Articolul 13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Cantit─â╚¢ile de gaze naturale v├óndute ├«n condi╚¢iileart. 12 alin. (1)sunt exceptate de la aplicarea prevederilor Ordonan╚¢ei Guvernului nr. 7/2013 privind instituirea impozitului asupra veniturilor suplimentare ob╚¢inute ca urmare a dereglement─ârii pre╚¢urilor din sectorul gazelor naturale, aprobat─â cu modific─âri prin Legea nr. 73/2018, cu modific─ârile ╚Öi complet─ârile ulterioare, ╚Öi de la prevederile art. 20 din Legea nr. 256/2018 privind unele m─âsuri necesare pentru implementarea opera╚¢iunilor petroliere de c─âtre titularii de acorduri petroliere referitoare la perimetre petroliere offshore ╚Öi onshore de ad├óncime, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare.', NULL, 13, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (313, 19, '14', 'Articolul 14', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...├Äncep├ónd cu data de 1 aprilie 2025, pe perioada de aplicare a dispozi╚¢iilor prezentei ordonan╚¢e de urgen╚¢─â, produc─âtorii de energie electric─â, entit─â╚¢ile agregate de producere a energiei electrice, traderii, furnizorii care desf─â╚Öoar─â activitatea de trading ╚Öi agregatorii care tranzac╚¢ioneaz─â cantit─â╚¢i de energie electric─â ╚Öi/sau gaze naturale pe pia╚¢a angro, precum ╚Öi partenerii din contractele financiare de asigurare a riscurilor (contracte de hedging) ai produc─âtorilor de energie electric─â pl─âtesc o contribu╚¢ie la Fondul de Tranzi╚¢ie Energetic─â, stabilit─â ├«n conformitate cu prevederile metodologiilor prev─âzute ├«nanexele nr. 6,6.1╚Öi6.2....(2)...Capacit─â╚¢ilor de produc╚¢ie puse ├«n func╚¢iune dup─â data de 1 aprilie 2022, precum ╚Öi companiilor care furnizeaz─â servicii publice de termie care produc energie electric─â prin cogenerare nu le sunt aplicabile prevederilealin. (1)....(3)...Contractele bilaterale ├«ncheiate pe pia╚¢a angro prin negociere direct─â se raporteaz─â la ANRE de c─âtre p─âr╚¢ile contractante ├«n termen de 2 zile lucr─âtoare de la data ├«ncheierii....(4)...Contribu╚¢ia la Fondul de Tranzi╚¢ie Energetic─â se calculeaz─â, se declar─â ╚Öi se pl─âte╚Öte de c─âtre v├ónz─âtor lunar, p├ón─â la data de 25 inclusiv a lunii urm─âtoare celei pentru care se datoreaz─â....(5)...Sumele reprezent├ónd contravaloarea contribu╚¢iei la Fondul de Tranzi╚¢ie Energetic─â se pl─âtesc ├«ntr-un cont distinct de disponibil ├«n lei deschis la unit─â╚¢ile Trezoreriei Statului din cadrul organelor fiscale centrale competente, codificat cu codul de identificare fiscal─â al pl─âtitorului....(6)...├Än primele 3 zile lucr─âtoare ale fiec─ârei luni, sumele ├«ncasate ├«n contul prev─âzut laalin. (5)se vireaz─â de unit─â╚¢ile Trezoreriei Statului ├«ntr-un cont distinct de disponibil ├«n lei deschis pe numele Ministerului Finan╚¢elor la Activitatea de Trezorerie ╚Öi Contabilitate Public─â a Municipiului Bucure╚Öti, denumit ΓÇ₧Fondul pentru Tranzi╚¢ie Energetic─âΓÇ£....(7)...La solicitarea Ministerului Energiei ╚Öi Ministerului Muncii, Familiei, Tineretului ╚Öi Solidarit─â╚¢ii Sociale, se autorizeaz─â Ministerul Finan╚¢elor s─â majoreze veniturile bugetului de stat, la o pozi╚¢ie distinct─â, precum ╚Öi creditele de angajament ╚Öi creditele bugetare prev─âzute ├«n bugetele acestora, ├«n limita sumelor disponibile ├«n soldul contului prev─âzut laalin. (6), pentru plata compens─ârilor prev─âzute de prezenta ordonan╚¢─â de urgen╚¢─â....(8)...Sumele cu care au fost majorate veniturile bugetului de stat conform prevederiloralin. (6)se vireaz─â de Ministerul Finan╚¢elor din contul prev─âzut laalin. (5)├«n conturile de venituri bugetare prev─âzute laalin. (6)codificate cu codurile de identitate fiscal─â ale Ministerului Energiei ╚Öi Ministerului Muncii, Familiei, Tineretului ╚Öi Solidarit─â╚¢ii Sociale, dup─â caz....(9)...Se autorizeaz─â Ministerul Energiei ╚Öi Ministerul Muncii, Familiei, Tineretului ╚Öi Solidarit─â╚¢ii Sociale s─â detalieze influen╚¢ele aprobate potrivitalin. (7)├«n buget ╚Öi ├«n anexele la acesta ╚Öi s─â le comunice Ministerului Finan╚¢elor, ├«n termen de 5 zile lucr─âtoare de la majorarea bugetului....(10)...P├ón─â la data de 22 decembrie a anului bugetar, sumele r─âmase neutilizate determinate ca diferen╚¢─â ├«ntre veniturile ├«ncasate potrivitalin. (7)╚Öi pl─â╚¢ile efectuate se vireaz─â de c─âtre Ministerul Energiei ╚Öi Ministerul Muncii, Familiei, Tineretului ╚Öi Solidarit─â╚¢ii Sociale din contul de venituri ale bugetului de stat ├«n care au fost ├«ncasate ├«n contul de disponibil prev─âzut laalin. (6)....(11)...Sumele r─âmase disponibile la finele anului ├«n contul prev─âzut laalin. (6), inclusiv sumele virate potrivitalin. (10), se utilizeaz─â ├«n anul urm─âtor ├«n baza mecanismului prev─âzut laalin. (7)....(12)...Sumele aflate ├«n conturile prev─âzute laalin. (5)╚Öi(6)nu sunt purt─âtoare de dob├ónd─â ╚Öi nu sunt supuse execut─ârii silite.', NULL, 14, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (314, 19, '15', 'Articolul 15', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Nerespectarea obliga╚¢iilor prev─âzute laart. 1 alin. (1)-4),art. 2 alin. (2),art. 3-6,art. 12 alin. (1),art. 20╚Öipct. 2 lit. a),pct. 6,pct. 16 din anexa nr. 5constituie contraven╚¢ie ╚Öi se sanc╚¢ioneaz─â cu amend─â cuprins─â ├«ntre 1% ╚Öi 5% din cifra de afaceri anual─â din anul financiar anterior sanc╚¢ion─ârii....(2)...Nerespectarea obliga╚¢iilor prev─âzute laart. 1 alin. (5)-(13),art. 9 alin. (1)-(3),(7),(9)╚Öi(13),art. 14 alin. (3),art. 17,art. 21╚Öipct. 2 lit. b)╚Öie),pct. 8,pct. 9,pct. 12,pct. 13 din anexa nr. 5constituie contraven╚¢ie ╚Öi se sanc╚¢ioneaz─â cu amend─â contraven╚¢ional─â de la 100.000 de lei la 400.000 de lei....(3)...Nerespectarea obliga╚¢iilor prev─âzute laart. 11constituie contraven╚¢ie ╚Öi se sanc╚¢ioneaz─â conform dispozi╚¢iiloralin. (1), prin derogare de la dispozi╚¢iile art. 195 alin. (1) pct. 2 lit. d^1) din Legea nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioare....(4)...Contraven╚¢iile prev─âzute ├«n prezenta ordonan╚¢─â de urgen╚¢─â s─âv├ór╚Öite de c─âtre furnizorii/operatorii de distribu╚¢ie de energie electric─â ╚Öi gaze naturale ├«n raport cu clien╚¢ii casnici se constat─â ╚Öi se sanc╚¢ioneaz─â de c─âtre Autoritatea Na╚¢ional─â pentru Protec╚¢ia Consumatorilor....(5)...Constatarea contraven╚¢iilor s─âv├ór╚Öite de c─âtre furnizorii/operatorii de distribu╚¢ie de energie electric─â ╚Öi gaze naturale ├«n raport cu clien╚¢ii noncasnici ╚Öi aplicarea sanc╚¢iunilor se realizeaz─â de c─âtre reprezentan╚¢ii ├«mputernici╚¢i ai ANRE....(6)...Nerespectarea obliga╚¢iilor prev─âzute laart. 14 alin. (1)╚Öi(4)se constat─â ╚Öi se sanc╚¢ioneaz─â de c─âtre Agen╚¢ia Na╚¢ional─â de Administrare Fiscal─â....(7)...Nerespectarea de c─âtre operatori a instruc╚¢iunilor de ├«nc─ârcare a datelor prev─âzute ├«nanexele nr. 7╚Öi8constituie contraven╚¢ie ╚Öi se sanc╚¢ioneaz─â cu amend─â contraven╚¢ional─â de la 25.000 lei la 50.000 lei....(8)...Nerespectarea de c─âtre operatori a termenelor prev─âzute laart. 19 alin. 4╚Öiart. 23 pct. 2din prezenta ordonan╚¢─â de urgen╚¢─â, respectiv a termenului de rectificare a datelor ├«nc─ârcate pe platforma informatic─â ╚Öi redepunere a cererilor de decontare ╚Öi/sau a declara╚¢iilor pe propria r─âspundere, constituie contraven╚¢ie ╚Öi se sanc╚¢ioneaz─â cu amend─â contraven╚¢ional─â de la 25.000 lei la 50.000 lei....(9)...Constatarea contraven╚¢iilor ╚Öi aplicarea sanc╚¢iunilor pentru contraven╚¢iile prev─âzute laart. 7 alin. (4),art. 9 alin. (1)-(3),(7),(9)╚Öi(13),art. 12 alin. (1),art. 14 alin. (3),art. 15 alin. (1)-(3),alin. (7)╚Öi(8)╚Öiart. 17se constat─â ╚Öi se sanc╚¢ioneaz─â de c─âtre ANRE. Aplicarea de c─âtre ANRE a sanc╚¢iunilor contraven╚¢ionale raportate la cifra de afaceri se realizeaz─â ├«n condi╚¢iile dispozi╚¢iilor art. 95 alin. (2) ╚Öi (3) ╚Öi ale art. 198 alin. (2) ╚Öi (3) din Legea nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioare....(10)...Contraven╚¢iilor prev─âzute laalin. (1)-(4),(7)╚Öi(8)le sunt aplicabile prevederile Ordonan╚¢ei Guvernului nr. 2/2001 privind regimul juridic al contraven╚¢iilor, aprobat─â cu modific─âri ╚Öi complet─âri prin Legea nr. 180/2002, cu modific─ârile ╚Öi complet─ârile ulterioare, cu excep╚¢ia dispozi╚¢iilor art. 8 alin. (2) lit. a), ale art. 28 alin. (1), precum ╚Öi ale art. 29 din ordonan╚¢a anterior men╚¢ionat─â....(11)...Aplicarea sanc╚¢iunii amenzii contraven╚¢ionale se prescrie ├«n termen de 2 ani de la data s─âv├ór╚Öirii faptei....(12)...Prin cifra de afaceri anual─â se ├«n╚¢elege cifra de afaceri a persoanei juridice contraveniente realizat─â din activitatea licen╚¢iat─â, ├«n anul financiar anterior sanc╚¢ion─ârii faptei. ├Än cazul ├«n care, ├«n anul financiar anterior sanc╚¢ion─ârii, ├«ntreprinderea nu a ├«nregistrat cifr─â de afaceri sau aceasta nu poate fi determinat─â, va fi luat─â ├«n considerare cea aferent─â anului financiar ├«n care contravenientul a ├«nregistrat cifr─â de afaceri, an imediat anterior anului de referin╚¢─â pentru calcularea cifrei de afaceri ├«n vederea  aplic─ârii sanc╚¢iunii. ├Än ipoteza ├«n care nici ├«n anul anterior anului de referin╚¢─â pentru calcularea cifrei de afaceri ├«n vederea aplic─ârii sanc╚¢iunii contravenientul nu a realizat cifr─â de afaceri, va fi luat─â ├«n calcul ultima cifr─â de afaceri ├«nregistrat─â. Dac─â contravenientul este persoan─â juridic─â nou-├«nfiin╚¢at─â ╚Öi/sau care nu a ├«nregistrat cifr─â de afaceri ├«n anul anterior sanc╚¢ion─ârii, acesta va fi sanc╚¢ionat cu amend─â contraven╚¢ional─â de la 1.000.000 de lei la 10.000.000 de lei.', NULL, 15, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (315, 19, '16', 'Articolul 16', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Pentru aplicarea sanc╚¢iunii prin raportare la cifra de afaceri, la nivelul Autorit─â╚¢ii Na╚¢ionale pentru Protec╚¢ia Consumatorilor se constituie, prin ordin al pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale pentru Protec╚¢ia Consumatorilor, o comisie ce are ca scop analizarea documentelor de control ╚Öi stabilirea sanc╚¢iunii conform art. 21 alin. (2) din Ordonan╚¢a Guvernului nr. 2/2001, aprobat─â cu modific─âri ╚Öi complet─âri prin Legea nr. 180/2002, cu modific─ârile ╚Öi complet─ârile ulterioare, cu urm─âtoarea componen╚¢─â:a)directorul general al Direc╚¢iei generale control ╚Öi supraveghere pia╚¢─â sau ├«nlocuitorul desemnat de acesta;...b)comisarul-╚Öef din cadrul Direc╚¢iei generale control ╚Öi supraveghere pia╚¢─â sau ├«nlocuitorul desemnat de acesta;...c)╚Öeful Serviciului produse ╚Öi servicii alimentare ╚Öi nealimentare sau ├«nlocuitorul desemnat de acesta;...d)directorul general al Direc╚¢iei generale juridice ╚Öi armonizare european─â sau ├«nlocuitorul desemnat de acesta;...e)comisarul desemnat prin act administrativ de c─âtre pre╚Öedintele autorit─â╚¢ii, altul dec├ót cel care a ├«ntocmit documentul de control.......(2)...Comisarul ce a ├«ntocmit documentul de control sesizeaz─â comisia ├«n termen de maximum 3 zile, iar comisia se ├«ntrune╚Öte ├«n termen de maximum 10 zile calendaristice de la primirea documentului de control....(3)...Prin ordin al Pre╚Öedintelui Autorit─â╚¢ii Na╚¢ionale pentru Protec╚¢ia Consumatorilor se aprob─â procedura de lucru privind stabilirea ╚Öi aplicarea sanc╚¢iunii la cifra de afaceri, ordin ce se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I.', NULL, 16, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (316, 19, '17', 'Articolul 17', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Furnizorul va notifica clien╚¢ii afla╚¢i ├«n portofoliul propriu cu privire la modific─ârile care decurg din aplicarea prevederilor prezentei ordonan╚¢e de urgen╚¢─â odat─â cu prima factur─â transmis─â dup─â intrarea ├«n vigoare a prezentei ordonan╚¢e de urgen╚¢─â.', NULL, 17, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (317, 19, '18', 'Articolul 18', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...├Än scopul aplic─ârii schemei de sprijin, atunci c├ónd o perioad─â de regularizare cuprinde dou─â sau mai multe luni, aceasta urmeaz─â a fi defalcat─â pe fiecare lun─â calendaristic─â incident─â....(2)...├Än sensul prezentei ordonan╚¢e de urgen╚¢─â, no╚¢iunea de ΓÇ₧furnizorΓÇ£ include furnizorul de energie electric─â, furnizorul de gaze naturale, produc─âtorul de energie electric─â pentru clien╚¢ii racorda╚¢i ├«n instala╚¢iile sale ╚Öi operatorul de distribu╚¢ie de energie electric─â cu drept de rev├ónzare a energiei electrice.', NULL, 18, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (318, 19, '19', 'Articolul 19', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Prin procedura de regularizare se ├«n╚¢elege regularizarea volumelor facturate de energie electric─â ╚Öi gaze naturale ├«n perioada de aplicabilitate a schemei de sprijin instituite prin prezenta ordonan╚¢─â de urgen╚¢─â....(2)...P├ón─â la data de 31 decembrie 2026, pentru perioada de aplicare a schemei de sprijin instituite prin prezenta ordonan╚¢─â de urgen╚¢─â se va efectua regularizarea sumelor decontate c─âtre furnizorii de energie electric─â ╚Öi gaze naturale, dup─â transmiterea c─âtre ANRE, pentru fiecare lun─â din perioad─â ╚Öi pentru fiecare categorie de clien╚¢i beneficiari ai pre╚¢ului final plafonat, a informa╚¢iilor privind cantit─â╚¢ile de energie facturate pentru lunile de consum din perioada de aplicare, ca urmare a intervenirii unor modific─âri asupra sumelor deja decontate....(3)...├Än vederea verific─ârii ├«ncadr─ârii corecte de c─âtre furnizori a clien╚¢ilor finali beneficiari ai schemei de compensare, ├«n ceea ce prive╚Öte pre╚¢ul final facturat, ANRE solicit─â ╚Öi verific─â aleatoriu facturi emise ╚Öi contracte de furnizare ├«ncheiate de c─âtre furnizorii de energie electric─â ╚Öi gaze naturale pentru clien╚¢ii finali al c─âror pre╚¢ contractual este superior plafoanelor prev─âzute laart. 1 alin. (1)╚Öi(3), dar pre╚¢ul final facturat acestora a fost sub plafoanele prev─âzute laart. 1 alin. (1)╚Öi(3)....(4)...Pentru procedura de regularizare prev─âzut─â laalin. (1), precum ╚Öi pentru datele necesare decont─ârii lunare, termenul final de introducere a datelor necesare decont─ârii sumelor din Fondul de Tranzi╚¢ie Energetic─â sau, dup─â caz, regulariz─ârii sumelor decontate din Fondul de Tranzi╚¢ie Energetic─â este 31 decembrie 2026....(5)...Decontarea facturilor de regularizare se va face prin bugetul Ministerului Energiei sau al Ministerului Muncii, Familiei, Tineretului ╚Öi Solidarit─â╚¢ii Sociale, din Fondul de Tranzi╚¢ie Energetic─â, ├«n conformitate cu valorile calculate ╚Öi comunicate de ANRE. Dac─â valoarea determinat─â de ANRE ca urmare a procesului de regularizare este mai mic─â dec├ót valoarea decontat─â, furnizorii au obliga╚¢ia return─ârii diferen╚¢ei dintre cele dou─â valori ├«n termen de 10 zile lucr─âtoare de la data comunic─ârii valorii determinate de c─âtre ANRE.', NULL, 19, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (319, 19, '20', 'Articolul 20', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Pentru perioada 1 iulie 2025-30 iunie 2026, furnizorii de energie electric─â elaboreaz─â ╚Öi public─â/comunic─â oferte-tip doar pentru perioade pentru care au achizi╚¢ionat, la data elabor─ârii ofertei, o cantitate de energie electric─â cel pu╚¢in egal─â cu 50% din consumul clien╚¢ilor finali ├«n regim concuren╚¢ial ╚Öi ├«n regim de serviciu universal din portofoliu, dup─â caz, realizat ├«n ultima lun─â pentru care exist─â date de consum la data elabor─ârii ofertei....(2)...Pentru perioada 1 aprilie 2026-31 martie 2027, furnizorii de gaze naturale elaboreaz─â ╚Öi public─â/comunic─â oferte-tip doar pentru perioade pentru care au achizi╚¢ionat, la data elabor─ârii ofertei, o cantitate de gaze naturale cel pu╚¢in egal─â cu 50% din consumul clien╚¢ilor finali ├«n regim concuren╚¢ial realizat ├«n ultima lun─â pentru care exist─â date de consum la data elabor─ârii ofertei.', NULL, 20, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (320, 19, '21', 'Articolul 21', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '├Än ofertele-tip men╚¢ionate la art. 20, componenta de furnizare pentru activitatea de furnizare a energiei electrice este men╚¢ionat─â distinct fa╚¢─â de componenta de achizi╚¢ie a energiei electrice (inclusiv tariful de transport - componenta de introducere a energiei electrice ├«n re╚¢ea) din cadrul pre╚¢ului energiei electrice.', NULL, 21, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
