INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (328, 19, '1', 'Articolul 1', NULL, NULL, NULL, NULL, NULL, 'Scop', NULL, NULL, '(1)...Prezenta metodologie are ca scop stabilirea modului ├«n care produc─âtorii de energie electric─â care se supun prevederilorart. 14din ordonan╚¢a de urgen╚¢─â/entit─â╚¢ile agregate calculeaz─â contribu╚¢ia la Fondul de Tranzi╚¢ie Energetic─â ce urmeaz─â a fi pl─âtit─â conform prevederilor men╚¢ionate anterior....(2)...Prezenta metodologie stabile╚Öte:a)categoriile de operatori economici cu obliga╚¢ie de supunere la plata contribu╚¢iei la Fondul de Tranzi╚¢ie Energetic─â, prev─âzut─â laart. 14din ordonan╚¢a de urgen╚¢─â;...b)definirea veniturilor lunare provenite din v├ónzarea energiei electrice produse pe pie╚¢ele de electricitate de c─âtre operatorii economici prev─âzu╚¢i lalit. a);...c)definirea cheltuielilor lunare;...d)modul de calcul al venitului net lunar;...e)modul de calcul al pre╚¢ului lunar de v├ónzare a energiei electrice;...f)modul de calcul al venitului lunar suplimentar....', NULL, 29, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
