INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (330, 19, '3', 'Articolul 3', NULL, NULL, NULL, NULL, NULL, 'Domeniul de aplicare', NULL, NULL, 'Prezenta metodologie creeaz─â cadrul procedural de calcul al contribu╚¢iei la Fondul de Tranzi╚¢ie Energetic─â ╚Öi este utilizat─â drept instrument de lucru ├«n activitatea produc─âtorilor de energie electric─â care se supun prevederilorart. 14din ordonan╚¢a de urgen╚¢─â/entit─â╚¢ilor agregate.', NULL, 31, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (331, 19, '4', 'Articolul 4', NULL, NULL, NULL, NULL, NULL, 'Domeniul de aplicare', NULL, NULL, 'Prezenta metodologie se aplic─â de c─âtre to╚¢i produc─âtorii de energie electric─â care se supun prevederilorart. 14din ordonan╚¢a de urgen╚¢─â/entit─â╚¢ile agregate, ├«n perioada 1 aprilie 2025-30 iunie 2025.', NULL, 32, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (332, 19, '5', 'Articolul 5', NULL, NULL, NULL, NULL, NULL, 'Termeni utiliza╚¢i', NULL, NULL, 'Termenii specifici utiliza╚¢i ├«n prezenta metodologie sunt:Pie╚¢e de electricitatePotrivit defini╚¢iei din Legea energiei electrice ╚Öi a gazelor naturale nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioarePre╚¢ de referin╚¢─â400 lei MWhCantitate lunar─â v├óndut─â - C_1 (MWh)cantitatea livrat─â lunar pe pie╚¢ele de electricitate de produc─âtorul de energie electric─â/entitatea agregat─â din produc╚¢ia proprie ╚Öi/sau transferat─â din portofoliul de produc╚¢ie ├«n cel de furnizareVenituri lunare - V_1 (lei)veniturile ob╚¢inute din v├ónzarea energiei electrice cu livrare fizic─â din produc╚¢ia proprie ╚Öi/sau din transferarea acesteia din portofoliul de produc╚¢ie ├«n cel de furnizareCheltuieli lunare - Che_1 (lei):a) cheltuielile cu dezechilibrele, ├«n procent de maximum 10% din valoarea energiei electrice cu livrare fizic─â din produc╚¢ia proprie, ├«n cazul produc─âtorilor de energie electric─â din energie eolian─â ╚Öi solar─â; 5% ├«n cazul celorlal╚¢i produc─âtori;b) cheltuieli ├«n baza contractelor cu instrumente financiare derivate din domeniul energiei (hedging);c) cheltuielile cu certificatele de CO_2 aferente energiei electrice produse ╚Öi v├óndute. Nu se includ cheltuielile efective de produc╚¢ie pentru ob╚¢inerea unui MWh de energie electric─â.Venit net lunar - Vnl (lei)Diferen╚¢a dintre veniturile lunare ╚Öi cheltuielile lunarePre╚¢ul lunar de v├ónzare a energiei electrice - P_lv (lei/MWh)pre╚¢ul rezultat din diferen╚¢a dintre veniturile lunare ╚Öi cheltuielile lunare raportat─â la totalul cantit─â╚¢ii de energie livrat─â fizic din produc╚¢ia proprie ╚Öi/sau transferat─â din portofoliul de produc╚¢ie ├«n cel de furnizare ├«n luna respectiv─âContribu╚¢ia la Fondul de Tranzi╚¢ie Energetic─â - C_s (lei)0.8 * (diferen╚¢a dintre pre╚¢ul lunar de v├ónzare ╚Öi pre╚¢ul de referin╚¢─â) * cantitatea lunar─â livrat─â fizic din produc╚¢ia proprie ╚Öi/sau transferat─â din portofoliul de produc╚¢ie ├«n cel de furnizare', NULL, 33, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (333, 19, '6', 'Articolul 6', NULL, NULL, NULL, NULL, NULL, 'Calculul pre╚¢ului lunar de v├ónzare a energiei electrice  ╚Öi al contribu╚¢iei la Fondul de Tranzi╚¢ie Energetic─â', NULL, NULL, 'Pre╚¢ul lunar de v├ónzare a energiei electrice - P_lv se calculeaz─â ca raport ├«ntre venitul net lunar ╚Öi cantitatea produs─â ╚Öi livrat─â fizic lunar pe pie╚¢ele de electricitate ╚Öi/sau transferat─â din portofoliul de produc╚¢ie ├«n cel de furnizare:Plv = Vnl/Cl,unde: Vnl = Vl - Chel', NULL, 34, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (386, 21, '52', 'Articolul 52', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Regimul juridic privind cuantumul, plata ╚Öi eviden╚¢a tarifelor se reglementeaz─â prin ordin al pre╚Öedintelui ANRMPSG care se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I; tarifele se actualizeaz─â anual, prin ordin al pre╚Öedintelui ANRMPSG.', NULL, 23, '2025-11-07 23:44:17.541607', '2025-11-07 23:44:17.541607', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (338, 19, '1', 'Articolul 1', NULL, NULL, NULL, NULL, NULL, 'Scop', NULL, NULL, '(1)...Prezenta metodologie are ca scop stabilirea modului ├«n care entit─â╚¢ile produc─âtoare de energie electric─â ╚Öi gaze naturale, entit─â╚¢ile agregate de producere a energiei electrice, traderii de energie electric─â ╚Öi gaze naturale, furnizorii de energie electric─â ╚Öi gaze naturale ╚Öi agregatorii independen╚¢i de energie electric─â care se supun prevederilorart. 14din ordonan╚¢a de urgen╚¢─â calculeaz─â contribu╚¢ia la Fondul de Tranzi╚¢ie Energetic─â ce urmeaz─â a fi pl─âtit─â conform prevederilor men╚¢ionate anterior....(2)...Prezenta metodologie stabile╚Öte:a)definirea pre╚¢ului mediu ponderat lunar de v├ónzare...b)definirea pre╚¢ului mediu ponderat lunar de achizi╚¢ie;...c)modul de calcul al contribu╚¢iei la tranzi╚¢ia energetic─â....', NULL, 39, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (339, 19, '2', 'Articolul 2', NULL, NULL, NULL, NULL, NULL, 'Scop', NULL, NULL, 'Pre╚¢urile de v├ónzare ╚Öi de achizi╚¢ie a energiei electrice ╚Öi gaze naturale sunt utilizate pentru calculul valorii contribu╚¢iei la Fondul de Tranzi╚¢ie Energetic─â prev─âzut─â laart. 14din ordonan╚¢a de urgen╚¢─â.', NULL, 40, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (340, 19, '3', 'Articolul 3', NULL, NULL, NULL, NULL, NULL, 'Domeniul de aplicare', NULL, NULL, 'Prezenta metodologie creeaz─â cadrul procedural de calcul al contribu╚¢iei la tranzi╚¢ia energetic─â ╚Öi este utilizat─â drept instrument de lucru ├«n activitatea entit─â╚¢ilor produc─âtoare de energie electric─â ╚Öi gaze naturale, entit─â╚¢ilor agregate de producere a energiei electrice, traderilor de energie electric─â ╚Öi gaze naturale, furnizorilor ╚Öi agregatorilor care se supun prevederilorart. 14din ordonan╚¢a de urgen╚¢─â.', NULL, 41, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (341, 19, '4', 'Articolul 4', NULL, NULL, NULL, NULL, NULL, 'Domeniul de aplicare', NULL, NULL, 'Prezenta metodologie se aplic─â de c─âtre entit─â╚¢ile produc─âtoare de energie electric─â ╚Öi gaze naturale, entit─â╚¢ile agregate de producere a energiei electrice, traderii de energie electric─â ╚Öi gaze naturale, furnizorii ╚Öi agregatorii care se supun prevederilorart. 14din ordonan╚¢a de urgen╚¢─â, p├ón─â la 30 iunie 2025 aferent domeniului energie electric─â ╚Öi p├ón─â la 31 martie 2026 aferent domeniului gaze naturale.', NULL, 42, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (342, 19, '5', 'Articolul 5', NULL, NULL, NULL, NULL, NULL, 'Termeni utiliza╚¢i', NULL, NULL, 'Termenii specifici utiliza╚¢i ├«n prezenta metodologie sunt:Pre╚¢ de referin╚¢─âpre╚¢ul mediu lunar de achizi╚¢ie a energiei electrice, gazelor naturale, la care se adaug─â o marj─â de 20%Pia╚¢a angro de energie electric─âpotrivit defini╚¢iei din Legea energiei electrice ╚Öi a gazelor naturale nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioareQ_livrat─â lunarcantitatea livrat─â pe pia╚¢a angro de energie electric─â, gaze naturale, de c─âtre to╚¢i operatorii economici care desf─â╚Öoar─â activitatea de trading. La calculul cantit─â╚¢ii livrate sunt luate ├«n considerare toate cantit─â╚¢ile tranzac╚¢ionate cu livrare ├«n luna de referin╚¢─â, indiferent de data ├«ncheierii contractului, cu excep╚¢ia cantit─â╚¢ilor provenite din import ╚Öi/sau din spa╚¢iul intracomunitar, la prima tranzac╚¢ionarePre╚¢_mediu - vzpre╚¢ul mediu ponderat lunar de v├ónzare a energiei electrice/gazelor naturale livrate ├«n luna de referin╚¢─âPre╚¢_mediu aczpre╚¢ul mediu ponderat lunar de achizi╚¢ie a energiei electrice/gazelor naturale livrate ├«n luna de referin╚¢─âPentru furnizorii care desf─â╚Öoar─â activitate de trading ╚Öi nu aloc─â specific contractele de achizi╚¢ie ├«ntre activitatea de furnizare ╚Öi activitatea de trading, pentru calculul pre╚¢ului mediu de achizi╚¢ie pentru activitatea de trading se ia ├«n considerare ├«ntreaga achizi╚¢ie f─âcut─â de furnizori, indiferent de destina╚¢ia energiei electrice/gazelor naturale, cu excep╚¢ia achizi╚¢iilor de gaze naturale conform art. 12 din ordonan╚¢─â de urgen╚¢─â.', NULL, 43, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (343, 19, '6', 'Articolul 6', NULL, NULL, NULL, NULL, NULL, 'Calculul contribu╚¢ie la Fondul de Tranzi╚¢ie Energetic─â', NULL, NULL, 'Contribu╚¢ia la Fondul de Tranzi╚¢ie Energetic─â (C_TE) se determin─â astfel:C_TE = (Pre╚¢(mediu-vz) - Pre╚¢(mediu acz) x 1,2) x Q(livrat─â lunar)', NULL, 44, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (344, 19, '7', 'Articolul 7', NULL, NULL, NULL, NULL, NULL, 'Calculul contribu╚¢ie la Fondul de Tranzi╚¢ie Energetic─â', NULL, NULL, 'Contribu╚¢ia la Fondul de Tranzi╚¢ie Energetic─â (C_TE) se determin─â ├«n condi╚¢iile ├«n care pre╚¢ul de tranzac╚¢ionare este mai mare dec├ót pre╚¢ul de referin╚¢─â.', NULL, 45, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (345, 19, '8', 'Articolul 8', NULL, NULL, NULL, NULL, NULL, 'Calculul contribu╚¢ie la Fondul de Tranzi╚¢ie Energetic─â', NULL, NULL, 'Calculul prev─âzut laart. 6se realizeaz─â lunar de c─âtre entit─â╚¢ile produc─âtoare de energie electric─â ╚Öi gaze naturale, entit─â╚¢ile agregate de producere a energiei electrice, traderii de energie electric─â ╚Öi gaze naturale, furnizorii de energie electric─â ╚Öi gaze naturale ╚Öi agregatorii independen╚¢i de energie electric─â care se supun prevederilorart. 14din ordonan╚¢a de urgen╚¢─â.', NULL, 46, '2025-11-07 23:44:17.251809', '2025-11-07 23:44:17.251809', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (346, 20, '1', 'Articolul 1', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Prezenta ordonan╚¢─â de urgen╚¢─â stabile╚Öte criteriile de durabilitate pentru biocarburan╚¢i ╚Öi biolichide, precum ╚Öi specifica╚¢iile tehnice, metodele de calcul ╚Öi structura de raportare bazate pe considerente de s─ân─âtate ╚Öi mediu ale carburan╚¢ilor folosi╚¢i de autovehiculele echipate cu motoare cu aprindere prin sc├ónteie ╚Öi, respectiv, de cele echipate cu motoare cu aprindere prin compresie, ╚¢in├ónd seama de cerin╚¢ele tehnice ale acestor motoare, ╚Öi obiectivul de reducere a emisiilor de gaze cu efect de ser─â generate pe durata ciclului de via╚¢─â....(2)...Prevederile prezentei ordonan╚¢e de urgen╚¢─â se aplic─â carburan╚¢ilor utiliza╚¢i pentru propulsia autovehiculelor rutiere, utilajelor mobile nerutiere, inclusiv a tractoarelor agricole ╚Öi forestiere, navelor de naviga╚¢ie interioar─â ╚Öi ambarca╚¢iunilor de agrement atunci c├ónd nu se afl─â pe mare, precum ╚Öi electricit─â╚¢ii utilizate de c─âtre vehiculele rutiere....(3)...Introducerea pe pia╚¢─â a carburan╚¢ilor care ├«ndeplinesc cerin╚¢ele prezentei ordonan╚¢e de urgen╚¢─â nu poate fi interzis─â, limitat─â sau ├«mpiedicat─â.', NULL, 1, '2025-11-07 23:44:17.434105', '2025-11-07 23:44:17.434105', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (347, 20, '2', 'Articolul 2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...├Än sensul prezentei ordonan╚¢e de urgen╚¢─â, termenii ╚Öi expresiile de mai jos au urm─âtoarele ├«n╚¢elesuri:a)autoritate competent─â - organ de specialitate al administra╚¢iei publice centrale, ├«n subordinea Guvernului, care aplic─â strategia ╚Öi realizeaz─â politica guvernamental─â ├«n sectorul energiei;...b)activitatea de e╚Öantionare - prelevarea probelor de benzin─â ╚Öi motorin─â ╚Öi efectuarea ├«ncerc─ârilor de determinare a parametrilor specifica╚¢iilor tehnice prev─âzute ├«n prezenta ordonan╚¢─â de urgen╚¢─â;...c)benzin─â - orice combustibil destinat func╚¢ion─ârii motoarelor cu aprindere prin sc├ónteie, care se utilizeaz─â pentru propulsarea vehiculelor ╚Öi care se clasific─â la unul dintre codurile din nomenclatura combinat─â (NC) 2710 11 31, 2710 11 41, 2710 11 45 ╚Öi 2710 11 49 prev─âzute ├«n Legea nr. 227/2015 privind Codul fiscal, cu modific─ârile ╚Öi complet─ârile ulterioare, ╚Öi ├«n concordan╚¢─â cu Regulamentul (CEE) nr. 2.658/87 al Consiliului din 23 iulie 1987 privind Nomenclatura tarifar─â ╚Öi statistic─â ╚Öi Tariful Vamal Comun;...d)biocarburant - potrivit defini╚¢iei prev─âzute la art. 2 lit. d) din Legea nr. 220/2008 pentru stabilirea sistemului de promovare a producerii energiei din surse regenerabile de energie, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare;...e)biocarburan╚¢i cu risc sc─âzut din perspectiva schimb─ârii indirecte a utiliz─ârii terenurilor - biocarburan╚¢i ale c─âror materii prime au fost produse ├«n cadrul unor sisteme care reduc dislocarea produc╚¢iei care vizeaz─â alte scopuri dec├ót fabricarea de biocarburan╚¢i ╚Öi au fost produse ├«n conformitate cu criteriile de durabilitate pentru biocombustibili prev─âzute laart. 11 alin. (4)-(10);...f)biolichid - potrivit defini╚¢iei prev─âzute la art. 2 lit. c) din Legea nr. 220/2008, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare;...g)bitum natural - orice surs─â de materii prime de rafin─ârie, care prezint─â o greutate API, American Petroleum Institute - Institutul American al Petrolului, de 10 grade sau mai pu╚¢in atunci c├ónd se afl─â ├«ntr-un z─âc─âm├ónt la locul de extrac╚¢ie, definit─â ├«n conformitate cu metoda de testare a Societ─â╚¢ii americane pentru testare ╚Öi materiale, ASTM, (1) D287, care are o viscozitate medie anual─â la temperatura din rezervor mai mare dec├ót cea calculat─â prin ecua╚¢ia: Viscozitate (Centipoise) = 518,98^e-0,038T, unde T reprezint─â temperatura exprimat─â ├«n grade Celsius, care intr─â sub inciden╚¢a defini╚¢iei nisipurilor bituminoase conform codului din nomenclatura combinat─â NC 2714, astfel cum este prev─âzut ├«n Regulamentul (UE) nr. 1.006/2011 al Comisiei din 27 septembrie 2011 de modificare a anexei I la Regulamentul (CEE) nr. 2.658/87 al Consiliului privind Nomenclatura tarifar─â ╚Öi statistic─â ╚Öi Tariful Vamal Comun, ╚Öi la care mobilizarea sursei de materii prime este realizat─â prin extrac╚¢ie minier─â sau prin drenaj gravita╚¢ional ├«mbun─ât─â╚¢it din punct de vedere termic, unde energia termic─â este derivat─â ├«n principal din alte surse dec├ót ├«ns─â╚Öi sursa de materii prime;...h)buletin de trasabilitate pentru ciclul de via╚¢─â - documentul ╚Öi/sau setul de documente necesar pentru stabilirea emisiilor nete de CO_2, CH_4 ╚Öi N_2O atribuite unui carburant ╚Öi oric─âror componente ├«n amestec ale acestuia sau energiei furnizate, ├«n fiecare etap─â relevant─â a ciclului de via╚¢─â, ├«ncep├ónd cu extragerea sau cultivarea, inclusiv schimb─ârile aduse utiliz─ârii terenului, transportul ╚Öi distribu╚¢ia, prelucrarea ╚Öi arderea, indiferent de locul de pe t