INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (353, 20, '7', 'Articolul 7', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Autoritatea competent─â monitorizeaz─â respectarea cerin╚¢elor prev─âzute laart. 3-5, ├«n privin╚¢a calit─â╚¢ii benzinei ╚Öi a motorinei, ├«n conformitate cu prevederileanexelor nr. 2╚Öi3la prezenta ordonan╚¢─â de urgen╚¢─â....(2)...Autoritatea competent─â stabile╚Öte un sistem de monitorizare a calit─â╚¢ii carburan╚¢ilor ├«n conformitate cu cerin╚¢ele standardului relevant, sistemul de FQMS: SR EN 228, SR EN 590, SR EN 14274, SR EN 14275. Acest sistem de monitorizare urmeaz─â a fi aprobat prin ordin al conduc─âtorului autorit─â╚¢ii competente ├«n termen de 90 de zile de la data intr─ârii ├«n vigoare a prezentei ordonan╚¢e de urgen╚¢─â. Autoritatea competent─â accept─â utilizarea unui sistem voluntar de monitorizare a calit─â╚¢ii carburan╚¢ilor, cu condi╚¢ia ca un astfel de sistem s─â fie recunoscut de Comisia European─â. Furnizorii care ader─â la un astfel de sistem au obliga╚¢ia de a informa autoritatea competent─â....(3)...Prelevarea probelor de benzin─â ╚Öi motorin─â din cadrul sistemului de monitorizare se efectueaz─â conform standardului SR EN ISO/IEC 17020 ╚Öi evaluarea conformit─â╚¢ii acestora se realizeaz─â conform standardului SR EN ISO/IEC 17025, de c─âtre organisme de inspec╚¢ie de ter╚¢─â parte, recunoscute de autoritatea competent─â pe baza acredit─ârii emise de c─âtre Asocia╚¢ia de acreditare din Rom├ónia - RENAR, organismul na╚¢ional de acreditare, sau de c─âtre orice organism de acreditare conform Regulamentului (CE) nr. 765/2008 al Parlamentului European ╚Öi al Consiliului din 9 iulie 2008 de stabilire a cerin╚¢elor de acreditare ╚Öi de su