INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (368, 21, '4', 'Articolul 4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Activitatea de reglementare a ANRMPSG se refer─â la:a)acordarea/modificarea/transferul/suspendarea/refuzul ori retragerea de licen╚¢e, permise, acorduri sau autoriza╚¢ii, prev─âzute de lege ├«n competen╚¢a sa;...b)aprobarea pre╚¢urilor ╚Öi tarifelor din competen╚¢a sa ╚Öi/sau a metodologiilor de calcul al acestora;...c)aprobarea reglement─ârilor pentru punerea ├«n valoare, ├«n condi╚¢ii de siguran╚¢─â ╚Öi eficien╚¢─â, a resurselor minerale ╚Öi petrolului, precum ╚Öi aprobarea reglement─ârilor pentru stocarea geologic─â a dioxidului de carbon;...d)aprobarea/avizarea documentelor elaborate de operatorii economici reglementa╚¢i, conform prevederilor legale ├«n vigoare;...e)acordarea/modificarea/suspendarea/refuzul ori retragerea de atestate/autoriza╚¢ii operatorilor economici ╚Öi persoanelor fizice care desf─â╚Öoar─â activit─â╚¢i specifice ├«n sectorul resurselor minerale, petrolului ╚Öi stoc─ârii geologice a dioxidului de carbon;...f)aprobarea sau avizarea altor reglement─âri, norme, studii, documenta╚¢ii prev─âzute de lege pentru domeniul resurselor minerale, petrolului ╚Öi stoc─ârii geologice a dioxidului de carbon.......(2)...Actele administrative corespunz─âtoare activit─â╚¢ii de reglementare stabilite conformalin. (1), ├«nso╚¢ite de instrumentele de motivare, se dezbat ├«n mod obligatoriu ├«n Comitetul de reglementare ╚Öi se supun aprob─ârii pre╚Öedintelui cu votul majorit─â╚¢ii membrilor acestuia.', NULL, 5, '2025-11-07 23:44:17.541607', '2025-11-07 23:44:17.541607', 'pending', NULL, NULL, NULL, NULL);
