INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (379, 21, '25', 'Articolul 25', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Perimetrul de exploatare poate fi extins prin acordul autorit─â╚¢ii competente ╚Öi de reglementare ╚Öi al titularului, pe baza actualiz─ârii documenta╚¢iilor prev─âzute la art. 20 alin. (1), prin act adi╚¢ional care intr─â ├«n vigoare conform prevederilorart. 21^1 alin. (3), ├«n urm─âtoarele cazuri:a)├«n cazul ├«n care titularul licen╚¢ei de exploatare face dovada extinderii z─âc─âm├óntului de resurse minerale care face obiectul licen╚¢ei dincolo de limitele perimetrului de exploatare acordat prin licen╚¢─â, numai dup─â expirarea duratei ini╚¢iale de valabilitate a licen╚¢ei sau dup─â epuizarea resurselor/rezervelor minerale din perimetrul acordat ini╚¢ial, ├«n condi╚¢iile ├«n care extinderea nu se suprapune peste o alt─â concesiune/dare ├«n administrare;...b)├«n cazul ├«n care extinderea suprafe╚¢ei perimetrului este necesar─â ├«n vederea realiz─ârii/extinderii lucr─ârilor de dezvoltare, prelucrare/transport/stocare provizorie a produselor miniere;...c)extinderea este necesar─â ├«n vederea ├«ndrept─ârii unor erori topografice intervenite la momentul ├«ncheierii licen╚¢ei....', NULL, 16, '2025-11-07 23:44:17.541607', '2025-11-07 23:44:17.541607', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (380, 21, '38', 'Articolul 38', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Regimul juridic privind identificarea, eviden╚¢a ╚Öi modalitatea de punere ├«n valoare a golurilor miniere se stabile╚Öte prin ordin al pre╚Öedintelui ANRMPSG care se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I.', NULL, 17, '2025-11-07 23:44:17.541607', '2025-11-07 23:44:17.541607', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (381, 21, '48', 'Articolul 48', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Pentru actele eliberate ├«n exercitarea atribu╚¢iilor sale sau duplicate dup─â acestea - avize, permise de prospec╚¢iune ╚Öi exploatare, licen╚¢e de explorare ╚Öi exploatare, documente de ├«nregistrare a rezervelor, decizii de ├«ncetare a activit─â╚¢ii miniere, aprob─âri de asociere, de transfer al licen╚¢ei, acte adi╚¢ionale la licen╚¢e, atestate, autoriz─âri, autorizarea utiliz─ârii golurilor miniere sau alte asemenea acte -, precum ╚Öi pentru consultarea ╚Öi utilizarea unor documente ╚Öi informa╚¢ii referitoare la resursele minerale ╚Öi a C─âr╚¢ii miniere, autoritatea competent─â ╚Öi de reglementare percepe tarife....(2)...Veniturile realizate conform alin. (1) sunt utilizate de c─âtre autoritatea competent─â ╚Öi de reglementare pentru finan╚¢area cheltuielilor curente ╚Öi de capital....(3)...Veniturile r─âmase neutilizate la sf├ór╚Öitul anului bugetar se reporteaz─â ├«n anul urm─âtor, cu aceea╚Öi destina╚¢ie.', NULL, 18, '2025-11-07 23:44:17.541607', '2025-11-07 23:44:17.541607', 'pending', NULL, NULL, NULL, NULL);
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (382, 21, '48', 'Articolul 48', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Sumele cu titlu de transfer de tehnologie ╚Öi preg─âtire profesional─â se achit─â anual autorit─â╚¢ii competente ╚Öi de reglementare, p├ón─â la sf├ór╚Öitul primei luni a anului ├«n curs pentru care sunt datorate....(2)...Sumele cu titlu de transfer de tehnologie ╚Öi preg─âtire profesional─â constituie venituri proprii ale autorit─â╚¢ii competente ╚Öi de reglementare ╚Öi sunt destinate finan╚¢─ârii cheltuielilor curente ╚Öi de capital ale acesteia....(3)...Regimul juridic privind stabilirea, plata ╚Öi eviden╚¢a sumelor reprezent├ónd transfer de tehnologie ╚Öi perfec╚¢ionare profesional─â se reglementeaz─â prin ordin al pre╚Öedintelui ANRMPSG care se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I.', NULL, 19, '2025-11-07 23:44:17.541607', '2025-11-07 23:44:17.541607', 'pending', NULL, NULL, NULL, NULL);
