INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (389, 21, '12', 'Articolul 12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Dup─â articolul 32 din Ordonan╚¢a de urgen╚¢─â a Guvernului nr. 64/2011 privind stocarea geologic─â a dioxidului de carbon, publicat─â ├«n Monitorul Oficial al Rom├óniei, Partea I, nr. 461 din 30 iunie 2011, aprobat─â cu modific─âri ╚Öi complet─âri prin Legea nr. 114/2013, se introduc dou─â noi articole,art. 32^1╚Öiart. 32^2, cu urm─âtorul cuprins:Articolul 32^1(1)Pentru actele eliberate ├«n exercitarea atribu╚¢iilor sale: autoriza╚¢ii de explorare ╚Öi autoriza╚¢ii de stocare a dioxidului de carbon, avize, transferul autoriza╚¢iilor, decizii de aprobare a accesului unor ter╚¢i la siturile de stocare a dioxidului de carbon sau a transferului de responsabilitate c─âtre autoritatea competent─â ╚Öi de reglementare sau alte asemenea acte, precum ╚Öi pentru punerea la dispozi╚¢ie ├«n vederea consult─ârii ╚Öi utiliz─ârii unor date ╚Öi informa╚¢ii geologice sau tehnice, ANRMPSG percepe tarife.(2)Veniturile realizate conform alin. (1) sunt utilizate de ANRMPSG pentru finan╚¢area cheltuielilor curente ╚Öi de capital ale acesteia.(3)Veniturile r─âmase neutilizate la sf├ór╚Öitul anului bugetar se preiau ca venituri ├«n anul urm─âtor, la dispozi╚¢ia ANRMPSG.(4)Regimul juridic privind cuantumul, plata ╚Öi eviden╚¢a tarifelor stabilite de prezenta ordonan╚¢─â de urgen╚¢─â se reglementeaz─â prin ordin al pre╚Öedintelui ANRMPSG care se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I; tarifele stabilite de prezenta ordonan╚¢─â de urgen╚¢─â se actualizeaz─â anual, prin ordin al pre╚Öedintelui ANRMPSG.Articolul 32^2(1)Operatorul are obliga╚¢ia s─â suporte cheltuielile aferente transferului de tehnologie ╚Öi perfec╚¢ion─ârii profesionale, astfel cum sunt prev─âzute ├«n autoriza╚¢ia de explorare ╚Öi/sau stocare, care se constituie venit la bugetul ANRMPSG.(2)Sumele cu titlu de transfer de tehnologie ╚Öi perfec╚¢ionare profesional─â se achit─â anual ANRMPSG, p├ón─â la sf├ór╚Öitul primei luni a anului ├«n curs pentru care sunt datorate.(3)Sumele cu titlu de transfer de tehnologie ╚Öi perfec╚¢ionare profesional─â constituie venituri proprii ale ANRMPSG ╚Öi sunt destinate finan╚¢─ârii cheltuielilor curente ╚Öi de capital ale acesteia.(4)Regimul juridic privind stabilirea, plata ╚Öi eviden╚¢a sumelor reprezent├ónd transfer de tehnologie ╚Öi perfec╚¢ionare profesional─â stabilite de prezenta ordonan╚¢─â de urgen╚¢─â se reglementeaz─â prin ordin al pre╚Öedintelui ANRMPSG care se public─â ├«n Monitorul Oficial al Rom├óniei, Partea I.', NULL, 26, '2025-11-07 23:44:17.541607', '2025-11-07 23:44:17.541607', 'pending', NULL, NULL, NULL, NULL);
