INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1590, 7, '13', 'Articolul 13', NULL, NULL, NULL, '├Änfiin╚¢area asocia╚¢iilor ╚Öi a funda╚¢iilor', NULL, 'Constituirea ╚Öi ├«nscrierea asocia╚¢iei', NULL, NULL, '(1)...Asocia╚¢ia ├«╚Öi poate constitui filiale, ca structuri teritoriale, cu un num─âr minim de 2 membri, organe de conducere proprii ╚Öi un patrimoniu distinct de cel al asocia╚¢iei.(la 05-12-2020, 
