INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1592, 7, '14', 'Articolul 14', NULL, NULL, NULL, '├Änfiin╚¢area asocia╚¢iilor ╚Öi a funda╚¢iilor', NULL, 'Constituirea ╚Öi ├«nscrierea asocia╚¢iei', NULL, NULL, 'Dac─â asocia╚¢ia, prin natura scopului sau obiectivelor propuse, urmeaz─â s─â desf─â╚Öoare activit─â╚¢i pentru care, potrivit legii, sunt necesare autoriza╚¢ii administrative prealabile, aceste activit─â╚¢i nu vor putea fi ini╚¢iate, sub sanc╚¢iunea dizolv─ârii pe cale judec─âtoreasc─â, dec├ót dup─â ob╚¢inerea autoriza╚¢iilor respective.', NULL, 16, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1593, 7, '15', 'Articolul 15', NULL, NULL, NULL, '├Änfiin╚¢area asocia╚¢iilor ╚Öi a funda╚¢iilor', NULL, 'Constituirea ╚Öi ├«nscrierea funda╚¢iei', NULL, NULL, '(1)...Funda╚¢ia este subiectul de drept ├«nfiin╚¢at de una sau mai multe persoane care, pe baza unui act juridic ├«ntre vii ori pentru cauz─â de moarte, constituie un patrimoniu afectat, ├«n mod permanent ╚Öi irevocabil, realiz─ârii unui scop de interes general sau, dup─â caz, al unor colectivit─â╚¢i.(la 28-07-2005, 
			Alin. (1) al art. 15 a fost modificat de pct. 11 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)...(2)...Activul patrimonial ini╚¢ial al funda╚¢iei trebuie s─â includ─â bunuri ├«n natur─â sau ├«n numerar, a c─âror valoare total─â s─â fie de cel pu╚¢in de 10 ori salariul de baz─â minim brut pe ╚¢ar─â garantat ├«n plat─â, la data constituirii funda╚¢iei.(la 05-12-2020, 
			Alineatul (2) din Articolul 15 , Sectiunea a 2-a , Capitolul II  a fost modificat de Punctul 10, Articolul I din LEGEA nr. 276 din 27 noiembrie 2020, publicat─â ├«n MONITORUL OFICIAL nr. 1166 din 02 decembrie 2020
			)...(3)...Prin excep╚¢ie de la prevederilealin. (2), ├«n cazul funda╚¢iilor al c─âror scop exclusiv, sub sanc╚¢iunea dizolv─ârii pe cale judec─âtoreasc─â, este efectuarea opera╚¢iunilor de colectare de fonduri care s─â fie puse la dispozi╚¢ia altor asocia╚¢ii sau funda╚¢ii, ├«n vederea realiz─ârii de programe de c─âtre acestea din urm─â, activul patrimonial ini╚¢ial poate avea o valoare total─â de cel pu╚¢in de dou─â ori salariul de baz─â minim brut pe ╚¢ar─â garantat ├«n plat─â.(la 05-12-2020, 
