INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1600, 7, '22', 'Articolul 22', NULL, NULL, NULL, 'Organizarea ╚Öi func╚¢ionarea asocia╚¢iilor ╚Öi a funda╚¢iilor', NULL, 'Organizarea ╚Öi func╚¢ionarea asocia╚¢iei', NULL, NULL, '(1)...Asociatul care, ├«ntr-o anumit─â problema supus─â hot─âr├órii adun─ârii generale, este interesat personal sau prin so╚¢ul sau, ascenden╚¢ii sau descenden╚¢ii s─âi, rudele ├«n linie colateral─â sau afinii s─âi p├ón─â la gradul al patrulea inclusiv nu va putea lua parte la deliberare ╚Öi nici la vot....(2)...Asociatul care ├«ncalc─â dispozi╚¢iilealin. (1)este r─âspunz─âtor de daunele cauzate asocia╚¢iei dac─â f─âr─â votul sau nu s-ar fi putut ob╚¢ine majoritatea cerut─â.', NULL, 24, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
