INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1621, 7, '35', 'Articolul 35', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, '(1)...Doua sau mai multe asocia╚¢ii sau funda╚¢ii se pot constitui ├«n federa╚¢ie....(2)...Federa╚¢iile dob├óndesc personalitate juridic─â proprie ╚Öi func╚¢ioneaz─â ├«n condi╚¢iile prev─âzute de prezenta ordonan╚¢a pentru asocia╚¢iile f─âr─â scop patrimonial, condi╚¢ii care li se aplica ├«n mod corespunz─âtor, cu excep╚¢iile stabilite ├«n prezentul capitol....(3)...Cererea de ├«nscriere se solu╚¢ioneaz─â de tribunalul ├«n circumscrip╚¢ia c─âruia federa╚¢ia urmeaz─â s─â ├«╚Öi aib─â sediul.', NULL, 45, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1622, 7, '36', 'Articolul 36', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, '(1)...Federa╚¢ia devine persoana juridic─â din momentul ├«nscrierii sale ├«n Registrul federa╚¢iilor aflat la grefa tribunalului prev─âzut laart. 35 alin. (3)....(2)...Asocia╚¢iile sau funda╚¢iile care constituie o federa╚¢ie ├«╚Öi p─âstreaz─â propria personalitate juridic─â, inclusiv propriul patrimoniu.', NULL, 46, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1623, 7, '37', 'Articolul 37', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, '(1)...├Än cazul dizolv─ârii federa╚¢iilor, dac─â nu se prevede altfel ├«n lege sau ├«n statut, bunurile r─âmase ├«n urma lichid─ârii se transmit, ├«n cote egale, c─âtre persoanele juridice constituente....(2)...Dispozi╚¢iilealin. (1)se aplic─â prin asem─ânare ╚Öi ├«n cazul retragerii din federa╚¢ie a unei asocia╚¢ii sau funda╚¢ii....(3)...Retragerea din federa╚¢ie se poate face numai ├«n urma aprob─ârii de c─âtre cenzori sau exper╚¢i independen╚¢i a unui raport cu privire la exerci╚¢iul financiar.(la 28-07-2005, 
			Art. 37 a fost modificat de pct. 22 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
