INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1624, 7, '38', 'Articolul 38', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, '(1)...O asocia╚¢ie, funda╚¢ie sau federa╚¢ie poate fi recunoscut─â de Guvernul Rom├óniei ca fiind de utilitate public─â dac─â sunt ├«ntrunite cumulativ urm─âtoarele condi╚¢ii:a)activitatea acesteia se desf─â╚Öoar─â ├«n interes general sau al unor colectivit─â╚¢i, dup─â caz;...b)func╚¢ioneaz─â de cel pu╚¢in 3 ani ╚Öi a realizat o parte din obiectivele stabilite, f─âc├ónd dovada unei activit─â╚¢i ne├«ntrerupte prin ac╚¢iuni semnificative;...c)prezint─â un raport de activitate din care s─â rezulte desf─â╚Öurarea unei activit─â╚¢i anterioare semnificative, prin derularea unor programe ori proiecte specifice scopului s─âu, ├«nso╚¢it de situa╚¢iile financiare anuale ╚Öi de bugetele de venituri ╚Öi cheltuieli pe ultimii 3 ani anteriori datei depunerii cererii privind recunoa╚Öterea statutului de utilitate public─â;...d)de╚¢ine un patrimoniu, logistic─â, membri ╚Öi personal angajat, corespunz─âtor ├«ndeplinirii scopului propus;...d^1)valoarea activului patrimonial pe fiecare dintre cei 3 ani anteriori ├«n parte este cel pu╚¢in egal─â cu valoarea patrimoniului ini╚¢ial/de 3 ori valoarea salariului brut pe ╚¢ar─â garantat ├«n plat─â;(la 21-07-2019, 
			Alineatul (1)  din  Articolul 38 , Capitolul VI  a fost completat de Punctul 17, Articolul 53, Capitolul XI din LEGEA nr. 129 din 11 iulie 2019, publicat─â ├«n MONITORUL OFICIAL nr. 589 din 18 iulie 2019
			)...e)face dovada existen╚¢ei unor contracte de colaborare ╚Öi parteneriate cu institu╚¢ii publice sau asocia╚¢ii ori funda╚¢ii din ╚¢ar─â ╚Öi din str─âin─âtate;...f)face dovada ob╚¢inerii unor rezultate semnificative ├«n ceea ce prive╚Öte scopul propus sau prezint─â scrisori de recomandare din partea unor autorit─â╚¢i competente din ╚¢ar─â sau din str─âin─âtate, care recomand─â continuarea activit─â╚¢ii.(la 29-07-2012, 
			Alin. (1) al art. 38 a fost modificat de pct. 2 al art. unic din LEGEA nr. 145 din 23 iulie 2012, publicat─â ├«n MONITORUL OFICIAL nr. 517 din 26 iulie 2012.
			)......(2)...Abrogat.(la 29-07-2012, 
