INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1626, 7, '39', 'Articolul 39', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, '(1)...Recunoa╚Öterea unei asocia╚¢ii sau funda╚¢ii ca fiind de utilitate public─â se face prin hot─âr├óre a Guvernului. ├Än acest scop, asocia╚¢ia sau funda╚¢ia interesat─â adreseaz─â o cerere Secretariatului General al Guvernului, care o ├«nainteaz─â, ├«n termen de 15 zile, organului de specialitate al administra╚¢iei publice centrale ├«n a c─ârui sfer─â de competen╚¢─â ├«╚Öi desf─â╚Öoar─â activitatea.(la 29-07-2012, 
