INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1627, 7, '40', 'Articolul 40', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, '(1)...Autoritatea administrativ─â competent─â este obligat─â ca, ├«n termen de 60 de zile, s─â examineze cererea ╚Öi ├«ndeplinirea condi╚¢iilor prev─âzute de lege. ├Än cazul ├«n care constat─â ├«ndeplinirea acestor condi╚¢ii, autoritatea administrativ─â competent─â va propune Guvernului Rom├óniei recunoa╚Öterea. ├Än caz contrar aceasta va transmite persoanelor juridice solicitante un r─âspuns motivat, ├«n termen de 30 de zile de la data lu─ârii deciziei....(2)...├Än cel mult 90 de zile de la data depunerii cererii prev─âzute laalin. (1), precum ╚Öi a tuturor documentelor necesare lu─ârii deciziei, Guvernul Rom├óniei decide asupra propunerii de recunoa╚Ötere. Dac─â propunerea se respinge, solu╚¢ia va fi comunicat─â asocia╚¢iei sau funda╚¢iei de c─âtre autoritatea administrativ─â la care s-a ├«nregistrat cererea de recunoa╚Ötere, ├«n termen de 120 de zile de la data depunerii cererii ╚Öi a documentelor necesare lu─ârii deciziei.(la 28-07-2005, 
			Art. 40 a fost modificat de pct. 29 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
