INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1631, 7, '41', 'Articolul 41', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, 'Eliminat.-----------Art. 41^2 a fost eliminat prin respingerea ORDONAN╚ÜEI nr. 37 din 30 ianuarie 2003, publicate ├«n MONITORUL OFICIAL nr. 62 din 1 februarie 2003 de c─âtre LEGEA nr. 213 din 5 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 597 din 11 iulie 2005.', NULL, 55, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1632, 7, '42', 'Articolul 42', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, '(1)...Recunoa╚Öterea utilit─â╚¢ii publice se face pe durata nedeterminat─â....(2)...├Än cazul ├«n care asocia╚¢ia sau funda╚¢ia nu mai ├«ndepline╚Öte una ori mai multe dintre condi╚¢iile prev─âzute laart. 38care au stat la baza recunoa╚Öterii utilit─â╚¢ii publice, Guvernul va retrage, prin hot─âr├óre, statutul de recunoa╚Ötere a utilit─â╚¢ii publice, la propunerea autorit─â╚¢ii administrative competente sau a Ministerului Justi╚¢iei.(la 29-07-2012, 
			Alin. (2) al art. 42 a fost modificat de pct. 7 al art. unic din LEGEA nr. 145 din 23 iulie 2012, publicat─â ├«n MONITORUL OFICIAL nr. 517 din 26 iulie 2012.
			)...(3)...Retragerea va interveni ╚Öi ├«n situa╚¢ia ne├«ndeplinirii obliga╚¢iilor prev─âzute laart. 41 alin. (2).(la 29-07-2012, 
			Alin. (3) al art. 42 a fost modificat de pct. 7 al art. unic din LEGEA nr. 145 din 23 iulie 2012, publicat─â ├«n MONITORUL OFICIAL nr. 517 din 26 iulie 2012.
			)...(4)...├Ämprejur─ârile prev─âzute laalin. (2)╚Öi(3)pot fi sesizate autorit─â╚¢ii administrative competente, Ministerului Justi╚¢iei sau Guvernului de c─âtre orice persoan─â fizic─â sau persoan─â juridic─â interesat─â.(la 29-07-2012, 
			Alin. (4) al art. 42 a fost modificat de pct. 7 al art. unic din LEGEA nr. 145 din 23 iulie 2012, publicat─â ├«n MONITORUL OFICIAL nr. 517 din 26 iulie 2012.
			)', NULL, 56, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1633, 7, '43', 'Articolul 43', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, '(1)...├Än eviden╚¢ele contabile ale asocia╚¢iei sau funda╚¢iei se vor ├«nregistra separat bunurile achizi╚¢ionate sau edificate din bani publici. ├Än cazul dizolv─ârii asocia╚¢iei sau funda╚¢iei recunoscute ca fiind de utilitate public─â, bunurile provenite din resurse bugetare ╚Öi r─âmase ├«n urma lichid─ârii se vor repartiza, prin hot─âr├óre a Guvernului, c─âtre alte asocia╚¢ii ori funda╚¢ii cu scop similar sau c─âtre institu╚¢ii publice cu acela╚Öi obiect de activitate....(2)...├Än cazul dizolv─ârii, celelalte bunuri r─âmase ├«n patrimoniul asocia╚¢iei sau funda╚¢iei vor fi repartizate conform dispozi╚¢iilorart. 60.(la 28-07-2005, 
			Art. 43 a fost modificat de pct. 33 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)', NULL, 57, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1634, 7, '44', 'Articolul 44', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, 'Litigiile referitoare la recunoa╚Öterea utilit─â╚¢ii publice a asocia╚¢iilor ╚Öi funda╚¢iilor se solu╚¢ioneaz─â potrivit Legii contenciosului administrativ nr. 554/2004.(la 28-07-2005, 
			Art. 44 a fost modificat de pct. 34 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)', NULL, 58, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1635, 7, '45', 'Articolul 45', NULL, NULL, NULL, 'Federa╚¢ia', NULL, NULL, NULL, NULL, '(1)...Dispozi╚¢iile prezentului capitol referitoare la condi╚¢iile recunoa╚Öterii statutului de utilitate public─â, precum ╚Öi la drepturile ╚Öi obliga╚¢iile asocia╚¢iilor sau funda╚¢iilor recunoscute ca fiind de utilitate public─â se aplic─â ├«n mod corespunz─âtor ╚Öi federa╚¢iilor....(2)...O federa╚¢ie poate fi recunoscut─â de Guvernul Rom├óniei ca fiind de utilitate public─â dac─â cel pu╚¢in dou─â treimi din num─ârul asocia╚¢iilor ╚Öi funda╚¢iilor care o alc─âtuiesc sunt recunoscute ca fiind de utilitate public─â.(la 28-07-2005, 
			Art. 45 a fost modificat de pct. 35 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)', NULL, 59, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1636, 7, '46', 'Articolul 46', NULL, NULL, NULL, 'Veniturile', NULL, NULL, NULL, NULL, '(1)...Veniturile asocia╚¢iilor sau federa╚¢iilor provin din:a)cotiza╚¢iile membrilor;...b)dob├ónzile ╚Öi dividendele rezultate din plasarea sumelor disponibile, ├«n condi╚¢ii legale;...c)dividendele societ─â╚¢ilor comerciale ├«nfiin╚¢ate de asocia╚¢ii sau de federa╚¢ii;...d)venituri realizate din activit─â╚¢i economice directe;...e)dona╚¢ii, sponsoriz─âri sau legate;...f)resurse ob╚¢inute de la bugetul de stat sau de la bugetele locale;(la 28-07-2005, 
			Litera f) a alin. (1) al art. 46 a fost modificat─â de pct. 36 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)...g)alte venituri prev─âzute de lege.......(2)...Veniturile funda╚¢iilor sunt cele prev─âzute laalin. (1) lit. b)-g).', NULL, 60, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1637, 7, '47', 'Articolul 47', NULL, NULL, NULL, 'Veniturile', NULL, NULL, NULL, NULL, 'Asocia╚¢iile, funda╚¢iile ╚Öi federa╚¢iile pot ├«nfiin╚¢a societ─â╚¢i comerciale. Dividendele ob╚¢inute de asocia╚¢ii, funda╚¢ii ╚Öi federa╚¢ii din activit─â╚¢ile acestor societ─â╚¢i comerciale, dac─â nu se reinvestesc ├«n acelea╚Öi societ─â╚¢i comerciale, se folosesc ├«n mod obligatoriu pentru realizarea scopului asocia╚¢iei, funda╚¢iei sau federa╚¢iei.(la 28-07-2005, 
			Art. 47 a fost modificat de pct. 37 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)', NULL, 61, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1638, 7, '48', 'Articolul 48', NULL, NULL, NULL, 'Veniturile', NULL, NULL, NULL, NULL, 'Asocia╚¢iile, funda╚¢iile ╚Öi federa╚¢iile pot desf─â╚Öura orice alte activit─â╚¢i economice directe dac─â acestea au caracter accesoriu ╚Öi sunt ├«n str├óns─â leg─âtur─â cu scopul principal al persoanei juridice.(la 28-07-2005, 
			Art. 48 a fost modificat de pct. 38 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)', NULL, 62, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1639, 7, '48', 'Articolul 48', NULL, NULL, NULL, 'Veniturile', NULL, NULL, NULL, NULL, 'Controlul privind fundamentarea, acordarea ╚Öi justificarea sumelor primite de la bugetul general consolidat al statului ├«n condi╚¢iile prezentei ordonan╚¢e se efectueaz─â de c─âtre Ministerul Finan╚¢elor Publice prin aparatul de inspec╚¢ie economico-financiar─â, a╚Öa cum este definit la art. 3 alin. (1) din Normele metodologice privind ├«nfiin╚¢area, organizarea ╚Öi func╚¢ionarea inspec╚¢iei economico-financiare, aprobate prin Hot─âr├órea Guvernului nr. 101/2012....(la 03-09-2018, 
			Capitolul VII  a fost completat de Articolul III din ORDONAN╚ÜA nr. 18 din 29 august 2018, publicat─â ├«n MONITORUL OFICIAL nr. 755 din 31 august 2018
			)', NULL, 63, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1640, 7, '49', 'Articolul 49', NULL, NULL, NULL, 'Rela╚¢iile cu autorit─â╚¢ile publice', NULL, NULL, NULL, NULL, '(1)...Autorit─â╚¢ile administra╚¢iei publice locale vor sprijini persoanele juridice constituite ├«n temeiul prezentei ordonan╚¢e prin:a)punerea la dispozi╚¢ia acestora, ├«n func╚¢ie de posibilit─â╚¢i, a unor spa╚¢ii pentru sedii, ├«n condi╚¢iile legii;...b)atribuirea, ├«n func╚¢ie de posibilit─â╚¢i, a unor terenuri ├«n scopul ridic─ârii de construc╚¢ii necesare desf─â╚Öur─ârii activit─â╚¢ii lor.......(2)...Autorit─â╚¢ile administra╚¢iei publice locale ├«ntocmesc liste de prioritate pentru atribuirea imobilelor prev─âzute laalin. (1). Listele de prioritate se realizeaz─â pe baza unor proceduri de evaluare cuprinz├ónd ├«n mod explicit criteriile folosite. Autorit─â╚¢ile au obliga╚¢ia de a face publice aceste proceduri ├«nainte de utilizarea lor.(la 28-07-2005, 
			Alin. (2) al art. 49 a fost modificat de pct. 39 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)', NULL, 64, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1641, 7, '50', 'Articolul 50', NULL, NULL, NULL, 'Rela╚¢iile cu autorit─â╚¢ile publice', NULL, NULL, NULL, NULL, 'Autorit─â╚¢ile publice sunt obligate s─â pun─â la dispozi╚¢ia asocia╚¢iilor, funda╚¢iilor ╚Öi federa╚¢iilor informa╚¢iile de interes public, ├«n condi╚¢iile legii.', NULL, 65, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (247, 4, '64', 'Articolul 64', NULL, 'Dispozi╚¢ii generale', NULL, 'Unele dispozi╚¢ii procedurale', NULL, NULL, NULL, NULL, 'Abrogat....(la 26-11-2022, 
			Articolul 64  din  Capitolul V , Titlul II  a fost abrogat de Punctul 29, Articolul 129, Sectiunea a 10-a, Capitolul IV din LEGEA nr. 265 din 22 iulie 2022, publicat─â ├«n MONITORUL OFICIAL nr. 750 din 26 iulie 2022
			)', NULL, 68, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (248, 4, '65', 'Articolul 65', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, '(1)...├Än lips─â de stipula╚¢ie contrar─â, bunurile constituite ca aport ├«n societate devin proprietatea acesteia din momentul ├«nmatricul─ârii ei ├«n registrul comer╚¢ului....(2)...Asociatul care ├«nt├órzie s─â depun─â aportul social este r─âspunz─âtor de daunele pricinuite, iar dac─â aportul a fost stipulat ├«n numerar este obligat ╚Öi la plata dob├ónzilor legale din ziua ├«n care trebuia s─â fac─â v─ârs─âm├óntul.', NULL, 69, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (249, 4, '66', 'Articolul 66', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, '(1)...Pe durata societ─â╚¢ii, creditorii asociatului pot s─â-╚Öi exercite drepturile lor numai asupra p─âr╚¢ii din beneficiile cuvenite asociatului dup─â bilan╚¢ul contabil, iar dup─â dizolvarea societ─â╚¢ii, asupra p─âr╚¢ii ce i s-ar cuveni prin lichidare....(2)...Creditorii prev─âzu╚¢i laalin. (1)pot totu╚Öi popri, ├«n timpul duratei societ─â╚¢ii, p─âr╚¢ile ce s-ar cuveni asocia╚¢ilor prin lichidare sau pot sechestra ╚Öi vinde ac╚¢iunile ori p─âr╚¢ile sociale ale debitorului lor.(la 16-07-2015, 
			Alin. (2) al art. 66 a fost modificat de pct. 1 al art. II din LEGEA nr. 152 din 18 iunie 2015, publicat─â ├«n MONITORUL OFICIAL nr. 519 din 13 iulie 2015.
			)...(3)...Ipoteca legal constituit─â asupra ac╚¢iunilor sau p─âr╚¢ilor sociale poate fi executat─â potrivit legii. Administratorii/Membrii directoratului sunt obliga╚¢i s─â pun─â la dispozi╚¢ia creditorului ipotecar sau a organului de executare, la cererea acestora, situa╚¢iile financiare ╚Öi orice alte ├«nscrisuri ori informa╚¢ii necesare evalu─ârii ac╚¢iunilor sau p─âr╚¢ilor sociale, precum ╚Öi s─â faciliteze preluarea acestora.(la 16-07-2015, 
			Alin. (3) al art. 66 a fost introdus de pct. 2 al art. II din LEGEA nr. 152 din 18 iunie 2015, publicat─â ├«n MONITORUL OFICIAL nr. 519 din 13 iulie 2015.
			)', NULL, 70, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (250, 4, '66', 'Articolul 66', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, 'Publicitatea, prin registrul comer╚¢ului, a popririi ╚Öi a sechestrului prev─âzute laart. 66 alin. (2)se realizeaz─â la cererea organului de executare, nefiind supus─â prevederilor art. 7^1 din Ordonan╚¢a de urgen╚¢─â a Guvernului nr. 116/2009 pentru instituirea unor m─âsuri privind activitatea de ├«nregistrare ├«n registrul comer╚¢ului, aprobat─â cu modific─âri ╚Öi complet─âri prin Legea nr. 84/2010, cu modific─ârile ulterioare.(la 16-07-2015, 
			Art. 66^1 a fost introdus de pct. 3 al art. II din LEGEA nr. 152 din 18 iunie 2015, publicat─â ├«n MONITORUL OFICIAL nr. 519 din 13 iulie 2015.
			)', NULL, 71, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (251, 4, '67', 'Articolul 67', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, '(1)...Cota-parte din profit ce se pl─âte╚Öte fiec─ârui asociat constituie dividend....(2)...Dividendele se distribuie asocia╚¢ilor propor╚¢ional cu cota de participare la capitalul social v─ârsat, op╚¢ional trimestrial pe baza situa╚¢iilor financiare interimare ╚Öi anual, dup─â regularizarea efectuat─â prin situa╚¢iile financiare anuale, dac─â prin actul constitutiv nu se prevede altfel. Acestea se pot pl─âti ├«n mod op╚¢ional trimestrial ├«n termenul stabilit de adunarea general─â a asocia╚¢ilor sau, dup─â caz, prin legile speciale, regularizarea diferen╚¢elor rezultate din distribuirea dividendelor ├«n timpul anului urm├ónd s─â se fac─â prin situa╚¢iile financiare anuale. Plata diferen╚¢elor rezultate din regularizare se face ├«n termen de 60 de zile de la data aprob─ârii situa╚¢iilor financiare anuale aferente exerci╚¢iului financiar ├«ncheiat. ├Än caz contrar, societatea datoreaz─â, dup─â acest termen, dob├ónd─â penalizatoare calculat─â conform art. 3 din Ordonan╚¢a Guvernului nr. 13/2011 privind dob├ónda legal─â remuneratorie ╚Öi penalizatoare pentru obliga╚¢ii b─âne╚Öti, precum ╚Öi pentru reglementarea unor m─âsuri financiar-fiscale ├«n domeniul bancar, aprobat─â prin Legea nr. 43/2012, cu complet─ârile ulterioare, dac─â prin actul constitutiv sau prin hot─âr├órea adun─ârii generale a ac╚¢ionarilor care a aprobat situa╚¢iile financiare aferente exerci╚¢iului financiar ├«ncheiat nu sa stabilit o dob├ónd─â mai mare.(la 15-07-2018, 
			Alineatul (2) din Articolul 67 , Capitolul I , Titlul III  a fost modificat de Punctul 1, Articolul II din LEGEA nr. 163 din 10 iulie 2018, publicat─â ├«n MONITORUL OFICIAL nr. 595 din 12 iulie 2018
			)...(2^1)...├Än cazul distribuirii par╚¢iale a dividendelor ├«ntre asocia╚¢i sau ac╚¢ionari ├«n cursul anului financiar, situa╚¢iile financiare anuale vor eviden╚¢ia dividendele atribuite par╚¢ial ╚Öi vor regulariza diferen╚¢ele rezultate, ├«n mod corespunz─âtor.(la 15-07-2018, 
			Articolul 67  din  Capitolul I , Titlul III  a fost completat de Punctul 2, Articolul II din LEGEA nr. 163 din 10 iulie 2018, publicat─â ├«n MONITORUL OFICIAL nr. 595 din 12 iulie 2018
			)...(2^2)...├Än cazul ├«n care asocia╚¢ii sau ac╚¢ionarii datoreaz─â restituiri de dividende, ├«n urma regulariz─ârii operate ├«n situa╚¢iile financiare anuale, acestea se achit─â societ─â╚¢ii ├«n termen de 60 de zile de la data aprob─ârii situa╚¢iilor financiare anuale. ├Än caz contrar, asocia╚¢ii sau ac╚¢ionarii datoreaz─â, dup─â acest termen, dob├ónd─â penalizatoare calculat─â conform art. 3 din Ordonan╚¢a Guvernului nr. 13/2011, aprobat─â prin Legea nr. 43/2012, cu complet─ârile ulterioare, dac─â prin actul constitutiv sau prin hot─âr├órea adun─ârii generale a ac╚¢ionarilor care a aprobat situa╚¢iile financiare aferente exerci╚¢iului financiar ├«ncheiat nu s-a stabilit o dob├ónd─â mai mare.(la 15-07-2018, 
			Articolul 67  din  Capitolul I , Titlul III  a fost completat de Punctul 2, Articolul II din LEGEA nr. 163 din 10 iulie 2018, publicat─â ├«n MONITORUL OFICIAL nr. 595 din 12 iulie 2018
			)...(3)...Nu se vor putea distribui dividende dec├ót din profituri determinate potrivit legii....(4)...Dividendele pl─âtite contrar dispozi╚¢iiloralin. (2),(2^1),(2^2)╚Öi(3)se restituie, dac─â societatea dovede╚Öte c─â asocia╚¢ii au cunoscut neregularitatea distribuirii sau, ├«n ├«mprejur─ârile existente, trebuia s─â o cunoasc─â.(la 15-07-2018, 
			Alineatul (4) din Articolul 67 , Capitolul I , Titlul III  a fost modificat de Punctul 3, Articolul II din LEGEA nr. 163 din 10 iulie 2018, publicat─â ├«n MONITORUL OFICIAL nr. 595 din 12 iulie 2018
			)...(5)...Dreptul la ac╚¢iunea de restituire a dividendelor, pl─âtite contrar prevederiloralin. (2)╚Öi(3), se prescrie ├«n termen de 3 ani de la data distribuirii lor.(la 01-12-2006, 
			Alin. (5) a art. 67 a fost modificat de pct. 36 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...(6)...Dividendele care se cuvin dup─â data transmiterii ac╚¢iunilor apar╚¢in cesionarului, ├«n afar─â de cazul ├«n care p─âr╚¢ile au convenit altfel.', NULL, 72, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (252, 4, '68', 'Articolul 68', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, 'Aportul asocia╚¢ilor la capitalul social nu este purt─âtor de dob├ónzi.', NULL, 73, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (253, 4, '69', 'Articolul 69', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, 'Dac─â se constat─â o pierdere a activului net, capitalul social subscris va trebui re├«ntregit sau redus ├«nainte de a se putea face vreo repartizare sau distribuire de profit.(la 30-04-2008, 
			Art. 69 a fost modificat de pct. 1 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 52 din 21 aprilie 2008, publicat─â ├«n MONITORUL OFICIAL nr. 333 din 30 aprilie 2008.
			)', NULL, 74, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (254, 4, '70', 'Articolul 70', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, '(1)...Administratorii pot face toate opera╚¢iunile cerute pentru aducerea la ├«ndeplinire a obiectului de activitate al societ─â╚¢ii, afar─â de restric╚¢iile ar─âtate ├«n actul constitutiv....(2)...Ei sunt obliga╚¢i s─â ia parte la toate adun─ârile societ─â╚¢ii, la consiliile de administra╚¢ie ╚Öi la organele de conducere similare acestora.', NULL, 75, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (255, 4, '70', 'Articolul 70', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, 'Actele de dispozi╚¢ie asupra bunurilor unei societ─â╚¢i pot fi ├«ncheiate ├«n temeiul puterilor conferite reprezentan╚¢ilor legali ai societ─â╚¢ii, dup─â caz, prin lege, actul constitutiv sau hot─âr├órile organelor statutare ale societ─â╚¢ii adoptate ├«n conformitate cu prevederile prezentei legi ╚Öi ale actului constitutiv al societ─â╚¢ii, nefiind necesar─â o procur─â special─â ╚Öi ├«n form─â autentic─â ├«n acest scop, chiar dac─â actele de dispozi╚¢ie trebuie ├«ncheiate ├«n form─â autentic─â.(la 02-06-2012, 
			Art. 70^1 a fost modificat de pct. 31 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea sintagmei "societate comercial─â" cu termenul "societate".
			)', NULL, 76, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (256, 4, '71', 'Articolul 71', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, '(1)...Administratorii care au dreptul de a reprezenta societatea nu ├«l pot transmite dec├ót dac─â aceast─â facultate li s-a acordat ├«n mod expres....(2)...├Än cazul ├«nc─âlc─ârii prevederiloralin. (1), societatea poate pretinde de la cel substituit beneficiile rezultate din opera╚¢iune....(3)...Administratorul care, f─âr─â drept, ├«╚Öi substituie alt─â persoan─â r─âspunde solidar cu aceasta pentru eventualele pagube produse societ─â╚¢ii.', NULL, 77, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
