INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1642, 7, '51', 'Articolul 51', NULL, NULL, NULL, 'Rela╚¢iile cu autorit─â╚¢ile publice', NULL, NULL, NULL, NULL, '(1)...├Än cadrul Camerelor Parlamentului, Administra╚¢iei Preziden╚¢iale, aparatului de lucru al Guvernului, institu╚¢iei Avocatul Poporului, autorit─â╚¢ilor administrative autonome, ministerelor, al celorlalte organe de specialitate ale administra╚¢iei publice centrale ╚Öi autorit─â╚¢ilor administra╚¢iei publice locale func╚¢ioneaz─â structuri pentru rela╚¢ia cu mediul asociativ. Acolo unde asemenea structuri lipsesc, ele se vor constitui....(2)...Autorit─â╚¢ile publice men╚¢ionate laalin. (1)se vor consulta cu reprezentan╚¢ii asocia╚¢iilor ╚Öi funda╚¢iilor care ├«╚Öi desf─â╚Öoar─â activitatea ├«n sfera lor de competen╚¢─â, ├«n vederea stabilirii unor programe sau activit─â╚¢i comune.(la 28-07-2005, 
			Art. 51 a fost modificat de pct. 40 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)', NULL, 66, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1643, 7, '52', 'Articolul 52', NULL, NULL, NULL, 'Rela╚¢iile cu autorit─â╚¢ile publice', NULL, NULL, NULL, NULL, '(1)...Dup─â constituirea lor, asocia╚¢iile, funda╚¢iile ╚Öi federa╚¢iile interesate vor solicita autorit─â╚¢ilor administrative autonome, ministerelor, celorlalte organe de specialitate ale administra╚¢iei publice centrale ╚Öi autorit─â╚¢ilor administra╚¢iei publice locale s─â fie luate ├«n evidenta acestora, ├«n func╚¢ie de domeniul ├«n care activeaz─â....(2)...Autorit─â╚¢ile publice prev─âzute laalin. (1)sunt obligate s─â ╚¢in─â evidenta asocia╚¢iilor ╚Öi funda╚¢iilor care li s-au adresat ├«n acest scop....(3)...Conflictele de competen╚¢─â ap─ârute ├«ntre autorit─â╚¢ile publice prev─âzute laalin. (1)privind luarea ├«n evidenta se solu╚¢ioneaz─â, la cererea oric─âreia dintre p─âr╚¢i, de c─âtre Secretariatul General al Guvernului ├«n termen de 5 zile de la data sesiz─ârii. Dispozi╚¢iileart. 39 alin. (3)se aplica ├«n mod corespunz─âtor....(4)...├Än toate cazurile, Ministerul Justi╚¢iei va comunica, spre informare, autorit─â╚¢iilor publice competente prev─âzute laalin. (1)copii de pe hot─âr├órile judec─âtore╚Öti r─âmase irevocabile, precum ╚Öi de pe ├«nscrisurile doveditoare, ├«n termen de 5 zile de la primirea documentelor prev─âzute laart. 74.', NULL, 67, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1644, 7, '53', 'Articolul 53', NULL, NULL, NULL, 'Rela╚¢iile cu autorit─â╚¢ile publice', NULL, NULL, NULL, NULL, 'Litigiile n─âscute ├«n leg─âtur─â cu aplicarea dispozi╚¢iilor prezentului capitol se solu╚¢ioneaz─â ├«n conformitate cu Legea nr. 554/2004.(la 28-07-2005, 
			Art. 53 a fost modificat de pct. 41 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
