INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1645, 7, '54', 'Articolul 54', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Dizolvarea', NULL, NULL, '(1)...Asocia╚¢iile ╚Öi federa╚¢iile se dizolv─â:a)de drept;...b)prin hot─âr├órea judec─âtoriei sau a tribunalului, dup─â caz;...c)prin hot─âr├órea adun─ârii generale.......(2)...Funda╚¢iile se dizolv─â:a)de drept;...b)prin hot─âr├órea judec─âtoriei....', NULL, 69, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1646, 7, '55', 'Articolul 55', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Dizolvarea', NULL, NULL, '(1)...Asocia╚¢ia se dizolv─â de drept prin:a)├«mplinirea duratei pentru care a fost constituit─â;...b)realizarea sau, dup─â caz, imposibilitatea realiz─ârii scopului pentru care a fost constituit─â, dac─â ├«n termen de 3 luni de la constatarea unui astfel de fapt nu se produce schimbarea acestui scop;...c)imposibilitatea constituirii adun─ârii generale sau a consiliului director ├«n conformitate cu statutul asocia╚¢iei, dac─â aceast─â situa╚¢ie dureaz─â mai mult de un an de la data la care, potrivit statutului, adunarea general─â sau, dup─â caz, consiliul director trebuia s─â se constituie;(la 28-07-2005, 
			Litera c) a alin. (1) al art. 55 a fost modificat─â de pct. 42 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)...d)reducerea num─ârului de asocia╚¢i sub limita fixat─â de lege, dac─â acesta nu a fost complinit timp de 3 luni.......(2)...Constatarea dizolv─ârii se realizeaz─â prin hot─âr├órea judec─âtoriei ├«n a carei circumscrip╚¢ie se afla sediul asocia╚¢iei, la cererea oric─ârei persoane interesate.', NULL, 70, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1647, 7, '56', 'Articolul 56', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Dizolvarea', NULL, NULL, '(1)...Asocia╚¢ia se dizolv─â, prin hot─âr├óre judec─âtoreasc─â, la cererea Ministerului Public sau a oric─ârei alte persoane interesate:(la 21-07-2019, 
			Partea introductiv─â a Alineatului (1) din Articolul 56 , Sectiunea 1 , Capitolul IX  a fost modificat─â de Punctul 19, Articolul 53, Capitolul XI din LEGEA nr. 129 din 11 iulie 2019, publicat─â ├«n MONITORUL OFICIAL nr. 589 din 18 iulie 2019
			)...a)...c├ónd scopul sau activitatea asocia╚¢iei a devenit ilicit─â sau contrar─â ordinii publice;...b)...c├ónd realizarea scopului este urm─ârit─â prin mijloace ilicite sau contrare ordinii publice;...c)...c├ónd asocia╚¢ia urm─âre╚Öte un alt scop dec├ót cel pentru care s-a constituit;...d)...c├ónd asocia╚¢ia a devenit insolvabil─â;...e)...├«n cazul prev─âzut laart. 14....(1^1)...├Än cazul necomunic─ârii datelor de identificare ale beneficiarului real ├«n termenul prev─âzut laart. 34^5 alin. (7), asocia╚¢ia se dizolv─â, prin hot─âr├óre judec─âtoreasc─â, la cererea Ministerului Public sau a Oficiului Na╚¢ional de Prevenire ╚Öi Combatere a Sp─âl─ârii Banilor.(la 21-07-2019, 
			Articolul 56  din  Sectiunea 1 , Capitolul IX  a fost completat de Punctul 20, Articolul 53, Capitolul XI din LEGEA nr. 129 din 11 iulie 2019, publicat─â ├«n MONITORUL OFICIAL nr. 589 din 18 iulie 2019
			)...(2)...Instan╚¢a competent─â s─â hot─ârasc─â dizolvarea este judec─âtoria ├«n circumscrip╚¢ia c─âreia asocia╚¢ia ├«╚Öi are sediul.(la 28-07-2005, 
			Alin. (2) al art. 56 a fost modificat de pct. 43 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)...(3)...Cauza de dizolvare prev─âzut─â laalin. (1^1)poate fi ├«nl─âturat─â ├«nainte de a se pune concluzii ├«n fond.(la 21-07-2019, 
			Articolul 56  din  Sectiunea 1 , Capitolul IX  a fost completat de Punctul 21, Articolul 53, Capitolul XI din LEGEA nr. 129 din 11 iulie 2019, publicat─â ├«n MONITORUL OFICIAL nr. 589 din 18 iulie 2019
			)', NULL, 71, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1648, 7, '57', 'Articolul 57', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Dizolvarea', NULL, NULL, 'Asocia╚¢ia se poate dizolv─â ╚Öi prin hot─âr├órea adun─ârii generale. ├Än termen de 15 zile de la data ╚Öedin╚¢ei de dizolvare, hot─âr├órea adun─ârii generale se depune la judec─âtoria ├«n a c─ârei circumscrip╚¢ie ├«╚Öi are sediul, pentru a fi ├«nscris─â ├«n Registrul asocia╚¢iilor ╚Öi funda╚¢iilor.(la 02-06-2012, 
			Art. 57 a fost modificat de pct. 9 al art. 35 din LEGEA nr. 76 din 24 mai 2012 publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea unei sintagme.
			)', NULL, 72, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1649, 7, '58', 'Articolul 58', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Dizolvarea', NULL, NULL, 'Funda╚¢ia se dizolv─â de drept ├«n cazurile prev─âzute laart. 55 alin. (1) lit. a)╚Öib), precum ╚Öi ├«n situa╚¢ia imposibilit─â╚¢ii constituirii consiliului director ├«n conformitate cu statutul funda╚¢iei, dac─â aceasta situa╚¢ie dureaz─â mai mult de un an de la data la care, potrivit statutului, consiliul director trebuia constituit. Dispozi╚¢iileart. 55 alin. (2)se aplica ├«n mod corespunz─âtor.', NULL, 73, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1650, 7, '59', 'Articolul 59', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Dizolvarea', NULL, NULL, 'Dizolvarea funda╚¢iei prin hot─âr├óre judec─âtoreasc─â se face ├«n condi╚¢iileart. 56, care se aplica ├«n mod corespunz─âtor, precum ╚Öi ├«n cazul nerespect─ârii dispozi╚¢iilorart. 15 alin. (3).', NULL, 74, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1651, 7, '60', 'Articolul 60', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Dizolvarea', NULL, NULL, '(1)...├Än cazul dizolv─ârii asocia╚¢iei sau funda╚¢iei, bunurile r─âmase ├«n urma lichid─ârii nu se pot transmite c─âtre persoane fizice....(2)...Aceste bunuri pot fi transmise c─âtre persoane juridice de drept privat sau de drept public cu scop identic sau asem─ân─âtor, printr-o procedura stabilit─â ├«n statutul asocia╚¢iei sau al funda╚¢iei....(3)...Dac─â ├«n termen de 6 luni de la terminarea lichid─ârii lichidatorii nu au reu╚Öit s─â transmit─â bunurile ├«n condi╚¢iilealin. (2), precum ╚Öi ├«n cazul ├«n care statutul asocia╚¢iei sau al funda╚¢iei nu prevede o procedura de transmitere a bunurilor ori dac─â prevederea este contrar─â legii sau ordinii publice, bunurile r─âmase dup─â lichidare vor fi atribuite de instan╚¢a competent─â unei persoane juridice cu scop identic sau asem─ân─âtor....(4)...├Än cazul ├«n care asocia╚¢ia sau funda╚¢ia a fost dizolvat─â pentru motivele prev─âzute laart. 56 alin. (1) lit. a)-c)sauart. 56 alin. (1^1), bunurile r─âmase dup─â lichidare vor fi preluate de c─âtre stat, prin Ministerul Finan╚¢elor Publice, sau, dup─â caz, de comuna sau ora╚Öul ├«n a c─ârui raz─â teritorial─â asocia╚¢ia sau funda╚¢ia ├«╚Öi avea sediul, dac─â aceasta din urm─â era de interes local.(la 21-07-2019, 
			Alineatul (4) din Articolul 60 , Sectiunea 1 , Capitolul IX  a fost modificat de Punctul 22, Articolul 53, Capitolul XI din LEGEA nr. 129 din 11 iulie 2019, publicat─â ├«n MONITORUL OFICIAL nr. 589 din 18 iulie 2019
			)...(5)...Data transmiterii bunurilor este cea a ├«ntocmirii procesului-verbal de predare-preluare, dac─â prin acesta nu s-a stabilit o data ulterioar─â.', NULL, 75, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1652, 7, '61', 'Articolul 61', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, '(1)...├Än cazurile de dizolvare prev─âzute deart. 55,56,58╚Öi59, lichidatorii vor fi numi╚¢i prin ├«ns─â╚Öi hot─âr├órea judec─âtoreasc─â....(2)...├Än cazul dizolv─ârii prev─âzute deart. 57, lichidatorii vor fi numi╚¢i de c─âtre adunarea general─â, sub sanc╚¢iunea lipsirii de efecte juridice a hot─âr├órii de dizolvare....(3)...├Än toate cazurile, mandatul consiliului director ├«nceteaz─â o data cu numirea lichidatorilor....(4)...Lichidatorii vor putea fi persoane fizice sau persoane juridice, autorizate ├«n condi╚¢iile legii.(la 28-07-2005, 
			Alin. (4) al art. 61 a fost modificat de pct. 45 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)', NULL, 76, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1653, 7, '62', 'Articolul 62', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, '(1)...Imediat dup─â intrarea lor ├«n func╚¢ie, lichidatorii vor face inventarul ╚Öi vor ├«ncheia un bilan╚¢ care s─â constate situa╚¢ia exact─â a activului ╚Öi pasivului asocia╚¢iei sau ale funda╚¢iei....(2)...Lichidatorii sunt obliga╚¢i s─â primeasc─â ╚Öi s─â p─âstreze registrele ╚Öi orice alte acte ale asocia╚¢iei sau funda╚¢iei. De asemenea, ei vor tine un registru cu toate opera╚¢iunile lichid─ârii ├«n ordinea datei acestora....(3)...Lichidatorii ├«╚Öi ├«ndeplinesc mandatul sub controlul cenzorilor.', NULL, 77, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1654, 7, '63', 'Articolul 63', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, '(1)...Lichidatorii sunt obliga╚¢i s─â continue opera╚¢iunile juridice ├«n curs, s─â ├«ncaseze crean╚¢ele, s─â pl─âteasc─â creditorii ╚Öi, dac─â numerarul este insuficient, s─â transforme ╚Öi restul activului ├«n bani, proced├ónd la v├ónzarea prin licita╚¢ie public─â a bunurilor mobile ╚Öi imobile....(2)...Lichidatorii pot realiza numai acele opera╚¢iuni noi care sunt necesare finaliz─ârii celor aflate ├«n curs.', NULL, 78, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1655, 7, '64', 'Articolul 64', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, '(1)...Suma cuvenit─â creditorului cunoscut care refuza s─â primeasc─â plata crean╚¢ei sale se va consemna ├«n contul sau....(2)...Dac─â plata crean╚¢ei nu se poate face imediat sau atunci c├ónd crean╚¢a este contestat─â, lichidarea nu se va declara terminat─â ├«nainte de a se garanta creditorii.', NULL, 79, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1656, 7, '65', 'Articolul 65', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, '├Än orice caz, lichidatorii nu pot ├«ncheia opera╚¢iunile ╚Öi nu pot remite celor ├«n drept contul gestiunii dec├ót dup─â expirarea unui termen de 6 luni de la publicarea dizolv─ârii asocia╚¢iei sau funda╚¢iei.', NULL, 80, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1657, 7, '66', 'Articolul 66', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, 'Lichidatorii r─âspund solidar pentru daunele cauzate creditorilor din culpa lor.', NULL, 81, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1658, 7, '67', 'Articolul 67', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, 'At├ót fa╚¢─â de asocia╚¢ie sau funda╚¢ie, c├ót ╚Öi fa╚¢─â de asocia╚¢i sau, dup─â caz, fondatori, lichidatorii sunt supu╚Öi regulilor mandatului.', NULL, 82, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1659, 7, '68', 'Articolul 68', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, '(1)...Dup─â terminarea lichid─ârii, lichidatorii sunt obliga╚¢i ca ├«n termen de doua luni s─â depun─â bilan╚¢ul, registrul jurnal ╚Öi un memorandum, declar├ónd opera╚¢iunile de lichidare la Registrul asocia╚¢iilor ╚Öi funda╚¢iilor al judec─âtoriei ├«n a carei circumscrip╚¢ie ├«╚Öi are sediul asocia╚¢ia sau funda╚¢ia.(la 02-06-2012, 
			Alin. (1) al art. 68 a fost modificat de pct. 9 al art. 35 din LEGEA nr. 76 din 24 mai 2012 publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea unei sintagme.
			)...(2)...Lichidatorii sunt obliga╚¢i s─â ├«ndeplineasc─â toate procedurile pentru publicarea lichid─ârii ╚Öi radierea asocia╚¢iei sau funda╚¢iei din Registrul asocia╚¢iilor ╚Öi funda╚¢iilor....(3)...Publicarea lichid─ârii se face prin afi╚Öarea la usa instan╚¢ei ├«n a carei circumscrip╚¢ie ├«╚Öi are sediul persoana juridic─â, ├«n termen de doua luni de la terminarea lichid─ârii.(la 02-06-2012, 
			Alin. (3) al art. 68 a fost modificat de pct. 9 al art. 35 din LEGEA nr. 76 din 24 mai 2012 publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea unei sintagme.
			)', NULL, 83, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1660, 7, '69', 'Articolul 69', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, 'Dac─â ├«n termen de 30 de zile libere de la depunerea bilan╚¢ului nu se ├«nregistreaz─â nici o contesta╚¢ie, bilan╚¢ul se considera definitiv aprobat ╚Öi lichidatorii, cu autorizarea judec─âtoriei, vor remite celor ├«n drept bunurile ╚Öi sumele r─âmase de la lichidare, ├«mpreun─â cu toate registrele ╚Öi actele asocia╚¢iei sau funda╚¢iei ╚Öi ale lichid─ârii. Numai dup─â aceasta lichidatorii vor fi considera╚¢i desc─ârca╚¢i ╚Öi li se va elibera, ├«n acest scop, un act constatator.', NULL, 84, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1661, 7, '70', 'Articolul 70', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, '(1)...Contesta╚¢iile la bilan╚¢ul lichidatorilor se pot formula de orice persoana interesat─â la judec─âtoria ├«n a carei circumscrip╚¢ie se afla sediul persoanei juridice lichidate.(la 02-06-2012, 
			Alin. (1) al art. 70 a fost modificat de pct. 9 al art. 35 din LEGEA nr. 76 din 24 mai 2012 publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea unei sintagme.
			)...(2)...Toate contesta╚¢iile se solu╚¢ioneaz─â printr-o singur─â hot─âr├óre. Sentin╚¢a pronun╚¢at─â de judec─âtorie este executorie ╚Öi este supus─â numai apelului.(la 02-06-2012, 
			Alin. (2) al art. 70 a fost modificat de pct. 6 al art. 35 din LEGEA nr. 76 din 24 mai 2012 publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012.
			)...(3)...Dup─â terminarea lichid─ârii, lichidatorii trebuie s─â ceara radierea asocia╚¢iei sau funda╚¢iei din Registrul asocia╚¢iilor ╚Öi funda╚¢iilor.', NULL, 85, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1662, 7, '71', 'Articolul 71', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, '(1)...Asocia╚¢ia sau funda╚¢ia ├«nceteaz─â a fiin╚¢a la data radierii din Registrul asocia╚¢iilor ╚Öi funda╚¢iilor.(la 28-07-2005, 
			Alin. (1) al art. 71 a fost modificat de pct. 46 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)...(2)...Radierea se face ├«n baza actului constatator eliberat lichidatorilor, ├«n condi╚¢iile prev─âzute laart. 69, prin care se atesta desc─ârcarea acestora de obliga╚¢iile asumate.', NULL, 86, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1663, 7, '72', 'Articolul 72', NULL, NULL, NULL, 'Dizolvarea ╚Öi lichidarea', NULL, 'Lichidarea', NULL, NULL, 'Dispozi╚¢iile prezentului capitol referitoare la dizolvarea ╚Öi lichidarea asocia╚¢iilor ╚Öi funda╚¢iilor se aplica, ├«n mod corespunz─âtor, ╚Öi ├«n privin╚¢a dizolv─ârii ╚Öi lichid─ârii federa╚¢iilor. Instan╚¢a competent─â este tribunalul ├«n a c─ârui circumscrip╚¢ie se afla sediul federa╚¢iei supuse dizolv─ârii ╚Öi lichid─ârii.(la 02-06-2012, 
			Art. 72 a fost modificat de pct. 9 al art. 35 din LEGEA nr. 76 din 24 mai 2012 publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea unei sintagme.
			)', NULL, 87, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1664, 7, '73', 'Articolul 73', NULL, NULL, NULL, 'Registrul na╚¢ional al persoanelor juridice f─âr─â scop patrimonial', NULL, NULL, NULL, NULL, '(1)...Se constituie Registrul na╚¢ional al persoanelor juridice f─âr─â scop patrimonial - asocia╚¢ii, funda╚¢ii ╚Öi federa╚¢ii -, denumit ├«n continuare Registrul na╚¢ional, ├«n scopul eviden╚¢ei centralizate a acestora....(2)...Registrul na╚¢ional al persoanelor juridice f─âr─â scop patrimonial are patru sec╚¢iuni distincte pentru:a)asocia╚¢ii;...b)organiza╚¢iile cet─â╚¢enilor apar╚¢in├ónd minorit─â╚¢ilor na╚¢ionale;...c)funda╚¢ii;...d)federa╚¢ii.......(la 05-12-2020, 
			Articolul 73 din Capitolul X  a fost modificat de Punctul 31, Articolul I din LEGEA nr. 276 din 27 noiembrie 2020, publicat─â ├«n MONITORUL OFICIAL nr. 1166 din 02 decembrie 2020
			)', NULL, 88, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1665, 7, '74', 'Articolul 74', NULL, NULL, NULL, 'Registrul na╚¢ional al persoanelor juridice f─âr─â scop patrimonial', NULL, NULL, NULL, NULL, '(1)...├Än scopul constituirii ╚Öi func╚¢ion─ârii Registrului na╚¢ional, instan╚¢ele judec─âtore╚Öti sunt obligate ca, din oficiu, s─â comunice electronic Ministerului Justi╚¢iei copii de pe hot─âr├órile judec─âtore╚Öti r─âmase definitive privind constituirea, modificarea ╚Öi ├«ncetarea oric─ârei asocia╚¢ii, funda╚¢ii sau federa╚¢ii, precum ╚Öi de pe ├«nscrisurile doveditoare, ├«n termen de 3 zile de la data r─âm├ónerii definitive a fiec─ârei hot─âr├óri judec─âtore╚Öti. ├Än acela╚Öi termen, instan╚¢ele judec─âtore╚Öti vor efectua transferul ├«nscrierilor cuprinse ├«n registrele speciale ╚¢inute de acestea c─âtre serverul central instalat la sediul Ministerului Justi╚¢iei.(la 21-07-2019, 
			Alineatul (1) din Articolul 74 , Capitolul X  a fost modificat de Punctul 24, Articolul 53, Capitolul XI din LEGEA nr. 129 din 11 iulie 2019, publicat─â ├«n MONITORUL OFICIAL nr. 589 din 18 iulie 2019
			)...(2)...Asocia╚¢iile ╚Öi funda╚¢iile recunoscute ca fiind de utilitate public─â sunt obligate s─â comunice Ministerului Justi╚¢iei, ├«n extras, copii de pe rapoartele de activitate ╚Öi situa╚¢iile financiare anuale, ├«nso╚¢ite de dovada eliberat─â de Regia Autonom─â ┬½Monitorul Oficial┬╗ c─â s-a solicitat publicarea acestora ╚Öi ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a.(la 28-07-2005, 
			Alin. (2) al art. 74 a fost modificat de pct. 47 al art. I din LEGEA nr. 246 din 18 iulie 2005, publicat─â ├«n MONITORUL OFICIAL nr. 656 din 25 iulie 2005.
			)', NULL, 89, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1666, 7, '75', 'Articolul 75', NULL, NULL, NULL, 'Registrul na╚¢ional al persoanelor juridice f─âr─â scop patrimonial', NULL, NULL, NULL, NULL, '(1)...Registrul na╚¢ional al persoanelor juridice f─âr─â scop patrimonial este public, cu excep╚¢ia datelor care se supun reglement─ârilor privind protec╚¢ia datelor cu caracter personal ╚Öi a datelor din eviden╚¢a privind beneficiarii reali ai asocia╚¢iilor ╚Öi funda╚¢iilor.(la 21-07-2019, 
			Alineatul (1) din Articolul 75 , Capitolul X  a fost modificat de Punctul 25, Articolul 53, Capitolul XI din LEGEA nr. 129 din 11 iulie 2019, publicat─â ├«n MONITORUL OFICIAL nr. 589 din 18 iulie 2019
			)...(2)...Ministerul Justi╚¢iei elibereaz─â, pe cheltuiala persoanei solicitante, copii certificate de pe ├«nregistr─ârile efectuate ├«n Registrul na╚¢ional ╚Öi de pe ├«nscrisurile doveditoare, cu excep╚¢ia datelor care se supun reglement─ârilor privind protec╚¢ia datelor cu caracter personal.(la 21-07-2019, 
			Alineatul (2) din Articolul 75 , Capitolul X  a fost modificat de Punctul 25, Articolul 53, Capitolul XI din LEGEA nr. 129 din 11 iulie 2019, publicat─â ├«n MONITORUL OFICIAL nr. 589 din 18 iulie 2019
			)...(3)...├Änscrisurile prev─âzute laalin. (2)pot fi cerute ╚Öi eliberate ╚Öi prin corespondenta....(4)...Datele din Registrul na╚¢ional pot fi redate ╚Öi arhivate ╚Öi sub forma de ├«nregistr─âri pe microfilme ╚Öi pe suporturi accesibile echipamentelor de prelucrare automat─â a datelor.', NULL, 90, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1667, 7, '76', 'Articolul 76', NULL, NULL, NULL, 'Registrul na╚¢ional al persoanelor juridice f─âr─â scop patrimonial', NULL, NULL, NULL, NULL, '(1)...Persoanele juridice str─âine f─âr─â scop patrimonial pot fi recunoscute ├«n Rom├ónia, sub condi╚¢ia reciprocit─â╚¢ii, pe baza aprob─ârii prealabile a Guvernului, prin ├«nscrierea ├«n Registrul asocia╚¢iilor ╚Öi funda╚¢iilor de la grefa Tribunalului Bucure╚Öti, dac─â sunt valabil constituite ├«n statul a c─ârui na╚¢ionalitate o au, iar scopurile lor statutare nu contravin ordinii publice din Rom├ónia....(2)...├Än acest scop, reprezentan╚¢ii persoanelor juridice str─âine trebuie s─â ata╚Öeze la cererea de ├«nscriere urm─âtoarele documente, ├«n copii autentificate ╚Öi traduceri legalizate:a)actul de constituire ├«n statul a c─ârui na╚¢ionalitate o au persoanele juridice str─âine;...b)statutul (├«n m─âsura ├«n care exista ca act de sine st─ât─âtor);...c)hot─âr├órea organului de conducere a acelei persoane juridice, prin care se solicita recunoa╚Öterea ├«n Rom├ónia;...d)statutul viitoarei reprezentante ├«n Rom├ónia a acelei persoane juridice, cuprinz├ónd prevederi referitoare la sediu, la capacitatea juridic─â ╚Öi la persoanele care reprezint─â persoana juridic─â str─âin─â;...e)hot─âr├órea Guvernului Rom├óniei de aprobare a cererii de recunoa╚Ötere ├«n Rom├ónia a persoanei juridice solicitante.......(3)...Dispozi╚¢iileart. 8-12╚Öi aleart. 81se aplica ├«n mod corespunz─âtor.', NULL, 91, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1668, 7, '77', 'Articolul 77', NULL, NULL, NULL, 'Registrul na╚¢ional al persoanelor juridice f─âr─â scop patrimonial', NULL, NULL, NULL, NULL, '(1)...Asocia╚¢iile ╚Öi funda╚¢iile constituite ca persoane juridice rom├óne de c─âtre persoane fizice sau juridice str─âine pot dob├óndi pe ├«ntreaga durata de func╚¢ionare dreptul de proprietate ╚Öi orice alte drepturi reale asupra terenurilor necesare pentru realizarea scopului pentru care au fost constituite....(2)...├Än cazul dizolv─ârii ╚Öi lichid─ârii asocia╚¢iilor ╚Öi funda╚¢iilor prev─âzute laalin. (1), lichidatorii au obliga╚¢ia ├«nstr─âin─ârii terenurilor, ├«n termen de cel mult un an, numai c─âtre persoane care au capacitatea juridic─â de a dob├óndi astfel de bunuri. Termenul de un an se calculeaz─â de la data r─âm├ónerii definitive a hot─âr├órii judec─âtore╚Öti prin care se constat─â ori se dispune dizolvarea sau, dup─â caz, de la data hot─âr├órii de dizolvare voluntar─â.(la 02-06-2012, 
			Alin. (2) al art. 77 a fost modificat de pct. 7 al art. 35 din LEGEA nr. 76 din 24 mai 2012 publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012.
			)...(3)...├Än cazul nerespect─ârii termenului de un an pentru ├«nstr─âinarea terenurilor prev─âzute laalin. (2), instan╚¢a competent─â va dispune v├ónzarea acestora prin licita╚¢ie public─â....(4)...├Än toate cazurile, bunurile r─âmase dup─â lichidare, inclusiv terenurile ne├«nstr─âinate ├«n condi╚¢iilealin. (2)╚Öi(3)se atribuie cu respectarea dispozi╚¢iilorart. 60.', NULL, 92, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1669, 7, '78', 'Articolul 78', NULL, NULL, NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, 'Prezenta ordonan╚¢a intr─â ├«n vigoare ├«n termen de 3 luni de la data public─ârii ├«n Monitorul Oficial al Rom├óniei, Partea I.', NULL, 93, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1670, 7, '79', 'Articolul 79', NULL, NULL, NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, '├Än termenul prev─âzut laart. 78Ministerul Justi╚¢iei:a)va elabora ╚Öi va adopta Regulamentul privind organizarea Registrului asocia╚¢iilor ╚Öi funda╚¢iilor, Registrului federa╚¢iilor, precum ╚Öi a Registrului na╚¢ional al persoanelor juridice f─âr─â scop patrimonial, care va cuprinde ╚Öi prevederi privind accesul la acest registru al persoanelor juridice de drept public, al persoanelor juridice de drept privat, precum ╚Öi al persoanelor fizice;...b)va stabili forma ╚Öi con╚¢inutul certificatelor de ├«nscriere prev─âzute laart. 12 alin. (1)╚Öi laart. 17 alin. (3), precum ╚Öi condi╚¢iile de eliberare a dovezii prev─âzute laart. 7 alin. (2) lit. e).(la 05-12-2020, 
			Litera b) din Articolul 79 , Capitolul XII  a fost modificat─â de Punctul 32, Articolul I din LEGEA nr. 276 din 27 noiembrie 2020, publicat─â ├«n MONITORUL OFICIAL nr. 1166 din 02 decembrie 2020
			)...', NULL, 94, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1671, 7, '80', 'Articolul 80', NULL, NULL, NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, 'Dispozi╚¢iile prezentei ordonan╚¢e se completeaz─â cu dispozi╚¢iile Codului civil.(la 02-06-2012, 
			Art. 80 a fost modificat de pct. 8 al art. 35 din LEGEA nr. 76 din 24 mai 2012 publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012.
			)', NULL, 95, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1672, 7, '81', 'Articolul 81', NULL, NULL, NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, '├Än privin╚¢a asocia╚¢iilor ╚Öi funda╚¢iilor care au calitatea de persoane juridice str─âine sunt ╚Öi r─âm├ón aplicabile dispozi╚¢iile art. 43 ╚Öi urm─âtoarele din Legea nr. 105/1992 cu privire la reglementarea raporturilor de drept interna╚¢ional privat.', NULL, 96, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1673, 7, '82', 'Articolul 82', NULL, NULL, NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, 'Cererile de autorizare a ├«nfiin╚¢─ârii asocia╚¢iilor, funda╚¢iilor, federa╚¢iilor sau uniunilor de persoane, aflate ├«n curs de solu╚¢ionare pe rolul instan╚¢elor de judecata legal investite la data intr─ârii ├«n vigoare a prezentei ordonan╚¢e, vor continua s─â fie solu╚¢ionate de acele instan╚¢e.', NULL, 97, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1674, 7, '83', 'Articolul 83', NULL, NULL, NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, '(1)...Asocia╚¢iile ╚Öi funda╚¢iile constituite, p├ón─â la data intr─ârii ├«n vigoare a prezentei ordonan╚¢e, ├«n condi╚¢iile Legii nr. 21/1924 pentru persoanele juridice (Asocia╚¢ii ╚Öi Funda╚¢ii) ├«╚Öi p─âstreaz─â personalitatea juridic─â legal dob├óndit─â. Acestor asocia╚¢ii ╚Öi funda╚¢ii li se aplica, de la data intr─ârii ├«n vigoare a prezentei ordonan╚¢e, regimul juridic prev─âzut de aceasta....(2)...Uniunile, federa╚¢iile sau grup─ârile de persoane juridice, astfel cum sunt reglementate de Legea nr. 21/1924, ├«╚Öi p─âstreaz─â personalitatea juridic─â ╚Öi, de la data intr─ârii ├«n vigoare a prezentei ordonan╚¢e, li se va aplica regimul juridic al federa╚¢iilor prev─âzut de aceasta din urma.', NULL, 98, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1675, 7, '84', 'Articolul 84', NULL, NULL, NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, '(1)...├Än termen de 3 luni de la intrarea ├«n vigoare a prezentei ordonan╚¢e registrele persoanelor juridice, aflate la grefa tribunalelor ├«n circumscrip╚¢iile c─ârora func╚¢ioneaz─â persoanele juridice de drept privat prev─âzute de Legea nr. 21/1924 vor fi transmise judec─âtoriilor ├«n ale c─âror circumscrip╚¢ii acestea ├«╚Öi au sediul.(la 02-06-2012, 
			Alin. (1) al art. 84 a fost modificat de pct. 9 al art. 35 din LEGEA nr. 76 din 24 mai 2012 publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea unei sintagme.
			)...(2)...├Än acela╚Öi termen, registrele uniunilor ╚Öi federa╚¢iilor, aflate la grefa cur╚¢ilor de apel ├«n circumscrip╚¢iile c─ârora func╚¢ioneaz─â uniunile ╚Öi federa╚¢iile prev─âzute de Legea nr. 21/1924, vor fi transmise tribunalelor ├«n circumscrip╚¢iile c─ârora acestea ├«╚Öi au sediul....(3)...├Än vederea constituirii Registrului na╚¢ional prev─âzut laart. 73, ├«n termenul men╚¢ionat laalin. (1), instan╚¢ele judec─âtore╚Öti sunt obligate s─â transmit─â Ministerului Justi╚¢iei copii de pe registrele asocia╚¢iilor ╚Öi funda╚¢iilor, precum ╚Öi de pe registrele uniunilor ╚Öi federa╚¢iilor, aflate la grefa acestora.', NULL, 99, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1676, 7, '85', 'Articolul 85', NULL, NULL, NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, 'Persoanele juridice de utilitate public─â - asocia╚¢ii, funda╚¢ii sau alte organiza╚¢ii de acest fel ├«nfiin╚¢ate prin legi, ordonan╚¢e, decrete-lege, hot─âr├óri ale Guvernului sau prin orice alte acte de drept public nu intra sub incidenta prevederilor prezentei ordonan╚¢e, ci r─âm├ón supuse reglement─ârilor speciale care stau la baza ├«nfiin╚¢─ârii ╚Öi func╚¢ionarii lor.', NULL, 100, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1677, 7, '86', 'Articolul 86', NULL, NULL, NULL, 'Dispozi╚¢ii tranzitorii ╚Öi finale', NULL, NULL, NULL, NULL, 'Pe data intr─ârii ├«n vigoare a prezentei ordonan╚¢e se abroga Legea nr. 21/1924 pentru persoanele juridice (Asocia╚¢ii ╚Öi Funda╚¢ii), publicat─â ├«n Monitorul Oficial al Rom├óniei, Partea I, nr. 27 din 6 februarie 1924, cu modific─ârile ulterioare, precum ╚Öi orice alte dispozi╚¢ii contrare.', NULL, 101, '2025-11-19 19:12:52.02412', '2025-11-19 19:12:52.02412', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (180, 4, '1', 'Articolul 1', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, NULL, NULL, '(1)...├Än vederea desf─â╚Öur─ârii de activit─â╚¢i cu scop lucrativ, persoanele fizice ╚Öi persoanele juridice se pot asocia ╚Öi pot constitui societ─â╚¢i cu personalitate juridic─â, cu respectarea dispozi╚¢iilor prezentei legi....(2)...Societ─â╚¢ile prev─âzute laalin. (1)cu sediul ├«n Rom├ónia sunt persoane juridice rom├óne.(la 02-06-2012, 
			Art. 1 a fost modificat de pct. 2 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012.
			)', NULL, 1, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (181, 4, '2', 'Articolul 2', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, NULL, NULL, 'Dac─â prin lege nu se prevede altfel, societ─â╚¢ile cu personalitate juridic─â se constituie ├«n una dintre urm─âtoarele forme:(la 02-06-2012, 
			Partea introductiv─â a art. 2 a fost modificat─â de pct. 3 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012.
			)...a)...societate ├«n nume colectiv;...b)...societate ├«n comandit─â simpl─â;...c)...societate pe ac╚¢iuni;...d)...societate ├«n comandit─â pe ac╚¢iuni ╚Öi...e)...societate cu r─âspundere limitat─â.', NULL, 2, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (182, 4, '3', 'Articolul 3', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Obliga╚¢iile sociale sunt garantate cu patrimoniul social....(2)...Asocia╚¢ii ├«n societatea ├«n nume colectiv ╚Öi asocia╚¢ii comandita╚¢i ├«n societatea ├«n comandit─â simpl─â sau ├«n comandit─â pe ac╚¢iuni r─âspund nelimitat ╚Öi solidar pentru obliga╚¢iile sociale. Creditorii societ─â╚¢ii se vor ├«ndrepta mai ├«nt├ói ├«mpotriva acesteia pentru obliga╚¢iile ei ╚Öi, numai dac─â societatea nu le pl─âte╚Öte ├«n termen de cel mult 15 zile de la data punerii ├«n ├«nt├órziere, se vor putea ├«ndrepta ├«mpotriva acestor asocia╚¢i....(3)...Ac╚¢ionarii, asocia╚¢ii comanditari, precum ╚Öi asocia╚¢ii ├«n societatea cu r─âspundere limitat─â r─âspund numai p├ón─â la concuren╚¢a capitalului social subscris.', NULL, 3, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (183, 4, '4', 'Articolul 4', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, NULL, NULL, 'Societatea cu personalitate juridic─â va avea cel pu╚¢in 2 asocia╚¢i, ├«n afar─â de cazul ├«n care legea prevede altfel.(la 02-06-2012, 
