			)', NULL, 7, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (187, 4, '7', 'Articolul 7', NULL, 'Dispozi╚¢ii generale', NULL, 'Actul constitutiv al societ─â╚¢ii', NULL, NULL, NULL, NULL, 'Actul constitutiv al societ─â╚¢ii ├«n nume colectiv, ├«n comandit─â simpl─â sau cu r─âspundere limitat─â va cuprinde:a)datele de identificare a asocia╚¢ilor; la societatea ├«n comandit─â simpl─â se vor ar─âta ╚Öi asocia╚¢ii comandita╚¢i;(la 01-12-2006, 
