INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (201, 4, '19', 'Articolul 19', NULL, 'Dispozi╚¢ii generale', NULL, 'Formalit─â╚¢i specifice pentru constituirea societ─â╚¢ii pe ac╚¢iuni prin subscrip╚¢ie public─â', NULL, NULL, NULL, NULL, '(1)...Subscrierile de ac╚¢iuni se vor face pe unul sau pe mai multe exemplare ale prospectului de emisiune al fondatorilor.(la 26-11-2022, 
