INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (202, 4, '20', 'Articolul 20', NULL, 'Dispozi╚¢ii generale', NULL, 'Formalit─â╚¢i specifice pentru constituirea societ─â╚¢ii pe ac╚¢iuni prin subscrip╚¢ie public─â', NULL, NULL, NULL, NULL, 'Cel mai t├órziu ├«n termen de 15 zile de la data ├«nchiderii subscrierii, fondatorii vor convoca adunarea constitutiv─â, printr-o ├«n╚Ötiin╚¢are publicat─â ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a, ╚Öi ├«n dou─â ziare cu larg─â r─âsp├óndire, cu 15 zile ├«nainte de data fixat─â pentru adunare. ├Än╚Ötiin╚¢area va cuprinde locul ╚Öi data adun─ârii, care nu poate dep─â╚Öi dou─â luni de la data ├«nchiderii subscrierii, ╚Öi precizarea problemelor care vor face obiectul discu╚¢iilor.', NULL, 23, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (203, 4, '21', 'Articolul 21', NULL, 'Dispozi╚¢ii generale', NULL, 'Formalit─â╚¢i specifice pentru constituirea societ─â╚¢ii pe ac╚¢iuni prin subscrip╚¢ie public─â', NULL, NULL, NULL, NULL, '(1)...Societatea se poate constitui numai dac─â ├«ntregul capital social a fost subscris ╚Öi fiecare acceptant a v─ârsat ├«n numerar jum─âtate din valoarea ac╚¢iunilor subscrise la Casa de Economii ╚Öi Consemna╚¢iuni - C.E.C. - S.A. ori la o banc─â sau la una dintre unit─â╚¢ile acestora. Restul din capitalul social subscris va trebui v─ârsat ├«n termen de 12 luni de la ├«nmatriculare....(2)...Ac╚¢iunile ce reprezint─â aporturi ├«n natur─â vor trebui acoperite integral.', NULL, 24, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (204, 4, '22', 'Articolul 22', NULL, 'Dispozi╚¢ii generale', NULL, 'Formalit─â╚¢i specifice pentru constituirea societ─â╚¢ii pe ac╚¢iuni prin subscrip╚¢ie public─â', NULL, NULL, NULL, NULL, 'Dac─â subscrierile publice dep─â╚Öesc capitalul social prev─âzut ├«n prospectul de emisiune sau sunt mai mici dec├ót acesta, fondatorii sunt obliga╚¢i s─â supun─â aprob─ârii adun─ârii constitutive majorarea sau, dup─â caz, reducerea capitalului social la nivelul subscrip╚¢iei.', NULL, 25, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (205, 4, '23', 'Articolul 23', NULL, 'Dispozi╚¢ii generale', NULL, 'Formalit─â╚¢i specifice pentru constituirea societ─â╚¢ii pe ac╚¢iuni prin subscrip╚¢ie public─â', NULL, NULL, NULL, NULL, '(1)...Fondatorii sunt obliga╚¢i s─â ├«ntocmeasc─â o list─â a celor care, accept├ónd subscrip╚¢ia, au dreptul s─â participe la adunarea constitutiv─â, cu men╚¢ionarea num─ârului ac╚¢iunilor fiec─âruia....(2)...Aceast─â list─â va fi afi╚Öat─â la locul unde se va ╚¢ine adunarea, cu cel pu╚¢in 5 zile ├«nainte de adunare.', NULL, 26, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (206, 4, '24', 'Articolul 24', NULL, 'Dispozi╚¢ii generale', NULL, 'Formalit─â╚¢i specifice pentru constituirea societ─â╚¢ii pe ac╚¢iuni prin subscrip╚¢ie public─â', NULL, NULL, NULL, NULL, '(1)...Adunarea alege un pre╚Öedinte ╚Öi doi sau mai mul╚¢i secretari. Participarea acceptan╚¢ilor se va constata prin liste de prezen╚¢─â, semnate de fiecare dintre ei ╚Öi vizate de pre╚Öedinte ╚Öi de unul dintre secretari....(2)...Oricare acceptant are dreptul s─â fac─â observa╚¢ii asupra listei afi╚Öate de fondatori, ├«nainte de a se intra ├«n ordinea de zi a adun─ârii, care va decide asupra observa╚¢iilor.', NULL, 27, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (207, 4, '25', 'Articolul 25', NULL, 'Dispozi╚¢ii generale', NULL, 'Formalit─â╚¢i specifice pentru constituirea societ─â╚¢ii pe ac╚¢iuni prin subscrip╚¢ie public─â', NULL, NULL, NULL, NULL, '(1)...├Än adunarea constitutiv─â, fiecare acceptant are dreptul la un vot, indiferent de ac╚¢iunile subscrise. El poate fi reprezentat ╚Öi prin procur─â special─â....(2)...Nimeni nu poate reprezenta mai mult de 5 acceptan╚¢i....(3)...Acceptan╚¢ii care au constituit aporturi ├«n natur─â nu au drept de vot ├«n deliber─ârile referitoare la aporturile lor, chiar dac─â ei sunt ╚Öi subscriitori de ac╚¢iuni ├«n numerar ori se prezint─â ca mandatari ai altor acceptan╚¢i....(4)...Adunarea constitutiv─â este legal─â dac─â sunt prezen╚¢i jum─âtate plus unu din num─ârul acceptan╚¢ilor ╚Öi ia hot─âr├óri cu votul majorit─â╚¢ii simple a celor prezen╚¢i.', NULL, 28, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (208, 4, '26', 'Articolul 26', NULL, 'Dispozi╚¢ii generale', NULL, 'Formalit─â╚¢i specifice pentru constituirea societ─â╚¢ii pe ac╚¢iuni prin subscrip╚¢ie public─â', NULL, NULL, NULL, NULL, '(1)...Dac─â exist─â aporturi ├«n natur─â, avantaje acordate oric─ârei persoane care a participat la constituirea societ─â╚¢ii sau la tranzac╚¢ii conduc├ónd la acordarea autoriza╚¢iei, opera╚¢iuni ├«ncheiate de fondatori pe seama societ─â╚¢ii ce se constituie ╚Öi pe care aceasta urmeaz─â s─â le ia asupra sa, fondatorii vor solicita registratorului de registrul comer╚¢ului numirea unuia sau a mai multor exper╚¢i. Dispozi╚¢iileart. 38╚Öi39se aplic─â ├«n mod corespunz─âtor.(la 26-11-2022, 
