			)', NULL, 29, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (210, 4, '28', 'Articolul 28', NULL, 'Dispozi╚¢ii generale', NULL, 'Formalit─â╚¢i specifice pentru constituirea societ─â╚¢ii pe ac╚¢iuni prin subscrip╚¢ie public─â', NULL, NULL, NULL, NULL, 'Adunarea constitutiv─â are urm─âtoarele obliga╚¢ii:a)verific─â existen╚¢a v─ârs─âmintelor;...b)examineaz─â ╚Öi valideaz─â raportul exper╚¢ilor de evaluare a aporturilor ├«n natur─â;...c)aprob─â particip─ârile la profit ale fondatorilor ╚Öi opera╚¢iunile ├«ncheiate ├«n contul societ─â╚¢ii;...d)discut─â ╚Öi aprob─â actul constitutiv al societ─â╚¢ii, membrii prezen╚¢i reprezent├ónd, ├«n acest scop, ╚Öi pe cei absen╚¢i, ╚Öi ├«i desemneaz─â pe aceia care se vor prezenta pentru autentificarea actului ╚Öi ├«ndeplinirea formalit─â╚¢ilor cerute pentru constituirea societ─â╚¢ii;...e)nume╚Öte primii membri ai consiliului de administra╚¢ie, respectiv ai consiliului de supraveghere, ╚Öi primii cenzori sau, dup─â caz, primul auditor financiar.(la 01-12-2006, 
