INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (211, 4, '29', 'Articolul 29', NULL, 'Dispozi╚¢ii generale', NULL, 'Formalit─â╚¢i specifice pentru constituirea societ─â╚¢ii pe ac╚¢iuni prin subscrip╚¢ie public─â', NULL, NULL, NULL, NULL, '(1)...V─ârs─âmintele efectuate potrivitart. 21, pentru constituirea societ─â╚¢ii prin subscrip╚¢ie public─â, vor fi predate persoanelor ├«ns─ârcinate cu ├«ncasarea lor prin actul constitutiv, iar ├«n lipsa unei dispozi╚¢ii, persoanelor desemnate prin decizie a consiliului de administra╚¢ie, respectiv a directoratului, dup─â prezentarea certificatului la oficiul registrului comer╚¢ului, din care rezult─â ├«nmatricularea societ─â╚¢ii.(la 01-12-2006, 
