INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (225, 4, '43', 'Articolul 43', NULL, 'Dispozi╚¢ii generale', NULL, '├Änmatricularea societ─â╚¢ii', NULL, NULL, NULL, NULL, '(1)...Sucursalele sunt dezmembr─âminte f─âr─â personalitate juridic─â ale societ─â╚¢ilor ╚Öi se ├«nregistreaz─â, ├«nainte de ├«nceperea activit─â╚¢ii lor, ├«n registrul comer╚¢ului din jude╚¢ul ├«n care vor func╚¢iona.(la 02-06-2012, 
			Alin. (1) al art. 43 a fost modificat de pct. 31 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea sintagmei "societ─â╚¢i comerciale" cu termenul "societ─â╚¢i".
			)...(2)...Dac─â sucursala este deschis─â ├«ntr-o localitate din acela╚Öi jude╚¢ sau ├«n aceea╚Öi localitate cu societatea, ea se va ├«nregistra ├«n acela╚Öi registru al comer╚¢ului, ├«ns─â distinct, ca ├«nregistrare separat─â....(3)...Celelalte sedii secundare - agen╚¢ii, puncte de lucru sau alte asemenea sedii - sunt dezmembr─âminte f─âr─â personalitate juridic─â ale societ─â╚¢ilor ╚Öi se men╚¢ioneaz─â numai ├«n cadrul ├«nmatricul─ârii societ─â╚¢ii ├«n registrul comer╚¢ului de la sediul principal.(la 02-06-2012, 
