INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (226, 4, '44', 'Articolul 44', NULL, 'Dispozi╚¢ii generale', NULL, '├Änmatricularea societ─â╚¢ii', NULL, NULL, NULL, NULL, 'Societ─â╚¢ile str─âine pot ├«nfiin╚¢a ├«n Rom├ónia, cu respectarea legii rom├óne, filiale, precum ╚Öi sucursale, agen╚¢ii, reprezentan╚¢e sau alte sedii secundare, dac─â acest drept le este recunoscut de legea statutului lor organic.(la 02-06-2012, 
			Art. 44 a fost modificat de pct. 31 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea sintagmei "societ─â╚¢i comerciale" cu termenul "societ─â╚¢i".
			)', NULL, 47, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (237, 4, '54', 'Articolul 54', NULL, 'Dispozi╚¢ii generale', NULL, 'Efectele ├«nc─âlc─ârii cerin╚¢elor legale de constituire a societ─â╚¢ii', NULL, NULL, NULL, NULL, '(1)...Dup─â efectuarea formalit─â╚¢ilor de publicitate ├«n leg─âtur─â cu persoanele care, ca organe ale societ─â╚¢ii, sunt autorizate s─â o reprezinte, societatea nu poate opune ter╚¢ilor nicio neregularitate la numirea acestora, cu excep╚¢ia cazului ├«n care societatea face dovada c─â ter╚¢ii respectivi aveau cuno╚Ötin╚¢─â de aceast─â neregularitate....(2)...Societatea nu poate invoca fa╚¢─â de ter╚¢i numirile ├«n func╚¢iile prev─âzute laalin. (1)sau ├«ncetarea acestor func╚¢ii, dac─â ele nu au fost publicate ├«n conformitate cu legea.(la 01-12-2006, 
			Art. 54 a fost modificat de pct. 33 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
