INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (227, 4, '44', 'Articolul 44', NULL, 'Dispozi╚¢ii generale', NULL, '├Änmatricularea societ─â╚¢ii', NULL, NULL, NULL, NULL, '(1)...Dob├óndirea de c─âtre societate, ├«ntr-un interval de cel mult 2 ani de la constituire sau de la autorizarea ├«nceperii activit─â╚¢ii societ─â╚¢ii, a unui bun de la un fondator ori ac╚¢ionar, contra unei sume sau a altor contravalori reprezent├ónd cel pu╚¢in o zecime din valoarea capitalului social subscris, va fi supus─â aprob─ârii prealabile a adun─ârii generale a ac╚¢ionarilor, precum ╚Öi prevederilorart. 38╚Öi39, va fi men╚¢ionat─â ├«n registrul comer╚¢ului, ├«n baza actului translativ de proprietate, ├«ncheiat ├«n condi╚¢iile legii, ╚Öi va fi publicat─â ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a.(la 26-11-2022, 
