INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (230, 4, '47', 'Articolul 47', NULL, 'Dispozi╚¢ii generale', NULL, 'Efectele ├«nc─âlc─ârii cerin╚¢elor legale de constituire a societ─â╚¢ii', NULL, NULL, NULL, NULL, '(1)...├Än cazul ├«n care fondatorii sau reprezentan╚¢ii societ─â╚¢ii nu au cerut ├«nmatricularea ei ├«n termen legal, oricare asociat poate cere oficiului registrului comer╚¢ului efectuarea ├«nmatricul─ârii, dup─â ce, prin notificare sau scrisoare recomandat─â, i-a pus ├«n ├«nt├órziere, iar ei nu s-au conformat ├«n cel mult 8 zile de la primire....(2)...Dac─â, totu╚Öi, ├«nmatricularea nu s-a efectuat ├«n termenele prev─âzute de alineatul precedent, asocia╚¢ii sunt elibera╚¢i de obliga╚¢iile ce decurg din subscrip╚¢iile lor, dup─â trecerea a 3 luni de la data autentific─ârii actului constitutiv, ├«n afar─â de cazul ├«n care acesta prevede altfel....(3)...Dac─â un asociat a cerut ├«ndeplinirea formalit─â╚¢ilor de ├«nmatriculare, nu se va mai putea pretinde de nici unul dintre ei eliberarea de obliga╚¢iile ce decurg din subscrip╚¢ie.', NULL, 51, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (231, 4, '48', 'Articolul 48', NULL, 'Dispozi╚¢ii generale', NULL, 'Efectele ├«nc─âlc─ârii cerin╚¢elor legale de constituire a societ─â╚¢ii', NULL, NULL, NULL, NULL, '(1)...├Än cazul unor neregularit─â╚¢i constatate dup─â ├«nmatriculare, societatea este obligat─â s─â ia m─âsuri pentru ├«nl─âturarea lor, ├«n cel mult 8 zile de la data constat─ârii acelor neregularit─â╚¢i....(2)...Dac─â societatea nu se conformeaz─â, orice persoan─â interesat─â poate cere tribunalului s─â oblige organele societ─â╚¢ii s─â le regularizeze, sub sanc╚¢iunea pl─â╚¢ii de daune-interese potrivit dreptului comun.(la 02-06-2012, 
			Alin. (2) al art. 48 a fost modificat de pct. 6 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012.
			)...(3)...Dreptul la ac╚¢iunea de regularizare se prescrie prin trecerea unui termen de un an de la data ├«nmatricul─ârii societ─â╚¢ii.', NULL, 52, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (232, 4, '49', 'Articolul 49', NULL, 'Dispozi╚¢ii generale', NULL, 'Efectele ├«nc─âlc─ârii cerin╚¢elor legale de constituire a societ─â╚¢ii', NULL, NULL, NULL, NULL, 'Fondatorii, reprezentan╚¢ii societ─â╚¢ii, precum ╚Öi primii membri ai organelor de conducere, de administrare ╚Öi de control ale societ─â╚¢ii r─âspund nelimitat ╚Öi solidar pentru prejudiciul cauzat prin neregularit─â╚¢ile la care se refer─âart. 46-48.', NULL, 53, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (233, 4, '50', 'Articolul 50', NULL, 'Dispozi╚¢ii generale', NULL, 'Efectele ├«nc─âlc─ârii cerin╚¢elor legale de constituire a societ─â╚¢ii', NULL, NULL, NULL, NULL, '(1)...Actele sau faptele, pentru care nu s-a efectuat publicitatea prev─âzut─â de lege, nu pot fi opuse ter╚¢ilor, ├«n afar─â de cazul ├«n care societatea face dovada c─â ace╚Ötia le cuno╚Öteau....(2)...Opera╚¢iunile efectuate de societate ├«nainte de a 16-a zi de la data efectu─ârii publicit─â╚¢ii prev─âzute de lege nu sunt opozabile ter╚¢ilor care dovedesc c─â au fost ├«n imposibilitate de a lua cuno╚Ötin╚¢─â despre ele.(la 26-11-2022, 
			Alineatul (2) din Articolul 50 , Capitolul IV , Titlul II  a fost modificat de Punctul 23, Articolul 129, Sectiunea a 10-a, Capitolul IV din LEGEA nr. 265 din 22 iulie 2022, publicat─â ├«n MONITORUL OFICIAL nr. 750 din 26 iulie 2022
			)', NULL, 54, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (234, 4, '51', 'Articolul 51', NULL, 'Dispozi╚¢ii generale', NULL, 'Efectele ├«nc─âlc─ârii cerin╚¢elor legale de constituire a societ─â╚¢ii', NULL, NULL, NULL, NULL, 'Ter╚¢ii sunt ├«ntotdeauna ├«n m─âsur─â s─â invoce actele sau faptele cu privire la care nu s-a ├«ndeplinit publicitatea, ├«n afar─â de cazul ├«n care omisiunea publicit─â╚¢ii le lipse╚Öte de efecte....(la 26-11-2022, 
