INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (236, 4, '53', 'Articolul 53', NULL, 'Dispozi╚¢ii generale', NULL, 'Efectele ├«nc─âlc─ârii cerin╚¢elor legale de constituire a societ─â╚¢ii', NULL, NULL, NULL, NULL, '(1)...Fondatorii, reprezentan╚¢ii ╚Öi alte persoane, care au lucrat ├«n numele unei societ─â╚¢i ├«n curs de constituire, r─âspund solidar ╚Öi nelimitat fa╚¢─â de ter╚¢i pentru actele juridice ├«ncheiate cu ace╚Ötia ├«n contul societ─â╚¢ii, ├«n afar─â de cazul ├«n care societatea, dup─â ce a dob├óndit personalitate juridic─â, le-a preluat asupra sa. Actele astfel preluate sunt considerate a fi fost ale societ─â╚¢ii ├«nc─â de la data ├«ncheierii lor....(2)...├Än cazul ├«n care societatea, datorit─â obiectului s─âu de activitate, nu ├«╚Öi poate ├«ncepe activitatea f─âr─â a fi autorizat─â ├«n acest sens, prevederilealin. (1)nu sunt aplicabile angajamentelor rezultate din contracte ├«ncheiate de societate, sub condi╚¢ia primirii acestei autoriza╚¢ii. ├Än aceast─â situa╚¢ie, r─âspunderea revine societ─â╚¢ii.(la 01-12-2006, 
