			)', NULL, 57, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (238, 4, '55', 'Articolul 55', NULL, 'Dispozi╚¢ii generale', NULL, 'Efectele ├«nc─âlc─ârii cerin╚¢elor legale de constituire a societ─â╚¢ii', NULL, NULL, NULL, NULL, '(1)...├Än raporturile cu ter╚¢ii, societatea este angajat─â prin actele organelor sale, chiar dac─â aceste acte dep─â╚Öesc obiectul de activitate al societ─â╚¢ii, ├«n afar─â de cazul ├«n care ea dovede╚Öte c─â ter╚¢ii cuno╚Öteau sau, ├«n ├«mprejur─ârile date, trebuiau s─â cunoasc─â dep─â╚Öirea acestuia ori c├ónd actele astfel ├«ncheiate dep─â╚Öesc limitele puterilor prev─âzute de lege pentru organele respective. Publicarea actului constitutiv nu poate constitui, singur─â, dovada cunoa╚Öterii.(la 01-12-2006, 
			Alin. (1) al art. 55 a fost modificat de pct. 34 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...(2)...Clauzele actului constitutiv ori hot─âr├órile organelor statutare ale societ─â╚¢ilor prev─âzute ├«n alineatul precedent, care limiteaz─â puterile conferite de lege acestor organe, sunt inopozabile ter╚¢ilor, chiar dac─â au fost publicate.', NULL, 59, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (239, 4, '56', 'Articolul 56', NULL, 'Dispozi╚¢ii generale', NULL, 'Efectele ├«nc─âlc─ârii cerin╚¢elor legale de constituire a societ─â╚¢ii', NULL, NULL, NULL, NULL, 'Nulitatea unei societ─â╚¢i ├«nmatriculate ├«n registrul comer╚¢ului poate fi declarat─â de tribunal numai atunci c├ónd:a)lipse╚Öte actul constitutiv sau nu a fost ├«ncheiat ├«n form─â autentic─â, ├«n situa╚¢iile prev─âzute laart. 5 alin. (6);...b)to╚¢i fondatorii au fost, potrivit legii, incapabili, la data constituirii societ─â╚¢ii;...c)obiectul de activitate al societ─â╚¢ii este ilicit sau contrar ordinii publice;...d)lipse╚Öte ├«ncheierea registratorului de registrul comer╚¢ului de ├«nmatriculare a societ─â╚¢ii;(la 26-11-2022, 
			Litera d) din Articolul 56 , Capitolul IV , Titlul II  a fost modificat─â de Punctul 26, Articolul 129, Sectiunea a 10-a, Capitolul IV din LEGEA nr. 265 din 22 iulie 2022, publicat─â ├«n MONITORUL OFICIAL nr. 750 din 26 iulie 2022
			)...e)lipse╚Öte autorizarea legal─â administrativ─â de constituire a societ─â╚¢ii;...f)actul constitutiv nu prevede denumirea societ─â╚¢ii, obiectul s─âu de activitate, aporturile asocia╚¢ilor sau capitalul social subscris;(la 01-12-2006, 
			Lit. f) a art. 56 a fost modificat─â de pct. 35 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...g)s-au ├«nc─âlcat dispozi╚¢iile legale privind capitalul social minim, subscris ╚Öi v─ârsat;...h)nu s-a respectat num─ârul minim de asocia╚¢i, prev─âzut de lege....', NULL, 60, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (240, 4, '57', 'Articolul 57', NULL, 'Dispozi╚¢ii generale', NULL, 'Efectele ├«nc─âlc─ârii cerin╚¢elor legale de constituire a societ─â╚¢ii', NULL, NULL, NULL, NULL, 'Nulitatea nu poate fi declarat─â ├«n cazul ├«n care cauza ei, invocat─â ├«n cererea de anulare, a fost ├«nl─âturat─â ├«nainte de a se pune concluzii ├«n fond la tribunal.', NULL, 61, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
