INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (258, 4, '73', 'Articolul 73', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, '(1)...Administratorii sunt solidar r─âspunz─âtori fa╚¢─â de societate pentru:a)realitatea v─ârs─âmintelor efectuate de asocia╚¢i;...b)existen╚¢a real─â a dividendelor pl─âtite;...c)existen╚¢a registrelor cerute de lege ╚Öi corecta lor ╚¢inere;...d)exacta ├«ndeplinire a hot─âr├órilor adun─ârilor generale;...e)stricta ├«ndeplinire a ├«ndatoririlor pe care legea, actul constitutiv le impun.......(1^1)...├Än cazul ├«n care societatea se afl─â ├«n stare de dificultate, administratorii/directorii au ├«n vedere cel pu╚¢in urm─âtoarele:a)interesele creditorilor, ale de╚¢in─âtorilor de titluri de participa╚¢ie ╚Öi ale altor p─âr╚¢i interesate;...b)necesitatea de a lua m─âsuri rezonabile ╚Öi adecvate pentru evitarea insolven╚¢ei ╚Öi pentru reducerea la minimum a pierderilor suferite de creditori, de angajai, de de╚¢in─âtorii de titluri de participa╚¢ie ╚Öi de alte p─âr╚¢i interesate;...c)necesitatea de a evita adoptarea, cu inten╚¢ie sau din grav─â neglijen╚¢─â, a unei conduite care amenin╚¢─â viabilitatea ├«ntreprinderii....(la 17-07-2022, 
