INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (259, 4, '73', 'Articolul 73', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, '(1)...Persoanele care, potrivitart. 6 alin. (2), nu pot fi fondatori nu pot fi nici administratori, directori, membri ai consiliului de supraveghere ╚Öi ai directoratului, cenzori sau auditori financiari, iar dac─â au fost alese, sunt dec─âzute din drepturi.(la 29-06-2007, 
			Art. 73^1 a fost modificat de pct. 6 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)...(2)...Prin acceptarea mandatului, administratorii, membrii consiliului de administra╚¢ie, directorii, membrii directoratului, cenzorii, iar prin ├«ncheierea contractului de prest─âri servicii, auditorul financiar ├«╚Öi asum─â r─âspunderea pentru ├«ndeplinirea condi╚¢iilor prev─âzute laart. 6pentru a de╚¢ine ╚Öi exercita aceast─â func╚¢ie, ├«n contract fiind inclus─â o clauz─â ├«n acest sens.(la 26-11-2022, 
