INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (260, 4, '74', 'Articolul 74', NULL, 'Dispozi╚¢ii generale', NULL, 'Dispozi╚¢ii comune', NULL, NULL, NULL, NULL, '(1)...├Än orice factur─â, ofert─â, comand─â, tarif, prospect ╚Öi alte documente ├«ntrebuin╚¢ate ├«n comer╚¢, eman├ónd de la o societate, trebuie s─â se men╚¢ioneze denumirea, forma juridic─â, sediul social, num─ârul din registrul comer╚¢ului ╚Öi codul unic de ├«nregistrare. Sunt exceptate bonurile fiscale emise de aparatele de marcat electronice, care vor cuprinde elementele prev─âzute de legisla╚¢ia din domeniu....(2)...Dac─â societatea pe ac╚¢iuni opteaz─â pentru un sistem dualist de administrare, ├«n conformitate cu prevederileart. 153, documentele prev─âzute laalin. (1)vor con╚¢ine ╚Öi men╚¢iunea ┬½societate administrat─â ├«n sistem dualist┬╗....(3)...├Än documentele prev─âzute laalin. (1), dac─â acestea provin de la o societate cu r─âspundere limitat─â, se va men╚¢iona ╚Öi capitalul social, iar dac─â ele provin de la o societate pe ac╚¢iuni sau ├«n comandit─â pe ac╚¢iuni, se vor men╚¢iona at├ót capitalul social subscris, c├ót ╚Öi cel v─ârsat....(4)...├Än situa╚¢ia ├«n care documentele prev─âzute laalin. (1)sunt emise de o sucursal─â, acestea trebuie s─â men╚¢ioneze ╚Öi oficiul registrului comer╚¢ului la care a fost ├«nregistrat─â sucursala ╚Öi num─ârul ei de ├«nregistrare....(5)...Dac─â societatea de╚¢ine o pagin─â de internet proprie, informa╚¢iile prev─âzute laalin. (1)╚Öi(3)vor fi publicate ╚Öi pe pagina de internet a societ─â╚¢ii.(la 01-12-2006, 
