INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (261, 4, '75', 'Articolul 75', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, 'Dreptul de a reprezenta societatea apar╚¢ine fiec─ârui administrator, afar─â de stipula╚¢ie contrar─â ├«n actul constitutiv.', NULL, 82, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (262, 4, '76', 'Articolul 76', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, '(1)...Dac─â actul constitutiv dispune ca administratorii s─â lucreze ├«mpreun─â, decizia trebuie luat─â ├«n unanimitate; ├«n caz de divergen╚¢─â ├«ntre administratori, vor decide asocia╚¢ii care reprezint─â majoritatea absolut─â a capitalului social....(2)...Pentru actele urgente, a c─âror ne├«ndeplinire ar cauza o pagub─â mare societ─â╚¢ii, poate decide un singur administrator ├«n lipsa celorlal╚¢i, care se g─âsesc ├«n imposibilitate, chiar momentan─â, de a lua parte la administra╚¢ie.', NULL, 83, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (263, 4, '77', 'Articolul 77', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, '(1)...Asocia╚¢ii care reprezint─â majoritatea absolut─â a capitalului social pot alege unul sau mai mul╚¢i administratori dintre ei, fix├óndu-le puterile, durata ├«ns─ârcin─ârii ╚Öi eventuala lor remunera╚¢ie, afar─â numai dac─â prin actul constitutiv nu se dispune altfel....(2)...Cu aceea╚Öi majoritate asocia╚¢ii pot decide asupra revoc─ârii administratorilor sau asupra limit─ârii puterilor lor, afar─â de cazul ├«n care administratorii au fost numi╚¢i prin actul constitutiv.', NULL, 84, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (264, 4, '78', 'Articolul 78', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, '(1)...Dac─â un administrator ia ini╚¢iativa unei opera╚¢iuni ce dep─â╚Öe╚Öte limitele opera╚¢iunilor obi╚Önuite comer╚¢ului pe care ├«l exercit─â societatea, acesta trebuie s─â ├«n╚Ötiin╚¢eze pe ceilal╚¢i administratori, ├«nainte de a o ├«ncheia, sub sanc╚¢iunea suport─ârii pierderilor ce ar rezulta din aceasta....(2)...├Än caz de opozi╚¢ie a vreunuia dintre ei, vor decide asocia╚¢ii care reprezint─â majoritatea absolut─â a capitalului social....(3)...Opera╚¢iunea ├«ncheiat─â ├«n contra opozi╚¢iei f─âcute este valabil─â fa╚¢─â de ter╚¢ii c─ârora nu li se va fi comunicat aceast─â opozi╚¢ie.', NULL, 85, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (265, 4, '79', 'Articolul 79', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, '(1)...Asociatul care, ├«ntr-o opera╚¢iune determinat─â, are, pe cont propriu sau pe contul altuia, interese contrare acelora ale societ─â╚¢ii, nu poate lua parte la nici o deliberare sau decizie privind aceast─â opera╚¢iune....(2)...Asociatul care contravine dispozi╚¢iiloralin. (1)este r─âspunz─âtor de daunele cauzate societ─â╚¢ii, dac─â, f─âr─â votul s─âu, nu s-ar fi ob╚¢inut majoritatea cerut─â.', NULL, 86, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (266, 4, '80', 'Articolul 80', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, 'Asociatul care, f─âr─â consim╚¢─âm├óntul scris al celorlal╚¢i asocia╚¢i, ├«ntrebuin╚¢eaz─â capitalul, bunurile sau creditul societ─â╚¢ii ├«n folosul s─âu sau ├«n acela al unei alte persoane este obligat s─â restituie societ─â╚¢ii beneficiile ce au rezultat ╚Öi s─â pl─âteasc─â desp─âgubiri pentru daunele cauzate.', NULL, 87, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1466, 5, '91', 'Articolul 91', NULL, 'Energia electric─â', NULL, 'Infrac╚¢iuni ╚Öi contraven╚¢ii', NULL, NULL, NULL, NULL, 'R─âspunderi...├Änc─âlcarea prevederilor prezentului titlu atrage r─âspunderea disciplinar─â, civil─â, contraven╚¢ional─â sau penal─â, dup─â caz, a persoanelor vinovate.', NULL, 91, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (267, 4, '81', 'Articolul 81', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, '(1)...Nici un asociat nu poate lua din fondurile societ─â╚¢ii mai mult dec├ót i s-a fixat pentru cheltuielile f─âcute sau pentru cele ce urmeaz─â s─â le fac─â ├«n interesul societ─â╚¢ii....(2)...Asociatul care contravine acestei dispozi╚¢ii este r─âspunz─âtor de sumele luate ╚Öi de daune....(3)...Se va putea stipula, prin actul constitutiv, c─â asocia╚¢ii pot lua din casa societ─â╚¢ii anumite sume pentru cheltuielile lor particulare.', NULL, 88, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (268, 4, '82', 'Articolul 82', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, '(1)...Asocia╚¢ii nu pot lua parte, ca asocia╚¢i cu r─âspundere nelimitat─â, ├«n alte societ─â╚¢i concurente sau av├ónd acela╚Öi obiect de activitate, nici s─â fac─â opera╚¢iuni ├«n contul lor sau al altora, ├«n acela╚Öi fel de comer╚¢ sau ├«ntr-unul asem─ân─âtor, f─âr─â consim╚¢─âm├óntul celorlal╚¢i asocia╚¢i....(2)...Consim╚¢─âm├óntul se socote╚Öte dat dac─â participarea sau opera╚¢iunile fiind anterioare actului constitutiv au fost cunoscute de ceilal╚¢i asocia╚¢i ╚Öi ace╚Ötia nu au interzis continuarea lor....(3)...├Än caz de ├«nc─âlcare a prevederiloralin. (1)╚Öi(2), societatea, ├«n afar─â de dreptul de a exclude pe asociat, poate s─â decid─â c─â acesta a lucrat ├«n contul ei sau s─â cear─â desp─âgubiri....(4)...Acest drept se stinge dup─â trecerea a 3 luni din ziua c├ónd societatea a avut cuno╚Ötin╚¢─â, f─âr─â s─â fi luat vreo hot─âr├óre.', NULL, 89, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (269, 4, '83', 'Articolul 83', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, 'C├ónd aportul la capitalul social apar╚¢ine mai multor persoane, acestea sunt obligate solidar fa╚¢─â de societate ╚Öi trebuie s─â desemneze un reprezentant comun pentru exercitarea drepturilor decurg├ónd din acest aport.', NULL, 90, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (270, 4, '84', 'Articolul 84', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, '(1)...Asociatul care a depus ca aport una sau mai multe crean╚¢e nu este liberat c├ót timp societatea nu a ob╚¢inut plata sumei pentru care au fost aduse....(2)...Dac─â plata nu s-a putut ob╚¢ine prin urm─ârirea debitorului cedat, asociatul, ├«n afar─â de daune, r─âspunde de suma datorat─â, cu dob├ónda legal─â din ziua scaden╚¢ei crean╚¢elor.', NULL, 91, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (271, 4, '85', 'Articolul 85', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, '(1)...Asocia╚¢ii sunt obliga╚¢i nelimitat ╚Öi solidar pentru opera╚¢iunile ├«ndeplinite ├«n numele societ─â╚¢ii de persoanele care o reprezint─â....(2)...Hot─âr├órea judec─âtoreasc─â ob╚¢inut─â ├«mpotriva societ─â╚¢ii este opozabil─â fiec─ârui asociat.', NULL, 92, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
