INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (274, 4, '87', 'Articolul 87', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n nume colectiv', NULL, NULL, NULL, NULL, '(1)...Cesiunea aportului de capital social este posibil─â dac─â a fost permis─â prin actul constitutiv....(2)...Cesiunea nu libereaz─â pe asociatul cedent de ceea ce mai datoreaz─â societ─â╚¢ii din aportul s─âu de capital....(3)...Fa╚¢─â de ter╚¢i, cedentul r─âm├óne r─âspunz─âtor potrivitart. 225....(4)...C├ónd actul constitutiv prevede cazurile de retragere a unui asociat, se vor aplica dispozi╚¢iileart. 225╚Öi229.', NULL, 95, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (275, 4, '88', 'Articolul 88', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n comandit─â simpl─â', NULL, NULL, NULL, NULL, 'Administra╚¢ia societ─â╚¢ii ├«n comandit─â simpl─â se va ├«ncredin╚¢a unuia sau mai multor asocia╚¢i comandita╚¢i.', NULL, 96, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (276, 4, '89', 'Articolul 89', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile ├«n comandit─â simpl─â', NULL, NULL, NULL, NULL, '(1)...Comanditarul poate ├«ncheia opera╚¢iuni ├«n contul societ─â╚¢ii numai pe baza unei procuri speciale pentru opera╚¢iuni determinate, dat─â de reprezentan╚¢ii societ─â╚¢ii ╚Öi ├«nscris─â ├«n registrul comer╚¢ului. ├Än caz contrar, comanditarul devine r─âspunz─âtor fa╚¢─â de ter╚¢i nelimitat ╚Öi solidar, pentru toate obliga╚¢iunile societ─â╚¢ii contractate de la data opera╚¢iunii ├«ncheiate de el....(2)...Comanditarul poate ├«ndeplini servicii ├«n administra╚¢ia intern─â a societ─â╚¢ii, poate face acte de supraveghere, poate participa la numirea ╚Öi la revocarea administratorilor, ├«n cazurile prev─âzute de lege, sau poate acorda, ├«n limitele actului constitutiv, autorizarea administratorilor pentru opera╚¢iunile ce dep─â╚Öesc puterile lor....(3)...Comanditarul are, de asemenea, dreptul de a cere copie de pe situa╚¢iile financiare anuale ╚Öi de a controla exactitatea lor prin cercetarea registrelor comerciale ╚Öi a celorlalte documente justificative.', NULL, 97, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
