INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (278, 4, '91', 'Articolul 91', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre ac╚¢iuni', NULL, NULL, '(1)...├Än societatea pe ac╚¢iuni, capitalul social este reprezentat prin ac╚¢iuni nominative emise de societate....(2)...Felul ac╚¢iunilor va fi determinat prin actul constitutiv. Ac╚¢iunile nominative pot fi emise ├«n form─â material─â, pe suport h├órtie, sau ├«n form─â dematerializat─â, caz ├«n care se ├«nregistreaz─â ├«n registrul ac╚¢ionarilor....(la 21-07-2019, 
