INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (283, 4, '96', 'Articolul 96', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre ac╚¢iuni', NULL, NULL, 'Titularii fiec─ârei categorii de ac╚¢iuni se reunesc ├«n adun─âri speciale, ├«n condi╚¢iile stabilite de actul constitutiv al societ─â╚¢ii. Orice titular al unor asemenea ac╚¢iuni poate participa la aceste adun─âri.', NULL, 104, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (284, 4, '97', 'Articolul 97', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre ac╚¢iuni', NULL, NULL, '├Än cazul ├«n care nu a emis ╚Öi nu a eliberat ac╚¢iuni ├«n form─â material─â, societatea, din oficiu sau la cererea ac╚¢ionarilor, le va elibera c├óte un certificat de ac╚¢ionar cuprinz├ónd datele prev─âzute laart. 93 alin. (2)╚Öi(3)╚Öi, ├«n plus, num─ârul, categoria ╚Öi valoarea nominal─â a ac╚¢iunilor, proprietate a ac╚¢ionarului, pozi╚¢ia la care acesta este ├«nscris ├«n registrul ac╚¢ionarilor ╚Öi, dup─â caz, num─ârul de ordine al ac╚¢iunilor.', NULL, 105, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (285, 4, '98', 'Articolul 98', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre ac╚¢iuni', NULL, NULL, '(1)...Dreptul de proprietate asupra ac╚¢iunilor emise ├«n form─â material─â se transmite prin declara╚¢ie f─âcut─â ├«n registrul ac╚¢ionarilor ╚Öi prin men╚¢iunea f─âcut─â pe titlu, semnat─â de cedent ╚Öi de cesionar sau de mandatarii lor. Dreptul de proprietate asupra ac╚¢iunilor emise ├«n form─â dematerializat─â se transmite prin declara╚¢ie f─âcut─â ├«n registrul ac╚¢ionarilor, semnat─â de cedent ╚Öi de cesionar sau de mandatarii lor. Prin actul constitutiv se pot prevedea ╚Öi alte forme de transmitere a dreptului de proprietate asupra ac╚¢iunilor.(la 21-07-2019, 
			Alineatul (1) din Articolul 98 , Sectiunea I , Capitolul IV , Titlul III  a fost modificat de Punctul 5, Articolul 54, Capitolul XI din LEGEA nr. 129 din 11 iulie 2019, publicat─â ├«n MONITORUL OFICIAL nr. 589 din 18 iulie 2019
			)...(2)...Dreptul de proprietate asupra ac╚¢iunilor emise ├«n form─â dematerializat─â ╚Öi tranzac╚¢ionate pe o pia╚¢─â reglementat─â sau ├«n cadrul unui sistem alternativ de tranzac╚¢ionare se transmite potrivit prevederilor legisla╚¢iei pie╚¢ei de capital.(la 01-12-2006, 
			Alin. (2) al art. 98 a fost modificat de pct. 45 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...(3)...Subscriitorii ╚Öi cesion─ârii ulteriori sunt r─âspunz─âtori solidar de plata ac╚¢iunilor timp de 3 ani, socoti╚¢i de la data c├ónd s-a f─âcut men╚¢iunea de transmitere ├«n registrul ac╚¢ionarilor.', NULL, 106, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
