INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (287, 4, '99', 'Articolul 99', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre ac╚¢iuni', NULL, NULL, '(1)...Constituirea de ipoteci mobiliare asupra ac╚¢iunilor se face prin ├«nscris sub semn─âtur─â privat─â, ├«n care se vor ar─âta cuantumul datoriei, valoarea ╚Öi categoria ac╚¢iunilor cu care se garanteaz─â, iar ├«n cazul ac╚¢iunilor emise ├«n form─â material─â, ╚Öi prin men╚¢ionarea ipotecii pe titlu, semnat─â de creditor ╚Öi debitorul ac╚¢ionar sau de mandatarii acestora.(la 21-07-2019, 
