INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (293, 4, '104', 'Articolul 104', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre ac╚¢iuni', NULL, NULL, '(1)...Restric╚¢iile prev─âzute laart. 103^1nu se aplic─â:a)ac╚¢iunilor dob├óndite ├«n conformitate cuart. 207 alin. (1) lit. c), ca urmare a unei decizii a adun─ârii generale de reducere a capitalului social;...b)ac╚¢iunilor dob├óndite ca urmare a unui transfer cu titlu universal;...c)ac╚¢iunilor integral liberate, dob├óndite prin efectul unei hot─âr├óri judec─âtore╚Öti, ├«ntr-o procedur─â de executare silit─â ├«mpotriva unui ac╚¢ionar, debitor al societ─â╚¢ii;...d)ac╚¢iunilor integral liberate, dob├óndite cu titlu gratuit.......(2)...Restric╚¢iile prev─âzute laart. 103^1, cu excep╚¢ia celei prev─âzute laart. 103^1 alin. (1) lit. d), nu se aplic─â ac╚¢iunilor dob├óndite ├«n conformitate cuart. 134.(la 01-12-2006, 
