INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (301, 4, '109', 'Articolul 109', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre ac╚¢iuni', NULL, NULL, 'Situa╚¢ia ac╚¢iunilor trebuie s─â fie cuprins─â ├«n anexa la situa╚¢ia financiar─â anual─â ╚Öi, ├«n mod deosebit, s─â se precizeze dac─â ele au fost integral liberate ╚Öi, dup─â caz, num─ârul ac╚¢iunilor pentru care s-a cerut, f─âr─â rezultat, efectuarea v─ârs─âmintelor.', NULL, 122, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (302, 4, '110', 'Articolul 110', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre adun─ârile generale', NULL, NULL, '(1)...Adun─ârile generale sunt ordinare ╚Öi extraordinare....(2)...C├ónd actul constitutiv nu dispune altfel, ele se vor ╚¢ine la sediul societ─â╚¢ii ╚Öi ├«n localul ce se va indica ├«n convocare.', NULL, 123, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (303, 4, '111', 'Articolul 111', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre adun─ârile generale', NULL, NULL, '(1)...Adunarea general─â ordinar─â se ├«ntrune╚Öte cel pu╚¢in o dat─â pe an, ├«n cel mult 5 luni de la ├«ncheierea exerci╚¢iului financiar.(la 01-12-2006, 
			Alin. (1) al art. 111 a fost modificat de pct. 57 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...(2)...├Än afar─â de dezbaterea altor probleme ├«nscrise la ordinea de zi, adunarea general─â este obligat─â:a)s─â discute, s─â aprobe sau s─â modifice situa╚¢iile financiare anuale, pe baza rapoartelor prezentate de consiliul de administra╚¢ie, respectiv de directorat ╚Öi de consiliul de supraveghere, de cenzori sau, dup─â caz, de auditorul financiar, ╚Öi s─â fixeze dividendul;(la 01-12-2006, 
			Lit. a) a alin. (2) al art. 111 a fost modificat─â de pct. 58 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...b)s─â aleag─â ╚Öi s─â revoce membrii consiliului de administra╚¢ie, respectiv ai consiliului de supraveghere, ╚Öi cenzorii;(la 01-12-2006, 
			Lit. b) a alin. (2) al art. 111 a fost modificat─â de pct. 58 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...b^1)├«n cazul societ─â╚¢ilor ale c─âror situa╚¢ii financiare sunt auditate, s─â numeasc─â sau s─â demit─â auditorul financiar ╚Öi s─â fixeze durata minim─â a contractului de audit financiar;(la 29-06-2007, 
			Lit. b^1) a alin. (2) al art. 111 a fost modificat─â de pct. 10 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)...c)s─â fixeze remunera╚¢ia cuvenit─â pentru exerci╚¢iul ├«n curs membrilor consiliului de administra╚¢ie, respectiv membrilor consiliului de supraveghere, ╚Öi cenzorilor, dac─â nu a fost stabilit─â prin actul constitutiv;(la 01-12-2006, 
